<?php

use JetBrains\PhpStorm\ArrayShape;
use JetBrains\PhpStorm\Internal\LanguageLevelTypeAware;
use JetBrains\PhpStorm\Internal\PhpStormStubsElementAvailable;
use JetBrains\PhpStorm\Pure;

/**
*<div id="function.bzopen" class="refentry"> <div class="refnamediv">  <h1 class="refname">bzopen</h1>  <p class="verinfo">(PHP 4 &gt;= 4.0.4, PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">bzopen</span> &mdash; <span class="dc-title">打开 bzip2 压缩文件</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.bzopen-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>bzopen</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">string</span>|<span class="type" style="color:#EAB766">resource</span></span> <span class="parameter" style="color:#3A95FF">$file</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$mode</span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">resource</span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>  <p class="para rdfs-comment">   <span class="function"><strong style="color:#CC7832">bzopen()</strong></span> 打开一个 bzip2（<var class="filename">.bz2</var>）文件用于读或写。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.bzopen-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">file</span></dt>     <dd>      <p class="para">       待打开的文件的文件名，或者已经存在的资源流。      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">mode</span></dt>     <dd>      <p class="para">       支持 <span>&#039;r&#039;</span>（读）和 <span>&#039;w&#039;</span>（写）模式。       其他任何模式都会导致 <span class="function"><strong style="color:#CC7832">bzopen()</strong></span> 返回 <strong><span>false</span></strong>。      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.bzopen-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   如果打开失败，<span class="function"><strong style="color:#CC7832">bzopen()</strong></span> 会返回 <strong><span>false</span></strong>，否则返回一个指向最新打开文件的指针。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.bzopen-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="example-2114">    <p><strong>示例 #1 <span class="function"><strong style="color:#CC7832">bzopen()</strong></span> 范例</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$file </span><span style="color: #007700">= </span><span style="color: #DD0000">"/tmp/foo.bz2"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$bz </span><span style="color: #007700">= </span><span style="color: #9876AA">bzopen</span><span style="color: #007700">(</span><span style="color: #9876AA">$file</span><span style="color: #007700">, </span><span style="color: #DD0000">"r"</span><span style="color: #007700">) or die(</span><span style="color: #DD0000">"Couldn't open </span><span style="color: #9876AA">$file</span><span style="color: #DD0000"> for reading"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">bzclose</span><span style="color: #007700">(</span><span style="color: #9876AA">$bz</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.bzopen-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link bzclose()} - 关闭一个 bzip2 文件</span></li>   </ul>  </span> </div></div>
 * @return resource|false If the open fails, <b>bzopen</b> returns <b>FALSE</b>, otherwise*/
function bzopen($file, string $mode) {}

/**
*<div id="function.bzread" class="refentry"> <div class="refnamediv">  <h1 class="refname">bzread</h1>  <p class="verinfo">(PHP 4 &gt;= 4.0.4, PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">bzread</span> &mdash; <span class="dc-title">bzip2 文件二进制安全地读取</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.bzread-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>bzread</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$bz</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$length</span><span class="initializer"> = 1024</span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">string</span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>  <p class="para rdfs-comment">   <span class="function"><strong style="color:#CC7832">bzread()</strong></span> 从指定的 bzip2 文件指针中读取数据。  </p>  <p class="para">    读取到 <span class="parameter" style="color:#3A95FF">length</span>（未经压缩的长度）个字节，或者到文件尾，取决于先到哪个。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.bzread-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">bz</span></dt>     <dd>      <p class="para">       文件指针。它必须是有效的并且指向 <span class="function">{@link bzopen()}</span> 成功打开的文件。      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">length</span></dt>     <dd>      <p class="para">       如果没有提供， <span class="function"><strong style="color:#CC7832">bzread()</strong></span> 一次会读入 1024 个字节（未经压缩的长度）。       一次最大可读入 8192 个未压缩的字节。      </p>     </dd>       </dl>  </span>  </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.bzread-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   返回解压的数据，在错误时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.bzread-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="example-2117">    <p><strong>示例 #1 <span class="function"><strong style="color:#CC7832">bzread()</strong></span> 范例</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$file </span><span style="color: #007700">= </span><span style="color: #DD0000">"/tmp/foo.bz2"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$bz </span><span style="color: #007700">= </span><span style="color: #9876AA">bzopen</span><span style="color: #007700">(</span><span style="color: #9876AA">$file</span><span style="color: #007700">, </span><span style="color: #DD0000">"r"</span><span style="color: #007700">) or die(</span><span style="color: #DD0000">"Couldn't open </span><span style="color: #9876AA">$file</span><span style="color: #DD0000">"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$decompressed_file </span><span style="color: #007700">= </span><span style="color: #DD0000">''</span><span style="color: #007700">;<br />while (!</span><span style="color: #9876AA">feof</span><span style="color: #007700">(</span><span style="color: #9876AA">$bz</span><span style="color: #007700">)) {<br />  </span><span style="color: #9876AA">$decompressed_file </span><span style="color: #007700">.= </span><span style="color: #9876AA">bzread</span><span style="color: #007700">(</span><span style="color: #9876AA">$bz</span><span style="color: #007700">, </span><span style="color: #9876AA">4096</span><span style="color: #007700">);<br />}<br /></span><span style="color: #9876AA">bzclose</span><span style="color: #007700">(</span><span style="color: #9876AA">$bz</span><span style="color: #007700">);<br /><br />echo </span><span style="color: #DD0000">"The contents of </span><span style="color: #9876AA">$file</span><span style="color: #DD0000"> are: &lt;br /&gt;\n"</span><span style="color: #007700">;<br />echo </span><span style="color: #9876AA">$decompressed_file</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.bzread-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link bzwrite()} - 二进制安全地写入 bzip2 文件</span></li>    <li class="member"><span class="function">{@link feof()} - 测试文件指针是否到了文件结束的位置</span></li>    <li class="member"><span class="function">{@link bzopen()} - 打开 bzip2 压缩文件</span></li>   </ul>  </span> </div></div>
 * @return string|false the uncompressed data, or <b>FALSE</b> on error.*/
function bzread($bz, int $length = 1024): string|false {}

/**
*<div id="function.bzwrite" class="refentry"> <div class="refnamediv">  <h1 class="refname">bzwrite</h1>  <p class="verinfo">(PHP 4 &gt;= 4.0.4, PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">bzwrite</span> &mdash; <span class="dc-title">二进制安全地写入 bzip2 文件</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.bzwrite-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>bzwrite</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$bz</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$data</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">int</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">$length</span><span class="initializer"> = <strong><span>null</span></strong></span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">int</span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>  <p class="para rdfs-comment">   <span class="function"><strong style="color:#CC7832">bzwrite()</strong></span> 把字符串（string）写入了指定的 bzip2 文件流。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.bzwrite-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">bz</span></dt>     <dd>      <p class="para">       文件指针。它必须是有效的并且指向 <span class="function">{@link bzopen()}</span> 成功打开的文件。      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">data</span></dt>     <dd>      <p class="para">       要写入的数据。      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">length</span></dt>     <dd>      <p class="para">       如果提供了这个参数，将仅仅写入 <span class="parameter" style="color:#3A95FF">length</span>（未压缩）个字节，若 <span class="parameter" style="color:#3A95FF">data</span> 小于该指定的长度则写入全部数据。      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.bzwrite-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   返回写入的数据字节数，错误时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 changelog" id="refsect1-function.bzwrite-changelog">  <h3 class="title">更新日志</h3>  <table class="doctable informaltable">       <thead>     <tr>      <th>版本</th>      <th>说明</th>     </tr>    </thead>    <tbody class="tbody">     <tr>      <td>8.0.0</td>      <td>       <span class="parameter" style="color:#3A95FF">length</span> 现在可为空（nullable）。      </td>     </tr>    </tbody>     </table> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.bzwrite-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="example-2120">    <p><strong>示例 #1 <span class="function"><strong style="color:#CC7832">bzwrite()</strong></span> 范例</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />$str </span><span style="color: #007700">= </span><span style="color: #DD0000">"uncompressed data"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$bz </span><span style="color: #007700">= </span><span style="color: #9876AA">bzopen</span><span style="color: #007700">(</span><span style="color: #DD0000">"/tmp/foo.bz2"</span><span style="color: #007700">, </span><span style="color: #DD0000">"w"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">bzwrite</span><span style="color: #007700">(</span><span style="color: #9876AA">$bz</span><span style="color: #007700">, </span><span style="color: #9876AA">$str</span><span style="color: #007700">, </span><span style="color: #9876AA">strlen</span><span style="color: #007700">(</span><span style="color: #9876AA">$str</span><span style="color: #007700">));<br /></span><span style="color: #9876AA">bzclose</span><span style="color: #007700">(</span><span style="color: #9876AA">$bz</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.bzwrite-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link bzread()} - bzip2 文件二进制安全地读取</span></li>    <li class="member"><span class="function">{@link bzopen()} - 打开 bzip2 压缩文件</span></li>   </ul>  </span> </div></div>
 * @return int|false the number of bytes written, or <b>FALSE</b> on error.*/
function bzwrite($bz, string $data, ?int $length): int|false {}

/**
*<div id="function.bzflush" class="refentry"> <div class="refnamediv">  <h1 class="refname">bzflush</h1>  <p class="verinfo">(PHP 4 &gt;= 4.0.4, PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">bzflush</span> &mdash; <span class="dc-title">什么都不做</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.bzflush-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>bzflush</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$bz</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   该函数应该强制写入 bzip2 文件指针 <span class="parameter" style="color:#3A95FF">bz</span>    的所有写缓冲数据。但在 libbz2 中实现为空函数，因此什么都不做。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.bzflush-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">bz</span></dt>     <dd>      <p class="para">       文件指针。它必须是有效的并且指向 <span class="function">{@link bzopen()}</span> 成功打开的文件。      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.bzflush-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.bzflush-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link bzread()} - bzip2 文件二进制安全地读取</span></li>    <li class="member"><span class="function">{@link bzwrite()} - 二进制安全地写入 bzip2 文件</span></li>   </ul>  </span> </div></div>
 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
function bzflush($bz): bool {}

/**
*<div id="function.bzclose" class="refentry"> <div class="refnamediv">  <h1 class="refname">bzclose</h1>  <p class="verinfo">(PHP 4 &gt;= 4.0.4, PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">bzclose</span> &mdash; <span class="dc-title">关闭一个 bzip2 文件</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.bzclose-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>bzclose</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$bz</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   关闭给出的 bzip2 文件指针。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.bzclose-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">bz</span></dt>     <dd>      <p class="para">       文件指针。它必须是有效的并且指向 <span class="function">{@link bzopen()}</span> 成功打开的文件。      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.bzclose-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.bzclose-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link bzopen()} - 打开 bzip2 压缩文件</span></li>   </ul>  </span> </div></div>
 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
function bzclose($bz): bool {}

/**
