    ) {}

/**
*<div id="domnode.replacechild" class="refentry"> <div class="refnamediv">  <h1 class="refname">DOMNode::replaceChild</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">DOMNode::replaceChild</span> &mdash; <span class="dc-title">   Replaces a child  </span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-domnode.replacechild-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>DOMNode::replaceChild</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.domnode.php" class="type DOMNode" style="color:#EAB766">DOMNode</a></span> <span class="parameter" style="color:#3A95FF">$node</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.domnode.php" class="type DOMNode" style="color:#EAB766">DOMNode</a></span> <span class="parameter" style="color:#3A95FF">$child</span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.domnode.php" class="type DOMNode" style="color:#EAB766">DOMNode</a></span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>  <p class="para rdfs-comment">   This function replaces the child <span class="parameter" style="color:#3A95FF">child</span>    with the passed new node. If the <span class="parameter" style="color:#3A95FF">node</span> is already a child it    will not be added a second time. If the replacement succeeds the    old node is returned.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-domnode.replacechild-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">node</span></dt>     <dd>      <p class="para">       The new node. It must be a member of the target document, i.e.       created by one of the DOMDocument-&gt;createXXX() methods or imported in       the document by {@link DOMDocument::importNode}.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">child</span></dt>     <dd>      <p class="para">       The old node.      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-domnode.replacechild-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   The old node or <strong><span>false</span></strong> if an error occur.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 errors" id="refsect1-domnode.replacechild-errors">  <h3 class="title">错误／异常</h3>  <p class="para">   <dl>         <dt><strong><span>DOM_NO_MODIFICATION_ALLOWED_ERR</span></strong></dt>     <dd>      <p class="para">       Raised if this node is readonly or if the previous parent of the node       being inserted is readonly.      </p>     </dd>             <dt><strong><span>DOM_HIERARCHY_REQUEST_ERR</span></strong></dt>     <dd>      <p class="para">       Raised if this node is of a type that does not allow children of the        type of the <span class="parameter" style="color:#3A95FF">node</span> node, or if the node to        put in is one of this node&#039;s ancestors or this node itself.      </p>     </dd>             <dt><strong><span>DOM_WRONG_DOCUMENT_ERR</span></strong></dt>     <dd>      <p class="para">       Raised if <span class="parameter" style="color:#3A95FF">node</span> was created from a different        document than the one that created this node.      </p>     </dd>             <dt><strong><span>DOM_NOT_FOUND</span></strong></dt>     <dd>      <p class="para">       Raised if <span class="parameter" style="color:#3A95FF">child</span> is not a child of this node.      </p>     </dd>       </dl>  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-domnode.replacechild-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="methodname" style="color:#CC7832">{@link DOMChildNode::replaceWith()} - Replaces the node with new nodes</span></li>    <li class="member"><span class="methodname" style="color:#CC7832">{@link DOMNode::appendChild()} - Adds new child at the end of the children</span></li>    <li class="member"><span class="methodname" style="color:#CC7832">{@link DOMNode::removeChild()} - Removes child from list of children</span></li>   </ul>  </span> </div></div>
     * @return DOMNode|false The old node or false if an error occur.*/
    public function replaceChild(DOMNode $node, DOMNode $child) {}

/**
*<div id="domnode.removechild" class="refentry"> <div class="refnamediv">  <h1 class="refname">DOMNode::removeChild</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">DOMNode::removeChild</span> &mdash; <span class="dc-title">   Removes child from list of children  </span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-domnode.removechild-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>DOMNode::removeChild</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.domnode.php" class="type DOMNode" style="color:#EAB766">DOMNode</a></span> <span class="parameter" style="color:#3A95FF">$child</span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.domnode.php" class="type DOMNode" style="color:#EAB766">DOMNode</a></span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>  <p class="para rdfs-comment">   This functions removes a child from a list of children.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-domnode.removechild-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">child</span></dt>     <dd>      <p class="para">       The removed child.      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-domnode.removechild-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   If the child could be removed the function returns the old child or <strong><span>false</span></strong> on error.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 errors" id="refsect1-domnode.removechild-errors">  <h3 class="title">错误／异常</h3>  <p class="para">   <dl>         <dt><strong><span>DOM_NO_MODIFICATION_ALLOWED_ERR</span></strong></dt>     <dd>      <p class="para">       Raised if this node is readonly.      </p>     </dd>             <dt><strong><span>DOM_NOT_FOUND</span></strong></dt>     <dd>      <p class="para">       Raised if <span class="parameter" style="color:#3A95FF">child</span> is not a child of this node.      </p>     </dd>       </dl>  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-domnode.removechild-examples">  <h3 class="title">示例</h3>  <span>   The following example will delete the <span>chapter</span> element of our XML document.   <div class="example" id="">    <p><strong>示例 #1 Removing a child</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$doc </span><span style="color: #007700">= new </span><span style="color: #9876AA">DOMDocument</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$doc</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">load</span><span style="color: #007700">(</span><span style="color: #DD0000">'book.xml'</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$book </span><span style="color: #007700">= </span><span style="color: #9876AA">$doc</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">documentElement</span><span style="color: #007700">;<br /><br /></span><span style="color: #FF8000">// we retrieve the chapter and remove it from the book<br /></span><span style="color: #9876AA">$chapter </span><span style="color: #007700">= </span><span style="color: #9876AA">$book</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">getElementsByTagName</span><span style="color: #007700">(</span><span style="color: #DD0000">'chapter'</span><span style="color: #007700">)-&gt;</span><span style="color: #9876AA">item</span><span style="color: #007700">(</span><span style="color: #9876AA">0</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$oldchapter </span><span style="color: #007700">= </span><span style="color: #9876AA">$book</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">removeChild</span><span style="color: #007700">(</span><span style="color: #9876AA">$chapter</span><span style="color: #007700">);<br /><br />echo </span><span style="color: #9876AA">$doc</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">saveXML</span><span style="color: #007700">();<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>&lt;?xml version=&quot;1.0&quot; encoding=&quot;utf-8&quot;?&gt;<br>&lt;!DOCTYPE book PUBLIC &quot;-//OASIS//DTD DocBook XML V4.1.2//EN&quot; <br>          &quot;http://www.oasis-open.org/docbook/xml/4.1.2/docbookx.dtd&quot;&gt;<br>&lt;book id=&quot;listing&quot;&gt;<br> &lt;title&gt;My lists&lt;/title&gt;<br> <br>&lt;/book&gt;<br></span></div>    </div>   </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-domnode.removechild-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="methodname" style="color:#CC7832">{@link DOMChildNode::remove()} - Removes the node</span></li>    <li class="member"><span class="methodname" style="color:#CC7832">{@link DOMNode::appendChild()} - Adds new child at the end of the children</span></li>    <li class="member"><span class="methodname" style="color:#CC7832">{@link DOMNode::replaceChild()} - Replaces a child</span></li>   </ul>  </span> </div></div>
     * @return DOMNode If the child could be removed the functions returns the old child.*/
    public function removeChild(DOMNode $child) {}

/**
*<div id="domnode.appendchild" class="refentry"> <div class="refnamediv">  <h1 class="refname">DOMNode::appendChild</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">DOMNode::appendChild</span> &mdash; <span class="dc-title">   Adds new child at the end of the children  </span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-domnode.appendchild-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>DOMNode::appendChild</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.domnode.php" class="type DOMNode" style="color:#EAB766">DOMNode</a></span> <span class="parameter" style="color:#3A95FF">$node</span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.domnode.php" class="type DOMNode" style="color:#EAB766">DOMNode</a></span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>  <p class="para rdfs-comment">   This function appends a child to an existing list of children or creates   a new list of children. The child can be created with e.g.    <span class="methodname" style="color:#CC7832">{@link DOMDocument::createElement()}</span>,   <span class="methodname" style="color:#CC7832">{@link DOMDocument::createTextNode()}</span> etc. or simply by using   any other node.  </p>  <p class="para">   When using an existing node it will be moved.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-domnode.appendchild-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">node</span></dt>     <dd>      <p class="para">       The appended child.      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-domnode.appendchild-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   The node added or <strong><span>false</span></strong> on error.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 errors" id="refsect1-domnode.appendchild-errors">  <h3 class="title">错误／异常</h3>  <p class="para">   <dl>         <dt><strong><span>DOM_NO_MODIFICATION_ALLOWED_ERR</span></strong></dt>     <dd>      <p class="para">       Raised if this node is readonly or if the previous parent of the node       being inserted is readonly.      </p>     </dd>             <dt><strong><span>DOM_HIERARCHY_REQUEST_ERR</span></strong></dt>     <dd>      <p class="para">       Raised if this node is of a type that does not allow children of the        type of the <span class="parameter" style="color:#3A95FF">node</span> node, or if the node to        append is one of this node&#039;s ancestors or this node itself.      </p>     </dd>             <dt><strong><span>DOM_WRONG_DOCUMENT_ERR</span></strong></dt>     <dd>      <p class="para">       Raised if <span class="parameter" style="color:#3A95FF">node</span> was created from a different        document than the one that created this node.      </p>     </dd>       </dl>  </p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-domnode.appendchild-examples">  <h3 class="title">示例</h3>  <span>   The following example will add a new element node to a fresh document.   <div class="example" id="">    <p><strong>示例 #1 Adding a child</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$doc </span><span style="color: #007700">= new </span><span style="color: #9876AA">DOMDocument</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">$node </span><span style="color: #007700">= </span><span style="color: #9876AA">$doc</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">createElement</span><span style="color: #007700">(</span><span style="color: #DD0000">"para"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$newnode </span><span style="color: #007700">= </span><span style="color: #9876AA">$doc</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">appendChild</span><span style="color: #007700">(</span><span style="color: #9876AA">$node</span><span style="color: #007700">);<br /><br />echo </span><span style="color: #9876AA">$doc</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">saveXML</span><span style="color: #007700">();<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span>    <p class="para">   <div class="example" id="">    <p><strong>示例 #2 Nested children</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$doc </span><span style="color: #007700">= new </span><span style="color: #9876AA">DOMDocument</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">$headNode </span><span style="color: #007700">= </span><span style="color: #9876AA">$doc</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">createElement</span><span style="color: #007700">(</span><span style="color: #DD0000">"head"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$doc</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">appendChild</span><span style="color: #007700">(</span><span style="color: #9876AA">$headNode</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$titleNode </span><span style="color: #007700">= </span><span style="color: #9876AA">$doc</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">createElement</span><span style="color: #007700">(</span><span style="color: #DD0000">"title"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$headNode</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">appendChild</span><span style="color: #007700">(</span><span style="color: #9876AA">$titleNode</span><span style="color: #007700">);<br /><br />echo </span><span style="color: #9876AA">$doc</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">saveXML</span><span style="color: #007700">();<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-domnode.appendchild-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="methodname" style="color:#CC7832">{@link DOMChildNode::after()} - Adds nodes after the node</span></li>    <li class="member"><span class="methodname" style="color:#CC7832">{@link DOMNode::insertBefore()} - Adds a new child before a reference node</span></li>    <li class="member"><span class="methodname" style="color:#CC7832">{@link DOMNode::removeChild()} - Removes child from list of children</span></li>    <li class="member"><span class="methodname" style="color:#CC7832">{@link DOMNode::replaceChild()} - Replaces a child</span></li>   </ul>  </span> </div></div>
     * @return DOMNode The node added.*/
    public function appendChild(DOMNode $node) {}

/**
*<div id="domnode.haschildnodes" class="refentry"> <div class="refnamediv">  <h1 class="refname">DOMNode::hasChildNodes</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">DOMNode::hasChildNodes</span> &mdash; <span class="dc-title">   Checks if node has children  </span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-domnode.haschildnodes-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>DOMNode::hasChildNodes</strong></span>(): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   This function checks if the node has children.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-domnode.haschildnodes-parameters">  <h3 class="title">参数</h3>  <span>此函数没有参数。</span> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-domnode.haschildnodes-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-domnode.haschildnodes-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="methodname" style="color:#CC7832">{@link DOMNode::hasAttributes()} - Checks if node has attributes</span></li>   </ul>  </span> </div></div>
     * @return bool true on success or false on failure.*/
    public function hasChildNodes(): bool {}

