    public function finfo($options, $arg) {}

/**
*<div id="finfo.set-flags" class="refentry"> <div class="refnamediv">  <h1 class="refname">finfo::set_flags</h1>  <p class="verinfo">(PHP &gt;= 5.3.0, PHP 7, PHP 8, PECL fileinfo &gt;= 0.1.0)</p><p class="refpurpose"><span class="refname">finfo::set_flags</span> &mdash; <span class="dc-title">别名 {@link finfo_set_flags()}</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-finfo.set-flags-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>finfo::set_flags</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$flags</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   此函数是该函数的别名： {@link finfo_set_flags()}  </p> </div></div>
