    public function getcolor($as_array = null, $normalize_array = null) {}

/**
*<div id="gmagickpixel.getcolorcount" class="refentry"> <div class="refnamediv">  <h1 class="refname">GmagickPixel::getcolorcount</h1>  <p class="verinfo">(PECL gmagick &gt;= Unknown)</p><p class="refpurpose"><span class="refname">GmagickPixel::getcolorcount</span> &mdash; <span class="dc-title">Returns the color count associated with this color</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-gmagickpixel.getcolorcount-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>GmagickPixel::getcolorcount</strong></span>(): <span class="type" style="color:#EAB766">int</span></div>  <p class="para rdfs-comment">   Returns the color count associated with this color.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-gmagickpixel.getcolorcount-parameters">  <h3 class="title">参数</h3>  <span>此函数没有参数。</span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-gmagickpixel.getcolorcount-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   Returns the color count as an integer on success, throws <span class="classname"><strong class="classname">GmagickPixelException</strong></span> on failure.   </p> </div></div>
