<?php

// Start of gmp v.
use JetBrains\PhpStorm\ArrayShape;
use JetBrains\PhpStorm\Deprecated;
use JetBrains\PhpStorm\Pure;

/**
*<div id="function.gmp-init" class="refentry"> <div class="refnamediv">  <h1 class="refname">gmp_init</h1>  <p class="verinfo">(PHP 4 &gt;= 4.0.4, PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">gmp_init</span> &mdash; <span class="dc-title">Create GMP number</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.gmp-init-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>gmp_init</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">int</span>|<span class="type" style="color:#EAB766">string</span></span> <span class="parameter" style="color:#3A95FF">$num</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$base</span><span class="initializer"> = 0</span></span>): <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.gmp.php" class="type GMP" style="color:#EAB766">GMP</a></span></div>  <p class="para rdfs-comment">   Creates a GMP number from an integer or string.   </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.gmp-init-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">num</span></dt>     <dd>      <p class="para">       An integer or a string. The string representation can be decimal,        hexadecimal or octal.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">base</span></dt>     <dd>      <p class="para">       The base.      </p>      <p class="para">       The base may vary from 2 to 62. If base is 0 (default value), the       actual base is determined from the leading characters: if the first       two characters are <span>0x</span> or <span>0X</span>,       hexadecimal is assumed, if the first two characters are <span>0b</span> or <span>0B</span>,       binary is assumed, otherwise if the first character is <span>0</span>,       octal is assumed, otherwise decimal is assumed.       For bases up to 36, case is ignored; upper-case and lower-case letters have the same value.       For bases 37 to 62, upper-case letter represent the usual 10 to 35 while lower-case letter represent 36 to 61.      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.gmp-init-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   <span class="classname"><a href="https://www.php.net/manual/zh/class.gmp.php" class="classname">GMP</a></span> 对象。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.gmp-init-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="">    <p><strong>示例 #1 Creating GMP number</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />$a </span><span style="color: #007700">= </span><span style="color: #9876AA">gmp_init</span><span style="color: #007700">(</span><span style="color: #9876AA">123456</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$b </span><span style="color: #007700">= </span><span style="color: #9876AA">gmp_init</span><span style="color: #007700">(</span><span style="color: #DD0000">"0xFFFFDEBACDFEDF7200"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.gmp-init-notes">  <h3 class="title">注释</h3>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    It is not necessary to call this function in order to use integers or    strings in place of GMP numbers in GMP functions (such as with    <span class="function">{@link gmp_add()}</span>). Function arguments are automatically    converted to GMP numbers, if such conversion is possible and needed, using    the same rules as <span class="function"><strong style="color:#CC7832">gmp_init()</strong></span>.   </p>  </p></blockquote> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.gmp-init-seealso">  <h3 class="title">参见</h3>  <ul class="simplelist">   <li class="member"><span class="methodname" style="color:#CC7832">{@link GMP::__construct()} - Create GMP number</span></li>  </ul> </div> </div>
 * @return resource|GMP A GMP number resource.*/
function gmp_init(string|int $num, int $base = 0): GMP {}

/**
*<div id="function.gmp-intval" class="refentry"> <div class="refnamediv">  <h1 class="refname">gmp_intval</h1>  <p class="verinfo">(PHP 4 &gt;= 4.0.4, PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">gmp_intval</span> &mdash; <span class="dc-title">Convert GMP number to integer</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.gmp-intval-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>gmp_intval</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.gmp.php" class="type GMP" style="color:#EAB766">GMP</a></span>|<span class="type" style="color:#EAB766">int</span>|<span class="type" style="color:#EAB766">string</span></span> <span class="parameter" style="color:#3A95FF">$num</span></span>): <span class="type" style="color:#EAB766">int</span></div>  <p class="para rdfs-comment">   This function converts GMP number into native PHP <span class="type" style="color:#EAB766">int</span>s.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.gmp-intval-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">num</span></dt>     <dd>      <p class="para"><span class="classname"><a href="https://www.php.net/manual/zh/class.gmp.php" class="classname">GMP</a></span> 对象或 <span class="type" style="color:#EAB766">int</span> ，或数字<span class="type" style="color:#EAB766">string</span>。</p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.gmp-intval-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   The <span class="type" style="color:#EAB766">int</span> value of <span class="parameter" style="color:#3A95FF">num</span>.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.gmp-intval-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="">    <p><strong>示例 #1 <span class="function"><strong style="color:#CC7832">gmp_intval()</strong></span> example</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /></span><span style="color: #FF8000">// displays correct result<br /></span><span style="color: #007700">echo </span><span style="color: #9876AA">gmp_intval</span><span style="color: #007700">(</span><span style="color: #DD0000">"2147483647"</span><span style="color: #007700">) . </span><span style="color: #DD0000">"\n"</span><span style="color: #007700">;<br /><br /></span><span style="color: #FF8000">// displays wrong result, above PHP integer limit<br /></span><span style="color: #007700">echo </span><span style="color: #9876AA">gmp_intval</span><span style="color: #007700">(</span><span style="color: #DD0000">"2147483648"</span><span style="color: #007700">) . </span><span style="color: #DD0000">"\n"</span><span style="color: #007700">;<br /><br /></span><span style="color: #FF8000">// displays correct result<br /></span><span style="color: #007700">echo </span><span style="color: #9876AA">gmp_strval</span><span style="color: #007700">(</span><span style="color: #DD0000">"2147483648"</span><span style="color: #007700">) . </span><span style="color: #DD0000">"\n"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>2147483647<br>2147483647<br>2147483648<br></span></div>    </div>   </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.gmp-intval-notes">  <h3 class="title">注释</h3>  <div class="warning"><strong class="warning">警告</strong>   <p class="simpara">    This function returns a useful result only if the number actually    fits the PHP integer (i.e., signed long type). To simply print the GMP    number, use <span class="function">{@link gmp_strval()}</span>.   </p>  </div> </div></div>
 * @return int An integer value of <i>gmpnumber</i>.*/
function gmp_intval(GMP|string|int $num): int {}

/**
*<div id="function.gmp-random-seed" class="refentry"> <div class="refnamediv">  <h1 class="refname">gmp_random_seed</h1>  <p class="verinfo">(PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">gmp_random_seed</span> &mdash; <span class="dc-title">Sets the RNG seed</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.gmp-random-seed-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>gmp_random_seed</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.gmp.php" class="type GMP" style="color:#EAB766">GMP</a></span>|<span class="type" style="color:#EAB766">int</span>|<span class="type" style="color:#EAB766">string</span></span> <span class="parameter" style="color:#3A95FF">$seed</span></span>): <span class="type" style="color:#EAB766"><span class="type void" style="color:#EAB766">void</span></span></div> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.gmp-random-seed-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">seed</span></dt>     <dd>      <p class="para">       The seed to be set for the <span class="function">{@link gmp_random()}</span>,       <span class="function">{@link gmp_random_bits()}</span>, and       <span class="function">{@link gmp_random_range()}</span> functions.      </p>      <p class="para"><span class="classname"><a href="https://www.php.net/manual/zh/class.gmp.php" class="classname">GMP</a></span> 对象或 <span class="type" style="color:#EAB766">int</span> ，或数字<span class="type" style="color:#EAB766">string</span>。</p>      </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.gmp-random-seed-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   没有返回值。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 errors" id="refsect1-function.gmp-random-seed-errors">  <h3 class="title">错误／异常</h3>  <p class="para">   Throws a <span class="classname"><a href="https://www.php.net/manual/zh/class.valueerror.php" class="classname">ValueError</a></span> if <span class="parameter" style="color:#3A95FF">seed</span>   is invalid.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 changelog" id="refsect1-function.gmp-random-seed-changelog">  <h3 class="title">更新日志</h3>  <table class="doctable informaltable">       <thead>     <tr>      <th>版本</th>      <th>说明</th>     </tr>    </thead>    <tbody class="tbody">     <tr>      <td>8.0.0</td>      <td>       If <span class="parameter" style="color:#3A95FF">seed</span> is invalid, <span class="function"><strong style="color:#CC7832">gmp_random_seed()</strong></span>       now throws a <span class="classname"><a href="https://www.php.net/manual/zh/class.valueerror.php" class="classname">ValueError</a></span>.       Previously it emitted an <strong><span>E_WARNING</span></strong> and returned <strong><span>false</span></strong>.      </td>     </tr>    </tbody>     </table> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.gmp-random-seed-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="">    <p><strong>示例 #1 <span class="function"><strong style="color:#CC7832">gmp_random_seed()</strong></span> example</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /></span><span style="color: #FF8000">// set the seed<br /></span><span style="color: #9876AA">gmp_random_seed</span><span style="color: #007700">(</span><span style="color: #9876AA">100</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">gmp_strval</span><span style="color: #007700">(</span><span style="color: #9876AA">gmp_random</span><span style="color: #007700">(</span><span style="color: #9876AA">1</span><span style="color: #007700">)));<br /><br /></span><span style="color: #FF8000">// set the seed to something else<br /></span><span style="color: #9876AA">gmp_random_seed</span><span style="color: #007700">(</span><span style="color: #9876AA">gmp_init</span><span style="color: #007700">(-</span><span style="color: #9876AA">100</span><span style="color: #007700">));<br /><br /></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">gmp_strval</span><span style="color: #007700">(</span><span style="color: #9876AA">gmp_random_bits</span><span style="color: #007700">(</span><span style="color: #9876AA">10</span><span style="color: #007700">)));<br /><br /></span><span style="color: #FF8000">// set the seed to something invalid<br /></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">gmp_random_seed</span><span style="color: #007700">(</span><span style="color: #DD0000">'not a number'</span><span style="color: #007700">));</span></span></span></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>string(20) &quot;15370156633245019617&quot;<br>string(3) &quot;683&quot;<br><br>Warning: gmp_random_seed(): Unable to convert variable to GMP - string is not an integer in %s on line %d<br>bool(false)<br></span></div>    </div>   </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.gmp-random-seed-seealso">  <h3 class="title">参见</h3>  <ul class="simplelist">   <li class="member"><span class="function">{@link gmp_init()} - Create GMP number</span></li>   <li class="member"><span class="function">{@link gmp_random()} - Random number</span></li>   <li class="member"><span class="function">{@link gmp_random_bits()} - Random number</span></li>   <li class="member"><span class="function">{@link gmp_random_range()} - Get a uniformly selected integer</span></li>  </ul> </div></div>
 * @return void|null|false Returns NULL on success.*/
function gmp_random_seed(GMP|string|int $seed): void {}
/**
*<div id="function.gmp-strval" class="refentry"> <div class="refnamediv">  <h1 class="refname">gmp_strval</h1>  <p class="verinfo">(PHP 4 &gt;= 4.0.4, PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">gmp_strval</span> &mdash; <span class="dc-title">Convert GMP number to string</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.gmp-strval-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>gmp_strval</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.gmp.php" class="type GMP" style="color:#EAB766">GMP</a></span>|<span class="type" style="color:#EAB766">int</span>|<span class="type" style="color:#EAB766">string</span></span> <span class="parameter" style="color:#3A95FF">$num</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$base</span><span class="initializer"> = 10</span></span>): <span class="type" style="color:#EAB766">string</span></div>  <p class="para rdfs-comment">   Convert GMP number to string representation in base   <span class="parameter" style="color:#3A95FF">base</span>. The default base is 10.    </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.gmp-strval-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">num</span></dt>     <dd>      <p class="para">       The GMP number that will be converted to a string.      </p>      <p class="para"><span class="classname"><a href="https://www.php.net/manual/zh/class.gmp.php" class="classname">GMP</a></span> 对象或 <span class="type" style="color:#EAB766">int</span> ，或数字<span class="type" style="color:#EAB766">string</span>。</p>      </dd>             <dt><span class="parameter" style="color:#3A95FF">base</span></dt>     <dd>      <p class="para">       The base of the returned number. The default base is 10.        Allowed values for the base are from 2 to 62 and -2 to -36.      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.gmp-strval-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   The number, as a <span class="type" style="color:#EAB766">string</span>.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.gmp-strval-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="">    <p><strong>示例 #1 Converting a GMP number to a string</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />$a </span><span style="color: #007700">= </span><span style="color: #9876AA">gmp_init</span><span style="color: #007700">(</span><span style="color: #DD0000">"0x41682179fbf5"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">printf</span><span style="color: #007700">(</span><span style="color: #DD0000">"Decimal: %s, 36-based: %s"</span><span style="color: #007700">, </span><span style="color: #9876AA">gmp_strval</span><span style="color: #007700">(</span><span style="color: #9876AA">$a</span><span style="color: #007700">), </span><span style="color: #9876AA">gmp_strval</span><span style="color: #007700">(</span><span style="color: #9876AA">$a</span><span style="color: #007700">,</span><span style="color: #9876AA">36</span><span style="color: #007700">));<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span> </div></div>
 * @return string The number, as a string.*/
function gmp_strval(GMP|string|int $num, int $base = 10): string {}

/**
*<div id="function.gmp-add" class="refentry"> <div class="refnamediv">  <h1 class="refname">gmp_add</h1>  <p class="verinfo">(PHP 4 &gt;= 4.0.4, PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">gmp_add</span> &mdash; <span class="dc-title">Add numbers</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.gmp-add-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>gmp_add</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.gmp.php" class="type GMP" style="color:#EAB766">GMP</a></span>|<span class="type" style="color:#EAB766">int</span>|<span class="type" style="color:#EAB766">string</span></span> <span class="parameter" style="color:#3A95FF">$num1</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.gmp.php" class="type GMP" style="color:#EAB766">GMP</a></span>|<span class="type" style="color:#EAB766">int</span>|<span class="type" style="color:#EAB766">string</span></span> <span class="parameter" style="color:#3A95FF">$num2</span></span>): <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.gmp.php" class="type GMP" style="color:#EAB766">GMP</a></span></div>  <p class="para rdfs-comment">   Add two numbers.   </p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.gmp-add-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">num1</span></dt>     <dd>      <p class="para">       The first summand (augent).      </p>      <p class="para"><span class="classname"><a href="https://www.php.net/manual/zh/class.gmp.php" class="classname">GMP</a></span> 对象或 <span class="type" style="color:#EAB766">int</span> ，或数字<span class="type" style="color:#EAB766">string</span>。</p>      </dd>             <dt><span class="parameter" style="color:#3A95FF">num2</span></dt>     <dd>      <p class="para">       The second summand (addend).      </p>      <p class="para"><span class="classname"><a href="https://www.php.net/manual/zh/class.gmp.php" class="classname">GMP</a></span> 对象或 <span class="type" style="color:#EAB766">int</span> ，或数字<span class="type" style="color:#EAB766">string</span>。</p>      </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.gmp-add-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   A GMP number representing the sum of the arguments.  </p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.gmp-add-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="">    <p><strong>示例 #1 <span class="function"><strong style="color:#CC7832">gmp_add()</strong></span> example</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />$sum </span><span style="color: #007700">= </span><span style="color: #9876AA">gmp_add</span><span style="color: #007700">(</span><span style="color: #DD0000">"123456789012345"</span><span style="color: #007700">, </span><span style="color: #DD0000">"76543210987655"</span><span style="color: #007700">);<br />echo </span><span style="color: #9876AA">gmp_strval</span><span style="color: #007700">(</span><span style="color: #9876AA">$sum</span><span style="color: #007700">) . </span><span style="color: #DD0000">"\n"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>200000000000000<br></span></div>    </div>   </div>  </span> </div> </div>
 * @return resource|GMP A GMP number representing the sum of the arguments.*/
function gmp_add(GMP|string|int $num1, GMP|string|int $num2): GMP {}

/**
*<div id="function.gmp-sub" class="refentry"> <div class="refnamediv">  <h1 class="refname">gmp_sub</h1>  <p class="verinfo">(PHP 4 &gt;= 4.0.4, PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">gmp_sub</span> &mdash; <span class="dc-title">Subtract numbers</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.gmp-sub-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>gmp_sub</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.gmp.php" class="type GMP" style="color:#EAB766">GMP</a></span>|<span class="type" style="color:#EAB766">int</span>|<span class="type" style="color:#EAB766">string</span></span> <span class="parameter" style="color:#3A95FF">$num1</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.gmp.php" class="type GMP" style="color:#EAB766">GMP</a></span>|<span class="type" style="color:#EAB766">int</span>|<span class="type" style="color:#EAB766">string</span></span> <span class="parameter" style="color:#3A95FF">$num2</span></span>): <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.gmp.php" class="type GMP" style="color:#EAB766">GMP</a></span></div>  <p class="para rdfs-comment">   Subtracts <span class="parameter" style="color:#3A95FF">num2</span> from <span class="parameter" style="color:#3A95FF">num1</span>   and returns the result.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.gmp-sub-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">num1</span></dt>     <dd>      <p class="para">       The number being subtracted from.      </p>      <p class="para"><span class="classname"><a href="https://www.php.net/manual/zh/class.gmp.php" class="classname">GMP</a></span> 对象或 <span class="type" style="color:#EAB766">int</span> ，或数字<span class="type" style="color:#EAB766">string</span>。</p>      </dd>             <dt><span class="parameter" style="color:#3A95FF">num2</span></dt>     <dd>      <p class="para">       The number subtracted from <span class="parameter" style="color:#3A95FF">num1</span>.      </p>      <p class="para"><span class="classname"><a href="https://www.php.net/manual/zh/class.gmp.php" class="classname">GMP</a></span> 对象或 <span class="type" style="color:#EAB766">int</span> ，或数字<span class="type" style="color:#EAB766">string</span>。</p>      </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.gmp-sub-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   <span class="classname"><a href="https://www.php.net/manual/zh/class.gmp.php" class="classname">GMP</a></span> 对象。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.gmp-sub-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="">    <p><strong>示例 #1 <span class="function"><strong style="color:#CC7832">gmp_sub()</strong></span> example</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />$sub </span><span style="color: #007700">= </span><span style="color: #9876AA">gmp_sub</span><span style="color: #007700">(</span><span style="color: #DD0000">"281474976710656"</span><span style="color: #007700">, </span><span style="color: #DD0000">"4294967296"</span><span style="color: #007700">); </span><span style="color: #FF8000">// 2^48 - 2^32<br /></span><span style="color: #007700">echo </span><span style="color: #9876AA">gmp_strval</span><span style="color: #007700">(</span><span style="color: #9876AA">$sub</span><span style="color: #007700">) . </span><span style="color: #DD0000">"\n"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>281470681743360<br></span></div>    </div>   </div>  </span> </div></div>
