<?php

// Start of ibm_db2 v.1.6.0

/**
*<div id="function.db2-connect" class="refentry"> <div class="refnamediv">  <h1 class="refname">db2_connect</h1>  <p class="verinfo">(PECL ibm_db2 &gt;= 1.0.0)</p><p class="refpurpose"><span class="refname">db2_connect</span> &mdash; <span class="dc-title">   Returns a connection to a database  </span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.db2-connect-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>db2_connect</strong></span>(<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$database</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">string</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">$username</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">string</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">$password</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">array</span> <span class="parameter" style="color:#3A95FF">$options</span><span class="initializer"> = []</span></span><br>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">resource</span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>  <p class="para rdfs-comment">   Creates a new connection to an IBM DB2 Universal Database, IBM Cloudscape,   or Apache Derby database.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.db2-connect-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">database</span></dt>     <dd>      <p class="para">       For a cataloged connection to a database, <span class="parameter" style="color:#3A95FF">database</span>       represents the database alias in the DB2 client catalog.      </p>      <p class="para">       For an uncataloged connection to a database,       <span class="parameter" style="color:#3A95FF">database</span> represents a complete connection       string in the following format:       <span>DATABASE=<code class="parameter" style="color:#9070A1">database</code>;HOSTNAME=<code class="parameter" style="color:#9070A1">hostname</code>;PORT=<code class="parameter" style="color:#9070A1">port</code>;PROTOCOL=TCPIP;UID=<code class="parameter" style="color:#9070A1">username</code>;PWD=<code class="parameter" style="color:#9070A1">password</code>;</span>       where the parameters represent the following values:       <dl>                 <dt><span class="parameter" style="color:#3A95FF">database</span></dt>         <dd>          <p class="para">           The name of the database.          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">hostname</span></dt>         <dd>          <p class="para">           The hostname or IP address of the database server.          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">port</span></dt>         <dd>          <p class="para">           The TCP/IP port on which the database is listening for           requests.          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">username</span></dt>         <dd>          <p class="para">           The username with which you are connecting to the           database.          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">password</span></dt>         <dd>          <p class="para">           The password with which you are connecting to the database.          </p>         </dd>               </dl>      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">username</span></dt>     <dd>      <p class="para">       The username with which you are connecting to the database.      </p>      <p class="para">       For uncataloged connections, you must pass a <strong><span>null</span></strong> value or empty       string.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">password</span></dt>     <dd>      <p class="para">       The password with which you are connecting to the database.      </p>      <p class="para">       For uncataloged connections, you must pass a <strong><span>null</span></strong> value or empty       string.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">options</span></dt>     <dd>      <p class="para">       An associative array of connection options that affect the behavior       of the connection, where valid array keys include:       <dl>                 <dt><span class="parameter" style="color:#3A95FF">autocommit</span></dt>         <dd>          <p class="para">           Passing the <span>DB2_AUTOCOMMIT_ON</span> value turns           autocommit on for this connection handle.          </p>          <p class="para">           Passing the <span>DB2_AUTOCOMMIT_OFF</span> value turns           autocommit off for this connection handle.          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">DB2_ATTR_CASE</span></dt>         <dd>          <p class="para">           Passing the <span>DB2_CASE_NATURAL</span> value specifies           that column names are returned in natural case.          </p>          <p class="para">           Passing the <span>DB2_CASE_LOWER</span> value specifies           that column names are returned in lower case.          </p>          <p class="para">           Passing the <span>DB2_CASE_UPPER</span> value specifies           that column names are returned in upper case.          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">CURSOR</span></dt>         <dd>          <p class="para">           Passing the <span>DB2_FORWARD_ONLY</span> value specifies a           forward-only cursor for a statement resource. This is the default           cursor type and is supported on all database servers.          </p>          <p class="para">           Passing the <span>DB2_SCROLLABLE</span> value specifies a           scrollable cursor for a statement resource. This mode enables           random access to rows in a result set, but currently is supported           only by IBM DB2 Universal Database.          </p>         </dd>               </dl>      </p>      <p class="para">       The following new option is available in ibm_db2 version 1.7.0 and later.       <dl>                 <dt><span class="parameter" style="color:#3A95FF">trustedcontext</span></dt>         <dd>          <p class="para">           Passing the DB2_TRUSTED_CONTEXT_ENABLE value turns trusted context           on for this connection handle. This parameter cannot be set using           <span class="function">{@link db2_set_option()}</span>.          </p>          <p class="para">           This key works only if the database is cataloged (even if the           database is local), or if you specify the full DSN when you create           the connection.          </p>          <p class="para">           To catalog the database, use following commands:          </p>          <p class="para">           <span>db2 catalog tcpip node loopback remote &lt;SERVERNAME&gt; server &lt;SERVICENAME&gt;<br>db2 catalog database &lt;LOCALDBNAME&gt; as &lt;REMOTEDBNAME&gt; at node loopback<br>db2 &quot;update dbm cfg using svcename &lt;SERVICENAME&gt;&quot;<br>db2set DB2COMM=TCPIP</span>          </p>         </dd>              </dl>      </p>      <p class="para">       The following new i5/OS options are available in ibm_db2 version 1.5.1       and later.       <dl>                 <dt><span class="parameter" style="color:#3A95FF">i5_lib</span></dt>         <dd>          <p class="para">           A character value that indicates the default library that will be           used for resolving unqualified file references. This is not valid           if the connection is using system naming mode.          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">i5_naming</span></dt>         <dd>          <p class="para">           <span>DB2_I5_NAMING_ON</span> value turns on DB2 UDB CLI iSeries           system naming mode. Files are qualified using the slash (/) delimiter.           Unqualified files are resolved using the library list for the job.          </p>          <p class="para">           <span>DB2_I5_NAMING_OFF</span> value turns off DB2 UDB CLI default           naming mode, which is SQL naming. Files are qualified using the period (.)           delimiter. Unqualified files are resolved using either the default library           or the current user ID.          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">i5_commit</span></dt>         <dd>          <p class="para">           The <span class="parameter" style="color:#3A95FF">i5_commit</span> attribute should be set before the           <span class="function"><strong style="color:#CC7832">db2_connect()</strong></span>. If the value is changed after the           connection has been established, and the connection is to a remote data           source, the change does not take effect until the next successful           <span class="function"><strong style="color:#CC7832">db2_connect()</strong></span> for the connection handle.           <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:             <p class="para">             The php.ini setting <span class="parameter" style="color:#3A95FF">ibm_db2.i5_allow_commit</span>==0             or <span>DB2_I5_TXN_NO_COMMIT</span> is the default, but may be             overridden with the <span class="parameter" style="color:#3A95FF">i5_commit</span> option.            </p>           </p></blockquote>          </p>          <p class="para">           <span>DB2_I5_TXN_NO_COMMIT</span> - Commitment control is not used.          </p>          <p class="para">           <span>DB2_I5_TXN_READ_UNCOMMITTED</span> - Dirty reads, nonrepeatable           reads, and phantoms are possible.          </p>          <p class="para">           <span>DB2_I5_TXN_READ_COMMITTED</span> - Dirty reads are not possible.           Nonrepeatable reads, and phantoms are possible.          </p>          <p class="para">           <span>DB2_I5_TXN_REPEATABLE_READ</span> - Dirty reads and nonrepeatable           reads are not possible. Phantoms are possible.          </p>          <p class="para">           <span>DB2_I5_TXN_SERIALIZABLE</span> - Transactions are serializable.           Dirty reads, non-repeatable reads, and phantoms are not possible          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">i5_query_optimize</span></dt>         <dd>          <p class="para">            <span>DB2_FIRST_IO</span> All queries are optimized with the goal of            returning the first page of output as fast as possible. This goal works well            when the output is controlled by a user who is most likely to cancel the query            after viewing the first page of output data. Queries coded with an            OPTIMIZE FOR nnn ROWS clause honor the goal specified by the clause.          </p>          <p class="para">           <span>DB2_ALL_IO</span> All queries are optimized with the goal of running           the entire query to completion in the shortest amount of elapsed time. This is a           good option when the output of a query is being written to a file or report, or           the interface is queuing the output data. Queries coded with an OPTIMIZE FOR nnn           ROWS clause honor the goal specified by the clause. This is the default.          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">i5_dbcs_alloc</span></dt>         <dd>          <p class="para">           <span>DB2_I5_DBCS_ALLOC_ON</span> value turns on DB2 6X allocation scheme           for DBCS translation column size growth.          </p>          <p class="para">           <span>DB2_I5_DBCS_ALLOC_OFF</span> value turns off DB2 6X allocation scheme           for DBCS translation column size growth.          </p>          <p class="para">           Note: php.ini setting <span class="parameter" style="color:#3A95FF">ibm_db2.i5_dbcs_alloc</span>==0 or           <span>DB2_I5_DBCS_ALLOC_OFF</span> is the default, but may be overridden           with the <span class="parameter" style="color:#3A95FF">i5_dbcs_alloc</span> option.          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">i5_date_fmt</span></dt>         <dd>          <p class="para">           <span>DB2_I5_FMT_ISO</span> - The International Organization for Standardization           (ISO) date format yyyy-mm-dd is used. This is the default.          </p>          <p class="para">           <span>DB2_I5_FMT_USA</span> - The United States date format mm/dd/yyyy is used.          </p>          <p class="para">           <span>DB2_I5_FMT_EUR</span> - The European date format dd.mm.yyyy is used.          </p>          <p class="para">           <span>DB2_I5_FMT_JIS</span> - The Japanese Industrial Standard date format           yyyy-mm-dd is used.          </p>          <p class="para">           <span>DB2_I5_FMT_MDY</span> - The date format mm/dd/yyyy is used.          </p>          <p class="para">           <span>DB2_I5_FMT_DMY</span> - The date format dd/mm/yyyy is used.          </p>          <p class="para">           <span>DB2_I5_FMT_YMD</span> - The date format yy/mm/dd is used.          </p>          <p class="para">           <span>DB2_I5_FMT_JUL</span> - The Julian date format yy/ddd is used.          </p>          <p class="para">           <span>DB2_I5_FMT_JOB</span> - The job default is used.          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">i5_date_sep</span></dt>         <dd>          <p class="para">           <span>DB2_I5_SEP_SLASH</span> - A slash ( / ) is used as the date separator.           This is the default.          </p>          <p class="para">           <span>DB2_I5_SEP_DASH</span> - A dash ( - ) is used as the date separator.          </p>          <p class="para">           <span>DB2_I5_SEP_PERIOD</span> - A period ( . ) is used as the date           separator.          </p>          <p class="para">           <span>DB2_I5_SEP_COMMA</span> - A comma ( , ) is used as the date separator.          </p>          <p class="para">           <span>DB2_I5_SEP_BLANK</span> - A blank is used as the date separator.          </p>          <p class="para">           <span>DB2_I5_SEP_JOB</span> - The job default is used          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">i5_time_fmt</span></dt>         <dd>          <p class="para">           <span>DB2_I5_FMT_ISO</span> - The International Organization for           Standardization (ISO) time format hh.mm.ss is used. This is the default.          </p>          <p class="para">           <span>DB2_I5_FMT_USA</span> - The United States time format           hh:mmxx is used, where xx is AM or PM.          </p>          <p class="para">           <span>DB2_I5_FMT_EUR</span> - The European time format hh.mm.ss           is used.          </p>          <p class="para">           <span>DB2_I5_FMT_JIS</span> - The Japanese Industrial Standard           time format hh:mm:ss is used.          </p>          <p class="para">           <span>DB2_I5_FMT_HMS</span> - The hh:mm:ss format is used.          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">i5_time_sep</span></dt>         <dd>          <p class="para">           <span>DB2_I5_SEP_COLON</span> - A colon ( : ) is used as the time           separator. This is the default.          </p>          <p class="para">           <span>DB2_I5_SEP_PERIOD</span> - A period ( . ) is used as the time           separator.          </p>          <p class="para">           <span>DB2_I5_SEP_COMMA</span> - A comma ( , ) is used as the time           separator.          </p>          <p class="para">           <span>DB2_I5_SEP_BLANK</span> - A blank is used as the time separator.          </p>          <p class="para">           <span>DB2_I5_SEP_JOB</span> - The job default is used.          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">i5_decimal_sep</span></dt>         <dd>          <p class="para">           <span>DB2_I5_SEP_PERIOD</span> - A period ( . ) is used as           the decimal separator. This is the default.          </p>          <p class="para">           <span>DB2_I5_SEP_COMMA</span> - A comma ( , ) is used as the           decimal separator.          </p>          <p class="para">           <span>DB2_I5_SEP_JOB</span> - The job default is used.          </p>         </dd>               </dl>      </p>      <p class="para">       The following new i5/OS option is available in ibm_db2 version 1.8.0       and later.       <dl>                 <dt><span class="parameter" style="color:#3A95FF">i5_libl</span></dt>         <dd>          <p class="para">           A character value that indicates the library list that will be used for           resolving unqualified file references. Specify the library list           elements separated by blanks &#039;i5_libl&#039;=&gt;&quot;MYLIB YOURLIB ANYLIB&quot;.          </p>          <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:            <p class="para">            <span class="parameter" style="color:#3A95FF">i5_libl</span> calls qsys2/qcmdexc(&#039;cmd&#039;,cmdlen), which is only            available in i5/OS V5R4 and later.           </p>          </p></blockquote>         </dd>               </dl>      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.db2-connect-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   Returns a connection handle resource if the connection attempt is   successful. If the connection attempt fails, <span class="function"><strong style="color:#CC7832">db2_connect()</strong></span>   returns <strong><span>false</span></strong>.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.db2-connect-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="regexiterator.setpregflags.example.basic">    <p><strong>示例 #1 Creating a cataloged connection</strong></p>    <div class="example-contents"><p>     Cataloged connections require you to have previously cataloged the target     database through the DB2 Command Line Processor (CLP) or DB2     Configuration Assistant.    </p></div>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />$database </span><span style="color: #007700">= </span><span style="color: #DD0000">'SAMPLE'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$user </span><span style="color: #007700">= </span><span style="color: #DD0000">'db2inst1'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$password </span><span style="color: #007700">= </span><span style="color: #DD0000">'ibmdb2'</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_connect</span><span style="color: #007700">(</span><span style="color: #9876AA">$database</span><span style="color: #007700">, </span><span style="color: #9876AA">$user</span><span style="color: #007700">, </span><span style="color: #9876AA">$password</span><span style="color: #007700">);<br /><br />if (</span><span style="color: #9876AA">$conn</span><span style="color: #007700">) {<br />    echo </span><span style="color: #DD0000">"Connection succeeded."</span><span style="color: #007700">;<br />    </span><span style="color: #9876AA">db2_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br />}<br />else {<br />    echo </span><span style="color: #DD0000">"Connection failed."</span><span style="color: #007700">;<br />}<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>Connection succeeded.<br></span></div>    </div>   </div>   <div class="example" id="example-3761">    <p><strong>示例 #2 Creating an uncataloged connection</strong></p>    <div class="example-contents"><p>     An uncataloged connection enables you to dynamically connect to a     database.    </p></div>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />$database </span><span style="color: #007700">= </span><span style="color: #DD0000">'SAMPLE'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$user </span><span style="color: #007700">= </span><span style="color: #DD0000">'db2inst1'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$password </span><span style="color: #007700">= </span><span style="color: #DD0000">'ibmdb2'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$hostname </span><span style="color: #007700">= </span><span style="color: #DD0000">'localhost'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$port </span><span style="color: #007700">= </span><span style="color: #9876AA">50000</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">$conn_string </span><span style="color: #007700">= </span><span style="color: #DD0000">"DRIVER={IBM DB2 ODBC DRIVER};DATABASE=</span><span style="color: #9876AA">$database</span><span style="color: #DD0000">;" </span><span style="color: #007700">.<br />  </span><span style="color: #DD0000">"HOSTNAME=</span><span style="color: #9876AA">$hostname</span><span style="color: #DD0000">;PORT=</span><span style="color: #9876AA">$port</span><span style="color: #DD0000">;PROTOCOL=TCPIP;UID=</span><span style="color: #9876AA">$user</span><span style="color: #DD0000">;PWD=</span><span style="color: #9876AA">$password</span><span style="color: #DD0000">;"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_connect</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn_string</span><span style="color: #007700">, </span><span style="color: #DD0000">''</span><span style="color: #007700">, </span><span style="color: #DD0000">''</span><span style="color: #007700">);<br /><br />if (</span><span style="color: #9876AA">$conn</span><span style="color: #007700">) {<br />    echo </span><span style="color: #DD0000">"Connection succeeded."</span><span style="color: #007700">;<br />    </span><span style="color: #9876AA">db2_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br />}<br />else {<br />    echo </span><span style="color: #DD0000">"Connection failed."</span><span style="color: #007700">;<br />}<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>Connection succeeded.<br></span></div>    </div>   </div>   <div class="example" id="example-3764">    <p><strong>示例 #3 Creating a connection with autocommit off by default</strong></p>    <div class="example-contents"><p>     Passing an array of options to <span class="function"><strong style="color:#CC7832">db2_connect()</strong></span> enables     you to modify the default behavior of the connection handle.    </p></div>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />$database </span><span style="color: #007700">= </span><span style="color: #DD0000">'SAMPLE'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$user </span><span style="color: #007700">= </span><span style="color: #DD0000">'db2inst1'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$password </span><span style="color: #007700">= </span><span style="color: #DD0000">'ibmdb2'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$options </span><span style="color: #007700">= array(</span><span style="color: #DD0000">'autocommit' </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">DB2_AUTOCOMMIT_OFF</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_connect</span><span style="color: #007700">(</span><span style="color: #9876AA">$database</span><span style="color: #007700">, </span><span style="color: #9876AA">$user</span><span style="color: #007700">, </span><span style="color: #9876AA">$password</span><span style="color: #007700">, </span><span style="color: #9876AA">$options</span><span style="color: #007700">);<br /><br />if (</span><span style="color: #9876AA">$conn</span><span style="color: #007700">) {<br />    echo </span><span style="color: #DD0000">"Connection succeeded.\n"</span><span style="color: #007700">;<br />    if (</span><span style="color: #9876AA">db2_autocommit</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">)) {<br />         echo </span><span style="color: #DD0000">"Autocommit is on.\n"</span><span style="color: #007700">;<br />    }<br />    else {<br />         echo </span><span style="color: #DD0000">"Autocommit is off.\n"</span><span style="color: #007700">;<br />    }<br />    </span><span style="color: #9876AA">db2_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br />}<br />else {<br />    echo </span><span style="color: #DD0000">"Connection failed."</span><span style="color: #007700">;<br />}<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>Connection succeeded.<br>Autocommit is off.<br></span></div>    </div>   </div>   <div class="example" id="example-3767">    <p><strong>示例 #4 i5/OS best performance</strong></p>    <div class="example-contents"><p>     To achieve best performance for your i5/OS ibm_db2 1.5.1 PHP application     use the default host, userid, and password for your     <span class="function"><strong style="color:#CC7832">db2_connect()</strong></span>.    </p></div>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />  $library </span><span style="color: #007700">= </span><span style="color: #DD0000">"ADC"</span><span style="color: #007700">;<br />  </span><span style="color: #9876AA">$i5 </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">""</span><span style="color: #007700">, </span><span style="color: #DD0000">""</span><span style="color: #007700">, </span><span style="color: #DD0000">""</span><span style="color: #007700">, array(</span><span style="color: #DD0000">"i5_lib"</span><span style="color: #007700">=&gt;</span><span style="color: #DD0000">"qsys2"</span><span style="color: #007700">));<br />  </span><span style="color: #9876AA">$result </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_exec</span><span style="color: #007700">(</span><span style="color: #9876AA">$i5</span><span style="color: #007700">,<br />       </span><span style="color: #DD0000">"select * from systables where table_schema = '</span><span style="color: #9876AA">$library</span><span style="color: #DD0000">'"</span><span style="color: #007700">);<br />  while (</span><span style="color: #9876AA">$row </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_fetch_both</span><span style="color: #007700">(</span><span style="color: #9876AA">$result</span><span style="color: #007700">)) {<br />     echo </span><span style="color: #9876AA">$row</span><span style="color: #007700">[</span><span style="color: #DD0000">'TABLE_NAME'</span><span style="color: #007700">].</span><span style="color: #DD0000">"&lt;/br&gt;"</span><span style="color: #007700">;<br />  }<br />  </span><span style="color: #9876AA">db2_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$i5</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>ANIMALS<br>NAMES<br>PICTURES<br></span></div>    </div>   </div>   <div class="example" id="example-3770">    <p><strong>示例 #5 Using trusted context</strong></p>    <div class="example-contents"><p>     The following example shows how to enable trusted context, switch     users, and get the current user ID.    </p></div>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$database </span><span style="color: #007700">= </span><span style="color: #DD0000">"SAMPLE"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$hostname </span><span style="color: #007700">= </span><span style="color: #DD0000">"localhost"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$port </span><span style="color: #007700">= </span><span style="color: #9876AA">50000</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$authID </span><span style="color: #007700">= </span><span style="color: #DD0000">"db2inst1"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$auth_pass </span><span style="color: #007700">= </span><span style="color: #DD0000">"ibmdb2"</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">$tc_user </span><span style="color: #007700">= </span><span style="color: #DD0000">"tcuser"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$tc_pass </span><span style="color: #007700">= </span><span style="color: #DD0000">"tcpassword"</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">$dsn </span><span style="color: #007700">= </span><span style="color: #DD0000">"DATABASE=</span><span style="color: #9876AA">$database</span><span style="color: #DD0000">;HOSTNAME=</span><span style="color: #9876AA">$hostname</span><span style="color: #DD0000">;PORT=</span><span style="color: #9876AA">$port</span><span style="color: #DD0000">;<br />  PROTOCOL=TCPIP;UID=</span><span style="color: #9876AA">$authID</span><span style="color: #DD0000">;PWD=</span><span style="color: #9876AA">$auth_pass</span><span style="color: #DD0000">;"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$options </span><span style="color: #007700">= array (</span><span style="color: #DD0000">"trustedcontext" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">DB2_TRUSTED_CONTEXT_ENABLE</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$tc_conn </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_connect</span><span style="color: #007700">(</span><span style="color: #9876AA">$dsn</span><span style="color: #007700">, </span><span style="color: #DD0000">""</span><span style="color: #007700">, </span><span style="color: #DD0000">""</span><span style="color: #007700">, </span><span style="color: #9876AA">$options</span><span style="color: #007700">);<br />if(</span><span style="color: #9876AA">$tc_conn</span><span style="color: #007700">) {<br />    echo </span><span style="color: #DD0000">"Explicit trusted connection succeeded.\n"</span><span style="color: #007700">;<br /><br />    if(</span><span style="color: #9876AA">db2_get_option</span><span style="color: #007700">(</span><span style="color: #9876AA">$tc_conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"trustedcontext"</span><span style="color: #007700">)) {<br />        </span><span style="color: #9876AA">$userBefore </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_get_option</span><span style="color: #007700">(</span><span style="color: #9876AA">$tc_conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"trusted_user"</span><span style="color: #007700">);<br /><br />        </span><span style="color: #FF8000">//Do some work as user 1.<br /><br />        //Switching to trusted user.<br />        </span><span style="color: #9876AA">$parameters </span><span style="color: #007700">= array(</span><span style="color: #DD0000">"trusted_user" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">$tc_user</span><span style="color: #007700">,<br />          </span><span style="color: #DD0000">"trusted_password" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">$tcuser_pass</span><span style="color: #007700">);<br />        </span><span style="color: #9876AA">$res </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_set_option </span><span style="color: #007700">(</span><span style="color: #9876AA">$tc_conn</span><span style="color: #007700">, </span><span style="color: #9876AA">$parameters</span><span style="color: #007700">, </span><span style="color: #9876AA">1</span><span style="color: #007700">);<br /><br />        </span><span style="color: #9876AA">$userAfter </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_get_option</span><span style="color: #007700">(</span><span style="color: #9876AA">$tc_conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"trusted_user"</span><span style="color: #007700">);<br />        </span><span style="color: #FF8000">//Do more work as trusted user.<br /><br />        </span><span style="color: #007700">if(</span><span style="color: #9876AA">$userBefore </span><span style="color: #007700">!= </span><span style="color: #9876AA">$userAfter</span><span style="color: #007700">) {<br />            echo </span><span style="color: #DD0000">"User has been switched." </span><span style="color: #007700">. </span><span style="color: #DD0000">"\n"</span><span style="color: #007700">;<br />        }<br />    }<br /><br />    </span><span style="color: #9876AA">db2_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$tc_conn</span><span style="color: #007700">);<br />}<br />else {<br />    echo </span><span style="color: #DD0000">"Explicit trusted connection failed.\n"</span><span style="color: #007700">;<br />}<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>Explicit trusted connection succeeded.<br>User has been switched.<br></span></div>    </div>   </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.db2-connect-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link db2_close()} - Closes a database connection</span></li>    <li class="member"><span class="function">{@link db2_pconnect()} - Returns a persistent connection to a database</span></li>   </ul>  </span> </div></div>
 * @return resource|false A connection handle resource if the connection attempt is*/
function db2_connect(#[\SensitiveParameter] string $database, ?string $username, #[\SensitiveParameter] ?string $password, array $options = []) {}

/**
*<div id="function.db2-commit" class="refentry"> <div class="refnamediv">  <h1 class="refname">db2_commit</h1>  <p class="verinfo">(PECL ibm_db2 &gt;= 1.0.0)</p><p class="refpurpose"><span class="refname">db2_commit</span> &mdash; <span class="dc-title">   Commits a transaction  </span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.db2-commit-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>db2_commit</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$connection</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   Commits an in-progress transaction on the specified connection resource and   begins a new transaction. PHP applications normally default to AUTOCOMMIT   mode, so <span class="function"><strong style="color:#CC7832">db2_commit()</strong></span> is not necessary unless AUTOCOMMIT   has been turned off for the connection resource.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.db2-commit-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">connection</span></dt>     <dd>      <p class="para">       A valid database connection resource variable as returned from       <span class="function">{@link db2_connect()}</span> or <span class="function">{@link db2_pconnect()}</span>.      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.db2-commit-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.db2-commit-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link db2_autocommit()} - Returns or sets the AUTOCOMMIT state for a database connection</span></li>    <li class="member"><span class="function">{@link db2_rollback()} - Rolls back a transaction</span></li>   </ul>  </span> </div></div>
 * @return bool true on success or false on failure.*/
function db2_commit($connection): bool {}

/**
*<div id="function.db2-pconnect" class="refentry"> <div class="refnamediv">  <h1 class="refname">db2_pconnect</h1>  <p class="verinfo">(PECL ibm_db2 &gt;= 1.0.0)</p><p class="refpurpose"><span class="refname">db2_pconnect</span> &mdash; <span class="dc-title">   Returns a persistent connection to a database  </span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.db2-pconnect-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>db2_pconnect</strong></span>(<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$database</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">string</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">$username</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">string</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">$password</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">array</span> <span class="parameter" style="color:#3A95FF">$options</span><span class="initializer"> = []</span></span><br>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">resource</span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>  <p class="para rdfs-comment">   Returns a persistent connection to an IBM DB2 Universal Database, IBM   Cloudscape, or Apache Derby database.  </p>  <p class="para">   For more information on persistent   connections, refer to <a href="https://www.php.net/manual/zh/features.persistent-connections.php" class="xref">持久数据库连接</a>.  </p>  <p class="para">   Calling <span class="function">{@link db2_close()}</span> on a persistent connection always   returns <strong><span>true</span></strong>, but the underlying DB2 client connection remains open and   waiting to serve the next matching <span class="function"><strong style="color:#CC7832">db2_pconnect()</strong></span>   request.  </p>  <p class="para">   Users running version 1.9.0 or later of ibm_db2 should be aware that the   extension will perform a transaction rollback on persistent connections at   the end of a request, thus ending the transaction.  This prevents the   transaction block from carrying over to the next request which uses that   connection if script execution ends before the transaction block does.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.db2-pconnect-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">database</span></dt>     <dd>      <p class="para">       The database alias in the DB2 client catalog.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">username</span></dt>     <dd>      <p class="para">       The username with which you are connecting to the database.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">password</span></dt>     <dd>      <p class="para">       The password with which you are connecting to the database.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">options</span></dt>     <dd>      <p class="para">       An associative array of connection options that affect the behavior       of the connection, where valid array keys include:       <dl>                 <dt><span class="parameter" style="color:#3A95FF">autocommit</span></dt>         <dd>          <p class="para">           Passing the <span>DB2_AUTOCOMMIT_ON</span> value turns           autocommit on for this connection handle.          </p>          <p class="para">           Passing the <span>DB2_AUTOCOMMIT_OFF</span> value turns           autocommit off for this connection handle.          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">DB2_ATTR_CASE</span></dt>         <dd>          <p class="para">           Passing the <span>DB2_CASE_NATURAL</span> value specifies           that column names are returned in natural case.          </p>          <p class="para">           Passing the <span>DB2_CASE_LOWER</span> value specifies           that column names are returned in lower case.          </p>          <p class="para">           Passing the <span>DB2_CASE_UPPER</span> value specifies           that column names are returned in upper case.          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">CURSOR</span></dt>         <dd>          <p class="para">           Passing the <span>DB2_FORWARD_ONLY</span> value specifies a           forward-only cursor for a statement resource. This is the default           cursor type and is supported on all database servers.          </p>          <p class="para">           Passing the <span>DB2_SCROLLABLE</span> value specifies a           scrollable cursor for a statement resource. This mode enables           random access to rows in a result set, but currently is supported           only by IBM DB2 Universal Database.          </p>         </dd>               </dl>      </p>      <p class="para">       The following new option is available in ibm_db2 version 1.7.0       and later.       <dl>                 <dt><span class="parameter" style="color:#3A95FF">trustedcontext</span></dt>         <dd>          <p class="para">           Passing the DB2_TRUSTED_CONTEXT_ENABLE value turns trusted context           on for this connection handle. This parameter cannot be set using           <span class="function">{@link db2_set_option()}</span>.          </p>          <p class="para">           This key works only if the database is cataloged (even if the           database is local), or if you specify the full DSN when you create           the connection.          </p>          <p class="para">           To catalog the database, use following commands:          </p>          <p class="para">           <span>db2 catalog tcpip node loopback remote &lt;SERVERNAME&gt; server &lt;SERVICENAME&gt;<br>db2 catalog database &lt;LOCALDBNAME&gt; as &lt;REMOTEDBNAME&gt; at node loopback<br>db2 &quot;update dbm cfg using svcename &lt;SERVICENAME&gt;&quot;<br>db2set DB2COMM=TCPIP</span>          </p>         </dd>              </dl>      </p>      <p class="para">       The following new i5/OS options are available in ibm_db2 version 1.5.1       and later.       <div class="tip"><strong class="tip">小技巧</strong>        <p class="para">Conflicting connection attributes used in conjunction with persistent         connections can produce indeterminate results on i5/OS. Site policies should         be establish for all applications using each persistent connection user         profile. The default DB2_AUTOCOMMIT_ON is suggested when using persistent connections.        </p>       </div>       <dl>                 <dt><span class="parameter" style="color:#3A95FF">i5_lib</span></dt>         <dd>          <p class="para">           A character value that indicates the default library that will be           used for resolving unqualified file references. This is not valid           if the connection is using system naming mode.          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">i5_naming</span></dt>         <dd>          <p class="para">           <span>DB2_I5_NAMING_ON</span> value turns on DB2 UDB CLI iSeries           system naming mode. Files are qualified using the slash (/) delimiter.           Unqualified files are resolved using the library list for the job.          </p>          <p class="para">           <span>DB2_I5_NAMING_OFF</span> value turns off DB2 UDB CLI default           naming mode, which is SQL naming. Files are qualified using the period (.)           delimiter. Unqualified files are resolved using either the default library           or the current user ID.          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">i5_commit</span></dt>         <dd>          <p class="para">           The <span class="parameter" style="color:#3A95FF">i5_commit</span> attribute should be set before the           <span class="function"><strong style="color:#CC7832">db2_pconnect()</strong></span>. If the value is changed after the           connection has been established, and the connection is to a remote data           source, the change does not take effect until the next successful           <span class="function"><strong style="color:#CC7832">db2_pconnect()</strong></span> for the connection handle.           <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:             <p class="para">             The php.ini setting <span class="parameter" style="color:#3A95FF">ibm_db2.i5_allow_commit</span>==0             or <span>DB2_I5_TXN_NO_COMMIT</span> is the default, but may be             overridden with the <span class="parameter" style="color:#3A95FF">i5_commit</span> option.            </p>           </p></blockquote>          </p>          <p class="para">           <span>DB2_I5_TXN_NO_COMMIT</span> - Commitment control is not used.          </p>          <p class="para">           <span>DB2_I5_TXN_READ_UNCOMMITTED</span> - Dirty reads, nonrepeatable           reads, and phantoms are possible.          </p>          <p class="para">           <span>DB2_I5_TXN_READ_COMMITTED</span> - Dirty reads are not possible.           Nonrepeatable reads, and phantoms are possible.          </p>          <p class="para">           <span>DB2_I5_TXN_REPEATABLE_READ</span> - Dirty reads and nonrepeatable           reads are not possible. Phantoms are possible.          </p>          <p class="para">           <span>DB2_I5_TXN_SERIALIZABLE</span> - Transactions are serializable.           Dirty reads, non-repeatable reads, and phantoms are not possible          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">i5_query_optimize</span></dt>         <dd>          <p class="para">            <span>DB2_FIRST_IO</span> All queries are optimized with the goal of            returning the first page of output as fast as possible. This goal works well            when the output is controlled by a user who is most likely to cancel the query            after viewing the first page of output data. Queries coded with an            OPTIMIZE FOR nnn ROWS clause honor the goal specified by the clause.          </p>          <p class="para">           <span>DB2_ALL_IO</span> All queries are optimized with the goal of running           the entire query to completion in the shortest amount of elapsed time. This is a           good option when the output of a query is being written to a file or report, or           the interface is queuing the output data. Queries coded with an OPTIMIZE FOR nnn           ROWS clause honor the goal specified by the clause. This is the default.          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">i5_dbcs_alloc</span></dt>         <dd>          <p class="para">           <span>DB2_I5_DBCS_ALLOC_ON</span> value turns on DB2 6X allocation scheme           for DBCS translation column size growth.          </p>          <p class="para">           <span>DB2_I5_DBCS_ALLOC_OFF</span> value turns off DB2 6X allocation scheme           for DBCS translation column size growth.           <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:             <p class="para">             The php.ini setting <span class="parameter" style="color:#3A95FF">ibm_db2.i5_dbcs_alloc</span>==0 or             <span>DB2_I5_DBCS_ALLOC_OFF</span> is the default, but may be overridden             with the <span class="parameter" style="color:#3A95FF">i5_dbcs_alloc</span> option.            </p>           </p></blockquote>           </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">i5_date_fmt</span></dt>         <dd>          <p class="para">           <span>DB2_I5_FMT_ISO</span> - The International Organization for Standardization           (ISO) date format yyyy-mm-dd is used. This is the default.          </p>          <p class="para">           <span>DB2_I5_FMT_USA</span> - The United States date format mm/dd/yyyy is used.          </p>          <p class="para">           <span>DB2_I5_FMT_EUR</span> - The European date format dd.mm.yyyy is used.          </p>          <p class="para">           <span>DB2_I5_FMT_JIS</span> - The Japanese Industrial Standard date format           yyyy-mm-dd is used.          </p>          <p class="para">           <span>DB2_I5_FMT_MDY</span> - The date format mm/dd/yyyy is used.          </p>          <p class="para">           <span>DB2_I5_FMT_DMY</span> - The date format dd/mm/yyyy is used.          </p>          <p class="para">           <span>DB2_I5_FMT_YMD</span> - The date format yy/mm/dd is used.          </p>          <p class="para">           <span>DB2_I5_FMT_JUL</span> - The Julian date format yy/ddd is used.          </p>          <p class="para">           <span>DB2_I5_FMT_JOB</span> - The job default is used.          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">i5_date_sep</span></dt>         <dd>          <p class="para">           <span>DB2_I5_SEP_SLASH</span> - A slash ( / ) is used as the date separator.           This is the default.          </p>          <p class="para">           <span>DB2_I5_SEP_DASH</span> - A dash ( - ) is used as the date separator.          </p>          <p class="para">           <span>DB2_I5_SEP_PERIOD</span> - A period ( . ) is used as the date           separator.          </p>          <p class="para">           <span>DB2_I5_SEP_COMMA</span> - A comma ( , ) is used as the date separator.          </p>          <p class="para">           <span>DB2_I5_SEP_BLANK</span> - A blank is used as the date separator.          </p>          <p class="para">           <span>DB2_I5_SEP_JOB</span> - The job default is used          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">i5_time_fmt</span></dt>         <dd>          <p class="para">           <span>DB2_I5_FMT_ISO</span> - The International Organization for           Standardization (ISO) time format hh.mm.ss is used. This is the default.          </p>          <p class="para">           <span>DB2_I5_FMT_USA</span> - The United States time format           hh:mmxx is used, where xx is AM or PM.          </p>          <p class="para">           <span>DB2_I5_FMT_EUR</span> - The European time format hh.mm.ss           is used.          </p>          <p class="para">           <span>DB2_I5_FMT_JIS</span> - The Japanese Industrial Standard           time format hh:mm:ss is used.          </p>          <p class="para">           <span>DB2_I5_FMT_HMS</span> - The hh:mm:ss format is used.          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">i5_time_sep</span></dt>         <dd>          <p class="para">           <span>DB2_I5_SEP_COLON</span> - A colon ( : ) is used as the time           separator. This is the default.          </p>          <p class="para">           <span>DB2_I5_SEP_PERIOD</span> - A period ( . ) is used as the time           separator.          </p>          <p class="para">           <span>DB2_I5_SEP_COMMA</span> - A comma ( , ) is used as the time           separator.          </p>          <p class="para">           <span>DB2_I5_SEP_BLANK</span> - A blank is used as the time separator.          </p>          <p class="para">           <span>DB2_I5_SEP_JOB</span> - The job default is used.          </p>         </dd>                         <dt><span class="parameter" style="color:#3A95FF">i5_decimal_sep</span></dt>         <dd>          <p class="para">           <span>DB2_I5_SEP_PERIOD</span> - A period ( . ) is used as           the decimal separator. This is the default.          </p>          <p class="para">           <span>DB2_I5_SEP_COMMA</span> - A comma ( , ) is used as the           decimal separator.          </p>          <p class="para">           <span>DB2_I5_SEP_JOB</span> - The job default is used.          </p>         </dd>               </dl>      </p>      <p class="para">       The following new i5/OS option is available in ibm_db2 version 1.8.0       and later.       <dl>                 <dt><span class="parameter" style="color:#3A95FF">i5_libl</span></dt>         <dd>          <p class="para">           A character value that indicates the library list that will be used for           resolving unqualified file references. Specify the library list           elements separated by blanks &#039;i5_libl&#039;=&gt;&quot;MYLIB YOURLIB ANYLIB&quot;.           <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:             <p class="para">             i5_libl calls qsys2/qcmdexc(&#039;cmd&#039;,cmdlen), which is only available             in i5/OS V5R4 and later.            </p>           </p></blockquote>          </p>         </dd>               </dl>      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.db2-pconnect-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   Returns a connection handle resource if the connection attempt is   successful. <span class="function"><strong style="color:#CC7832">db2_pconnect()</strong></span> tries to reuse an existing   connection resource that exactly matches the   <span class="parameter" style="color:#3A95FF">database</span>, <span class="parameter" style="color:#3A95FF">username</span>, and   <span class="parameter" style="color:#3A95FF">password</span> parameters. If the connection attempt fails,   <span class="function"><strong style="color:#CC7832">db2_pconnect()</strong></span> returns <strong><span>false</span></strong>.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 changelog" id="refsect1-function.db2-pconnect-changelog">  <h3 class="title">更新日志</h3>  <span>   <table class="doctable informaltable">         <thead>      <tr>       <th>版本</th>       <th>说明</th>      </tr>     </thead>     <tbody class="tbody">      <tr>       <td>ibm_db2 1.9.0</td>       <td>        Active transactions within a persistent connection will be rolled back        at the end of each request.       </td>      </tr>      <tr>       <td>ibm_db2 1.8.0</td>       <td>        The <span class="parameter" style="color:#3A95FF">i5_libl</span> option is available for i5/OS        users.       </td>      </tr>      <tr>       <td>ibm_db2 1.7.0</td>       <td>        The <span class="parameter" style="color:#3A95FF">trustedcontext</span> option is available.       </td>      </tr>      <tr>       <td>ibm_db2 1.5.1</td>       <td>        The <span class="parameter" style="color:#3A95FF">i5_lib</span>, <span class="parameter" style="color:#3A95FF">i5_naming</span>,        <span class="parameter" style="color:#3A95FF">i5_commit</span>,        <span class="parameter" style="color:#3A95FF">i5_query_optimize</span>,        <span class="parameter" style="color:#3A95FF">i5_dbcs_alloc</span>,        <span class="parameter" style="color:#3A95FF">i5_date_fmt</span>,        <span class="parameter" style="color:#3A95FF">i5_date_sep</span>,        <span class="parameter" style="color:#3A95FF">i5_time_fmt</span>, <span class="parameter" style="color:#3A95FF">i5_time_sep</span>        and <span class="parameter" style="color:#3A95FF">i5_decimal_sep</span> options are available for        i5/OS users.       </td>      </tr>     </tbody>       </table>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.db2-pconnect-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="example-3851">    <p><strong>示例 #1 A <span class="function"><strong style="color:#CC7832">db2_pconnect()</strong></span> example</strong></p>    <div class="example-contents"><p>     In the following example, the first call to     <span class="function"><strong style="color:#CC7832">db2_pconnect()</strong></span> returns a new persistent connection     resource. The second call to <span class="function"><strong style="color:#CC7832">db2_pconnect()</strong></span> returns     a persistent connection resource that simply reuses the first persistent     connection resource.    </p></div>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />$database </span><span style="color: #007700">= </span><span style="color: #DD0000">'SAMPLE'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$user </span><span style="color: #007700">= </span><span style="color: #DD0000">'db2inst1'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$password </span><span style="color: #007700">= </span><span style="color: #DD0000">'ibmdb2'</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">$pconn </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_pconnect</span><span style="color: #007700">(</span><span style="color: #9876AA">$database</span><span style="color: #007700">, </span><span style="color: #9876AA">$user</span><span style="color: #007700">, </span><span style="color: #9876AA">$password</span><span style="color: #007700">);<br /><br />if (</span><span style="color: #9876AA">$pconn</span><span style="color: #007700">) {<br />    echo </span><span style="color: #DD0000">"Persistent connection succeeded."</span><span style="color: #007700">;<br />}<br />else {<br />    echo </span><span style="color: #DD0000">"Persistent connection failed."</span><span style="color: #007700">;<br />}<br /><br /></span><span style="color: #9876AA">$pconn2 </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_pconnect</span><span style="color: #007700">(</span><span style="color: #9876AA">$database</span><span style="color: #007700">, </span><span style="color: #9876AA">$user</span><span style="color: #007700">, </span><span style="color: #9876AA">$password</span><span style="color: #007700">);<br />if (</span><span style="color: #9876AA">$pconn</span><span style="color: #007700">) {<br />    echo </span><span style="color: #DD0000">"Second persistent connection succeeded."</span><span style="color: #007700">;<br />}<br />else {<br />    echo </span><span style="color: #DD0000">"Second persistent connection failed."</span><span style="color: #007700">;<br />}<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>Persistent connection succeeded.<br>Second persistent connection succeeded.<br></span></div>    </div>   </div>   <div class="example" id="example-3854">    <p><strong>示例 #2 Using trusted context</strong></p>    <div class="example-contents"><p>     The following example shows how to enable trusted context, switch     users, and get the current user ID.    </p></div>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$database </span><span style="color: #007700">= </span><span style="color: #DD0000">"SAMPLE"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$hostname </span><span style="color: #007700">= </span><span style="color: #DD0000">"localhost"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$port </span><span style="color: #007700">= </span><span style="color: #9876AA">50000</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$authID </span><span style="color: #007700">= </span><span style="color: #DD0000">"db2inst1"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$auth_pass </span><span style="color: #007700">= </span><span style="color: #DD0000">"ibmdb2"</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">$tc_user </span><span style="color: #007700">= </span><span style="color: #DD0000">"tcuser"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$tc_pass </span><span style="color: #007700">= </span><span style="color: #DD0000">"tcpassword"</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">$dsn </span><span style="color: #007700">= </span><span style="color: #DD0000">"DATABASE=</span><span style="color: #9876AA">$database</span><span style="color: #DD0000">;HOSTNAME=</span><span style="color: #9876AA">$hostname</span><span style="color: #DD0000">;PORT=</span><span style="color: #9876AA">$port</span><span style="color: #DD0000">;<br />  PROTOCOL=TCPIP;UID=</span><span style="color: #9876AA">$authID</span><span style="color: #DD0000">;PWD=</span><span style="color: #9876AA">$auth_pass</span><span style="color: #DD0000">;"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$options </span><span style="color: #007700">= array (</span><span style="color: #DD0000">"trustedcontext" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">DB2_TRUSTED_CONTEXT_ENABLE</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$tc_conn </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_pconnect</span><span style="color: #007700">(</span><span style="color: #9876AA">$dsn</span><span style="color: #007700">, </span><span style="color: #DD0000">""</span><span style="color: #007700">, </span><span style="color: #DD0000">""</span><span style="color: #007700">, </span><span style="color: #9876AA">$options</span><span style="color: #007700">);<br />if(</span><span style="color: #9876AA">$tc_conn</span><span style="color: #007700">) {<br />    echo </span><span style="color: #DD0000">"Explicit trusted connection succeeded.\n"</span><span style="color: #007700">;<br /><br />    if(</span><span style="color: #9876AA">db2_get_option</span><span style="color: #007700">(</span><span style="color: #9876AA">$tc_conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"trustedcontext"</span><span style="color: #007700">)) {<br />        </span><span style="color: #9876AA">$userBefore </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_get_option</span><span style="color: #007700">(</span><span style="color: #9876AA">$tc_conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"trusted_user"</span><span style="color: #007700">);<br /><br />        </span><span style="color: #FF8000">//Do some work as user 1.<br /><br />        //Switching to trusted user.<br />        </span><span style="color: #9876AA">$parameters </span><span style="color: #007700">= array(</span><span style="color: #DD0000">"trusted_user" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">$tc_user</span><span style="color: #007700">,<br />          </span><span style="color: #DD0000">"trusted_password" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">$tcuser_pass</span><span style="color: #007700">);<br />        </span><span style="color: #9876AA">$res </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_set_option </span><span style="color: #007700">(</span><span style="color: #9876AA">$tc_conn</span><span style="color: #007700">, </span><span style="color: #9876AA">$parameters</span><span style="color: #007700">, </span><span style="color: #9876AA">1</span><span style="color: #007700">);<br /><br />        </span><span style="color: #9876AA">$userAfter </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_get_option</span><span style="color: #007700">(</span><span style="color: #9876AA">$tc_conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"trusted_user"</span><span style="color: #007700">);<br />        </span><span style="color: #FF8000">//Do more work as trusted user.<br /><br />        </span><span style="color: #007700">if(</span><span style="color: #9876AA">$userBefore </span><span style="color: #007700">!= </span><span style="color: #9876AA">$userAfter</span><span style="color: #007700">) {<br />            echo </span><span style="color: #DD0000">"User has been switched." </span><span style="color: #007700">. </span><span style="color: #DD0000">"\n"</span><span style="color: #007700">;<br />        }<br />    }<br /><br />    </span><span style="color: #9876AA">db2_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$tc_conn</span><span style="color: #007700">);<br />}<br />else {<br />    echo </span><span style="color: #DD0000">"Explicit trusted connection failed.\n"</span><span style="color: #007700">;<br />}<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>Explicit trusted connection succeeded.<br>User has been switched.<br></span></div>    </div>   </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.db2-pconnect-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link db2_connect()} - Returns a connection to a database</span></li>   </ul>  </span> </div></div>
 * @return resource|false A connection handle resource if the connection attempt is*/
function db2_pconnect(#[\SensitiveParameter] string $database, ?string $username, #[\SensitiveParameter] ?string $password, array $options = []) {}

/**
*<div id="function.db2-autocommit" class="refentry"> <div class="refnamediv">  <h1 class="refname">db2_autocommit</h1>  <p class="verinfo">(PECL ibm_db2 &gt;= 1.0.0)</p><p class="refpurpose"><span class="refname">db2_autocommit</span> &mdash; <span class="dc-title">   Returns or sets the AUTOCOMMIT state for a database connection  </span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.db2-autocommit-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>db2_autocommit</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$connection</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$value</span><span class="initializer"> = ?</span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">int</span>|<span class="type" style="color:#EAB766">bool</span></span></div>  <p class="para rdfs-comment">   Sets or gets the AUTOCOMMIT behavior of the specified connection resource.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.db2-autocommit-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">connection</span></dt>     <dd>      <p class="para">       A valid database connection resource variable as returned from       <span class="function">{@link db2_connect()}</span> or <span class="function">{@link db2_pconnect()}</span>.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">value</span></dt>     <dd>      <p class="para">       One of the following constants:        <dl>                 <dt><span>DB2_AUTOCOMMIT_OFF</span></dt>         <dd>          <p class="para">           Turns AUTOCOMMIT off.          </p>         </dd>                         <dt><span>DB2_AUTOCOMMIT_ON</span></dt>         <dd>          <p class="para">           Turns AUTOCOMMIT on.          </p>         </dd>               </dl>      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.db2-autocommit-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   When <span class="function"><strong style="color:#CC7832">db2_autocommit()</strong></span> receives only the   <span class="parameter" style="color:#3A95FF">connection</span> parameter, it returns the current state   of AUTOCOMMIT for the requested connection as an integer value. A value of   <strong><span>DB2_AUTOCOMMIT_OFF</span></strong> indicates that AUTOCOMMIT is off,   while a value of <strong><span>DB2_AUTOCOMMIT_ON</span></strong> indicates that   AUTOCOMMIT is on.  </p>  <p class="para">   When <span class="function"><strong style="color:#CC7832">db2_autocommit()</strong></span> receives both the   <span class="parameter" style="color:#3A95FF">connection</span> parameter and   <span class="parameter" style="color:#3A95FF">autocommit</span> parameter, it attempts to set the   AUTOCOMMIT state of the requested connection to the corresponding state.   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.db2-autocommit-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="example-3731">    <p><strong>示例 #1 Retrieving the AUTOCOMMIT value for a connection</strong></p>    <div class="example-contents"><p>     In the following example, a connection which has been created with     AUTOCOMMIT turned off is tested with the     <span class="function"><strong style="color:#CC7832">db2_autocommit()</strong></span> function.    </p></div>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />$options </span><span style="color: #007700">= array(</span><span style="color: #DD0000">'autocommit' </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">DB2_AUTOCOMMIT_OFF</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_connect</span><span style="color: #007700">(</span><span style="color: #9876AA">$database</span><span style="color: #007700">, </span><span style="color: #9876AA">$user</span><span style="color: #007700">, </span><span style="color: #9876AA">$password</span><span style="color: #007700">, </span><span style="color: #9876AA">$options</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$ac </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_autocommit</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br />if (</span><span style="color: #9876AA">$ac </span><span style="color: #007700">== </span><span style="color: #9876AA">DB2_AUTOCOMMIT_OFF</span><span style="color: #007700">) {<br />    print </span><span style="color: #DD0000">"</span><span style="color: #9876AA">$ac</span><span style="color: #DD0000"> -- AUTOCOMMIT is off."</span><span style="color: #007700">;<br />} else {<br />    print </span><span style="color: #DD0000">"</span><span style="color: #9876AA">$ac</span><span style="color: #DD0000"> -- AUTOCOMMIT is on."</span><span style="color: #007700">;<br />}<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>0 -- AUTOCOMMIT is off.<br></span></div>    </div>   </div>   <div class="example" id="multipleiterator.example.basic">    <p><strong>示例 #2 Setting the AUTOCOMMIT value for a connection</strong></p>    <div class="example-contents"><p>     In the following example, a connection which was initially created with     AUTOCOMMIT turned off has its behavior changed to turn AUTOCOMMIT on.    </p></div>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />$options </span><span style="color: #007700">= array(</span><span style="color: #DD0000">'autocommit' </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">DB2_AUTOCOMMIT_OFF</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_connect</span><span style="color: #007700">(</span><span style="color: #9876AA">$database</span><span style="color: #007700">, </span><span style="color: #9876AA">$user</span><span style="color: #007700">, </span><span style="color: #9876AA">$password</span><span style="color: #007700">, </span><span style="color: #9876AA">$options</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// Turn AUTOCOMMIT on<br /></span><span style="color: #9876AA">$rc </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_autocommit</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #9876AA">DB2_AUTOCOMMIT_ON</span><span style="color: #007700">);<br />if (</span><span style="color: #9876AA">$rc</span><span style="color: #007700">) {<br />    print </span><span style="color: #DD0000">"Turning AUTOCOMMIT on succeeded.\n"</span><span style="color: #007700">;<br />}<br /><br /></span><span style="color: #FF8000">// Check AUTOCOMMIT state<br /></span><span style="color: #9876AA">$ac </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_autocommit</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br />if (</span><span style="color: #9876AA">$ac </span><span style="color: #007700">== </span><span style="color: #9876AA">DB2_AUTOCOMMIT_OFF</span><span style="color: #007700">) {<br />    print </span><span style="color: #DD0000">"</span><span style="color: #9876AA">$ac</span><span style="color: #DD0000"> -- AUTOCOMMIT is off."</span><span style="color: #007700">;<br />} else {<br />    print </span><span style="color: #DD0000">"</span><span style="color: #9876AA">$ac</span><span style="color: #DD0000"> -- AUTOCOMMIT is on."</span><span style="color: #007700">;<br />}<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>Turning AUTOCOMMIT on succeeded.<br>1 -- AUTOCOMMIT is on.<br></span></div>    </div>   </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.db2-autocommit-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link db2_connect()} - Returns a connection to a database</span></li>    <li class="member"><span class="function">{@link db2_pconnect()} - Returns a persistent connection to a database</span></li>   </ul>  </span> </div></div>
 * @return int|bool <p>When db2_autocommit receives only the*/
function db2_autocommit($connection, int $value = null): int|bool {}

/**
*<div id="function.db2-bind-param" class="refentry"> <div class="refnamediv">  <h1 class="refname">db2_bind_param</h1>  <p class="verinfo">(PECL ibm_db2 &gt;= 1.0.0)</p><p class="refpurpose"><span class="refname">db2_bind_param</span> &mdash; <span class="dc-title">   Binds a PHP variable to an SQL statement parameter  </span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.db2-bind-param-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>db2_bind_param</strong></span>(<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$stmt</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$parameter_number</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$variable_name</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$parameter_type</span><span class="initializer"> = <strong><span>DB2_PARAM_IN</span></strong></span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$data_type</span><span class="initializer"> = 0</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$precision</span><span class="initializer"> = -1</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$scale</span><span class="initializer"> = 0</span></span><br>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   Binds a PHP variable to an SQL statement parameter in a statement resource   returned by <span class="function">{@link db2_prepare()}</span>. This function gives you more   control over the parameter type, data type, precision, and scale for the   parameter than simply passing the variable as part of the optional input   array to <span class="function">{@link db2_execute()}</span>.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.db2-bind-param-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">stmt</span></dt>     <dd>      <p class="para">       A prepared statement returned from <span class="function">{@link db2_prepare()}</span>.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">parameter_number</span></dt>     <dd>      <p class="para">       Specifies the 1-indexed position of the parameter in the prepared       statement.      </p>     </dd>            <dt><span class="parameter" style="color:#3A95FF">variable_name</span></dt>     <dd>      <p class="para">       A string specifying the name of the PHP variable to bind to the       parameter specified by <span class="parameter" style="color:#3A95FF">parameter_number</span>.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">parameter_type</span></dt>     <dd>      <p class="para">       A constant specifying whether the PHP variable should be bound to the       SQL parameter as an input parameter (<span>DB2_PARAM_IN</span>),       an output parameter (<span>DB2_PARAM_OUT</span>), or as a       parameter that accepts input and returns output       (<span>DB2_PARAM_INOUT</span>). To avoid memory overhead, you can       also specify <span>DB2_PARAM_FILE</span> to bind the PHP variable       to the name of a file that contains large object (BLOB, CLOB, or DBCLOB)       data.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">data_type</span></dt>     <dd>      <p class="para">       A constant specifying the SQL data type that the PHP variable should be       bound as: one of <span>DB2_BINARY</span>,       <span>DB2_CHAR</span>, <span>DB2_DOUBLE</span>, or       <span>DB2_LONG</span> .      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">precision</span></dt>     <dd>      <p class="para">       Specifies the precision with which the variable should be bound to the       database. This parameter can also be used for retrieving XML output values        from stored procedures. A non-negative value specifies the maximum size of        the XML data that will be retrieved from the database. If this parameter        is not used, a default of 1MB will be assumed for retrieving the XML        output value from the stored procedure.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">scale</span></dt>     <dd>      <p class="para">       Specifies the scale with which the variable should be bound to the       database.      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.db2-bind-param-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.db2-bind-param-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="example-3737">    <p><strong>示例 #1 Binding PHP variables to a prepared statement</strong></p>    <div class="example-contents"><p>     The SQL statement in the following example uses two input parameters in     the WHERE clause. We call <span class="function"><strong style="color:#CC7832">db2_bind_param()</strong></span> to bind two     PHP variables to the corresponding SQL parameters. Notice that the PHP     variables do not have to be declared or assigned before the call to     <span class="function"><strong style="color:#CC7832">db2_bind_param()</strong></span>; in the example,     <span>$lower_limit</span> is assigned a value before the call to     <span class="function"><strong style="color:#CC7832">db2_bind_param()</strong></span>, but <span>$upper_limit</span>     is assigned a value after the call to     <span class="function"><strong style="color:#CC7832">db2_bind_param()</strong></span>. The variables must be bound and, for     parameters that accept input, must have any value assigned, before calling     <span class="function">{@link db2_execute()}</span>.    </p></div>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$sql </span><span style="color: #007700">= </span><span style="color: #DD0000">'SELECT name, breed, weight FROM animals<br />    WHERE weight &gt; ? AND weight &lt; ?'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_connect</span><span style="color: #007700">(</span><span style="color: #9876AA">$database</span><span style="color: #007700">, </span><span style="color: #9876AA">$user</span><span style="color: #007700">, </span><span style="color: #9876AA">$password</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$stmt </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_prepare</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #9876AA">$sql</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// We can declare the variable before calling db2_bind_param()<br /></span><span style="color: #9876AA">$lower_limit </span><span style="color: #007700">= </span><span style="color: #9876AA">1</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">db2_bind_param</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">, </span><span style="color: #9876AA">1</span><span style="color: #007700">, </span><span style="color: #DD0000">"lower_limit"</span><span style="color: #007700">, </span><span style="color: #9876AA">DB2_PARAM_IN</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">db2_bind_param</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">, </span><span style="color: #9876AA">2</span><span style="color: #007700">, </span><span style="color: #DD0000">"upper_limit"</span><span style="color: #007700">, </span><span style="color: #9876AA">DB2_PARAM_IN</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// We can also declare the variable after calling db2_bind_param()<br /></span><span style="color: #9876AA">$upper_limit </span><span style="color: #007700">= </span><span style="color: #9876AA">15.0</span><span style="color: #007700">;<br /><br />if (</span><span style="color: #9876AA">db2_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">)) {<br />    while (</span><span style="color: #9876AA">$row </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_fetch_array</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">)) {<br />        print </span><span style="color: #DD0000">"</span><span style="color: #007700">{</span><span style="color: #9876AA">$row</span><span style="color: #007700">[</span><span style="color: #9876AA">0</span><span style="color: #007700">]}</span><span style="color: #DD0000">, </span><span style="color: #007700">{</span><span style="color: #9876AA">$row</span><span style="color: #007700">[</span><span style="color: #9876AA">1</span><span style="color: #007700">]}</span><span style="color: #DD0000">, </span><span style="color: #007700">{</span><span style="color: #9876AA">$row</span><span style="color: #007700">[</span><span style="color: #9876AA">2</span><span style="color: #007700">]}</span><span style="color: #DD0000">\n"</span><span style="color: #007700">;<br />    }<br />}<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>Pook, cat, 3.2<br>Rickety Ride, goat, 9.7<br>Peaches, dog, 12.3<br></span></div>    </div>   </div>   <div class="example" id="recursivecallbackfilteriterator.examples.basic">    <p><strong>示例 #2 Calling stored procedures with IN and OUT parameters</strong></p>    <div class="example-contents"><p>     The stored procedure match_animal in the following example accepts     three different parameters:     <ol type="1">      <li class="listitem">       <p class="para">        an input (IN) parameter that accepts the name of the first animal as        input       </p>      </li>      <li class="listitem">       <p class="para">        an input-output (INOUT) parameter that accepts the name of the second        animal as input and returns the string <span>TRUE</span> if an        animal in the database matches that name       </p>      </li>      <li class="listitem">       <p class="para">        an output (OUT) parameter that returns the sum of the weight of the        two identified animals       </p>      </li>     </ol>     In addition, the stored procedure returns a result set consisting of the     animals listed in alphabetic order starting at the animal corresponding     to the input value of the first parameter and ending at the animal     corresponding to the input value of the second parameter.    </p></div>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$sql </span><span style="color: #007700">= </span><span style="color: #DD0000">'CALL match_animal(?, ?, ?)'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_connect</span><span style="color: #007700">(</span><span style="color: #9876AA">$database</span><span style="color: #007700">, </span><span style="color: #9876AA">$user</span><span style="color: #007700">, </span><span style="color: #9876AA">$password</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$stmt </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_prepare</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #9876AA">$sql</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$name </span><span style="color: #007700">= </span><span style="color: #DD0000">"Peaches"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$second_name </span><span style="color: #007700">= </span><span style="color: #DD0000">"Rickety Ride"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$weight </span><span style="color: #007700">= </span><span style="color: #9876AA">0</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">db2_bind_param</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">, </span><span style="color: #9876AA">1</span><span style="color: #007700">, </span><span style="color: #DD0000">"name"</span><span style="color: #007700">, </span><span style="color: #9876AA">DB2_PARAM_IN</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">db2_bind_param</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">, </span><span style="color: #9876AA">2</span><span style="color: #007700">, </span><span style="color: #DD0000">"second_name"</span><span style="color: #007700">, </span><span style="color: #9876AA">DB2_PARAM_INOUT</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">db2_bind_param</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">, </span><span style="color: #9876AA">3</span><span style="color: #007700">, </span><span style="color: #DD0000">"weight"</span><span style="color: #007700">, </span><span style="color: #9876AA">DB2_PARAM_OUT</span><span style="color: #007700">);<br /><br />print </span><span style="color: #DD0000">"Values of bound parameters _before_ CALL:\n"</span><span style="color: #007700">;<br />print </span><span style="color: #DD0000">"  1: </span><span style="color: #007700">{</span><span style="color: #9876AA">$name</span><span style="color: #007700">}</span><span style="color: #DD0000"> 2: </span><span style="color: #007700">{</span><span style="color: #9876AA">$second_name</span><span style="color: #007700">}</span><span style="color: #DD0000"> 3: </span><span style="color: #007700">{</span><span style="color: #9876AA">$weight</span><span style="color: #007700">}</span><span style="color: #DD0000">\n\n"</span><span style="color: #007700">;<br /><br />if (</span><span style="color: #9876AA">db2_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">)) {<br />    print </span><span style="color: #DD0000">"Values of bound parameters _after_ CALL:\n"</span><span style="color: #007700">;<br />    print </span><span style="color: #DD0000">"  1: </span><span style="color: #007700">{</span><span style="color: #9876AA">$name</span><span style="color: #007700">}</span><span style="color: #DD0000"> 2: </span><span style="color: #007700">{</span><span style="color: #9876AA">$second_name</span><span style="color: #007700">}</span><span style="color: #DD0000"> 3: </span><span style="color: #007700">{</span><span style="color: #9876AA">$weight</span><span style="color: #007700">}</span><span style="color: #DD0000">\n\n"</span><span style="color: #007700">;<br /><br />    print </span><span style="color: #DD0000">"Results:\n"</span><span style="color: #007700">;<br />    while (</span><span style="color: #9876AA">$row </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_fetch_array</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">)) {<br />        print </span><span style="color: #DD0000">"  </span><span style="color: #007700">{</span><span style="color: #9876AA">$row</span><span style="color: #007700">[</span><span style="color: #9876AA">0</span><span style="color: #007700">]}</span><span style="color: #DD0000">, </span><span style="color: #007700">{</span><span style="color: #9876AA">$row</span><span style="color: #007700">[</span><span style="color: #9876AA">1</span><span style="color: #007700">]}</span><span style="color: #DD0000">, </span><span style="color: #007700">{</span><span style="color: #9876AA">$row</span><span style="color: #007700">[</span><span style="color: #9876AA">2</span><span style="color: #007700">]}</span><span style="color: #DD0000">\n"</span><span style="color: #007700">;<br />    }<br />}<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>Values of bound parameters _before_ CALL:<br>  1: Peaches 2: Rickety Ride 3: 0<br><br>Values of bound parameters _after_ CALL:<br>  1: Peaches 2: TRUE 3: 22<br><br>Results:<br>  Peaches, dog, 12.3<br>  Pook, cat, 3.2<br>  Rickety Ride, goat, 9.7<br></span></div>    </div>   </div>   <div class="example" id="recursivedirectoryiterator.getsubpath.example.basic">    <p><strong>示例 #3 Inserting a binary large object (BLOB) directly from a file</strong></p>    <div class="example-contents"><p>     The data for large objects are typically stored in files, such as XML     documents or audio files. Rather than reading an entire file into a PHP     variable, and then binding that PHP variable into an SQL statement, you     can avoid some memory overhead by binding the file directly to the input     parameter of your SQL statement. The following example demonstrates how     to bind a file directly into a BLOB column.    </p></div>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />$stmt </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_prepare</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"INSERT INTO animal_pictures(picture) VALUES (?)"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$picture </span><span style="color: #007700">= </span><span style="color: #DD0000">"/opt/albums/spook/grooming.jpg"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$rc </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_bind_param</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">, </span><span style="color: #9876AA">1</span><span style="color: #007700">, </span><span style="color: #DD0000">"picture"</span><span style="color: #007700">, </span><span style="color: #9876AA">DB2_PARAM_FILE</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$rc </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.db2-bind-param-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link db2_execute()} - Executes a prepared SQL statement</span></li>    <li class="member"><span class="function">{@link db2_prepare()} - Prepares an SQL statement to be executed</span></li>   </ul>  </span> </div></div>
 * @return bool true on success or false on failure.*/
function db2_bind_param($stmt, int $parameter_number, string $variable_name, int $parameter_type = DB2_PARAM_IN, int $data_type = 0, int $precision = -1, int $scale = 0): bool {}

/**
*<div id="function.db2-close" class="refentry"> <div class="refnamediv">  <h1 class="refname">db2_close</h1>  <p class="verinfo">(PECL ibm_db2 &gt;= 1.0.0)</p><p class="refpurpose"><span class="refname">db2_close</span> &mdash; <span class="dc-title">   Closes a database connection  </span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.db2-close-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>db2_close</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$connection</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   This function closes a DB2 client connection created with   <span class="function">{@link db2_connect()}</span> and returns the corresponding   resources to the database server.  </p>  <p class="para">   If you attempt to close a persistent DB2 client connection created with   <span class="function">{@link db2_pconnect()}</span>, the close request is ignored and the   persistent DB2 client connection remains available for the next caller.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.db2-close-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">connection</span></dt>      <dd>       <p class="para">        Specifies an active DB2 client connection.       </p>      </dd>        </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.db2-close-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.db2-close-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="example-3749">    <p><strong>示例 #1 Closing a connection</strong></p>    <div class="example-contents"><p>     The following example demonstrates a successful attempt to close a      connection to an IBM DB2, Cloudscape, or Apache Derby database.    </p></div>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'SAMPLE'</span><span style="color: #007700">, </span><span style="color: #DD0000">'db2inst1'</span><span style="color: #007700">, </span><span style="color: #DD0000">'ibmdb2'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$rc </span><span style="color: #007700">= </span><span style="color: #9876AA">db2_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br />if (</span><span style="color: #9876AA">$rc</span><span style="color: #007700">) {<br />    echo </span><span style="color: #DD0000">"Connection was successfully closed."</span><span style="color: #007700">;<br />}<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>Connection was successfully closed.<br></span></div>    </div>   </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.db2-close-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link db2_connect()} - Returns a connection to a database</span></li>    <li class="member"><span class="function">{@link db2_pclose()} - Closes a persistent database connection</span></li>    <li class="member"><span class="function">{@link db2_pconnect()} - Returns a persistent connection to a database</span></li>   </ul>  </span> </div></div>
 * @return bool true on success or false on failure.*/
function db2_close($connection): bool {}

/**
