/**
*<div id="function.imap-fetchstructure" class="refentry"> <div class="refnamediv">  <h1 class="refname">imap_fetchstructure</h1>  <p class="verinfo">(PHP 4, PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">imap_fetchstructure</span> &mdash; <span class="dc-title">Read the structure of a particular message</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.imap-fetchstructure-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>imap_fetchstructure</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.imap-connection.php" class="type IMAP\Connection" style="color:#EAB766">IMAP\Connection</a></span> <span class="parameter" style="color:#3A95FF">$imap</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$message_num</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$flags</span><span class="initializer"> = 0</span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.stdclass.php" class="type stdClass" style="color:#EAB766">stdClass</a></span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>  <p class="para rdfs-comment">   Fetches all the structured information for a given message.   </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.imap-fetchstructure-parameters">  <h3 class="title">参数</h3>  <span>   <dl>    <dt><span class="parameter" style="color:#3A95FF">imap</span></dt><dd><p class="para"><span class="classname"><a href="https://www.php.net/manual/zh/class.imap-connection.php" class="classname">IMAP\Connection</a></span> 实例。</p></dd>         <dt><span class="parameter" style="color:#3A95FF">message_num</span></dt>     <dd>      <p class="para">       The message number      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">flags</span></dt>     <dd>      <p class="para">       This optional parameter only has a single option,        <strong><span>FT_UID</span></strong>, which tells the function to treat the       <span class="parameter" style="color:#3A95FF">message_num</span> argument as a        <span>UID</span>.      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.imap-fetchstructure-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   Returns an object with properties listed in the table below,  或者在失败时返回 <strong><span>false</span></strong>.  </p>  <p class="para">   <table class="doctable table">    <caption><strong>     Returned Object for <span class="function"><strong style="color:#CC7832">imap_fetchstructure()</strong></span>    </strong></caption>         <tbody class="tbody">      <tr>       <td>type</td>       <td>Primary body type</td>      </tr>      <tr>       <td>encoding</td>       <td>Body transfer encoding</td>      </tr>      <tr>       <td>ifsubtype</td>       <td><strong><span>true</span></strong> if there is a subtype string</td>      </tr>      <tr>       <td>subtype</td>       <td><abbr title="Multipurpose Internet Mail Extensions">MIME</abbr> subtype</td>      </tr>      <tr>       <td>ifdescription</td>       <td><strong><span>true</span></strong> if there is a description string</td>      </tr>      <tr>       <td>description</td>       <td>Content description string</td>      </tr>      <tr>       <td>ifid</td>       <td><strong><span>true</span></strong> if there is an identification string</td>      </tr>      <tr>       <td>id</td>       <td>Identification string</td>      </tr>      <tr>       <td>lines</td>       <td>Number of lines</td>      </tr>      <tr>       <td>bytes</td>       <td>Number of bytes</td>      </tr>      <tr>       <td>ifdisposition</td>       <td><strong><span>true</span></strong> if there is a disposition string</td>      </tr>      <tr>       <td>disposition</td>       <td>Disposition string</td>      </tr>      <tr>       <td>ifdparameters</td>       <td><strong><span>true</span></strong> if the <var class="varname">dparameters</var> array exists</td>      </tr>      <tr>       <td>dparameters</td>       <td>An array of objects where each object has an        <span>&quot;attribute&quot;</span> and a <span>&quot;value&quot;</span>        property corresponding to the parameters on the        <span>Content-disposition</span> <abbr title="Multipurpose Internet Mail Extensions">MIME</abbr>        header.</td>      </tr>      <tr>       <td>ifparameters</td>       <td><strong><span>true</span></strong> if the parameters array exists</td>      </tr>      <tr>       <td>parameters</td>       <td>An array of objects where each object has an        <span>&quot;attribute&quot;</span> and a <span>&quot;value&quot;</span>        property.</td>      </tr>      <tr>       <td>parts</td>       <td>An array of objects identical in structure to the top-level       object, each of which corresponds to a <abbr title="Multipurpose Internet Mail Extensions">MIME</abbr> body       part.</td>      </tr>     </tbody>       </table>  </p>  <p class="para">   <table class="doctable table">    <caption><strong>Primary body type (value may vary with used library, use of constants is recommended)</strong></caption>         <thead>      <tr><th>Value</th><th>Type</th><th>Constant</th></tr>     </thead>     <tbody class="tbody">      <tr><td>0</td><td>text</td><td>TYPETEXT</td></tr>      <tr><td>1</td><td>multipart</td><td>TYPEMULTIPART</td></tr>      <tr><td>2</td><td>message</td><td>TYPEMESSAGE</td></tr>      <tr><td>3</td><td>application</td><td>TYPEAPPLICATION</td></tr>      <tr><td>4</td><td>audio</td><td>TYPEAUDIO</td></tr>      <tr><td>5</td><td>image</td><td>TYPEIMAGE</td></tr>      <tr><td>6</td><td>video</td><td>TYPEVIDEO</td></tr>      <tr><td>7</td><td>model</td><td>TYPEMODEL</td></tr>      <tr><td>8</td><td>other</td><td>TYPEOTHER</td></tr>     </tbody>       </table>  </p>  <p class="para">   <table class="doctable table">    <caption><strong>Transfer encodings (value may vary with used library, use of constants is recommended)</strong></caption>         <thead>      <tr><th>Value</th><th>Type</th><th>Constant</th></tr>     </thead>     <tbody class="tbody">      <tr><td>0</td><td>7bit</td><td>ENC7BIT</td></tr>      <tr><td>1</td><td>8bit</td><td>ENC8BIT</td></tr>      <tr><td>2</td><td>Binary</td><td>ENCBINARY</td></tr>      <tr><td>3</td><td>Base64</td><td>ENCBASE64</td></tr>      <tr><td>4</td><td>Quoted-Printable</td><td>ENCQUOTEDPRINTABLE</td></tr>      <tr><td>5</td><td>other</td><td>ENCOTHER</td></tr>     </tbody>       </table>  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 changelog" id="refsect1-function.imap-fetchstructure-changelog">  <h3 class="title">更新日志</h3>  <table class="doctable informaltable">       <thead>     <tr>      <th>版本</th>      <th>说明</th>     </tr>    </thead>    <tbody class="tbody">     <tr> <td>8.1.0</td> <td>  现在 <span class="parameter" style="color:#3A95FF">imap</span> 参数接受 <span class="classname"><a href="https://www.php.net/manual/zh/class.imap-connection.php" class="classname">IMAP\Connection</a></span>  实例，之前接受有效的 <span>imap</span> <a href="https://www.php.net/manual/zh/language.types.resource.php" class="link">resource</a>。 </td></tr>    </tbody>     </table> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.imap-fetchstructure-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link imap_fetchbody()} - Fetch a particular section of the body of the message</span></li>    <li class="member"><span class="function">{@link imap_bodystruct()} - Read the structure of a specified body section of a specific message</span></li>   </ul>  </span> </div></div>
 * @return object|stdClass|false an object includes the envelope, internal date, size, flags and*/
function imap_fetchstructure(#[LanguageLevelTypeAware(['8.1' => 'IMAP\Connection'], default: 'resource')] $imap, int $message_num, int $flags = 0): stdClass|false {}

/**
*<div id="function.imap-gc" class="refentry"> <div class="refnamediv">  <h1 class="refname">imap_gc</h1>  <p class="verinfo">(PHP 5 &gt;= 5.3.0, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">imap_gc</span> &mdash; <span class="dc-title">Clears IMAP cache</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.imap-gc-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>imap_gc</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.imap-connection.php" class="type IMAP\Connection" style="color:#EAB766">IMAP\Connection</a></span> <span class="parameter" style="color:#3A95FF">$imap</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$flags</span></span>): <span class="type" style="color:#EAB766"><span class="type true" style="color:#EAB766">true</span></span></div>  <p class="para rdfs-comment">   Purges the cache of entries of a specific type.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.imap-gc-parameters">  <h3 class="title">参数</h3>  <span>   <dl>     <dt><span class="parameter" style="color:#3A95FF">imap</span></dt><dd><p class="para"><span class="classname"><a href="https://www.php.net/manual/zh/class.imap-connection.php" class="classname">IMAP\Connection</a></span> 实例。</p></dd>         <dt><span class="parameter" style="color:#3A95FF">flags</span></dt>     <dd>      <p class="para">       Specifies the cache to purge. It may one or a combination       of the following constants:        <strong><span>IMAP_GC_ELT</span></strong> (message cache elements),        <strong><span>IMAP_GC_ENV</span></strong> (envelope and bodies),       <strong><span>IMAP_GC_TEXTS</span></strong> (texts).      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.imap-gc-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   总是返回 <strong><span>true</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 errors" id="refsect1-function.imap-gc-errors">  <h3 class="title">错误／异常</h3>  <p class="para">   Throws a <span class="classname"><a href="https://www.php.net/manual/zh/class.valueerror.php" class="classname">ValueError</a></span> if <span class="parameter" style="color:#3A95FF">flags</span>   is invalid.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 changelog" id="refsect1-function.imap-gc-changelog">  <h3 class="title">更新日志</h3>  <table class="doctable informaltable">       <thead>     <tr>      <th>版本</th>      <th>说明</th>     </tr>    </thead>    <tbody class="tbody">     <tr> <td>8.1.0</td> <td>  现在 <span class="parameter" style="color:#3A95FF">imap</span> 参数接受 <span class="classname"><a href="https://www.php.net/manual/zh/class.imap-connection.php" class="classname">IMAP\Connection</a></span>  实例，之前接受有效的 <span>imap</span> <a href="https://www.php.net/manual/zh/language.types.resource.php" class="link">resource</a>。 </td></tr>     <tr>      <td>8.0.0</td>      <td>       A <span class="classname"><a href="https://www.php.net/manual/zh/class.valueerror.php" class="classname">ValueError</a></span> is now thrown on invalid       <span class="parameter" style="color:#3A95FF">flags</span> parameter values. Previously,       a warning was emitted and the function returned <strong><span>false</span></strong>.      </td>     </tr>    </tbody>     </table> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.imap-gc-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="">    <p><strong>示例 #1 <span class="function"><strong style="color:#CC7832">imap_gc()</strong></span> example</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$mbox </span><span style="color: #007700">= </span><span style="color: #9876AA">imap_open</span><span style="color: #007700">(</span><span style="color: #DD0000">"{imap.example.org:143}"</span><span style="color: #007700">, </span><span style="color: #DD0000">"username"</span><span style="color: #007700">, </span><span style="color: #DD0000">"password"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">imap_gc</span><span style="color: #007700">(</span><span style="color: #9876AA">$mbox</span><span style="color: #007700">, </span><span style="color: #9876AA">IMAP_GC_ELT</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span> </div></div>
*/
function imap_gc(
    #[LanguageLevelTypeAware(['8.1' => 'IMAP\Connection'], default: 'resource')] $imap,
    #[PhpStormStubsElementAvailable(from: '5.3', to: '7.4')] int $flags = 0,
    #[PhpStormStubsElementAvailable(from: '8.0')] int $flags
) {}

