function imap_append(#[LanguageLevelTypeAware(['8.1' => 'IMAP\Connection'], default: 'resource')] $imap, string $folder, string $message, ?string $options = null, ?string $internal_date = null): bool {}

/**
*<div id="function.imap-ping" class="refentry"> <div class="refnamediv">  <h1 class="refname">imap_ping</h1>  <p class="verinfo">(PHP 4, PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">imap_ping</span> &mdash; <span class="dc-title">Check if the IMAP stream is still active</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.imap-ping-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>imap_ping</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.imap-connection.php" class="type IMAP\Connection" style="color:#EAB766">IMAP\Connection</a></span> <span class="parameter" style="color:#3A95FF">$imap</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   <span class="function"><strong style="color:#CC7832">imap_ping()</strong></span> pings the stream to see if it&#039;s still   active. It may discover new mail; this is the preferred method for a   periodic &quot;new mail check&quot; as well as a &quot;keep alive&quot; for servers which   have inactivity timeout.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.imap-ping-parameters">  <h3 class="title">参数</h3>  <span>   <dl>    <dt><span class="parameter" style="color:#3A95FF">imap</span></dt><dd><p class="para"><span class="classname"><a href="https://www.php.net/manual/zh/class.imap-connection.php" class="classname">IMAP\Connection</a></span> 实例。</p></dd>   </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.imap-ping-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   Returns <strong><span>true</span></strong> if the stream is still alive, <strong><span>false</span></strong> otherwise.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 changelog" id="refsect1-function.imap-ping-changelog">  <h3 class="title">更新日志</h3>  <table class="doctable informaltable">       <thead>     <tr>      <th>版本</th>      <th>说明</th>     </tr>    </thead>    <tbody class="tbody">     <tr> <td>8.1.0</td> <td>  现在 <span class="parameter" style="color:#3A95FF">imap</span> 参数接受 <span class="classname"><a href="https://www.php.net/manual/zh/class.imap-connection.php" class="classname">IMAP\Connection</a></span>  实例，之前接受有效的 <span>imap</span> <a href="https://www.php.net/manual/zh/language.types.resource.php" class="link">resource</a>。 </td></tr>    </tbody>     </table> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.imap-ping-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="">    <p><strong>示例 #1 <span class="function"><strong style="color:#CC7832">imap_ping()</strong></span> Example</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$imap </span><span style="color: #007700">= </span><span style="color: #9876AA">imap_open</span><span style="color: #007700">(</span><span style="color: #DD0000">"{imap.example.org}"</span><span style="color: #007700">, </span><span style="color: #DD0000">"mailadmin"</span><span style="color: #007700">, </span><span style="color: #DD0000">"password"</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// after some sleeping<br /></span><span style="color: #007700">if (!</span><span style="color: #9876AA">imap_ping</span><span style="color: #007700">(</span><span style="color: #9876AA">$imap</span><span style="color: #007700">)) {<br />    </span><span style="color: #FF8000">// do some stuff to reconnect<br /></span><span style="color: #007700">}<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span> </div></div>
 * @return bool <b>TRUE</b> if the stream is still alive, <b>FALSE</b> otherwise.*/
function imap_ping(#[LanguageLevelTypeAware(['8.1' => 'IMAP\Connection'], default: 'resource')] $imap): bool {}

