/**
*<div id="class.intldatepatterngenerator" class="reference"> <h1 class="title">The IntlDatePatternGenerator class</h1>  <div class="partintro"><p class="verinfo">(PHP 8 &gt;= 8.1.0)</p>    <div class="section" id="intldatepatterngenerator.intro">   <h2 class="title">简介</h2>      <p class="simpara">    Generates localized date and/or time format pattern strings suitable for use in <span class="classname"><a href="https://www.php.net/manual/zh/class.intldateformatter.php" class="classname">IntlDateFormatter</a></span>.   </p>   </div>    <div class="section" id="intldatepatterngenerator.synopsis">   <h2 class="title">类摘要</h2>      <div class="classsynopsis"><div class="classsynopsisinfo">         <span class="modifier">class</span> <strong class="classname"><strong class="classname">IntlDatePatternGenerator</strong></strong>     {</div>    <div class="classsynopsisinfo classsynopsisinfo_comment">// 方法 </div>    <div class="constructorsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/intldatepatterngenerator.create.php" class="methodname" style="color:#CC7832">__construct</a></span>(<span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">string</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">$locale</span><span class="initializer"> = <strong><span>null</span></strong></span></span>)</div>    <div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="modifier">static</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/intldatepatterngenerator.create.php" class="methodname" style="color:#CC7832">create</a></span>(<span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">string</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">$locale</span><span class="initializer"> = <strong><span>null</span></strong></span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.intldatepatterngenerator.php" class="type IntlDatePatternGenerator" style="color:#EAB766">IntlDatePatternGenerator</a></span></span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/intldatepatterngenerator.getbestpattern.php" class="methodname" style="color:#CC7832">getBestPattern</a></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$skeleton</span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">string</span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>   }</div>     </div> </div> <h2>目录</h2><ul class="chunklist chunklist_reference"><li>{@link IntlDatePatternGenerator::create} — Creates a new IntlDatePatternGenerator instance</li><li>{@link IntlDatePatternGenerator::getBestPattern} — Determines the most suitable date/time format</li></ul></div>
*/
class IntlDatePatternGenerator
{
    public function __construct(?string $locale = null) {}

/**
*<div id="intldatepatterngenerator.create" class="refentry"> <div class="refnamediv">  <h1 class="refname">IntlDatePatternGenerator::create</h1>  <h1 class="refname">IntlDatePatternGenerator::__construct</h1>  <p class="verinfo">(PHP 8 &gt;= 8.1.0)</p><p class="refpurpose"><span class="refname">IntlDatePatternGenerator::create</span> -- <span class="refname">IntlDatePatternGenerator::__construct</span> &mdash; <span class="dc-title">Creates a new IntlDatePatternGenerator instance</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-intldatepatterngenerator.create-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="modifier">static</span> <span class="methodname" style="color:#CC7832"><strong>IntlDatePatternGenerator::create</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">string</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">$locale</span><span class="initializer"> = <strong><span>null</span></strong></span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.intldatepatterngenerator.php" class="type IntlDatePatternGenerator" style="color:#EAB766">IntlDatePatternGenerator</a></span></span></div>  <div class="constructorsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>IntlDatePatternGenerator::__construct</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">string</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">$locale</span><span class="initializer"> = <strong><span>null</span></strong></span></span>)</div>  <p class="para rdfs-comment">   Creates a new <span class="classname"><a href="https://www.php.net/manual/zh/class.intldatepatterngenerator.php" class="classname">IntlDatePatternGenerator</a></span> instance.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-intldatepatterngenerator.create-parameters">  <h3 class="title">参数</h3>  <dl>       <dt><span class="parameter" style="color:#3A95FF">locale</span></dt>    <dd>     <span>      The locale.      If <strong><span>null</span></strong> is passed, uses the ini setting <a href="https://www.php.net/manual/zh/intl.configuration.php#ini.intl.default-locale" class="link">intl.default_locale</a>.     </span>    </dd>     </dl> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-intldatepatterngenerator.create-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   Returns an <span class="classname"><a href="https://www.php.net/manual/zh/class.intldatepatterngenerator.php" class="classname">IntlDatePatternGenerator</a></span> instance on success, or <strong><span>null</span></strong> on failure.  </p> </div></div>
