/**
*<div id="intldatepatterngenerator.getbestpattern" class="refentry"> <div class="refnamediv">  <h1 class="refname">IntlDatePatternGenerator::getBestPattern</h1>  <p class="verinfo">(PHP 8 &gt;= 8.1.0)</p><p class="refpurpose"><span class="refname">IntlDatePatternGenerator::getBestPattern</span> &mdash; <span class="dc-title">Determines the most suitable date/time format</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-intldatepatterngenerator.getbestpattern-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>IntlDatePatternGenerator::getBestPattern</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$skeleton</span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">string</span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>  <p class="para rdfs-comment">   Determines which date/time format is most suitable for a particular locale.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-intldatepatterngenerator.getbestpattern-parameters">  <h3 class="title">参数</h3>  <dl>       <dt><span class="parameter" style="color:#3A95FF">skeleton</span></dt>    <dd>     <span>      The skeleton.     </span>    </dd>     </dl> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-intldatepatterngenerator.getbestpattern-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   Returns a format, accepted by <span class="methodname" style="color:#CC7832">{@link DateTimeInterface::format()}</span> on success,  或者在失败时返回 <strong><span>false</span></strong>.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-intldatepatterngenerator.getbestpattern-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="">    <p><strong>示例 #1 <span class="methodname" style="color:#CC7832"><strong>IntlDatePatternGenerator::getBestPattern()</strong></span> example</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$skeleton </span><span style="color: #007700">= </span><span style="color: #DD0000">'YYYYMMdd'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$today </span><span style="color: #007700">= </span><span style="color: #9876AA">\DateTimeImmutable</span><span style="color: #007700">::</span><span style="color: #9876AA">createFromFormat</span><span style="color: #007700">(</span><span style="color: #DD0000">'Y-m-d'</span><span style="color: #007700">, </span><span style="color: #DD0000">'2021-04-24'</span><span style="color: #007700">);<br /> <br /></span><span style="color: #9876AA">$patternGenerator </span><span style="color: #007700">= new </span><span style="color: #9876AA">\IntlDatePatternGenerator</span><span style="color: #007700">(</span><span style="color: #DD0000">'de_DE'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$pattern </span><span style="color: #007700">= </span><span style="color: #9876AA">$patternGenerator</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">getBestPattern</span><span style="color: #007700">(</span><span style="color: #9876AA">$skeleton</span><span style="color: #007700">);<br />echo </span><span style="color: #DD0000">'de: '</span><span style="color: #007700">, </span><span style="color: #9876AA">\IntlDateFormatter</span><span style="color: #007700">::</span><span style="color: #9876AA">formatObject</span><span style="color: #007700">(</span><span style="color: #9876AA">$today</span><span style="color: #007700">, </span><span style="color: #9876AA">$pattern</span><span style="color: #007700">, </span><span style="color: #DD0000">'de_DE'</span><span style="color: #007700">), </span><span style="color: #DD0000">"\n"</span><span style="color: #007700">;<br /> <br /></span><span style="color: #9876AA">$patternGenerator </span><span style="color: #007700">= new </span><span style="color: #9876AA">\IntlDatePatternGenerator</span><span style="color: #007700">(</span><span style="color: #DD0000">'en_US'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$pattern </span><span style="color: #007700">= </span><span style="color: #9876AA">$patternGenerator</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">getBestPattern</span><span style="color: #007700">(</span><span style="color: #9876AA">$skeleton</span><span style="color: #007700">);<br />echo </span><span style="color: #DD0000">'en: '</span><span style="color: #007700">, </span><span style="color: #9876AA">\IntlDateFormatter</span><span style="color: #007700">::</span><span style="color: #9876AA">formatObject</span><span style="color: #007700">(</span><span style="color: #9876AA">$today</span><span style="color: #007700">, </span><span style="color: #9876AA">$pattern</span><span style="color: #007700">, </span><span style="color: #DD0000">'en_US'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>de: 24.04.2021<br>en: 04/24/2021<br></span></div>    </div>   </div>  </span> </div></div>
*/
    public function getBestPattern(string $skeleton): string|false {}
