    public function next_result(): bool {}

/**
*<div id="mysqli-stmt.num-rows" class="refentry"> <div class="refnamediv">  <h1 class="refname">mysqli_stmt::$num_rows</h1>  <h1 class="refname">mysqli_stmt::num_rows</h1>  <h1 class="refname">mysqli_stmt_num_rows</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">mysqli_stmt::$num_rows</span> -- <span class="refname">mysqli_stmt::num_rows</span> -- <span class="refname">mysqli_stmt_num_rows</span> &mdash; <span class="dc-title">Returns the number of rows fetched from the server</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-mysqli-stmt.num-rows-description">  <h3 class="title">说明</h3>  <p class="para">面向对象风格</p>  <div class="fieldsynopsis"><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">int</span>|<span class="type" style="color:#EAB766">string</span></span> <var class="varname"><a href="https://www.php.net/manual/zh/mysqli-stmt.num-rows.php">$mysqli_stmt-&gt;num_rows</a></var>;</div>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>mysqli_stmt::num_rows</strong></span>(): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">int</span>|<span class="type" style="color:#EAB766">string</span></span></div>  <p class="para rdfs-comment">过程化风格</p>  <div class="methodsynopsis dc-description"><span class="methodname" style="color:#CC7832"><strong>mysqli_stmt_num_rows</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.mysqli-stmt.php" class="type mysqli_stmt" style="color:#EAB766">mysqli_stmt</a></span> <span class="parameter" style="color:#3A95FF">$statement</span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">int</span>|<span class="type" style="color:#EAB766">string</span></span></div>  <p class="para rdfs-comment">   Returns the number of rows buffered in the statement. This function will only   work after <span class="function"><a href="https://www.php.net/manual/zh/mysqli-stmt.store-result.php" class="function">mysqli_stmt_store_result()</a></span> is called to buffer   the entire result set in the statement handle.  </p>  <p class="para">   This function returns <span>0</span> unless all rows have been   fetched from the server.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-mysqli-stmt.num-rows-parameters">  <h3 class="title">参数</h3>  <span>   <dl>    <dt><span class="parameter" style="color:#3A95FF">statement</span></dt><dd><p class="para">仅以过程化样式：由<span class="function"><a href="https://www.php.net/manual/zh/mysqli.stmt-init.php" class="function">mysqli_stmt_init()</a></span> 返回的 <span class="classname"><a href="https://www.php.net/manual/zh/class.mysqli-stmt.php" class="classname">mysqli_stmt</a></span> 对象。</p></dd>   </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-mysqli-stmt.num-rows-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   An <span class="type" style="color:#EAB766">int</span> representing the number of buffered rows.   Returns <span>0</span> in unbuffered mode unless all rows have been   fetched from the server.  </p>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>: <p class="para">If the number of rows is greater than <strong><span>PHP_INT_MAX</span></strong>,the number will be returned as a <span class="type" style="color:#EAB766">string</span>.</p></p></blockquote> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-mysqli-stmt.num-rows-examples">  <h3 class="title">示例</h3>  <div class="example" id="example-4532">   <p><strong>示例 #1 面向对象风格</strong></p>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />mysqli_report</span><span style="color: #007700">(</span><span style="color: #9876AA">MYSQLI_REPORT_ERROR </span><span style="color: #007700">| </span><span style="color: #9876AA">MYSQLI_REPORT_STRICT</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$mysqli </span><span style="color: #007700">= new </span><span style="color: #9876AA">mysqli</span><span style="color: #007700">(</span><span style="color: #DD0000">"localhost"</span><span style="color: #007700">, </span><span style="color: #DD0000">"my_user"</span><span style="color: #007700">, </span><span style="color: #DD0000">"my_password"</span><span style="color: #007700">, </span><span style="color: #DD0000">"world"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$query </span><span style="color: #007700">= </span><span style="color: #DD0000">"SELECT Name, CountryCode FROM City ORDER BY Name LIMIT 20"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$stmt </span><span style="color: #007700">= </span><span style="color: #9876AA">$mysqli</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">prepare</span><span style="color: #007700">(</span><span style="color: #9876AA">$query</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$stmt</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">execute</span><span style="color: #007700">();<br /><br /></span><span style="color: #FF8000">// store the result in an internal buffer <br /></span><span style="color: #9876AA">$stmt</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">store_result</span><span style="color: #007700">();<br /><br /></span><span style="color: #9876AA">printf</span><span style="color: #007700">(</span><span style="color: #DD0000">"Number of rows: %d.\n"</span><span style="color: #007700">, </span><span style="color: #9876AA">$stmt</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">num_rows</span><span style="color: #007700">);</span></span></span></div>   </div>  </div>  <div class="example" id="example-4535">   <p><strong>示例 #2 过程化风格</strong></p>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />mysqli_report</span><span style="color: #007700">(</span><span style="color: #9876AA">MYSQLI_REPORT_ERROR </span><span style="color: #007700">| </span><span style="color: #9876AA">MYSQLI_REPORT_STRICT</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$link </span><span style="color: #007700">= </span><span style="color: #9876AA">mysqli_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">"localhost"</span><span style="color: #007700">, </span><span style="color: #DD0000">"my_user"</span><span style="color: #007700">, </span><span style="color: #DD0000">"my_password"</span><span style="color: #007700">, </span><span style="color: #DD0000">"world"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$query </span><span style="color: #007700">= </span><span style="color: #DD0000">"SELECT Name, CountryCode FROM City ORDER BY Name LIMIT 20"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$stmt </span><span style="color: #007700">= </span><span style="color: #9876AA">mysqli_prepare</span><span style="color: #007700">(</span><span style="color: #9876AA">$link</span><span style="color: #007700">, </span><span style="color: #9876AA">$query</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">mysqli_stmt_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// store the result in an internal buffer <br /></span><span style="color: #9876AA">mysqli_stmt_store_result</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">printf</span><span style="color: #007700">(</span><span style="color: #DD0000">"Number of rows: %d.\n"</span><span style="color: #007700">, </span><span style="color: #9876AA">mysqli_stmt_num_rows</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">));</span></span></span></div>   </div>   <div class="example-contents"><p>以上示例会输出：</p></div>   <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>Number of rows: 20.<br></span></div>   </div>  </div> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-mysqli-stmt.num-rows-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function"><a href="https://www.php.net/manual/zh/mysqli-stmt.store-result.php" class="function" rel="rdfs-seeAlso">mysqli_stmt_store_result()</a> - Stores a result set in an internal buffer</span></li>    <li class="member"><span class="function"><a href="https://www.php.net/manual/zh/mysqli-stmt.affected-rows.php" class="function" rel="rdfs-seeAlso">mysqli_stmt_affected_rows()</a> - Returns the total number of rows changed, deleted, inserted, or  matched by the last statement executed</span></li>    <li class="member"><span class="function"><a href="https://www.php.net/manual/zh/mysqli.prepare.php" class="function" rel="rdfs-seeAlso">mysqli_prepare()</a> - 预处理执行 SQL</span></li>   </ul>  </span> </div></div>
     * @return string|int An integer representing the number of rows in result set.*/
    public function num_rows(): string|int {}

/**
*<div id="mysqli-stmt.send-long-data" class="refentry"> <div class="refnamediv">  <h1 class="refname">mysqli_stmt::send_long_data</h1>  <h1 class="refname">mysqli_stmt_send_long_data</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">mysqli_stmt::send_long_data</span> -- <span class="refname">mysqli_stmt_send_long_data</span> &mdash; <span class="dc-title">Send data in blocks</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-mysqli-stmt.send-long-data-description">  <h3 class="title">说明</h3>  <p class="para">面向对象风格</p>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>mysqli_stmt::send_long_data</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$param_num</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$data</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">过程化风格</p>  <div class="methodsynopsis dc-description"><span class="methodname" style="color:#CC7832"><strong>mysqli_stmt_send_long_data</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.mysqli-stmt.php" class="type mysqli_stmt" style="color:#EAB766">mysqli_stmt</a></span> <span class="parameter" style="color:#3A95FF">$statement</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$param_num</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$data</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   Allows to send parameter data to the server in pieces (or chunks), e.g. if the   size of a blob exceeds the size of <span>max_allowed_packet</span>.   This function can be called multiple times to send the parts of a character or   binary data value for a column, which must be one of the TEXT or BLOB datatypes.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-mysqli-stmt.send-long-data-parameters">  <h3 class="title">参数</h3>  <span>   <dl>    <dt><span class="parameter" style="color:#3A95FF">statement</span></dt><dd><p class="para">仅以过程化样式：由<span class="function"><a href="https://www.php.net/manual/zh/mysqli.stmt-init.php" class="function">mysqli_stmt_init()</a></span> 返回的 <span class="classname"><a href="https://www.php.net/manual/zh/class.mysqli-stmt.php" class="classname">mysqli_stmt</a></span> 对象。</p></dd>         <dt><span class="parameter" style="color:#3A95FF">param_num</span></dt>     <dd>      <p class="para">       Indicates which parameter to associate the data with. Parameters are       numbered beginning with 0.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">data</span></dt>     <dd>      <p class="para">       A string containing data to be sent.      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-mysqli-stmt.send-long-data-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-mysqli-stmt.send-long-data-examples">  <h3 class="title">示例</h3>  <div class="example" id="example-4553">   <p><strong>示例 #1 面向对象风格</strong></p>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />$stmt </span><span style="color: #007700">= </span><span style="color: #9876AA">$mysqli</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">prepare</span><span style="color: #007700">(</span><span style="color: #DD0000">"INSERT INTO messages (message) VALUES (?)"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$null </span><span style="color: #007700">= </span><span style="color: #9876AA">NULL</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$stmt</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">bind_param</span><span style="color: #007700">(</span><span style="color: #DD0000">"b"</span><span style="color: #007700">, </span><span style="color: #9876AA">$null</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$fp </span><span style="color: #007700">= </span><span style="color: #9876AA">fopen</span><span style="color: #007700">(</span><span style="color: #DD0000">"messages.txt"</span><span style="color: #007700">, </span><span style="color: #DD0000">"r"</span><span style="color: #007700">);<br />while (!</span><span style="color: #9876AA">feof</span><span style="color: #007700">(</span><span style="color: #9876AA">$fp</span><span style="color: #007700">)) {<br />    </span><span style="color: #9876AA">$stmt</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">send_long_data</span><span style="color: #007700">(</span><span style="color: #9876AA">0</span><span style="color: #007700">, </span><span style="color: #9876AA">fread</span><span style="color: #007700">(</span><span style="color: #9876AA">$fp</span><span style="color: #007700">, </span><span style="color: #9876AA">8192</span><span style="color: #007700">));<br />}<br /></span><span style="color: #9876AA">fclose</span><span style="color: #007700">(</span><span style="color: #9876AA">$fp</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$stmt</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">execute</span><span style="color: #007700">();<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>  </div>  </div> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-mysqli-stmt.send-long-data-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function"><a href="https://www.php.net/manual/zh/mysqli.prepare.php" class="function" rel="rdfs-seeAlso">mysqli_prepare()</a> - 预处理执行 SQL</span></li>    <li class="member"><span class="function"><a href="https://www.php.net/manual/zh/mysqli-stmt.bind-param.php" class="function" rel="rdfs-seeAlso">mysqli_stmt_bind_param()</a> - Binds variables to a prepared statement as parameters</span></li>   </ul>  </span> </div></div>
     * @return bool true on success or false on failure.*/
    public function send_long_data(int $param_num, string $data): bool {}

    /**
     * No documentation available
     * @removed 5.4
     */
    #[Deprecated(since: '5.3')]
    public function stmt() {}

/**
*<div id="mysqli-stmt.free-result" class="refentry"> <div class="refnamediv">  <h1 class="refname">mysqli_stmt::free_result</h1>  <h1 class="refname">mysqli_stmt_free_result</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">mysqli_stmt::free_result</span> -- <span class="refname">mysqli_stmt_free_result</span> &mdash; <span class="dc-title">Frees stored result memory for the given statement handle</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-mysqli-stmt.free-result-description">  <h3 class="title">说明</h3>  <p class="para">面向对象风格</p>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>mysqli_stmt::free_result</strong></span>(): <span class="type" style="color:#EAB766"><span class="type void" style="color:#EAB766">void</span></span></div>  <p class="para rdfs-comment">过程化风格</p>  <div class="methodsynopsis dc-description"><span class="methodname" style="color:#CC7832"><strong>mysqli_stmt_free_result</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.mysqli-stmt.php" class="type mysqli_stmt" style="color:#EAB766">mysqli_stmt</a></span> <span class="parameter" style="color:#3A95FF">$statement</span></span>): <span class="type" style="color:#EAB766"><span class="type void" style="color:#EAB766">void</span></span></div>  <p class="para rdfs-comment">   Frees the result memory associated with the statement, which was allocated by   <span class="function"><a href="https://www.php.net/manual/zh/mysqli-stmt.store-result.php" class="function">mysqli_stmt_store_result()</a></span>.   </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-mysqli-stmt.free-result-parameters">  <h3 class="title">参数</h3>  <span>   <dl>    <dt><span class="parameter" style="color:#3A95FF">statement</span></dt><dd><p class="para">仅以过程化样式：由<span class="function"><a href="https://www.php.net/manual/zh/mysqli.stmt-init.php" class="function">mysqli_stmt_init()</a></span> 返回的 <span class="classname"><a href="https://www.php.net/manual/zh/class.mysqli-stmt.php" class="classname">mysqli_stmt</a></span> 对象。</p></dd>   </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-mysqli-stmt.free-result-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   没有返回值。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-mysqli-stmt.free-result-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function"><a href="https://www.php.net/manual/zh/mysqli-stmt.store-result.php" class="function" rel="rdfs-seeAlso">mysqli_stmt_store_result()</a> - Stores a result set in an internal buffer</span></li>   </ul>  </span> </div></div>
     * @return void*/
    public function free_result(): void {}

/**
*<div id="mysqli-stmt.reset" class="refentry"> <div class="refnamediv">  <h1 class="refname">mysqli_stmt::reset</h1>  <h1 class="refname">mysqli_stmt_reset</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">mysqli_stmt::reset</span> -- <span class="refname">mysqli_stmt_reset</span> &mdash; <span class="dc-title">Resets a prepared statement</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-mysqli-stmt.reset-description">  <h3 class="title">说明</h3>  <p class="para">面向对象风格</p>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>mysqli_stmt::reset</strong></span>(): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">过程化风格</p>  <div class="methodsynopsis dc-description"><span class="methodname" style="color:#CC7832"><strong>mysqli_stmt_reset</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.mysqli-stmt.php" class="type mysqli_stmt" style="color:#EAB766">mysqli_stmt</a></span> <span class="parameter" style="color:#3A95FF">$statement</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   Resets a prepared statement on client and server to state after prepare.  </p>  <p class="para">    It resets the statement on the server, data sent using <span class="function"><a href="https://www.php.net/manual/zh/mysqli-stmt.send-long-data.php" class="function">mysqli_stmt_send_long_data()</a></span>,     unbuffered result sets and current errors. It does not clear bindings or stored result sets.     Stored result sets will be cleared when executing the prepared statement (or closing it).  </p>  <p class="para">   To prepare a statement with another query use function   <span class="function"><a href="https://www.php.net/manual/zh/mysqli-stmt.prepare.php" class="function">mysqli_stmt_prepare()</a></span>.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-mysqli-stmt.reset-parameters">  <h3 class="title">参数</h3>  <span>   <dl>    <dt><span class="parameter" style="color:#3A95FF">statement</span></dt><dd><p class="para">仅以过程化样式：由<span class="function"><a href="https://www.php.net/manual/zh/mysqli.stmt-init.php" class="function">mysqli_stmt_init()</a></span> 返回的 <span class="classname"><a href="https://www.php.net/manual/zh/class.mysqli-stmt.php" class="classname">mysqli_stmt</a></span> 对象。</p></dd>   </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-mysqli-stmt.reset-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-mysqli-stmt.reset-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function"><a href="https://www.php.net/manual/zh/mysqli.prepare.php" class="function" rel="rdfs-seeAlso">mysqli_prepare()</a> - 预处理执行 SQL</span></li>   </ul>  </span> </div></div>
     * @return bool true on success or false on failure.*/
    public function reset(): bool {}

/**
*<div id="mysqli-stmt.prepare" class="refentry"> <div class="refnamediv">  <h1 class="refname">mysqli_stmt::prepare</h1>  <h1 class="refname">mysqli_stmt_prepare</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">mysqli_stmt::prepare</span> -- <span class="refname">mysqli_stmt_prepare</span> &mdash; <span class="dc-title">Prepares an SQL statement for execution</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-mysqli-stmt.prepare-description">  <h3 class="title">说明</h3>  <p class="para">面向对象风格</p>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>mysqli_stmt::prepare</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$query</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">过程化风格</p>  <div class="methodsynopsis dc-description"><span class="methodname" style="color:#CC7832"><strong>mysqli_stmt_prepare</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.mysqli-stmt.php" class="type mysqli_stmt" style="color:#EAB766">mysqli_stmt</a></span> <span class="parameter" style="color:#3A95FF">$statement</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$query</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   Prepares a statement for execution.   The query must consist of a single SQL statement.  </p>  <p class="para">   The statement template can contain zero or more question mark   (<span>?</span>) parameter markers⁠—also called placeholders.   The parameter markers must be bound to application variables using   <span class="function"><a href="https://www.php.net/manual/zh/mysqli-stmt.bind-param.php" class="function">mysqli_stmt_bind_param()</a></span> before executing the statement.  </p>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    In the case where a statement is passed to    <span class="function"><strong style="color:#CC7832">mysqli_stmt_prepare()</strong></span> that is longer than    <span>max_allowed_packet</span> of the server, the returned    error codes are different depending on whether you are using MySQL    Native Driver (<span>mysqlnd</span>) or MySQL Client Library    (<span>libmysqlclient</span>). The behavior is as follows:   </p>   <ul class="itemizedlist">    <li class="listitem">     <p class="para">      <span>mysqlnd</span> on Linux returns an error code of 1153.      The error message means <q class="quote">got a packet bigger than      <span>max_allowed_packet</span> bytes</q>.     </p>    </li>    <li class="listitem">     <p class="para">      <span>mysqlnd</span> on Windows returns an error code 2006.      This error message means <q class="quote">server has gone away</q>.     </p>    </li>    <li class="listitem">     <p class="para">      <span>libmysqlclient</span> on all platforms returns an error code      2006. This error message means <q class="quote">server has gone      away</q>.     </p>    </li>   </ul>  </p></blockquote> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-mysqli-stmt.prepare-parameters">  <h3 class="title">参数</h3>  <span>   <dl>    <dt><span class="parameter" style="color:#3A95FF">statement</span></dt><dd><p class="para">仅以过程化样式：由<span class="function"><a href="https://www.php.net/manual/zh/mysqli.stmt-init.php" class="function">mysqli_stmt_init()</a></span> 返回的 <span class="classname"><a href="https://www.php.net/manual/zh/class.mysqli-stmt.php" class="classname">mysqli_stmt</a></span> 对象。</p></dd>         <dt><span class="parameter" style="color:#3A95FF">query</span></dt>     <dd>      <p class="para">       The query, as a string. It must consist of a single SQL statement.      </p>      <p class="para">       The SQL statement may contain zero or more parameter markers       represented by question mark (<span>?</span>) characters       at the appropriate positions.      </p>      <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:        <p class="para">        The markers are legal only in certain places in SQL statements.        For example, they are permitted in the <span>VALUES()</span>        list of an <span>INSERT</span> statement (to specify column        values for a row), or in a comparison with a column in a        <span>WHERE</span> clause to specify a comparison value.        However, they are not permitted for identifiers (such as table or        column names).       </p>      </p></blockquote>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-mysqli-stmt.prepare-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 errors" id="refsect1-mysqli-stmt.prepare-errors">  <h3 class="title">错误／异常</h3>  <p class="para">If mysqli error reporting is enabled (<strong><span>MYSQLI_REPORT_ERROR</span></strong>) and the requested operation fails,a warning is generated. If, in addition, the mode is set to <strong><span>MYSQLI_REPORT_STRICT</span></strong>,a <span class="classname"><a href="https://www.php.net/manual/zh/class.mysqli-sql-exception.php" class="classname">mysqli_sql_exception</a></span> is thrown instead.</p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-mysqli-stmt.prepare-examples">  <h3 class="title">示例</h3>  <div class="example" id="example-4544">   <p><strong>示例 #1 <span class="methodname" style="color:#CC7832"><strong>mysqli_stmt::prepare()</strong></span> example</strong></p>   <div class="example-contents"><p>面向对象风格</p></div>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />mysqli_report</span><span style="color: #007700">(</span><span style="color: #9876AA">MYSQLI_REPORT_ERROR </span><span style="color: #007700">| </span><span style="color: #9876AA">MYSQLI_REPORT_STRICT</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$mysqli </span><span style="color: #007700">= new </span><span style="color: #9876AA">mysqli</span><span style="color: #007700">(</span><span style="color: #DD0000">"localhost"</span><span style="color: #007700">, </span><span style="color: #DD0000">"my_user"</span><span style="color: #007700">, </span><span style="color: #DD0000">"my_password"</span><span style="color: #007700">, </span><span style="color: #DD0000">"world"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$city </span><span style="color: #007700">= </span><span style="color: #DD0000">"Amersfoort"</span><span style="color: #007700">;<br /><br /></span><span style="color: #FF8000">// create a prepared statement <br /></span><span style="color: #9876AA">$stmt </span><span style="color: #007700">= </span><span style="color: #9876AA">$mysqli</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">stmt_init</span><span style="color: #007700">();<br /></span><span style="color: #9876AA">$stmt</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">prepare</span><span style="color: #007700">(</span><span style="color: #DD0000">"SELECT District FROM City WHERE Name=?"</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// bind parameters for markers <br /></span><span style="color: #9876AA">$stmt</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">bind_param</span><span style="color: #007700">(</span><span style="color: #DD0000">"s"</span><span style="color: #007700">, </span><span style="color: #9876AA">$city</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// execute query <br /></span><span style="color: #9876AA">$stmt</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">execute</span><span style="color: #007700">();<br /><br /></span><span style="color: #FF8000">// bind result variables <br /></span><span style="color: #9876AA">$stmt</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">bind_result</span><span style="color: #007700">(</span><span style="color: #9876AA">$district</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// fetch value <br /></span><span style="color: #9876AA">$stmt</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">fetch</span><span style="color: #007700">();<br /><br /></span><span style="color: #9876AA">printf</span><span style="color: #007700">(</span><span style="color: #DD0000">"%s is in district %s\n"</span><span style="color: #007700">, </span><span style="color: #9876AA">$city</span><span style="color: #007700">, </span><span style="color: #9876AA">$district</span><span style="color: #007700">);</span></span></span></div>   </div>   <div class="example-contents"><p>过程化风格</p></div>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />mysqli_report</span><span style="color: #007700">(</span><span style="color: #9876AA">MYSQLI_REPORT_ERROR </span><span style="color: #007700">| </span><span style="color: #9876AA">MYSQLI_REPORT_STRICT</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$link </span><span style="color: #007700">= </span><span style="color: #9876AA">mysqli_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">"localhost"</span><span style="color: #007700">, </span><span style="color: #DD0000">"my_user"</span><span style="color: #007700">, </span><span style="color: #DD0000">"my_password"</span><span style="color: #007700">, </span><span style="color: #DD0000">"world"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$city </span><span style="color: #007700">= </span><span style="color: #DD0000">"Amersfoort"</span><span style="color: #007700">;<br /><br /></span><span style="color: #FF8000">// create a prepared statement <br /></span><span style="color: #9876AA">$stmt </span><span style="color: #007700">= </span><span style="color: #9876AA">mysqli_stmt_init</span><span style="color: #007700">(</span><span style="color: #9876AA">$link</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">mysqli_stmt_prepare</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">, </span><span style="color: #DD0000">"SELECT District FROM City WHERE Name=?"</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// bind parameters for markers <br /></span><span style="color: #9876AA">mysqli_stmt_bind_param</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">, </span><span style="color: #DD0000">"s"</span><span style="color: #007700">, </span><span style="color: #9876AA">$city</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// execute query <br /></span><span style="color: #9876AA">mysqli_stmt_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// bind result variables <br /></span><span style="color: #9876AA">mysqli_stmt_bind_result</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">, </span><span style="color: #9876AA">$district</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// fetch value <br /></span><span style="color: #9876AA">mysqli_stmt_fetch</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">printf</span><span style="color: #007700">(</span><span style="color: #DD0000">"%s is in district %s\n"</span><span style="color: #007700">, </span><span style="color: #9876AA">$city</span><span style="color: #007700">, </span><span style="color: #9876AA">$district</span><span style="color: #007700">);</span></span></span></div>   </div>   <div class="example-contents"><p>以上示例会输出：</p></div>   <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>Amersfoort is in district Utrecht<br></span></div>   </div>  </div> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-mysqli-stmt.prepare-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function"><a href="https://www.php.net/manual/zh/mysqli.stmt-init.php" class="function" rel="rdfs-seeAlso">mysqli_stmt_init()</a> - 初始化语句并返回用于 mysqli_stmt_prepare（调用）的对象</span></li>    <li class="member"><span class="function"><a href="https://www.php.net/manual/zh/mysqli-stmt.execute.php" class="function" rel="rdfs-seeAlso">mysqli_stmt_execute()</a> - Executes a prepared statement</span></li>    <li class="member"><span class="function"><a href="https://www.php.net/manual/zh/mysqli-stmt.fetch.php" class="function" rel="rdfs-seeAlso">mysqli_stmt_fetch()</a> - Fetch results from a prepared statement into the bound variables</span></li>    <li class="member"><span class="function"><a href="https://www.php.net/manual/zh/mysqli-stmt.bind-param.php" class="function" rel="rdfs-seeAlso">mysqli_stmt_bind_param()</a> - Binds variables to a prepared statement as parameters</span></li>    <li class="member"><span class="function"><a href="https://www.php.net/manual/zh/mysqli-stmt.bind-result.php" class="function" rel="rdfs-seeAlso">mysqli_stmt_bind_result()</a> - Binds variables to a prepared statement for result storage</span></li>    <li class="member"><span class="function"><a href="https://www.php.net/manual/zh/mysqli-stmt.get-result.php" class="function" rel="rdfs-seeAlso">mysqli_stmt_get_result()</a> - Gets a result set from a prepared statement as a mysqli_result object</span></li>    <li class="member"><span class="function"><a href="https://www.php.net/manual/zh/mysqli-stmt.close.php" class="function" rel="rdfs-seeAlso">mysqli_stmt_close()</a> - Closes a prepared statement</span></li>   </ul>  </span> </div></div>
     * @return bool true on success or false on failure.*/
    public function prepare(string $query): bool {}

/**
*<div id="mysqli-stmt.store-result" class="refentry"> <div class="refnamediv">  <h1 class="refname">mysqli_stmt::store_result</h1>  <h1 class="refname">mysqli_stmt_store_result</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">mysqli_stmt::store_result</span> -- <span class="refname">mysqli_stmt_store_result</span> &mdash; <span class="dc-title">Stores a result set in an internal buffer</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-mysqli-stmt.store-result-description">  <h3 class="title">说明</h3>  <p class="para">面向对象风格</p>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>mysqli_stmt::store_result</strong></span>(): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">过程化风格</p>  <div class="methodsynopsis dc-description"><span class="methodname" style="color:#CC7832"><strong>mysqli_stmt_store_result</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.mysqli-stmt.php" class="type mysqli_stmt" style="color:#EAB766">mysqli_stmt</a></span> <span class="parameter" style="color:#3A95FF">$statement</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   This function should be called for queries that successfully    produce a result set (e.g. <span>SELECT, SHOW, DESCRIBE, EXPLAIN</span>)   only if the complete result set needs to be buffered in PHP. Each subsequent   <span class="function"><a href="https://www.php.net/manual/zh/mysqli-stmt.fetch.php" class="function">mysqli_stmt_fetch()</a></span> call will return buffered data.  </p>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    It is unnecessary to call <span class="function"><strong style="color:#CC7832">mysqli_stmt_store_result()</strong></span> for other queries,    but if you do, it will not harm or cause any notable performance loss in all cases.    You can detect whether the query produced a result set by checking if    <span class="function"><a href="https://www.php.net/manual/zh/mysqli-stmt.result-metadata.php" class="function">mysqli_stmt_result_metadata()</a></span> returns <strong><span>false</span></strong>.   </p>  </p></blockquote> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-mysqli-stmt.store-result-parameters">  <h3 class="title">参数</h3>  <span>   <dl>    <dt><span class="parameter" style="color:#3A95FF">statement</span></dt><dd><p class="para">仅以过程化样式：由<span class="function"><a href="https://www.php.net/manual/zh/mysqli.stmt-init.php" class="function">mysqli_stmt_init()</a></span> 返回的 <span class="classname"><a href="https://www.php.net/manual/zh/class.mysqli-stmt.php" class="classname">mysqli_stmt</a></span> 对象。</p></dd>   </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-mysqli-stmt.store-result-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 errors" id="refsect1-mysqli-stmt.store-result-errors">  <h3 class="title">错误／异常</h3>  <p class="para">If mysqli error reporting is enabled (<strong><span>MYSQLI_REPORT_ERROR</span></strong>) and the requested operation fails,a warning is generated. If, in addition, the mode is set to <strong><span>MYSQLI_REPORT_STRICT</span></strong>,a <span class="classname"><a href="https://www.php.net/manual/zh/class.mysqli-sql-exception.php" class="classname">mysqli_sql_exception</a></span> is thrown instead.</p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-mysqli-stmt.store-result-examples">  <h3 class="title">示例</h3>  <div class="example" id="example-4562">   <p><strong>示例 #1 面向对象风格</strong></p>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />mysqli_report</span><span style="color: #007700">(</span><span style="color: #9876AA">MYSQLI_REPORT_ERROR </span><span style="color: #007700">| </span><span style="color: #9876AA">MYSQLI_REPORT_STRICT</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$mysqli </span><span style="color: #007700">= new </span><span style="color: #9876AA">mysqli</span><span style="color: #007700">(</span><span style="color: #DD0000">"localhost"</span><span style="color: #007700">, </span><span style="color: #DD0000">"my_user"</span><span style="color: #007700">, </span><span style="color: #DD0000">"my_password"</span><span style="color: #007700">, </span><span style="color: #DD0000">"world"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$query </span><span style="color: #007700">= </span><span style="color: #DD0000">"SELECT Name, CountryCode FROM City ORDER BY Name LIMIT 20"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$stmt </span><span style="color: #007700">= </span><span style="color: #9876AA">$mysqli</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">prepare</span><span style="color: #007700">(</span><span style="color: #9876AA">$query</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$stmt</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">execute</span><span style="color: #007700">();<br /><br /></span><span style="color: #FF8000">// store the result in an internal buffer <br /></span><span style="color: #9876AA">$stmt</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">store_result</span><span style="color: #007700">();<br /><br /></span><span style="color: #9876AA">printf</span><span style="color: #007700">(</span><span style="color: #DD0000">"Number of rows: %d.\n"</span><span style="color: #007700">, </span><span style="color: #9876AA">$stmt</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">num_rows</span><span style="color: #007700">);</span></span></span></div>   </div>  </div>  <div class="example" id="example-4565">   <p><strong>示例 #2 过程化风格</strong></p>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />mysqli_report</span><span style="color: #007700">(</span><span style="color: #9876AA">MYSQLI_REPORT_ERROR </span><span style="color: #007700">| </span><span style="color: #9876AA">MYSQLI_REPORT_STRICT</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$link </span><span style="color: #007700">= </span><span style="color: #9876AA">mysqli_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">"localhost"</span><span style="color: #007700">, </span><span style="color: #DD0000">"my_user"</span><span style="color: #007700">, </span><span style="color: #DD0000">"my_password"</span><span style="color: #007700">, </span><span style="color: #DD0000">"world"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$query </span><span style="color: #007700">= </span><span style="color: #DD0000">"SELECT Name, CountryCode FROM City ORDER BY Name LIMIT 20"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$stmt </span><span style="color: #007700">= </span><span style="color: #9876AA">mysqli_prepare</span><span style="color: #007700">(</span><span style="color: #9876AA">$link</span><span style="color: #007700">, </span><span style="color: #9876AA">$query</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">mysqli_stmt_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// store the result in an internal buffer <br /></span><span style="color: #9876AA">mysqli_stmt_store_result</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">printf</span><span style="color: #007700">(</span><span style="color: #DD0000">"Number of rows: %d.\n"</span><span style="color: #007700">, </span><span style="color: #9876AA">mysqli_stmt_num_rows</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">));</span></span></span></div>   </div>   <div class="example-contents"><p>以上示例会输出：</p></div>   <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>Number of rows: 20.<br></span></div>   </div>  </div> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-mysqli-stmt.store-result-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function"><a href="https://www.php.net/manual/zh/mysqli.prepare.php" class="function" rel="rdfs-seeAlso">mysqli_prepare()</a> - 预处理执行 SQL</span></li>    <li class="member"><span class="function"><a href="https://www.php.net/manual/zh/mysqli-stmt.result-metadata.php" class="function" rel="rdfs-seeAlso">mysqli_stmt_result_metadata()</a> - Returns result set metadata from a prepared statement</span></li>    <li class="member"><span class="function"><a href="https://www.php.net/manual/zh/mysqli-stmt.fetch.php" class="function" rel="rdfs-seeAlso">mysqli_stmt_fetch()</a> - Fetch results from a prepared statement into the bound variables</span></li>   </ul>  </span> </div></div>
     * @return bool true on success or false on failure.*/
    public function store_result(): bool {}

/**
*<div id="mysqli-stmt.get-result" class="refentry"> <div class="refnamediv">  <h1 class="refname">mysqli_stmt::get_result</h1>  <h1 class="refname">mysqli_stmt_get_result</h1>  <p class="verinfo">(PHP 5 &gt;= 5.3.0, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">mysqli_stmt::get_result</span> -- <span class="refname">mysqli_stmt_get_result</span> &mdash; <span class="dc-title">Gets a result set from a prepared statement as a <span class="classname"><a href="https://www.php.net/manual/zh/class.mysqli-result.php" class="classname">mysqli_result</a></span> object</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-mysqli-stmt.get-result-description">  <h3 class="title">说明</h3>  <p class="para">面向对象风格</p>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>mysqli_stmt::get_result</strong></span>(): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.mysqli-result.php" class="type mysqli_result" style="color:#EAB766">mysqli_result</a></span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>  <p class="para rdfs-comment">过程化风格</p>  <div class="methodsynopsis dc-description"><span class="methodname" style="color:#CC7832"><strong>mysqli_stmt_get_result</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.mysqli-stmt.php" class="type mysqli_stmt" style="color:#EAB766">mysqli_stmt</a></span> <span class="parameter" style="color:#3A95FF">$statement</span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.mysqli-result.php" class="type mysqli_result" style="color:#EAB766">mysqli_result</a></span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>  <p class="para rdfs-comment">    Retrieves a result set from a prepared statement as a    <span class="classname"><a href="https://www.php.net/manual/zh/class.mysqli-result.php" class="classname">mysqli_result</a></span> object. The data will be fetched from    the MySQL server to PHP. This method should be called only for    queries which produce a result set.  </p>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    仅可用于 <a href="https://www.php.net/manual/zh/book.mysqlnd.php" class="link">mysqlnd</a>。   </p>  </p></blockquote>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    This function cannot be used together    with <span class="function"><a href="https://www.php.net/manual/zh/mysqli-stmt.store-result.php" class="function">mysqli_stmt_store_result()</a></span>. Both of these functions    retrieve the full result set from the MySQL server.   </p>  </p></blockquote> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-mysqli-stmt.get-result-parameters">  <h3 class="title">参数</h3>  <span>   <dl>    <dt><span class="parameter" style="color:#3A95FF">statement</span></dt><dd><p class="para">仅以过程化样式：由<span class="function"><a href="https://www.php.net/manual/zh/mysqli.stmt-init.php" class="function">mysqli_stmt_init()</a></span> 返回的 <span class="classname"><a href="https://www.php.net/manual/zh/class.mysqli-stmt.php" class="classname">mysqli_stmt</a></span> 对象。</p></dd>   </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-mysqli-stmt.get-result-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   Returns <strong><span>false</span></strong> on failure. For successful queries which produce a result set, such as <span>SELECT, SHOW, DESCRIBE</span> or   <span>EXPLAIN</span>, <span class="function"><strong style="color:#CC7832">mysqli_stmt_get_result()</strong></span> will return   a <span class="classname"><a href="https://www.php.net/manual/zh/class.mysqli-result.php" class="classname">mysqli_result</a></span> object. For other successful queries, <span class="function"><strong style="color:#CC7832">mysqli_stmt_get_result()</strong></span> will   return <strong><span>false</span></strong>. The <span class="function"><a href="https://www.php.net/manual/zh/mysqli-stmt.errno.php" class="function">mysqli_stmt_errno()</a></span> function can be   used to distinguish between the two reasons for <strong><span>false</span></strong>; due to a bug, prior to PHP 7.4.13,   <span class="function"><a href="https://www.php.net/manual/zh/mysqli.errno.php" class="function">mysqli_errno()</a></span> had to be used for this purpose.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 errors" id="refsect1-mysqli-stmt.get-result-errors">  <h3 class="title">错误／异常</h3>  <p class="para">If mysqli error reporting is enabled (<strong><span>MYSQLI_REPORT_ERROR</span></strong>) and the requested operation fails,a warning is generated. If, in addition, the mode is set to <strong><span>MYSQLI_REPORT_STRICT</span></strong>,a <span class="classname"><a href="https://www.php.net/manual/zh/class.mysqli-sql-exception.php" class="classname">mysqli_sql_exception</a></span> is thrown instead.</p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-mysqli-stmt.get-result-examples">  <h3 class="title">示例</h3>  <div class="example" id="example-4526">   <p><strong>示例 #1 面向对象风格</strong></p>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />mysqli_report</span><span style="color: #007700">(</span><span style="color: #9876AA">MYSQLI_REPORT_ERROR </span><span style="color: #007700">| </span><span style="color: #9876AA">MYSQLI_REPORT_STRICT</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$mysqli </span><span style="color: #007700">= new </span><span style="color: #9876AA">mysqli</span><span style="color: #007700">(</span><span style="color: #DD0000">"localhost"</span><span style="color: #007700">, </span><span style="color: #DD0000">"my_user"</span><span style="color: #007700">, </span><span style="color: #DD0000">"my_password"</span><span style="color: #007700">, </span><span style="color: #DD0000">"world"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$query </span><span style="color: #007700">= </span><span style="color: #DD0000">"SELECT Name, Population, Continent FROM Country WHERE Continent=? ORDER BY Name LIMIT 1"</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">$stmt </span><span style="color: #007700">= </span><span style="color: #9876AA">$mysqli</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">prepare</span><span style="color: #007700">(</span><span style="color: #9876AA">$query</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$stmt</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">bind_param</span><span style="color: #007700">(</span><span style="color: #DD0000">"s"</span><span style="color: #007700">, </span><span style="color: #9876AA">$continent</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$continentList </span><span style="color: #007700">= array(</span><span style="color: #DD0000">'Europe'</span><span style="color: #007700">, </span><span style="color: #DD0000">'Africa'</span><span style="color: #007700">, </span><span style="color: #DD0000">'Asia'</span><span style="color: #007700">, </span><span style="color: #DD0000">'North America'</span><span style="color: #007700">);<br /><br />foreach (</span><span style="color: #9876AA">$continentList </span><span style="color: #007700">as </span><span style="color: #9876AA">$continent</span><span style="color: #007700">) {<br />    </span><span style="color: #9876AA">$stmt</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">execute</span><span style="color: #007700">();<br />    </span><span style="color: #9876AA">$result </span><span style="color: #007700">= </span><span style="color: #9876AA">$stmt</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">get_result</span><span style="color: #007700">();<br />    while (</span><span style="color: #9876AA">$row </span><span style="color: #007700">= </span><span style="color: #9876AA">$result</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">fetch_array</span><span style="color: #007700">(</span><span style="color: #9876AA">MYSQLI_NUM</span><span style="color: #007700">)) {<br />        foreach (</span><span style="color: #9876AA">$row </span><span style="color: #007700">as </span><span style="color: #9876AA">$r</span><span style="color: #007700">) {<br />            print </span><span style="color: #DD0000">"</span><span style="color: #9876AA">$r</span><span style="color: #DD0000"> "</span><span style="color: #007700">;<br />        }<br />        print </span><span style="color: #DD0000">"\n"</span><span style="color: #007700">;<br />    }<br />}</span></span></span></div>   </div>  </div>  <div class="example" id="example-4529">   <p><strong>示例 #2 过程化风格</strong></p>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />mysqli_report</span><span style="color: #007700">(</span><span style="color: #9876AA">MYSQLI_REPORT_ERROR </span><span style="color: #007700">| </span><span style="color: #9876AA">MYSQLI_REPORT_STRICT</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$link </span><span style="color: #007700">= </span><span style="color: #9876AA">mysqli_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">"localhost"</span><span style="color: #007700">, </span><span style="color: #DD0000">"my_user"</span><span style="color: #007700">, </span><span style="color: #DD0000">"my_password"</span><span style="color: #007700">, </span><span style="color: #DD0000">"world"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$query </span><span style="color: #007700">= </span><span style="color: #DD0000">"SELECT Name, Population, Continent FROM Country WHERE Continent=? ORDER BY Name LIMIT 1"</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">$stmt </span><span style="color: #007700">= </span><span style="color: #9876AA">mysqli_prepare</span><span style="color: #007700">(</span><span style="color: #9876AA">$link</span><span style="color: #007700">, </span><span style="color: #9876AA">$query</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">mysqli_stmt_bind_param</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">, </span><span style="color: #DD0000">"s"</span><span style="color: #007700">, </span><span style="color: #9876AA">$continent</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$continentList</span><span style="color: #007700">= array(</span><span style="color: #DD0000">'Europe'</span><span style="color: #007700">, </span><span style="color: #DD0000">'Africa'</span><span style="color: #00770