function oci_server_version($connection) {}

/**
*<div id="function.oci-statement-type" class="refentry"> <div class="refnamediv">  <h1 class="refname">oci_statement_type</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8, PECL OCI8 &gt;= 1.1.0)</p><p class="refpurpose"><span class="refname">oci_statement_type</span> &mdash; <span class="dc-title">返回语句的类型</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-statement-type-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>oci_statement_type</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$statement</span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">string</span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>  <p class="para rdfs-comment">   返回关键字，标识 OCI8 <span class="parameter" style="color:#3A95FF">statement</span> 的类型。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-statement-type-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">statement</span></dt>     <dd>      <p class="para">       来自 <span class="function">{@link oci_parse()}</span> 的有效 OCI8 语句标识符。      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-statement-type-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   返回 <span class="parameter" style="color:#3A95FF">statement</span> 类型，可能为以下字符串之一。   <table class="doctable table">    <caption><strong>语句类型</strong></caption>         <thead>      <tr>       <th>返回字符串</th>       <th>说明</th>      </tr>     </thead>     <tbody class="tbody">      <tr>       <td><span>ALTER</span></td>       <td class="empty">&nbsp;</td>      </tr>      <tr>       <td><span>BEGIN</span></td>       <td class="empty">&nbsp;</td>      </tr>      <tr>       <td><span>CALL</span></td>       <td class="empty">&nbsp;</td>      </tr>      <tr>       <td><span>CREATE</span></td>       <td class="empty">&nbsp;</td>      </tr>      <tr>       <td><span>DECLARE</span></td>       <td class="empty">&nbsp;</td>      </tr>      <tr>       <td><span>DELETE</span></td>       <td class="empty">&nbsp;</td>      </tr>      <tr>       <td><span>DROP</span></td>       <td class="empty">&nbsp;</td>      </tr>      <tr>       <td><span>INSERT</span></td>       <td class="empty">&nbsp;</td>      </tr>      <tr>       <td><span>SELECT</span></td>       <td class="empty">&nbsp;</td>      </tr>      <tr>       <td><span>UPDATE</span></td>       <td class="empty">&nbsp;</td>      </tr>      <tr>       <td><span>UNKNOWN</span></td>       <td class="empty">&nbsp;</td>      </tr>     </tbody>       </table>  </p>  <p class="para">   错误时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.oci-statement-type-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="">    <p><strong>示例 #1 <span class="function"><strong style="color:#CC7832">oci_statement_type()</strong></span> 示例</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">, </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">'DELETE FROM departments WHERE department_id = 130;'</span><span style="color: #007700">);<br />if (</span><span style="color: #9876AA">oci_statement_type</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">) == </span><span style="color: #DD0000">"DELETE"</span><span style="color: #007700">) {<br />    </span><span style="color: #9876AA">trigger_error</span><span style="color: #007700">(</span><span style="color: #DD0000">'You are not allowed to delete from this table'</span><span style="color: #007700">, </span><span style="color: #9876AA">E_USER_ERROR</span><span style="color: #007700">);<br />}<br />else {<br />    </span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);  </span><span style="color: #FF8000">// delete the row<br /></span><span style="color: #007700">}<br /><br /></span><span style="color: #9876AA">oci_free_statement</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span> </div></div>
 * @return string|false The type of <i>statement</i> as one of the*/
function oci_statement_type($statement) {}

/**
*<div id="function.oci-num-rows" class="refentry"> <div class="refnamediv">  <h1 class="refname">oci_num_rows</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8, PECL OCI8 &gt;= 1.1.0)</p><p class="refpurpose"><span class="refname">oci_num_rows</span> &mdash; <span class="dc-title">返回语句执行后受影响的行数</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-num-rows-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>oci_num_rows</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$statement</span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">int</span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>  <p class="para rdfs-comment">   获取语句执行后受影响的行数。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-num-rows-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">statement</span></dt>     <dd>      <p class="para">       有效的 OCI 语句标识符。      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-num-rows-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   返回 int 类型的受影响的行数， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.oci-num-rows-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="example-5645">    <p><strong>示例 #1 <span class="function"><strong style="color:#CC7832">oci_num_rows()</strong></span> 示例</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">"hr"</span><span style="color: #007700">, </span><span style="color: #DD0000">"hrpwd"</span><span style="color: #007700">, </span><span style="color: #DD0000">"localhost/XE"</span><span style="color: #007700">);<br />if (!</span><span style="color: #9876AA">$conn</span><span style="color: #007700">) {<br />    </span><span style="color: #9876AA">$m </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_error</span><span style="color: #007700">();<br />    </span><span style="color: #9876AA">trigger_error</span><span style="color: #007700">(</span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$m</span><span style="color: #007700">[</span><span style="color: #DD0000">'message'</span><span style="color: #007700">]), </span><span style="color: #9876AA">E_USER_ERROR</span><span style="color: #007700">);<br />}<br /><br /></span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"create table emp2 as select * from employees"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br />echo </span><span style="color: #9876AA">oci_num_rows</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">) . </span><span style="color: #DD0000">" rows inserted.&lt;br /&gt;\n"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">oci_free_statement</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"delete from emp2"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_DEFAULT</span><span style="color: #007700">);<br />echo </span><span style="color: #9876AA">oci_num_rows</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">) . </span><span style="color: #DD0000">" rows deleted.&lt;br /&gt;\n"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">oci_commit</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_free_statement</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"drop table emp2"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_free_statement</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">oci_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.oci-num-rows-notes">  <h3 class="title">注释</h3>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    本函数<em>并不</em>返回 SELECT    查询出来的行数！对于 SELECT 语句本函数将返回用    <span class="function"><strong style="color:#CC7832">oci_fetch*()</strong></span> 函数从缓冲区获取的行数。   </p>  </p></blockquote> </div></div>
 * @return int|false The number of rows affected as an integer, or <b>FALSE</b> on errors.*/
function oci_num_rows($statement) {}

/**
*<div id="function.oci-close" class="refentry"> <div class="refnamediv">  <h1 class="refname">oci_close</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8, PECL OCI8 &gt;= 1.1.0)</p><p class="refpurpose"><span class="refname">oci_close</span> &mdash; <span class="dc-title">关闭 Oracle 连接</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-close-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>oci_close</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$connection</span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766">bool</span></span></div>  <p class="para rdfs-comment">   取消 <span class="parameter" style="color:#3A95FF">connection</span>。如果没有其他资源正在使用底层数据库连接并且它是使用 <span class="function">{@link oci_connect()}</span>   或 <span class="function">{@link oci_new_connect()}</span> 创建的，则关闭底层数据库连接。  </p>  <p class="para">   建议关闭不再需要的连接，因为这可以让其他用户使用数据库资源。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-close-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">connection</span></dt>     <dd>      <p class="para">       <span class="function">{@link oci_connect()}</span>、<span class="function">{@link oci_pconnect()}</span> 或 <span class="function">{@link oci_new_connect()}</span> 返回的 Oracle 连接标识符。      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-close-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   启用 <a href="https://www.php.net/manual/zh/oci8.configuration.php#ini.oci8.old-oci-close-semantics" class="link">oci8.old_oci_close_semantics</a> 时返回 <strong><span>null</span></strong>，否则返回 <strong><span>true</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.oci-close-examples">  <h3 class="title">示例</h3>  <div class="example" id="example-5471">   <p><strong>示例 #1 关闭连接</strong></p>   <div class="example-contents"><p>    应关闭与连接关联的资源，以确保正确终止底层数据库连接并释放数据库资源。   </p></div>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">, </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br />if (!</span><span style="color: #9876AA">$conn</span><span style="color: #007700">) {<br />    </span><span style="color: #9876AA">$e </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_error</span><span style="color: #007700">();<br />    </span><span style="color: #9876AA">trigger_error</span><span style="color: #007700">(</span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$e</span><span style="color: #007700">[</span><span style="color: #DD0000">'message'</span><span style="color: #007700">], </span><span style="color: #9876AA">ENT_QUOTES</span><span style="color: #007700">), </span><span style="color: #9876AA">E_USER_ERROR</span><span style="color: #007700">);<br />}<br /><br /></span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">'SELECT * FROM departments'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$r </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_fetch_all</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">, </span><span style="color: #9876AA">$res</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">$res</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// Free the statement identifier when closing the connection<br /></span><span style="color: #9876AA">oci_free_statement</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>   </div>  </div>  <div class="example" id="example-5474">   <p><strong>示例 #2 在关闭所有引用之前不会关闭数据库连接</strong></p>   <div class="example-contents"><p>    在关闭与数据库的底层连接之前，连接标识符的内部引用数必须为零。   </p></div>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">, </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br />if (!</span><span style="color: #9876AA">$conn</span><span style="color: #007700">) {<br />    </span><span style="color: #9876AA">$e </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_error</span><span style="color: #007700">();<br />    </span><span style="color: #9876AA">trigger_error</span><span style="color: #007700">(</span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$e</span><span style="color: #007700">[</span><span style="color: #DD0000">'message'</span><span style="color: #007700">], </span><span style="color: #9876AA">ENT_QUOTES</span><span style="color: #007700">), </span><span style="color: #9876AA">E_USER_ERROR</span><span style="color: #007700">);<br />}<br /><br /></span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">'SELECT * FROM departments'</span><span style="color: #007700">);  </span><span style="color: #FF8000">// this increases the refcount on $conn<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_fetch_all</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">, </span><span style="color: #9876AA">$res</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">$res</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">oci_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// $conn is no longer usable in the script but the underlying database<br />// connection is still held open until $stid is freed.<br /></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);  </span><span style="color: #FF8000">// prints NULL  <br /><br />// While PHP sleeps, querying the Oracle V$SESSION view in a<br />// terminal window will show that the database user is still connected.<br /></span><span style="color: #9876AA">sleep</span><span style="color: #007700">(</span><span style="color: #9876AA">10</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// When $stid is freed, the database connection is physically closed<br /></span><span style="color: #9876AA">oci_free_statement</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);  <br /><br /></span><span style="color: #FF8000">// While PHP sleeps, querying the Oracle V$SESSION view in a<br />// terminal window will show that the database user has disconnected.<br /></span><span style="color: #9876AA">sleep</span><span style="color: #007700">(</span><span style="color: #9876AA">10</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>   </div>  </div>  <div class="example" id="example-5477">   <p><strong>示例 #3 关闭多次打开的连接</strong></p>   <div class="example-contents"><p>     当重新使用数据库凭据时，必须在关闭底层数据库连接之前关闭两个连接。   </p></div>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$conn1 </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">, </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// Using the same credentials reuses the same underlying database connection<br />// Any uncommitted changes done on $conn1 will be visible in $conn2<br /></span><span style="color: #9876AA">$conn2 </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">, </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// While PHP sleeps, querying the Oracle V$SESSION view in a<br />// terminal window will show that only one database user is connected.<br /></span><span style="color: #9876AA">sleep</span><span style="color: #007700">(</span><span style="color: #9876AA">10</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">oci_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn1</span><span style="color: #007700">); </span><span style="color: #FF8000">// doesn't close the underlying database connection<br /></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn1</span><span style="color: #007700">);  </span><span style="color: #FF8000">// prints NULL because the variable $conn1 is no longer usable<br /></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn2</span><span style="color: #007700">);  </span><span style="color: #FF8000">// displays that $conn2 is still a valid connection resource<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>   </div>  </div>  <div class="example" id="example-5480">   <p><strong>示例 #4 当变量超出作用域时关闭连接</strong></p>   <div class="example-contents"><p>    当 PHP 释放了所有超出作用域的引用连接的变量时，将发生回滚（如果需要）并关闭与数据库的底层连接。   </p></div>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br /></span><span style="color: #007700">function </span><span style="color: #9876AA">myfunc</span><span style="color: #007700">() {<br />    </span><span style="color: #9876AA">$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">, </span><span style="color: #DD0000">'hrpwd'</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br />    if (!</span><span style="color: #9876AA">$conn</span><span style="color: #007700">) {<br />        </span><span style="color: #9876AA">$e </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_error</span><span style="color: #007700">();<br />        </span><span style="color: #9876AA">trigger_error</span><span style="color: #007700">(</span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$e</span><span style="color: #007700">[</span><span style="color: #DD0000">'message'</span><span style="color: #007700">], </span><span style="color: #9876AA">ENT_QUOTES</span><span style="color: #007700">), </span><span style="color: #9876AA">E_USER_ERROR</span><span style="color: #007700">);<br />    }<br /><br />    </span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">'UPDATE mytab SET id = 100'</span><span style="color: #007700">);<br />    </span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_NO_AUTO_COMMIT</span><span style="color: #007700">);<br />    return </span><span style="color: #DD0000">"Finished"</span><span style="color: #007700">;<br />}<br /><br /></span><span style="color: #9876AA">$r </span><span style="color: #007700">= </span><span style="color: #9876AA">myfunc</span><span style="color: #007700">();<br /></span><span style="color: #FF8000">// At this point a rollback occurred and the underlying database connection was released.<br /><br /></span><span style="color: #007700">print </span><span style="color: #9876AA">$r</span><span style="color: #007700">;  </span><span style="color: #FF8000">// displays the function return value "Finished"<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>   </div>  </div> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.oci-close-notes">  <h3 class="title">注释</h3>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    依赖于连接标识符的变量，例如 <span class="function">{@link oci_parse()}</span> 返回的语句标识符，也必须在底层数据库连接关闭之前释放。   </p>  </p></blockquote>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    <span class="function"><strong style="color:#CC7832">oci_close()</strong></span> 函数不会关闭使用 <span class="function">{@link oci_pconnect()}</span> 创建的底层数据库连接。   </p>  </p></blockquote> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.oci-close-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link oci_connect()} - 建立一个到 Oracle 服务器的连接</span></li>    <li class="member"><span class="function">{@link oci_free_statement()} - 释放关联于语句或游标的所有资源</span></li>   </ul>  </span> </div></div>
 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
function oci_close($connection) {}

/**
*<div id="function.oci-connect" class="refentry"> <div class="refnamediv">  <h1 class="refname">oci_connect</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8, PECL OCI8 &gt;= 1.1.0)</p><p class="refpurpose"><span class="refname">oci_connect</span> &mdash; <span class="dc-title">建立一个到 Oracle 服务器的连接</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-connect-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>oci_connect</strong></span>(<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$username</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$password</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">string</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">$connection_string</span><span class="initializer"> = <strong><span>null</span></strong></span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$encoding</span><span class="initializer"> = &quot;&quot;</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$session_mode</span><span class="initializer"> = <strong><span>OCI_DEFAULT</span></strong></span></span><br>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">resource</span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>  <p class="para rdfs-comment">   返回大多数其他 OCI8 操作所需的连接标识符。  </p>  <p class="para">   为了提高性能，大多数应用程序应该使用 <span class="function">{@link oci_pconnect()}</span> 的持久连接而不是   <span class="function"><strong style="color:#CC7832">oci_connect()</strong></span>。有关连接管理和连接池的基本信息，请参阅<a href="https://www.php.net/manual/zh/oci8.connection.php" class="link">连接处理</a>。  </p>  <p class="para">   使用相同参数对 <span class="function"><strong style="color:#CC7832">oci_connect()</strong></span> 的第二次和后续调用将返回从第一次调用返回的连接句柄。这意味着一个句柄中的事务也在其他句柄中，因为它们使用<em>相同的</em>底层数据库连接。如果两个句柄需要在事务上相互隔离，请改用 <span class="function">{@link oci_new_connect()}</span>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-connect-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">username</span></dt>     <dd>      <p class="para">       Oracle 用户名。      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">password</span></dt>     <dd>      <p class="para">       <span class="parameter" style="color:#3A95FF">username</span> 的密码。      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">connection_string</span></dt>     <dd>      <p class="para">包含要连接的<span>Oracle 实例</span>。可以是 <a href="https://www.php.net/manual/zh/https://www.oracle.com/pls/topic/lookup?ctx=dblatest&id=GUID-E5358DEA-D619-4B7B-A799-3D2F802500F1" class="link external">&raquo;&nbsp;Easy Connect 串</a>，或是<var class="filename">tnsnames.ora</var> 文件中的连接名，或是本地 Oracle 实例名。</p><p class="para"> 如果不指定或者为 <strong><span>null</span></strong>，PHP 使用环境变量来确定连接的 <span>Oracle 实例</span>，诸如 <strong><span>TWO_TASK</span></strong>（Linux 下）或  <strong><span>LOCAL</span></strong>（Windows 下）与 <strong><span>ORACLE_SID</span></strong> 等。</p><p class="para"> 要使用 Easy Connect 命名方法，PHP 必须与 Oracle 10<em>g</em> 或更高版本的客户端库进行链接。Oracle 10<em>g</em> 的 Easy Connect 串格式：<em>[//]host_name[:port][/service_name]</em>。Oracle 11<em>g</em> 则为：<em>[//]host_name[:port][/service_name][:server_type][/instance_name]</em>。 在 Oracle 19c 加入类更多选项，例如 timeout 和 keep-alive 设置。 请参考 Oracle 文档。 服务名可在数据库服务器机器上运行 Oracle 实用程序 <span>lsnrctl status</span> 找到。</p><p class="para"> <var class="filename">tnsnames.ora</var> 文件可在 Oracle Net 查找路径中，此路径包括 <var class="filename">/your/path/to/instantclient/network/admin</var>、<var class="filename">$ORACLE_HOME/network/admin</var> 、<var class="filename">/etc</var>。 另一种方法是设置 <span>TNS_ADMIN</span> 以便通过 <var class="filename">$TNS_ADMIN/tnsnames.ora</var> 来读取。表确认 web 守护进程可读取此文件。</p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">encoding</span></dt>     <dd>      <p class="para">使用 Oracle客户端库来确定字符集。字符集不需要与数据库的字符集相匹配。如果不匹配，Oracle会尽可能地将数据从数据库字符集进行转换。因为依赖于字符集，可能不能给出可用的结果。转换也增加一些时间开销。</p><p class="para"> 如果不指定，Oracle 客户端用 <strong><span>NLS_LANG</span></strong> 环境变量来决定字符集。</p><p class="para"> 传递此参数可减少连接时间。</p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">session_mode</span></dt>     <dd>      <p class="para">此参数在 PHP 5（PECL OCI81.1）版本开始可用，并收受下列值：<strong><span>OCI_DEFAULT</span></strong>，<strong><span>OCI_SYSOPER</span></strong>和 <strong><span>OCI_SYSDBA</span></strong>。如为<strong><span>OCI_SYSOPER</span></strong> 或 <strong><span>OCI_SYSDBA</span></strong>其中之一，此函数将会使用外部的证书建立有特权的连接。有特权的连接默认是禁用的。需要将<a href="https://www.php.net/manual/zh/oci8.configuration.php#ini.oci8.privileged-connect" class="link">oci8.privileged_connect</a>设为 <span>On</span> 来启用。</p><p class="para"> PHP 5.3（PECL OCI8 1.3.4）引进了 <strong><span>OCI_CRED_EXT</span></strong> 模式值。使用外部或操作系统认证必需在 Oracle 数据库中进行配置。<strong><span>OCI_CRED_EXT</span></strong> 标志只可用于用户为 &quot;/&quot;，密码为空的情况。<a href="https://www.php.net/manual/zh/oci8.configuration.php#ini.oci8.privileged-connect" class="link">oci8.privileged_connect</a> 可为 <span>On</span> 或 <span>Off</span>。</p><p class="para"> <strong><span>OCI_CRED_EXT</span></strong> 可与 <strong><span>OCI_SYSOPER</span></strong> 或 <strong><span>OCI_SYSDBA</span></strong> 模式组合使用。</p><p class="para"> <strong><span>OCI_CRED_EXT</span></strong> 由于安全的原因不支持 Windows 系统。</p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-connect-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   返回连接标识符或出错时为 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 changelog" id="refsect1-function.oci-connect-changelog">  <h3 class="title">更新日志</h3>  <table class="doctable informaltable">       <thead>     <tr>      <th>版本</th>      <th>说明</th>     </tr>    </thead>    <tbody class="tbody">     <tr>      <td>8.0.0、PECL OCI8 3.0.0</td>      <td>       <span class="parameter" style="color:#3A95FF">connection_string</span> 现在可为 null。      </td>     </tr>    </tbody>     </table> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.oci-connect-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="example-5486">    <p><strong>示例 #1 使用 Easy Connect 语法的基础 <span class="function"><strong style="color:#CC7832">oci_connect()</strong></span></strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br /></span><span style="color: #FF8000">// Connects to the XE service (i.e. database) on the "localhost" machine<br /></span><span style="color: #9876AA">$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">, </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br />if (!</span><span style="color: #9876AA">$conn</span><span style="color: #007700">) {<br />    </span><span style="color: #9876AA">$e </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_error</span><span style="color: #007700">();<br />    </span><span style="color: #9876AA">trigger_error</span><span style="color: #007700">(</span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$e</span><span style="color: #007700">[</span><span style="color: #DD0000">'message'</span><span style="color: #007700">], </span><span style="color: #9876AA">ENT_QUOTES</span><span style="color: #007700">), </span><span style="color: #9876AA">E_USER_ERROR</span><span style="color: #007700">);<br />}<br /><br /></span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">'SELECT * FROM employees'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /><br />echo </span><span style="color: #DD0000">"&lt;table border='1'&gt;\n"</span><span style="color: #007700">;<br />while (</span><span style="color: #9876AA">$row </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_fetch_array</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_ASSOC</span><span style="color: #007700">+</span><span style="color: #9876AA">OCI_RETURN_NULLS</span><span style="color: #007700">)) {<br />    echo </span><span style="color: #DD0000">"&lt;tr&gt;\n"</span><span style="color: #007700">;<br />    foreach (</span><span style="color: #9876AA">$row </span><span style="color: #007700">as </span><span style="color: #9876AA">$item</span><span style="color: #007700">) {<br />        echo </span><span style="color: #DD0000">"    &lt;td&gt;" </span><span style="color: #007700">. (</span><span style="color: #9876AA">$item </span><span style="color: #007700">!== </span><span style="color: #9876AA">null </span><span style="color: #007700">? </span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$item</span><span style="color: #007700">, </span><span style="color: #9876AA">ENT_QUOTES</span><span style="color: #007700">) : </span><span style="color: #DD0000">"&amp;nbsp;"</span><span style="color: #007700">) . </span><span style="color: #DD0000">"&lt;/td&gt;\n"</span><span style="color: #007700">;<br />    }<br />    echo </span><span style="color: #DD0000">"&lt;/tr&gt;\n"</span><span style="color: #007700">;<br />}<br />echo </span><span style="color: #DD0000">"&lt;/table&gt;\n"</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span>  <p class="para">   <div class="example" id="example-5489">    <p><strong>示例 #2 使用 Network Connect 名称的基础 <span class="function"><strong style="color:#CC7832">oci_connect()</strong></span></strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br /></span><span style="color: #FF8000">// Connects to the MYDB database described in tnsnames.ora file,<br />// One example tnsnames.ora entry for MYDB could be:<br />//   MYDB =<br />//     (DESCRIPTION =<br />//       (ADDRESS = (PROTOCOL = TCP)(HOST = mymachine.oracle.com)(PORT = 1521))<br />//       (CONNECT_DATA =<br />//         (SERVER = DEDICATED)<br />//         (SERVICE_NAME = XE)<br />//       )<br />//     )<br /><br /></span><span style="color: #9876AA">$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">, </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">, </span><span style="color: #DD0000">'MYDB'</span><span style="color: #007700">);<br />if (!</span><span style="color: #9876AA">$conn</span><span style="color: #007700">) {<br />    </span><span style="color: #9876AA">$e </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_error</span><span style="color: #007700">();<br />    </span><span style="color: #9876AA">trigger_error</span><span style="color: #007700">(</span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$e</span><span style="color: #007700">[</span><span style="color: #DD0000">'message'</span><span style="color: #007700">], </span><span style="color: #9876AA">ENT_QUOTES</span><span style="color: #007700">), </span><span style="color: #9876AA">E_USER_ERROR</span><span style="color: #007700">);<br />}<br /><br /></span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">'SELECT * FROM employees'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /><br />echo </span><span style="color: #DD0000">"&lt;table border='1'&gt;\n"</span><span style="color: #007700">;<br />while (</span><span style="color: #9876AA">$row </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_fetch_array</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_ASSOC</span><span style="color: #007700">+</span><span style="color: #9876AA">OCI_RETURN_NULLS</span><span style="color: #007700">)) {<br />    echo </span><span style="color: #DD0000">"&lt;tr&gt;\n"</span><span style="color: #007700">;<br />    foreach (</span><span style="color: #9876AA">$row </span><span style="color: #007700">as </span><span style="color: #9876AA">$item</span><span style="color: #007700">) {<br />        echo </span><span style="color: #DD0000">"    &lt;td&gt;" </span><span style="color: #007700">. (</span><span style="color: #9876AA">$item </span><span style="color: #007700">!== </span><span style="color: #9876AA">null </span><span style="color: #007700">? </span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$item</span><span style="color: #007700">, </span><span style="color: #9876AA">ENT_QUOTES</span><span style="color: #007700">) : </span><span style="color: #DD0000">"&amp;nbsp;"</span><span style="color: #007700">) . </span><span style="color: #DD0000">"&lt;/td&gt;\n"</span><span style="color: #007700">;<br />    }<br />    echo </span><span style="color: #DD0000">"&lt;/tr&gt;\n"</span><span style="color: #007700">;<br />}<br />echo </span><span style="color: #DD0000">"&lt;/table&gt;\n"</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </p>  <p class="para">   <div class="example" id="example-5492">    <p><strong>示例 #3 带有显式字符集的 <span class="function"><strong style="color:#CC7832">oci_connect()</strong></span></strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">, </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">, </span><span style="color: #DD0000">'AL32UTF8'</span><span style="color: #007700">);<br />if (!</span><span style="color: #9876AA">$conn</span><span style="color: #007700">) {<br />    </span><span style="color: #9876AA">$e </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_error</span><span style="color: #007700">();<br />    </span><span style="color: #9876AA">trigger_error</span><span style="color: #007700">(</span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$e</span><span style="color: #007700">[</span><span style="color: #DD0000">'message'</span><span style="color: #007700">], </span><span style="color: #9876AA">ENT_QUOTES</span><span style="color: #007700">), </span><span style="color: #9876AA">E_USER_ERROR</span><span style="color: #007700">);<br />}<br /><br /></span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">'SELECT * FROM employees'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /><br />echo </span><span style="color: #DD0000">"&lt;table border='1'&gt;\n"</span><span style="color: #007700">;<br />while (</span><span style="color: #9876AA">$row </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_fetch_array</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_ASSOC</span><span style="color: #007700">+</span><span style="color: #9876AA">OCI_RETURN_NULLS</span><span style="color: #007700">)) {<br />    echo </span><span style="color: #DD0000">"&lt;tr&gt;\n"</span><span style="color: #007700">;<br />    foreach (</span><span style="color: #9876AA">$row </span><span style="color: #007700">as </span><span style="color: #9876AA">$item</span><span style="color: #007700">) {<br />        echo </span><span style="color: #DD0000">"    &lt;td&gt;" </span><span style="color: #007700">. (</span><span style="color: #9876AA">$item </span><span style="color: #007700">!== </span><span style="color: #9876AA">null </span><span style="color: #007700">? </span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$item</span><span style="color: #007700">, </span><span style="color: #9876AA">ENT_QUOTES</span><span style="color: #007700">) : </span><span style="color: #DD0000">"&amp;nbsp;"</span><span style="color: #007700">) . </span><span style="color: #DD0000">"&lt;/td&gt;\n"</span><span style="color: #007700">;<br />    }<br />    echo </span><span style="color: #DD0000">"&lt;/tr&gt;\n"</span><span style="color: #007700">;<br />}<br />echo </span><span style="color: #DD0000">"&lt;/table&gt;\n"</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </p>  <p class="para">   <div class="example" id="example-5495">    <p><strong>示例 #4 多次调用 <span class="function"><strong style="color:#CC7832">oci_connect()</strong></span></strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$c1 </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">"hr"</span><span style="color: #007700">, </span><span style="color: #DD0000">"welcome"</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$c2 </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">"hr"</span><span style="color: #007700">, </span><span style="color: #DD0000">"welcome"</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// Both $c1 and $c2 show the same PHP resource id meaning they use the<br />// same underlying database connection<br /></span><span style="color: #007700">echo </span><span style="color: #DD0000">"c1 is </span><span style="color: #9876AA">$c1</span><span style="color: #DD0000">&lt;br&gt;\n"</span><span style="color: #007700">;<br />echo </span><span style="color: #DD0000">"c2 is </span><span style="color: #9876AA">$c2</span><span style="color: #DD0000">&lt;br&gt;\n"</span><span style="color: #007700">;<br /><br />function </span><span style="color: #9876AA">create_table</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">)<br />{<br />    </span><span style="color: #9876AA">$stmt </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"create table hallo (test varchar2(64))"</span><span style="color: #007700">);<br />    </span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">);<br />    echo </span><span style="color: #DD0000">"Created table&lt;br&gt;\n"</span><span style="color: #007700">;<br />}<br /><br />function </span><span style="color: #9876AA">drop_table</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">)<br />{<br />    </span><span style="color: #9876AA">$stmt </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"drop table hallo"</span><span style="color: #007700">);<br />    </span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">);<br />    echo </span><span style="color: #DD0000">"Dropped table&lt;br&gt;\n"</span><span style="color: #007700">;<br />}<br /><br />function </span><span style="color: #9876AA">insert_data</span><span style="color: #007700">(</span><span style="color: #9876AA">$connname</span><span style="color: #007700">, </span><span style="color: #9876AA">$conn</span><span style="color: #007700">)<br />{<br />    </span><span style="color: #9876AA">$stmt </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"insert into hallo<br />              values(to_char(sysdate,'DD-MON-YY HH24:MI:SS'))"</span><span style="color: #007700">);<br />    </span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_DEFAULT</span><span style="color: #007700">);<br />    echo </span><span style="color: #DD0000">"</span><span style="color: #9876AA">$connname</span><span style="color: #DD0000"> inserted row without committing&lt;br&gt;\n"</span><span style="color: #007700">;<br />}<br /><br />function </span><span style="color: #9876AA">rollback</span><span style="color: #007700">(</span><span style="color: #9876AA">$connname</span><span style="color: #007700">, </span><span style="color: #9876AA">$conn</span><span style="color: #007700">)<br />{<br />    </span><span style="color: #9876AA">oci_rollback</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br />    echo </span><span style="color: #DD0000">"</span><span style="color: #9876AA">$connname</span><span style="color: #DD0000"> rollback&lt;br&gt;\n"</span><span style="color: #007700">;<br />}<br /><br />function </span><span style="color: #9876AA">select_data</span><span style="color: #007700">(</span><span style="color: #9876AA">$connname</span><span style="color: #007700">, </span><span style="color: #9876AA">$conn</span><span style="color: #007700">)<br />{<br />    </span><span style="color: #9876AA">$stmt </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"select * from hallo"</span><span style="color: #007700">);<br />    </span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_DEFAULT</span><span style="color: #007700">);<br />    echo </span><span style="color: #DD0000">"</span><span style="color: #9876AA">$connname</span><span style="color: #DD0000"> ----selecting&lt;br&gt;\n"</span><span style="color: #007700">;<br />    while (</span><span style="color: #9876AA">oci_fetch</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">)) {<br />        echo </span><span style="color: #DD0000">"    " </span><span style="color: #007700">. </span><span style="color: #9876AA">oci_result</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">, </span><span style="color: #DD0000">"TEST"</span><span style="color: #007700">) . </span><span style="color: #DD0000">"&lt;br&gt;\n"</span><span style="color: #007700">;<br />    }<br />    echo </span><span style="color: #DD0000">"</span><span style="color: #9876AA">$connname</span><span style="color: #DD0000"> ----done&lt;br&gt;\n"</span><span style="color: #007700">;<br />}<br /><br /></span><span style="color: #9876AA">create_table</span><span style="color: #007700">(</span><span style="color: #9876AA">$c1</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">insert_data</span><span style="color: #007700">(</span><span style="color: #DD0000">'c1'</span><span style="color: #007700">, </span><span style="color: #9876AA">$c1</span><span style="color: #007700">);   </span><span style="color: #FF8000">// Insert a row using c1<br /></span><span style="color: #9876AA">sleep</span><span style="color: #007700">(</span><span style="color: #9876AA">2</span><span style="color: #007700">);                 </span><span style="color: #FF8000">// sleep to show a different timestamp for the 2nd row<br /></span><span style="color: #9876AA">insert_data</span><span style="color: #007700">(</span><span style="color: #DD0000">'c2'</span><span style="color: #007700">, </span><span style="color: #9876AA">$c2</span><span style="color: #007700">);   </span><span style="color: #FF8000">// Insert a row using c2<br /><br /></span><span style="color: #9876AA">select_data</span><span style="color: #007700">(</span><span style="color: #DD0000">'c1'</span><span style="color: #007700">, </span><span style="color: #9876AA">$c1</span><span style="color: #007700">);   </span><span style="color: #FF8000">// Results of both inserts are returned<br /></span><span style="color: #9876AA">select_data</span><span style="color: #007700">(</span><span style="color: #DD0000">'c2'</span><span style="color: #007700">, </span><span style="color: #9876AA">$c2</span><span style="color: #007700">);   </span><span style="color: #FF8000">// Results of both inserts are returned<br /><br /></span><span style="color: #9876AA">rollback</span><span style="color: #007700">(</span><span style="color: #DD0000">'c1'</span><span style="color: #007700">, </span><span style="color: #9876AA">$c1</span><span style="color: #007700">);      </span><span style="color: #FF8000">// Rollback using c1<br /><br /></span><span style="color: #9876AA">select_data</span><span style="color: #007700">(</span><span style="color: #DD0000">'c1'</span><span style="color: #007700">, </span><span style="color: #9876AA">$c1</span><span style="color: #007700">);   </span><span style="color: #FF8000">// Both inserts have been rolled back<br /></span><span style="color: #9876AA">select_data</span><span style="color: #007700">(</span><span style="color: #DD0000">'c2'</span><span style="color: #007700">, </span><span style="color: #9876AA">$c2</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">drop_table</span><span style="color: #007700">(</span><span style="color: #9876AA">$c1</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// Closing one of the connections makes the PHP variable unusable, but<br />// the other could be used<br /></span><span style="color: #9876AA">oci_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$c1</span><span style="color: #007700">);<br />echo </span><span style="color: #DD0000">"c1 is </span><span style="color: #9876AA">$c1</span><span style="color: #DD0000">&lt;br&gt;\n"</span><span style="color: #007700">;<br />echo </span><span style="color: #DD0000">"c2 is </span><span style="color: #9876AA">$c2</span><span style="color: #DD0000">&lt;br&gt;\n"</span><span style="color: #007700">;<br /><br /><br /></span><span style="color: #FF8000">// Output is:<br />//    c1 is Resource id #5<br />//    c2 is Resource id #5<br />//    Created table<br />//    c1 inserted row without committing<br />//    c2 inserted row without committing<br />//    c1 ----selecting<br />//        09-DEC-09 12:14:43<br />//        09-DEC-09 12:14:45<br />//    c1 ----done<br />//    c2 ----selecting<br />//        09-DEC-09 12:14:43<br />//        09-DEC-09 12:14:45<br />//    c2 ----done<br />//    c1 rollback<br />//    c1 ----selecting<br />//    c1 ----done<br />//    c2 ----selecting<br />//    c2 ----done<br />//    Dropped table<br />//    c1 is<br />//    c2 is Resource id #5<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.oci-connect-notes">  <h3 class="title">注释</h3>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    错误安装或配置的 OCI8 扩展通常会表现为连接问题或错误。有关故障排除信息，请参阅<a href="https://www.php.net/manual/zh/oci8.setup.php" class="link">安装/配置</a>。   </p>  </p></blockquote> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.oci-connect-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link oci_pconnect()} - 使用持久连接连，连接到 Oracle 数据库</span></li>    <li class="member"><span class="function">{@link oci_new_connect()} - 使用唯一连接，连接到 Oracle 服务器</span></li>    <li class="member"><span class="function">{@link oci_close()} - 关闭 Oracle 连接</span></li>   </ul>  </span> </div></div>
 * @return resource|false A connection identifier or <b>FALSE</b> on error.*/
function oci_connect($username, $password, $connection_string = null, $character_set = null, $session_mode = null) {}

/**
*<div id="function.oci-new-connect" class="refentry"> <div class="refnamediv">  <h1 class="refname">oci_new_connect</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8, PECL OCI8 &gt;= 1.1.0)</p><p class="refpurpose"><span class="refname">oci_new_connect</span> &mdash; <span class="dc-title">使用唯一连接，连接到 Oracle 服务器</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-new-connect-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>oci_new_connect</strong></span>(<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$username</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$password</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">string</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">$connection_string</span><span class="initializer"> = <strong><span>null</span></strong></span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$encoding</span><span class="initializer"> = &quot;&quot;</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$session_mode</span><span class="initializer"> = <strong><span>OCI_DEFAULT</span></strong></span></span><br>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">resource</span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>  <p class="para rdfs-comment">   建立与 Oracle 服务器的新连接并登录。  </p>  <p class="para">   与 <span class="function">{@link oci_connect()}</span> 和 <span class="function">{@link oci_pconnect()}</span> 不同，<span class="function"><strong style="color:#CC7832">oci_new_connect()</strong></span>   不缓存连接并且始终返回全新的刚打开的连接句柄。如果应用程序需要在两组查询之间进行事务隔离，这将很有用。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-new-connect-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">username</span></dt>     <dd>      <p class="para">       Oracle 用户名。      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">password</span></dt>     <dd>      <p class="para">       <span class="parameter" style="color:#3A95FF">username</span> 的密码。      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">connection_string</span></dt>     <dd>      <p class="para">包含要连接的<span>Oracle 实例</span>。可以是 <a href="https://www.php.net/manual/zh/https://www.oracle.com/pls/topic/lookup?ctx=dblatest&id=GUID-E5358DEA-D619-4B7B-A799-3D2F802500F1" class="link external">&raquo;&nbsp;Easy Connect 串</a>，或是<var class="filename">tnsnames.ora</var> 文件中的连接名，或是本地 Oracle 实例名。</p><p class="para"> 如果不指定或者为 <strong><span>null</span></strong>，PHP 使用环境变量来确定连接的 <span>Oracle 实例</span>，诸如 <strong><span>TWO_TASK</span></strong>（Linux 下）或  <strong><span>LOCAL</span></strong>（Windows 下）与 <strong><span>ORACLE_SID</span></strong> 等。</p><p class="para"> 要使用 Easy Connect 命名方法，PHP 必须与 Oracle 10<em>g</em> 或更高版本的客户端库进行链接。Oracle 10<em>g</em> 的 Easy Connect 串格式：<em>[//]host_name[:port][/service_name]</em>。Oracle 11<em>g</em> 则为：<em>[//]host_name[:port][/service_name][:server_type][/instance_name]</em>。 在 Oracle 19c 加入类更多选项，例如 timeout 和 keep-alive 设置。 请参考 Oracle 文档。 服务名可在数据库服务器机器上运行 Oracle 实用程序 <span>lsnrctl status</span> 找到。</p><p class="para"> <var class="filename">tnsnames.ora</var> 文件可在 Oracle Net 查找路径中，此路径包括 <var class="filename">/your/path/to/instantclient/network/admin</var>、<var class="filename">$ORACLE_HOME/network/admin</var> 、<var class="filename">/etc</var>。 另一种方法是设置 <span>TNS_ADMIN</span> 以便通过 <var class="filename">$TNS_ADMIN/tnsnames.ora</var> 来读取。表确认 web 守护进程可读取此文件。</p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">encoding</span></dt>     <dd>      <p class="para">使用 Oracle客户端库来确定字符集。字符集不需要与数据库的字符集相匹配。如果不匹配，Oracle会尽可能地将数据从数据库字符集进行转换。因为依赖于字符集，可能不能给出可用的结果。转换也增加一些时间开销。</p><p class="para"> 如果不指定，Oracle 客户端用 <strong><span>NLS_LANG</span></strong> 环境变量来决定字符集。</p><p class="para"> 传递此参数可减少连接时间。</p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">session_mode</span></dt>     <dd>      <p class="para">此参数在 PHP 5（PECL OCI81.1）版本开始可用，并收受下列值：<strong><span>OCI_DEFAULT</span></strong>，<strong><span>OCI_SYSOPER</span></strong>和 <strong><span>OCI_SYSDBA</span></strong>。如为<strong><span>OCI_SYSOPER</span></strong> 或 <strong><span>OCI_SYSDBA</span></strong>其中之一，此函数将会使用外部的证书建立有特权的连接。有特权的连接默认是禁用的。需要将<a href="https://www.php.net/manual/zh/oci8.configuration.php#ini.oci8.privileged-connect" class="link">oci8.privileged_connect</a>设为 <span>On</span> 来启用。</p><p class="para"> PHP 5.3（PECL OCI8 1.3.4）引进了 <strong><span>OCI_CRED_EXT</span></strong> 模式值。使用外部或操作系统认证必需在 Oracle 数据库中进行配置。<strong><span>OCI_CRED_EXT</span></strong> 标志只可用于用户为 &quot;/&quot;，密码为空的情况。<a href="https://www.php.net/manual/zh/oci8.configuration.php#ini.oci8.privileged-connect" class="link">oci8.privileged_connect</a> 可为 <span>On</span> 或 <span>Off</span>。</p><p class="para"> <strong><span>OCI_CRED_EXT</span></strong> 可与 <strong><span>OCI_SYSOPER</span></strong> 或 <strong><span>OCI_SYSDBA</span></strong> 模式组合使用。</p><p class="para"> <strong><span>OCI_CRED_EXT</span></strong> 由于安全的原因不支持 Windows 系统。</p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-new-connect-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   返回连接标识符或错误时为 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 changelog" id="refsect1-function.oci-new-connect-changelog">  <h3 class="title">更新日志</h3>  <table class="doctable informaltable">       <thead>     <tr>      <th>版本</th>      <th>说明</th>     </tr>    </thead>    <tbody class="tbody">     <tr>      <td>8.0.0、PECL OCI8 3.0.0</td>      <td>       <span class="parameter" style="color:#3A95FF">connection_string</span> 现在可为 null。      </td>     </tr>    </tbody>     </table> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.oci-new-connect-examples">  <h3 class="title">示例</h3>  <span>   下面演示了如何分离连接。   <div class="example" id="example-5630">    <p><strong>示例 #1 <span class="function"><strong style="color:#CC7832">oci_new_connect()</strong></span> 示例</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br /></span><span style="color: #FF8000">// create table mytab (mycol number);<br /><br /></span><span style="color: #007700">function </span><span style="color: #9876AA">query</span><span style="color: #007700">(</span><span style="color: #9876AA">$name</span><span style="color: #007700">, </span><span style="color: #9876AA">$c</span><span style="color: #007700">)<br />{<br />    echo </span><span style="color: #DD0000">"Querying </span><span style="color: #9876AA">$name</span><span style="color: #DD0000">\n"</span><span style="color: #007700">;<br />    </span><span style="color: #9876AA">$s </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$c</span><span style="color: #007700">, </span><span style="color: #DD0000">"select * from mytab"</span><span style="color: #007700">);<br />    </span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$s</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_NO_AUTO_COMMIT</span><span style="color: #007700">);<br />    </span><span style="color: #9876AA">$row </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_fetch_array</span><span style="color: #007700">(</span><span style="color: #9876AA">$s</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_ASSOC</span><span style="color: #007700">);<br />    if (!</span><span style="color: #9876AA">$row</span><span style="color: #007700">) {<br />        echo </span><span style="color: #DD0000">"No rows\n"</span><span style="color: #007700">;<br />    } else {<br />        do {<br />            foreach (</span><span style="color: #9876AA">$row </span><span style="color: #007700">as </span><span style="color: #9876AA">$item</span><span style="color: #007700">)<br />                echo </span><span style="color: #9876AA">$item </span><span style="color: #007700">. </span><span style="color: #DD0000">" "</span><span style="color: #007700">;<br />            echo </span><span style="color: #DD0000">"\n"</span><span style="color: #007700">;<br />        } while ((</span><span style="color: #9876AA">$row </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_fetch_array</span><span style="color: #007700">(</span><span style="color: #9876AA">$s</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_ASSOC</span><span style="color: #007700">)) != </span><span style="color: #9876AA">false</span><span style="color: #007700">);<br />    }<br />}<br /><br /></span><span style="color: #9876AA">$c1 </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">"hr"</span><span style="color: #007700">, </span><span style="color: #DD0000">"welcome"</span><span style="color: #007700">, </span><span style="color: #DD0000">"localhost/orcl"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$c2 </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_new_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">"hr"</span><span style="color: #007700">, </span><span style="color: #DD0000">"welcome"</span><span style="color: #007700">, </span><span style="color: #DD0000">"localhost/orcl"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$s </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$c1</span><span style="color: #007700">, </span><span style="color: #DD0000">"insert into mytab values(1234)"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$s</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_NO_AUTO_COMMIT</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">query</span><span style="color: #007700">(</span><span style="color: #DD0000">"basic connection"</span><span style="color: #007700">, </span><span style="color: #9876AA">$c1</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">query</span><span style="color: #007700">(</span><span style="color: #DD0000">"new connection"</span><span style="color: #007700">, </span><span style="color: #9876AA">$c2</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_commit</span><span style="color: #007700">(</span><span style="color: #9876AA">$c1</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">query</span><span style="color: #007700">(</span><span style="color: #DD0000">"new connection after commit"</span><span style="color: #007700">, </span><span style="color: #9876AA">$c2</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// Output is:<br />//   Querying basic connection<br />//   1234 <br />//   Querying new connection<br />//   No rows<br />//   Querying new connection after commit<br />//   1234 <br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span>  <p class="para">    有关参数使用的更多示例，请参见 <span class="function">{@link oci_connect()}</span>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.oci-new-connect-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link oci_connect()} - 建立一个到 Oracle 服务器的连接</span></li>    <li class="member"><span class="function">{@link oci_pconnect()} - 使用持久连接连，连接到 Oracle 数据库</span></li>   </ul>  </span> </div></div>
 * @return resource|false A connection identifier or <b>FALSE</b> on error.*/
function oci_new_connect($username, $password, $connection_string = null, $character_set = null, $session_mode = null) {}

/**
*<div id="function.oci-pconnect" class="refentry"> <div class="refnamediv">  <h1 class="refname">oci_pconnect</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8, PECL OCI8 &gt;= 1.1.0)</p><p class="refpurpose"><span class="refname">oci_pconnect</span> &mdash; <span class="dc-title">使用持久连接连，连接到 Oracle 数据库</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-pconnect-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>oci_pconnect</strong></span>(<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$username</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$password</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">string</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">$connection_string</span><span class="initializer"> = <strong><span>null</span></strong></span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$encoding</span><span class="initializer"> = &quot;&quot;</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$session_mode</span><span class="initializer"> = <strong><span>OCI_DEFAULT</span></strong></span></span><br>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">resource</span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>  <p class="para rdfs-comment">   创建到 Oracle 服务器的持久连接并登录。  </p>  <p class="para">   持久连接会被缓冲并在请求之间重复使用，可以降低每个页面加载的消耗；一个典型的 PHP 应用程序对于每个 Apache 子进程（或者 PHP FastCGI   进程）会有打开的持久连接到 Oracle 服务器。更多信息见<a href="https://www.php.net/manual/zh/oci8.connection.php" class="link">连接处理和连接池</a>一节。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-pconnect-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">username</span></dt>     <dd>      <p class="para">       Oracle 用户名。      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">password</span></dt>     <dd>      <p class="para">       <span class="parameter" style="color:#3A95FF">username</span> 的密码。      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">connection_string</span></dt>     <dd>      <p class="para">包含要连接的<span>Oracle 实例</span>。可以是 <a href="https://www.php.net/manual/zh/https://www.oracle.com/pls/topic/lookup?ctx=dblatest&id=GUID-E5358DEA-D619-4B7B-A799-3D2F802500F1" class="link external">&raquo;&nbsp;Easy Connect 串</a>，或是<var class="filename">tnsnames.ora</var> 文件中的连接名，或是本地 Oracle 实例名。</p><p class="para"> 如果不指定或者为 <strong><span>null</span></strong>，PHP 使用环境变量来确定连接的 <span>Oracle 实例</span>，诸如 <strong><span>TWO_TASK</span></strong>（Linux 下）或  <strong><span>LOCAL</span></strong>（Windows 下）与 <strong><span>ORACLE_SID</span></strong> 等。</p><p class="para"> 要使用 Easy Connect 命名方法，PHP 必须与 Oracle 10<em>g</em> 或更高版本的客户端库进行链接。Oracle 10<em>g</em> 的 Easy Connect 串格式：<em>[//]host_name[:port][/service_name]</em>。Oracle 11<em>g</em> 则为：<em>[//]host_name[:port][/service_name][:server_type][/instance_name]</em>。 在 Oracle 19c 加入类更多选项，例如 timeout 和 keep-alive 设置。 请参考 Oracle 文档。 服务名可在数据库服务器机器上运行 Oracle 实用程序 <span>lsnrctl status</span> 找到。</p><p class="para"> <var class="filename">tnsnames.ora</var> 文件可在 Oracle Net 查找路径中，此路径包括 <var class="filename">/your/path/to/instantclient/network/admin</var>、<var class="filename">$ORACLE_HOME/network/admin</var> 、<var class="filename">/etc</var>。 另一种方法是设置 <span>TNS_ADMIN</span> 以便通过 <var class="filename">$TNS_ADMIN/tnsnames.ora</var> 来读取。表确认 web 守护进程可读取此文件。</p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">encoding</span></dt>     <dd>      <p class="para">使用 Oracle客户端库来确定字符集。字符集不需要与数据库的字符集相匹配。如果不匹配，Oracle会尽可能地将数据从数据库字符集进行转换。因为依赖于字符集，可能不能给出可用的结果。转换也增加一些时间开销。</p><p class="para"> 如果不指定，Oracle 客户端用 <strong><span>NLS_LANG</span></strong> 环境变量来决定字符集。</p><p class="para"> 传递此参数可减少连接时间。</p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">session_mode</span></dt>     <dd>      <p class="para">此参数在 PHP 5（PECL OCI81.1）版本开始可用，并收受下列值：<strong><span>OCI_DEFAULT</span></strong>，<strong><span>OCI_SYSOPER</span></strong>和 <strong><span>OCI_SYSDBA</span></strong>。如为<strong><span>OCI_SYSOPER</span></strong> 或 <strong><span>OCI_SYSDBA</span></strong>其中之一，此函数将会使用外部的证书建立有特权的连接。有特权的连接默认是禁用的。需要将<a href="https://www.php.net/manual/zh/oci8.configuration.php#ini.oci8.privileged-connect" class="link">oci8.privileged_connect</a>设为 <span>On</span> 来启用。</p><p class="para"> PHP 5.3（PECL OCI8 1.3.4）引进了 <strong><span>OCI_CRED_EXT</span></strong> 模式值。使用外部或操作系统认证必需在 Oracle 数据库中进行配置。<strong><span>OCI_CRED_EXT</span></strong> 标志只可用于用户为 &quot;/&quot;，密码为空的情况。<a href="https://www.php.net/manual/zh/oci8.configuration.php#ini.oci8.privileged-connect" class="link">oci8.privileged_connect</a> 可为 <span>On</span> 或 <span>Off</span>。</p><p class="para"> <strong><span>OCI_CRED_EXT</span></strong> 可与 <strong><span>OCI_SYSOPER</span></strong> 或 <strong><span>OCI_SYSDBA</span></strong> 模式组合使用。</p><p class="para"> <strong><span>OCI_CRED_EXT</span></strong> 由于安全的原因不支持 Windows 系统。</p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-pconnect-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   返回连接标识符或错误时为 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.oci-pconnect-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="xmlwriter-write-attribute.example.intermix">    <p><strong>示例 #1 基础 <span class="function"><strong style="color:#CC7832">oci_pconnect()</strong></span> 示例，使用 Easy Connect 语法</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br /></span><span style="color: #FF8000">// Connects to the XE service (i.e. database) on the "localhost" machine<br /></span><span style="color: #9876AA">$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_pconnect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">, </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br />if (!</span><span style="color: #9876AA">$conn</span><span style="color: #007700">) {<br />    </span><span style="color: #9876AA">$e </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_error</span><span style="color: #007700">();<br />    </span><span style="color: #9876AA">trigger_error</span><span style="color: #007700">(</span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$e</span><span style="color: #007700">[</span><span style="color: #DD0000">'message'</span><span style="color: #007700">], </span><span style="color: #9876AA">ENT_QUOTES</span><span style="color: #007700">), </span><span style="color: #9876AA">E_USER_ERROR</span><span style="color: #007700">);<br />}<br /><br /></span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">'SELECT * FROM employees'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /><br />echo </span><span style="color: #DD0000">"&lt;table border='1'&gt;\n"</span><span style="color: #007700">;<br />while (</span><span style="color: #9876AA">$row </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_fetch_array</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_ASSOC</span><span style="color: #007700">+</span><span style="color: #9876AA">OCI_RETURN_NULLS</span><span style="color: #007700">)) {<br />    echo </span><span style="color: #DD0000">"&lt;tr&gt;\n"</span><span style="color: #007700">;<br />    foreach (</span><span style="color: #9876AA">$row </span><span style="color: #007700">as </span><span style="color: #9876AA">$item</span><span style="color: #007700">) {<br />        echo </span><span style="color: #DD0000">"    &lt;td&gt;" </span><span style="color: #007700">. (</span><span style="color: #9876AA">$item </span><span style="color: #007700">!== </span><span style="color: #9876AA">null </span><span style="color: #007700">? </span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$item</span><span style="color: #007700">, </span><span style="color: #9876AA">ENT_QUOTES</span><span style="color: #007700">) : </span><span style="color: #DD0000">"&amp;nbsp;"</span><span style="color: #007700">) . </span><span style="color: #DD0000">"&lt;/td&gt;\n"</span><span style="color: #007700">;<br />    }<br />    echo </span><span style="color: #DD0000">"&lt;/tr&gt;\n"</span><span style="color: #007700">;<br />}<br />echo </span><span style="color: #DD0000">"&lt;/table&gt;\n"</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span>  <p class="para">    有关参数使用的更多示例，请参见 <span class="function">{@link oci_connect()}</span>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.oci-pconnect-notes">  <h3 class="title">注释</h3>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <span class="simpara">    每个 PHP 进程的持久 Oracle 连接的生命周期和最大数目可以通过以下配置选项来调整：<a href="https://www.php.net/manual/zh/oci8.configuration.php#ini.oci8.persistent-timeout" class="link">oci8.persistent_timeout</a>，<a href="https://www.php.net/manual/zh/oci8.configuration.php#ini.oci8.ping-interval" class="link">oci8.ping_interval</a>    和 <a href="https://www.php.net/manual/zh/oci8.configuration.php#ini.oci8.max-persistent" class="link">oci8.max_persistent</a>。   </span>  </p></blockquote> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.oci-pconnect-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link oci_connect()} - 建立一个到 Oracle 服务器的连接</span></li>    <li class="member"><span class="function">{@link oci_new_connect()} - 使用唯一连接，连接到 Oracle 服务器</span></li>   </ul>  </span> </div></div>
 * @return resource|false A connection identifier or <b>FALSE</b> on error.*/
function oci_pconnect($username, $password, $connection_string = null, $character_set = null, $session_mode = null) {}

/**
*<div id="function.oci-error" class="refentry"> <div class="refnamediv">  <h1 class="refname">oci_error</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8, PECL OCI8 &gt;= 1.1.0)</p><p class="refpurpose"><span class="refname">oci_error</span> &mdash; <span class="dc-title">返回最后发现的错误</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-error-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>oci_error</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">resource</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">$connection_or_statement</span><span class="initializer"> = <strong><span>null</span></strong></span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">array</span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>  <p class="para rdfs-comment">   返回最后发现的错误。  </p>  <p class="para">   发生错误后应立即调用该函数。错误会由成功的语句清除。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-error-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">connection_or_statement</span></dt>     <dd>      <p class="para">       对于大多数错误，<span class="parameter" style="color:#3A95FF">connection_or_statement</span> 是传递给失败函数以供调用的资源句柄。对于       <span class="function">{@link oci_connect()}</span>、<span class="function">{@link oci_new_connect()}</span> 或 <span class="function">{@link oci_pconnect()}</span>       的连接错误，应传递 <strong><span>null</span></strong>。      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-error-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   如果没有发现错误，<span class="function"><strong style="color:#CC7832">oci_error()</strong></span> 返回 <strong><span>false</span></strong>。否则，<span class="function"><strong style="color:#CC7832">oci_error()</strong></span> 将错误信息作为关联数组返回。  </p>  <p class="para">   <table class="doctable table">   <caption><strong><span class="function"><strong style="color:#CC7832">oci_error()</strong></span> 数组说明</strong></caption>         <thead>      <tr>       <th>数组键</th>       <th>类型</th>       <th>说明</th>      </tr>     </thead>     <tbody class="tbody">      <tr>       <td><span>code</span></td>       <td><span class="type" style="color:#EAB766">int</span></td>       <td>         Oracle 错误编号。       </td>      </tr>      <tr>       <td><span>message</span></td>       <td><span class="type" style="color:#EAB766">string</span></td>       <td>         Oracle 错误文本。       </td>      </tr>      <tr>       <td><span>offset</span></td>       <td><span class="type" style="color:#EAB766">int</span></td>       <td>         SQL 语句中错误的字节位置。如果没有语句，则为 <span>0</span>       </td>      </tr>      <tr>       <td><span>sqltext</span></td>       <td><span class="type" style="color:#EAB766">string</span></td>       <td>         SQL 语句文本。如果没有语句，则是空字符串。       </td>      </tr>     </tbody>       </table>  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 changelog" id="refsect1-function.oci-error-changelog">  <h3 class="title">更新日志</h3>  <table class="doctable informaltable">       <thead>     <tr>      <th>版本</th>      <th>说明</th>     </tr>    </thead>    <tbody class="tbody">     <tr>      <td>8.0.0、PECL OCI8 3.0.0</td>      <td>       <span class="parameter" style="color:#3A95FF">connection_or_statement</span> 现在可为 null。      </td>     </tr>    </tbody>     </table> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.oci-error-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="example.com2">    <p><strong>示例 #1 连接错误后显示 Oracle 错误消息</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">"hr"</span><span style="color: #007700">, </span><span style="color: #DD0000">"welcome"</span><span style="color: #007700">, </span><span style="color: #DD0000">"localhost/XE"</span><span style="color: #007700">);<br />if (!</span><span style="color: #9876AA">$conn</span><span style="color: #007700">) {<br />    </span><span style="color: #9876AA">$e </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_error</span><span style="color: #007700">();   </span><span style="color: #FF8000">// For oci_connect errors do not pass a handle<br />    </span><span style="color: #9876AA">trigger_error</span><span style="color: #007700">(</span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$e</span><span style="color: #007700">[</span><span style="color: #DD0000">'message'</span><span style="color: #007700">]), </span><span style="color: #9876AA">E_USER_ERROR</span><span style="color: #007700">);<br />}<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span>   <p class="para">   <div class="example" id="compersisthelper.savetofile.example.basic">    <p><strong>示例 #2 语法解析错误后显示 Oracle 错误消息</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"select ' from dual"</span><span style="color: #007700">);  </span><span style="color: #FF8000">// note mismatched quote<br /></span><span style="color: #007700">if (!</span><span style="color: #9876AA">$stid</span><span style="color: #007700">) {<br />    </span><span style="color: #9876AA">$e </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_error</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);  </span><span style="color: #FF8000">// For oci_parse errors pass the connection handle<br />    </span><span style="color: #9876AA">trigger_error</span><span style="color: #007700">(</span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$e</span><span style="color: #007700">[</span><span style="color: #DD0000">'message'</span><span style="color: #007700">]), </span><span style="color: #9876AA">E_USER_ERROR</span><span style="color: #007700">);<br />}<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </p>  <p class="para">   <div class="example" id="example-5516">    <p><strong>示例 #3 显示 Oracle 错误信息、问题语句、执行错误问题所在位置</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"select does_not_exist from dual"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$r </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br />if (!</span><span style="color: #9876AA">$r</span><span style="color: #007700">) {<br />    </span><span style="color: #9876AA">$e </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_error</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);  </span><span style="color: #FF8000">// For oci_execute errors pass the statement handle<br />    </span><span style="color: #007700">print </span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$e</span><span style="color: #007700">[</span><span style="color: #DD0000">'message'</span><span style="color: #007700">]);<br />    print </span><span style="color: #DD0000">"\n&lt;pre&gt;\n"</span><span style="color: #007700">;<br />    print </span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$e</span><span style="color: #007700">[</span><span style="color: #DD0000">'sqltext'</span><span style="color: #007700">]);<br />    </span><span style="color: #9876AA">printf</span><span style="color: #007700">(</span><span style="color: #DD0000">"\n%"</span><span style="color: #007700">.(</span><span style="color: #9876AA">$e</span><span style="color: #007700">[</span><span style="color: #DD0000">'offset'</span><span style="color: #007700">]+</span><span style="color: #9876AA">1</span><span style="color: #007700">).</span><span style="color: #DD0000">"s"</span><span style="color: #007700">, </span><span style="color: #DD0000">"^"</span><span style="color: #007700">);<br />    print  </span><span style="color: #DD0000">"\n&lt;/pre&gt;\n"</span><span style="color: #007700">;<br />}<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </p> </div></div>
 * @return array|false If no error is found, {@see oci_error} returns*/
function oci_error($resource = null) {}

/**
*<div id="function.oci-free-descriptor" class="refentry"> <div class="refnamediv">  <h1 class="refname">oci_free_descriptor</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8, PECL OCI8 &gt;= 1.1.0)</p><p class="refpurpose"><span class="refname">oci_free_descriptor</span> &mdash; <span class="dc-title">Frees a descriptor</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-free-descriptor-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>oci_free_descriptor</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.ocilob.php" class="type OCILob" style="color:#EAB766">OCILob</a></span> <span class="parameter" style="color:#3A95FF">$lob</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   Frees a descriptor allocated by <span class="function">{@link oci_new_descriptor()}</span>.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-free-descriptor-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">descriptor</span></dt>     <dd>      <p class="para">       Descriptor allocated by <span class="function">{@link oci_new_descriptor()}</span>.      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-free-descriptor-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.oci-free-descriptor-notes">  <h3 class="title">注释</h3>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    This function is commonly used as a method {@link OCILOB::free}.   </p>  </p></blockquote> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.oci-free-descriptor-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member">{@link OCILOB::free}</li>   </ul>  </span> </div></div>
 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
function oci_free_descriptor($descriptor) {}

/**
*<div id="function.oci-lob-is-equal" class="refentry"> <div class="refnamediv">  <h1 class="refname">oci_lob_is_equal</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8, PECL OCI8 &gt;= 1.1.0)</p><p class="refpurpose"><span class="refname">oci_lob_is_equal</span> &mdash; <span class="dc-title">Compares two LOB/FILE locators for equality</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-lob-is-equal-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>oci_lob_is_equal</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.ocilob.php" class="type OCILob" style="color:#EAB766">OCILob</a></span> <span class="parameter" style="color:#3A95FF">$lob1</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.ocilob.php" class="type OCILob" style="color:#EAB766">OCILob</a></span> <span class="parameter" style="color:#3A95FF">$lob2</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   Compares two LOB/FILE locators.   </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-lob-is-equal-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">lob1</span></dt>     <dd>      <p class="para">       A LOB identifier.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">lob2</span></dt>     <dd>      <p class="para">       A LOB identifier.      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-lob-is-equal-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   Returns <strong><span>true</span></strong> if these objects are equal, <strong><span>false</span></strong> otherwise.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.oci-lob-is-equal-notes">  <h3 class="title">注释</h3>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    The OCILob class was called OCI-Lob prior to PHP 8 and PECL OCI8 3.0.0.   </p>  </p></blockquote> </div></div>
 * @return bool <b>TRUE</b> if these objects are equal, <b>FALSE</b> otherwise.*/
function oci_lob_is_equal(
    #[LanguageLevelTypeAware(['8.0' => 'OCILob'], default: 'OCI_Lob')] $lob1,
    #[LanguageLevelTypeAware(['8.0' => 'OCILob'], default: 'OCI_Lob')] $lob2
) {}

/**
*<div id="function.oci-lob-copy" class="refentry"> <div class="refnamediv">  <h1 class="refname">oci_lob_copy</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8, PECL OCI8 &gt;= 1.1.0)</p><p class="refpurpose"><span class="refname">oci_lob_copy</span> &mdash; <span class="dc-title">Copies large object</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-lob-copy-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>oci_lob_copy</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.ocilob.php" class="type OCILob" style="color:#EAB766">OCILob</a></span> <span class="parameter" style="color:#3A95FF">$to</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.ocilob.php" class="type OCILob" style="color:#EAB766">OCILob</a></span> <span class="parameter" style="color:#3A95FF">$from</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">int</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">$length</span><span class="initializer"> = <strong><span>null</span></strong></span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   Copies a large object or a part of a large object to another large object.   Old LOB-recipient data will be overwritten.  </p>  <p class="para">   If you need to copy a particular part of a LOB to a particular position of   a LOB, use <span class="function">{@link OCILob::seek()}</span> to move LOB internal pointers.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-lob-copy-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">to</span></dt>     <dd>      <p class="para">       The destination LOB.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">from</span></dt>     <dd>      <p class="para">       The copied LOB.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">length</span></dt>     <dd>      <p class="para">       Indicates the length of data to be copied.      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-lob-copy-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 changelog" id="refsect1-function.oci-lob-copy-changelog">  <h3 class="title">更新日志</h3>  <table class="doctable informaltable">       <thead>     <tr>      <th>版本</th>      <th>说明</th>     </tr>    </thead>    <tbody class="tbody">     <tr>      <td>8.0.0, PECL OCI8 3.0.0</td>      <td>       <span class="parameter" style="color:#3A95FF">length</span> is now nullable.      </td>     </tr>    </tbody>     </table> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.oci-lob-copy-notes">  <h3 class="title">注释</h3>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    The OCILob class was called OCI-Lob prior to PHP 8 and PECL OCI8 3.0.0.   </p>  </p></blockquote> </div></div>
 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
function oci_lob_copy(
    #[LanguageLevelTypeAware(['8.0' => 'OCILob'], default: 'OCI_Lob')] $lob_to,
    #[LanguageLevelTypeAware(['8.0' => 'OCILob'], default: 'OCI_Lob')] $lob_from,
    $length = 0
) {}

/**
*<div id="function.oci-commit" class="refentry"> <div class="refnamediv">  <h1 class="refname">oci_commit</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8, PECL OCI8 &gt;= 1.1.0)</p><p class="refpurpose"><span class="refname">oci_commit</span> &mdash; <span class="dc-title">提交未完成的数据库事务</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-commit-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>oci_commit</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$connection</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   为 Oracle <span class="parameter" style="color:#3A95FF">connection</span> 提交未完成的事务。提交结束当前事务并使所有更改永久化。释放所有持有的锁。  </p>  <p class="para">   当使用 <strong><span>OCI_NO_AUTO_COMMIT</span></strong> flag 通过 <span class="function">{@link oci_execute()}</span> 执行第一个更改数据的 SQL   语句时，事务开始。其他语句所做的进一步数据更改成为同一事务的一部分。在事务提交或回滚之前，事务中所做的数据更改是临时的。在提交之前，数据库的其他用户将看不到更改。  </p>  <p class="para">   插入或更新数据时，出于关系数据一致性和性能原因，建议使用事务。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-commit-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">connection</span></dt>     <dd>      <p class="para">       <span class="function">{@link oci_connect()}</span>、<span class="function">{@link oci_pconnect()}</span> 或 <span class="function">{@link oci_new_connect()}</span> 返回的 Oracle 连接标识符。      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-commit-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.oci-commit-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="example-5483">    <p><strong>示例 #1 <span class="function"><strong style="color:#CC7832">oci_commit()</strong></span> 示例</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br /></span><span style="color: #FF8000">// Insert into several tables, rolling back the changes if an error occurs<br /><br /></span><span style="color: #9876AA">$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">, </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"INSERT INTO mysalary (id, name) VALUES (1, 'Chris')"</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// The OCI_NO_AUTO_COMMIT flag tells Oracle not to commit the INSERT immediately<br /></span><span style="color: #9876AA">$r </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_NO_AUTO_COMMIT</span><span style="color: #007700">);<br />if (!</span><span style="color: #9876AA">$r</span><span style="color: #007700">) {    <br />    </span><span style="color: #9876AA">$e </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_error</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br />    </span><span style="color: #9876AA">trigger_error</span><span style="color: #007700">(</span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$e</span><span style="color: #007700">[</span><span style="color: #DD0000">'message'</span><span style="color: #007700">]), </span><span style="color: #9876AA">E_USER_ERROR</span><span style="color: #007700">);<br />}<br /><br /></span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">'INSERT INTO myschedule (startday) VALUES (12)'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$r </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_NO_AUTO_COMMIT</span><span style="color: #007700">);<br />if (!</span><span style="color: #9876AA">$r</span><span style="color: #007700">) {    <br />    </span><span style="color: #9876AA">$e </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_error</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br />    </span><span style="color: #9876AA">oci_rollback</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);  </span><span style="color: #FF8000">// rollback changes to both tables<br />    </span><span style="color: #9876AA">trigger_error</span><span style="color: #007700">(</span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$e</span><span style="color: #007700">[</span><span style="color: #DD0000">'message'</span><span style="color: #007700">]), </span><span style="color: #9876AA">E_USER_ERROR</span><span style="color: #007700">);<br />}<br /><br /></span><span style="color: #FF8000">// Commit the changes to both tables<br /></span><span style="color: #9876AA">$r </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_commit</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br />if (!</span><span style="color: #9876AA">$r</span><span style="color: #007700">) {<br />    </span><span style="color: #9876AA">$e </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_error</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br />    </span><span style="color: #9876AA">trigger_error</span><span style="color: #007700">(</span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$e</span><span style="color: #007700">[</span><span style="color: #DD0000">'message'</span><span style="color: #007700">]), </span><span style="color: #9876AA">E_USER_ERROR</span><span style="color: #007700">);<br />}<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.oci-commit-notes">  <h3 class="title">注释</h3>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    当关闭连接或脚本结束时（看哪个先）事务会自动回滚。需要明确地调用    <span class="function"><strong style="color:#CC7832">oci_commit()</strong></span> 来提交事务。   </p>   <p class="para">    Any call to <span class="function">{@link oci_execute()}</span> that uses    <strong><span>OCI_COMMIT_ON_SUCCESS</span></strong> mode explicitly or by    default will commit any previous uncommitted transaction.   </p>   <p class="para">    任何 Oracle DDL 语句（例如 <span>CREATE</span> 或 <span>DROP</span>）都会自动提交任何未提交的事务。   </p>  </p></blockquote> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.oci-commit-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link oci_execute()} - 执行语句</span></li>    <li class="member"><span class="function">{@link oci_rollback()} - 回滚未完成的事务</span></li>   </ul>  </span> </div></div>
 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
function oci_commit($connection) {}

/**
*<div id="function.oci-rollback" class="refentry"> <div class="refnamediv">  <h1 class="refname">oci_rollback</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8, PECL OCI8 &gt;= 1.1.0)</p><p class="refpurpose"><span class="refname">oci_rollback</span> &mdash; <span class="dc-title">回滚未完成的事务</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-rollback-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>oci_rollback</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$connection</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   恢复 Oracle <span class="parameter" style="color:#3A95FF">connection</span> 的所有未提交更改并结束事务。释放所有持有的锁。所有 Oracle <span>SAVEPOINTS</span> 都被擦除。  </p>  <p class="para">   当使用 <strong><span>OCI_NO_AUTO_COMMIT</span></strong> flag 通过 <span class="function">{@link oci_execute()}</span> 执行第一个更改数据的 SQL   语句时，事务开始。其他语句所做的后续数据更改成为同一事务的一部分。在事务提交或回滚之前，事务中所做的数据更改是临时的。在提交之前，数据库的其他用户将看不到更改。  </p>  <p class="para">   插入或更新数据时，出于关系数据一致性和性能原因，建议使用事务。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-rollback-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">connection</span></dt>     <dd>      <p class="para">       <span class="function">{@link oci_connect()}</span>、<span class="function">{@link oci_pconnect()}</span> 或 <span class="function">{@link oci_new_connect()}</span> 返回的 Oracle 连接标识符。      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-rollback-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.oci-rollback-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="xsl.examples-errors.capture">    <p><strong>示例 #1 <span class="function"><strong style="color:#CC7832">oci_rollback()</strong></span> 示例</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br /></span><span style="color: #FF8000">// Insert into several tables, rolling back the changes if an error occurs<br /><br /></span><span style="color: #9876AA">$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">, </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"INSERT INTO mysalary (id, name) VALUES (1, 'Chris')"</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// The OCI_NO_AUTO_COMMIT flag tells Oracle not to commit the INSERT immediately<br /></span><span style="color: #9876AA">$r </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_NO_AUTO_COMMIT</span><span style="color: #007700">);<br />if (!</span><span style="color: #9876AA">$r</span><span style="color: #007700">) {    <br />    </span><span style="color: #9876AA">$e </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_error</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br />    </span><span style="color: #9876AA">trigger_error</span><span style="color: #007700">(</span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$e</span><span style="color: #007700">[</span><span style="color: #DD0000">'message'</span><span style="color: #007700">]), </span><span style="color: #9876AA">E_USER_ERROR</span><span style="color: #007700">);<br />}<br /><br /></span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">'INSERT INTO myschedule (startday) VALUES (12)'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$r </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_NO_AUTO_COMMIT</span><span style="color: #007700">);<br />if (!</span><span style="color: #9876AA">$r</span><span style="color: #007700">) {    <br />    </span><span style="color: #9876AA">$e </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_error</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br />    </span><span style="color: #9876AA">oci_rollback</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);  </span><span style="color: #FF8000">// rollback changes to both tables<br />    </span><span style="color: #9876AA">trigger_error</span><span style="color: #007700">(</span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$e</span><span style="color: #007700">[</span><span style="color: #DD0000">'message'</span><span style="color: #007700">]), </span><span style="color: #9876AA">E_USER_ERROR</span><span style="color: #007700">);<br />}<br /><br /></span><span style="color: #FF8000">// Commit the changes to both tables<br /></span><span style="color: #9876AA">$r </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_commit</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br />if (!</span><span style="color: #9876AA">r</span><span style="color: #007700">) {<br />    </span><span style="color: #9876AA">$e </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_error</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br />    </span><span style="color: #9876AA">trigger_error</span><span style="color: #007700">(</span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$e</span><span style="color: #007700">[</span><span style="color: #DD0000">'message'</span><span style="color: #007700">]), </span><span style="color: #9876AA">E_USER_ERROR</span><span style="color: #007700">);<br />}<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span>  <p class="para">   <div class="example" id="example-5669">    <p><strong>示例 #2 回滚到 <span>SAVEPOINT</span> 示例</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">'UPDATE mytab SET id = 1111'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_NO_AUTO_COMMIT</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// Create the savepoint<br /></span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">'SAVEPOINT mysavepoint'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_NO_AUTO_COMMIT</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">'UPDATE mytab SET id = 2222'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_NO_AUTO_COMMIT</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// Use an explicit SQL statement to rollback to the savepoint<br /></span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">'ROLLBACK TO SAVEPOINT mysavepoint'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_NO_AUTO_COMMIT</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">oci_commit</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);  </span><span style="color: #FF8000">// mytab now has id of 1111<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.oci-rollback-notes">  <h3 class="title">注释</h3>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    当关闭连接或脚本结束时，事务会自动回滚。需要显式调用 <span class="function">{@link oci_commit()}</span> 提交事务。   </p>   <p class="para">    Any call to <span class="function">{@link oci_execute()}</span> that uses    <strong><span>OCI_COMMIT_ON_SUCCESS</span></strong> mode explicitly or by    default will commit any previous uncommitted transaction.   </p>   <p class="para">    任何 Oracle DDL 语句（例如 <span>CREATE</span> 或 <span>DROP</span>）都会自动提交任何未提交的事务。   </p>  </p></blockquote> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.oci-rollback-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link oci_commit()} - 提交未完成的数据库事务</span></li>    <li class="member"><span class="function">{@link oci_execute()} - 执行语句</span></li>   </ul>  </span> </div></div>
 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
function oci_rollback($connection) {}

/**
*<div id="function.oci-new-descriptor" class="refentry"> <div class="refnamediv">  <h1 class="refname">oci_new_descriptor</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8, PECL OCI8 &gt;= 1.1.0)</p><p class="refpurpose"><span class="refname">oci_new_descriptor</span> &mdash; <span class="dc-title">初始化新的空 LOB 或 FILE 描述符</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-new-descriptor-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>oci_new_descriptor</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$connection</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$type</span><span class="initializer"> = <strong><span>OCI_DTYPE_LOB</span></strong></span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.ocilob.php" class="type OCILob" style="color:#EAB766">OCILob</a></span></span></div>  <p class="para rdfs-comment">   分配保存描述符或 LOB 定位器的资源。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-new-descriptor-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">connection</span></dt>     <dd>      <p class="para">       Oracle 连接标识符，由 <span class="function">{@link oci_connect()}</span> 或 <span class="function">{@link oci_pconnect()}</span> 返回。      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">type</span></dt>     <dd>      <p class="para">       <span class="parameter" style="color:#3A95FF">type</span> 的有效值是：<strong><span>OCI_DTYPE_FILE</span></strong>、<strong><span>OCI_DTYPE_LOB</span></strong>       和 <strong><span>OCI_DTYPE_ROWID</span></strong>。      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-new-descriptor-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回新 LOB 或 FILE 描述符，失败时为 <strong><span>null</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.oci-new-descriptor-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="example-5636">    <p><strong>示例 #1 <span class="function"><strong style="color:#CC7832">oci_new_descriptor()</strong></span> 示例</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /></span><span style="color: #FF8000">// This script is designed to be called from a HTML form.<br /> * It expects $user, $password, $table, $where, and $commitsize<br /> * to be passed in from the form.  The script then deletes<br /> * the selected rows using the ROWID and commits after each<br /> * set of $commitsize rows. (Use with care, there is no rollback)<br /> <br /></span><span style="color: #9876AA">$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #9876AA">$user</span><span style="color: #007700">, </span><span style="color: #9876AA">$password</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$stmt </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"select rowid from </span><span style="color: #9876AA">$table</span><span style="color: #DD0000"> </span><span style="color: #9876AA">$where</span><span style="color: #DD0000">"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$rowid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_new_descriptor</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_D_ROWID</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_define_by_name</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">, </span><span style="color: #DD0000">"ROWID"</span><span style="color: #007700">, </span><span style="color: #9876AA">$rowid</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">);<br />while (</span><span style="color: #9876AA">oci_fetch</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">)) {<br />    </span><span style="color: #9876AA">$nrows </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_num_rows</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">);<br />    </span><span style="color: #9876AA">$delete </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"delete from </span><span style="color: #9876AA">$table</span><span style="color: #DD0000"> where ROWID = :rid"</span><span style="color: #007700">);<br />    </span><span style="color: #9876AA">oci_bind_by_name</span><span style="color: #007700">(</span><span style="color: #9876AA">$delete</span><span style="color: #007700">, </span><span style="color: #DD0000">":rid"</span><span style="color: #007700">, </span><span style="color: #9876AA">$rowid</span><span style="color: #007700">, -</span><span style="color: #9876AA">1</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_B_ROWID</span><span style="color: #007700">);<br />    </span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$delete</span><span style="color: #007700">);<br />    echo </span><span style="color: #DD0000">"</span><span style="color: #9876AA">$nrows</span><span style="color: #DD0000">\n"</span><span style="color: #007700">;<br />    if ((</span><span style="color: #9876AA">$nrows </span><span style="color: #007700">% </span><span style="color: #9876AA">$commitsize</span><span style="color: #007700">) == </span><span style="color: #9876AA">0</span><span style="color: #007700">) {<br />        </span><span style="color: #9876AA">oci_commit</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br />    }<br />}<br /></span><span style="color: #9876AA">$nrows </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_num_rows</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">);<br />echo </span><span style="color: #DD0000">"</span><span style="color: #9876AA">$nrows</span><span style="color: #DD0000"> deleted...\n"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">oci_free_statement</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />    </span><span style="color: #FF8000">// This script demonstrates file upload to LOB columns<br />     * The formfield used for this example looks like this<br />     * &lt;form action="upload.php" method="post" enctype="multipart/form-data"&gt;<br />     * &lt;input type="file" name="lob_upload" /&gt;<br />     * ...<br />     <br />  </span><span style="color: #007700">if (!isset(</span><span style="color: #9876AA">$lob_upload</span><span style="color: #007700">) || </span><span style="color: #9876AA">$lob_upload </span><span style="color: #007700">== </span><span style="color: #DD0000">'none'</span><span style="color: #007700">){<br /></span><span style="color: #9876AA">?&gt;<br /></span>&lt;form action="upload.php" method="post" enctype="multipart/form-data"&gt;<br />Upload file: &lt;input type="file" name="lob_upload" /&gt;&lt;br /&gt;<br />&lt;input type="submit" value="Upload" /&gt; - &lt;input type="reset" value="Reset" /&gt;<br />&lt;/form&gt;<br /><span style="color: #9876AA">&lt;?php<br />  </span><span style="color: #007700">} else {<br /><br />     </span><span style="color: #FF8000">// $lob_upload contains the temporary filename of the uploaded file<br /><br />     // see also the features section on file upload,<br />     // if you would like to use secure uploads<br /><br />     </span><span style="color: #9876AA">$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #9876AA">$user</span><span style="color: #007700">, </span><span style="color: #9876AA">$password</span><span style="color: #007700">);<br />     </span><span style="color: #9876AA">$lob </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_new_descriptor</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_D_LOB</span><span style="color: #007700">);<br />     </span><span style="color: #9876AA">$stmt </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"insert into </span><span style="color: #9876AA">$table</span><span style="color: #DD0000"> (id, the_blob)<br />               values(my_seq.NEXTVAL, EMPTY_BLOB()) returning the_blob into :the_blob"</span><span style="color: #007700">);<br />     </span><span style="color: #9876AA">oci_bind_by_name</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">, </span><span style="color: #DD0000">':the_blob'</span><span style="color: #007700">, </span><span style="color: #9876AA">$lob</span><span style="color: #007700">, -</span><span style="color: #9876AA">1</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_B_BLOB</span><span style="color: #007700">);<br />     </span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_DEFAULT</span><span style="color: #007700">);<br />     if (</span><span style="color: #9876AA">$lob</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">savefile</span><span style="color: #007700">(</span><span style="color: #9876AA">$lob_upload</span><span style="color: #007700">)){<br />        </span><span style="color: #9876AA">oci_commit</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br />        echo </span><span style="color: #DD0000">"Blob successfully uploaded\n"</span><span style="color: #007700">;<br />     }else{<br />        echo </span><span style="color: #DD0000">"Couldn't upload Blob\n"</span><span style="color: #007700">;<br />     }<br />     </span><span style="color: #9876AA">$lob</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">free</span><span style="color: #007700">();<br />     </span><span style="color: #9876AA">oci_free_statement</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">);<br />     </span><span style="color: #9876AA">oci_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br />  }<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>   <div class="example" id="example-5639">    <p><strong>示例 #2 <span class="function"><strong style="color:#CC7832">oci_new_descriptor()</strong></span> 示例</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /></span><span style="color: #FF8000">// Calling PL/SQL stored procedures which contain clobs as input<br /> * parameters.<br /> * Example PL/SQL stored procedure signature is:<br /> *<br /> * PROCEDURE save_data<br /> *   Argument Name                  Type                    In/Out Default?<br /> *   ------------------------------ ----------------------- ------ --------<br /> *   KEY                            NUMBER(38)              IN<br /> *   DATA                           CLOB                    IN<br /> *<br /> <br /><br /></span><span style="color: #9876AA">$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #9876AA">$user</span><span style="color: #007700">, </span><span style="color: #9876AA">$password</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$stmt </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">"begin save_data(:key, :data); end;"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$clob </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_new_descriptor</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_D_LOB</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_bind_by_name</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">, </span><span style="color: #DD0000">':key'</span><span style="color: #007700">, </span><span style="color: #9876AA">$key</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_bind_by_name</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">, </span><span style="color: #DD0000">':data'</span><span style="color: #007700">, </span><span style="color: #9876AA">$clob</span><span style="color: #007700">, -</span><span style="color: #9876AA">1</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_B_CLOB</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$clob</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">write</span><span style="color: #007700">(</span><span style="color: #9876AA">$data</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_DEFAULT</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_commit</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$clob</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">free</span><span style="color: #007700">();<br /></span><span style="color: #9876AA">oci_free_statement</span><span style="color: #007700">(</span><span style="color: #9876AA">$stmt</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.oci-new-descriptor-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link oci_bind_by_name()} - 绑定 PHP 变量到 Oracle 位置标志符</span></li>   </ul>  </span> </div></div>
 * @return OCI_Lob|OCILob|false A new LOB or FILE descriptor on success, <b>FALSE</b> on error.*/
function oci_new_descriptor($connection, $type = OCI_DTYPE_LOB) {}

/**
*<div id="function.oci-set-prefetch" class="refentry"> <div class="refnamediv">  <h1 class="refname">oci_set_prefetch</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8, PECL OCI8 &gt;= 1.1.0)</p><p class="refpurpose"><span class="refname">oci_set_prefetch</span> &mdash; <span class="dc-title">设置查询预读取的行数</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-set-prefetch-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>oci_set_prefetch</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$statement</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$rows</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   在调用 <span class="function">{@link oci_execute()}</span> 成功查询以及对数据库的每个后续内部读取请求之后，设置 Oracle   客户端库缓冲的行数。对于返回大量行的查询，可以通过在默认 <a href="https://www.php.net/manual/zh/oci8.configuration.php#ini.oci8.default-prefetch" class="link">oci8.default_prefetch</a> 值上增加预读取数来显着提高性能。  </p>  <p class="para">   预读取是 Oracle 在每个网络请求中从数据库返回多个数据行的有效方式。这可以提高网络和 CPU 利用率。行缓冲是 OCI8   的内部操作，无论预取数如何，OCI8 提取函数的行为都不会改变。例如，<span class="function">{@link oci_fetch_row()}</span>   将始终返回一行。预读取缓冲区是针对单个语句的，不会被重新执行的语句或其他连接使用。  </p>  <p class="para">   在调用 <span class="function">{@link oci_execute()}</span> 之前调用 <span class="function"><strong style="color:#CC7832">oci_set_prefetch()</strong></span>。  </p>  <p class="para">   调整目标是将预读取值设置为网络和数据库要处理的合理大小。对于返回大量行的查询，如果以多个块的形式从数据库中检索行（即设置预读取值小于行数），则整体系统效率可能会更好。这允许数据库在   PHP 脚本处理行的当前集合的同时处理其他用户的语句。  </p>  <p class="para">   Oracle 8<em>i</em> 中引入了查询预读取。REF CURSOR 预读取是在 Oracle 11<em>g</em>R2 中引入的，当 PHP 与 Oracle   11<em>g</em>R2（或更高版本）客户端库链接时发生。嵌套游标预读取是在 Oracle 11<em>g</em>R2 中引入的，它要求   Oracle Client 库和数据库都是 11<em>g</em>R2 或更高版本。  </p>  <p class="para">   当查询包含 LONG 或 LOB 列时，不支持预读取。预读取值将会忽略，并且在不支持预读取的所有情况下都将使用单行获取。  </p>  <p class="para">   使用 Oracle Database 12<em>c</em> 时，PHP 设置的预读取值可以被 Oracle 的客户端 <span>oraaccess.xml</span> 配置文件覆盖。有关详细信息，请参阅 Oracle 文档。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-set-prefetch-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">statement</span></dt>     <dd>       <p class="para">有效的 OCI8 报表标识符由 <span class="function">{@link oci_parse()}</span> 创建，被 <span class="function">{@link oci_execute()}</span>或 <span>REF CURSOR</span> statement 标识执行。</p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">rows</span></dt>     <dd>      <p class="para">       要预读取的行数，&gt;= 0      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-set-prefetch-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.oci-set-prefetch-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="">    <p><strong>示例 #1 更改查询的默认预读取值</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">, </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">'SELECT * FROM myverybigtable'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_set_prefetch</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">, </span><span style="color: #9876AA">300</span><span style="color: #007700">);  </span><span style="color: #FF8000">// 调用 oci_execute() 之前设置<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /><br />echo </span><span style="color: #DD0000">"&lt;table border='1'&gt;\n"</span><span style="color: #007700">;<br />while (</span><span style="color: #9876AA">$row </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_fetch_array</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_ASSOC</span><span style="color: #007700">+</span><span style="color: #9876AA">OCI_RETURN_NULLS</span><span style="color: #007700">)) {<br />    echo </span><span style="color: #DD0000">"&lt;tr&gt;\n"</span><span style="color: #007700">;<br />    foreach (</span><span style="color: #9876AA">$row </span><span style="color: #007700">as </span><span style="color: #9876AA">$item</span><span style="color: #007700">) {<br />        echo </span><span style="color: #DD0000">"    &lt;td&gt;"</span><span style="color: #007700">.(</span><span style="color: #9876AA">$item </span><span style="color: #007700">!== </span><span style="color: #9876AA">null </span><span style="color: #007700">? </span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$item</span><span style="color: #007700">, </span><span style="color: #9876AA">ENT_QUOTES</span><span style="color: #007700">) : </span><span style="color: #DD0000">"&amp;nbsp;"</span><span style="color: #007700">).</span><span style="color: #DD0000">"&lt;/td&gt;\n"</span><span style="color: #007700">;<br />    }<br />    echo </span><span style="color: #DD0000">"&lt;/tr&gt;\n"</span><span style="color: #007700">;<br />}<br />echo </span><span style="color: #DD0000">"&lt;/table&gt;\n"</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">oci_free_statement</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span>  <p class="para">   <div class="example" id="">    <p><strong>示例 #2 更改 REF CURSOR 获取的默认预获取值</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /></span><span style="color: #FF8000">//<br />  Create the PL/SQL stored procedure as:<br /><br />  CREATE OR REPLACE PROCEDURE myproc(p1 OUT SYS_REFCURSOR) AS<br />  BEGIN<br />    OPEN p1 FOR SELECT * FROM all_objects WHERE ROWNUM &lt; 5000;<br />  END;<br /><br /><br /></span><span style="color: #9876AA">$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">, </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">'BEGIN myproc(:rc); END;'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$refcur </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_new_cursor</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_bind_by_name</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">, </span><span style="color: #DD0000">':rc'</span><span style="color: #007700">, </span><span style="color: #9876AA">$refcur</span><span style="color: #007700">, -</span><span style="color: #9876AA">1</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_B_CURSOR</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// Change the prefetch before executing the cursor.<br />// REF CURSOR prefetching works when PHP is linked with Oracle 11gR2 or later Client libraries<br /></span><span style="color: #9876AA">oci_set_prefetch</span><span style="color: #007700">(</span><span style="color: #9876AA">$refcur</span><span style="color: #007700">, </span><span style="color: #9876AA">200</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$refcur</span><span style="color: #007700">);<br /><br />echo </span><span style="color: #DD0000">"&lt;table border='1'&gt;\n"</span><span style="color: #007700">;<br />while (</span><span style="color: #9876AA">$row </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_fetch_array</span><span style="color: #007700">(</span><span style="color: #9876AA">$refcur</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_ASSOC</span><span style="color: #007700">+</span><span style="color: #9876AA">OCI_RETURN_NULLS</span><span style="color: #007700">)) {<br />    echo </span><span style="color: #DD0000">"&lt;tr&gt;\n"</span><span style="color: #007700">;<br />    foreach (</span><span style="color: #9876AA">$row </span><span style="color: #007700">as </span><span style="color: #9876AA">$item</span><span style="color: #007700">) {<br />        echo </span><span style="color: #DD0000">"    &lt;td&gt;"</span><span style="color: #007700">.(</span><span style="color: #9876AA">$item </span><span style="color: #007700">!== </span><span style="color: #9876AA">null </span><span style="color: #007700">? </span><span style="color: #9876AA">htmlentities</span><span style="color: #007700">(</span><span style="color: #9876AA">$item</span><span style="color: #007700">, </span><span style="color: #9876AA">ENT_QUOTES</span><span style="color: #007700">) : </span><span style="color: #DD0000">"&amp;nbsp;"</span><span style="color: #007700">).</span><span style="color: #DD0000">"&lt;/td&gt;\n"</span><span style="color: #007700">;<br />    }<br />    echo </span><span style="color: #DD0000">"&lt;/tr&gt;\n"</span><span style="color: #007700">;<br />}<br />echo </span><span style="color: #DD0000">"&lt;/table&gt;\n"</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">oci_free_statement</span><span style="color: #007700">(</span><span style="color: #9876AA">$refcur</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_free_statement</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </p>  <p class="para">   如果 PHP OCI8 从 REF CURSOR 读取，然后将 REF CURSOR 传递回第二个 PL/SQL 过程以进行进一步处理，则将 REF CURSOR   预读取数设置为 0 以避免行从结果集中“丢失”。预读取值是在每个 OCI8 内部请求数据库中提取的额外行数，因此将其设置为 0 意味着一次只提取一行。   <div class="example" id="">    <p><strong>示例 #3 将 REF CURSOR 传回 Oracle 时设置读取值</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$conn </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">, </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/orcl'</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// get the REF CURSOR<br /></span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">'BEGIN myproc(:rc_out); END;'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$refcur </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_new_cursor</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_bind_by_name</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">, </span><span style="color: #DD0000">':rc_out'</span><span style="color: #007700">, </span><span style="color: #9876AA">$refcur</span><span style="color: #007700">, -</span><span style="color: #9876AA">1</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_B_CURSOR</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// Display two rows, but don't prefetch any extra rows otherwise<br />// those extra rows would not be passed back to myproc_use_rc().<br />// A prefetch value of 0 is allowed in PHP 5.3.2 and PECL OCI8 1.4<br /></span><span style="color: #9876AA">oci_set_prefetch</span><span style="color: #007700">(</span><span style="color: #9876AA">$refcur</span><span style="color: #007700">, </span><span style="color: #9876AA">0</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$refcur</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$row </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_fetch_array</span><span style="color: #007700">(</span><span style="color: #9876AA">$refcur</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">$row</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$row </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_fetch_array</span><span style="color: #007700">(</span><span style="color: #9876AA">$refcur</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">$row</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// pass the REF CURSOR to myproc_use_rc() to do more data processing<br />// with the result set<br /></span><span style="color: #9876AA">$stid </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$conn</span><span style="color: #007700">, </span><span style="color: #DD0000">'begin myproc_use_rc(:rc_in); end;'</span><span style="color: #007700">); <br /></span><span style="color: #9876AA">oci_bind_by_name</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">, </span><span style="color: #DD0000">':rc_in'</span><span style="color: #007700">, </span><span style="color: #9876AA">$refcur</span><span style="color: #007700">, -</span><span style="color: #9876AA">1</span><span style="color: #007700">, </span><span style="color: #9876AA">OCI_B_CURSOR</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$stid</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.oci-set-prefetch-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member">     <a href="https://www.php.net/manual/zh/oci8.configuration.php#ini.oci8.default-prefetch" class="link">oci8.default_prefetch</a>     ini 选项    </li>   </ul>  </span> </div></div>
 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
function oci_set_prefetch($statement, $rows) {}

/**
*<div id="function.oci-set-client-identifier" class="refentry"> <div class="refnamediv">  <h1 class="refname">oci_set_client_identifier</h1>  <p class="verinfo">(PHP 5 &gt;= 5.3.2, PHP 7, PHP 8, PECL OCI8 &gt;= 1.4.0)</p><p class="refpurpose"><span class="refname">oci_set_client_identifier</span> &mdash; <span class="dc-title">Sets the client identifier</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-set-client-identifier-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>oci_set_client_identifier</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$connection</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$client_id</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   Sets the client identifier used by various database components to   identify lightweight application users who authenticate as the same   database user.  </p>  <p class="para">   The client identifier is registered with the database when the next   &#039;round-trip&#039; from PHP to the database occurs, typically when an SQL   statement is executed.  </p>  <p class="para">   The identifier can subsequently be queried, for example   with <span>SELECT SYS_CONTEXT(&#039;USERENV&#039;,&#039;CLIENT_IDENTIFIER&#039;)   FROM DUAL</span>.  Database administration views such   as <span>V$SESSION</span> will also contain the value.  It   can be used with <span>DBMS_MONITOR.CLIENT_ID_TRACE_ENABLE</span>   for tracing and can also be used for auditing.  </p>  <p class="para">   The value may be retained across page requests that use the same persistent connection.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-set-client-identifier-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">connection</span></dt>     <dd>      <p class="para">Oracle 连接标识，由<span class="function">{@link oci_connect()}</span>，<span class="function">{@link oci_pconnect()}</span>，或<span class="function">{@link oci_new_connect()}</span> 返回。</p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">client_id</span></dt>     <dd>      <p class="para">       User chosen string up to 64 bytes long.      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-set-client-identifier-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.oci-set-client-identifier-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="example-5681">    <p><strong>示例 #1 Setting the client identifier to the application user</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br /></span><span style="color: #FF8000">// Find the application user's login name<br /></span><span style="color: #9876AA">session_start</span><span style="color: #007700">();<br /></span><span style="color: #9876AA">$un </span><span style="color: #007700">= </span><span style="color: #9876AA">my_validate_session</span><span style="color: #007700">(</span><span style="color: #9876AA">$_SESSION</span><span style="color: #007700">[</span><span style="color: #DD0000">'username'</span><span style="color: #007700">]);<br /></span><span style="color: #9876AA">$c </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'myschema'</span><span style="color: #007700">, </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// Tell Oracle who that user is<br /></span><span style="color: #9876AA">oci_set_client_identifier</span><span style="color: #007700">(</span><span style="color: #9876AA">$c</span><span style="color: #007700">, </span><span style="color: #9876AA">$un</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// The next round-trip to the database will piggyback the identifier<br /></span><span style="color: #9876AA">$s </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$c</span><span style="color: #007700">, </span><span style="color: #DD0000">'select mydata from mytable'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$s</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// ...<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.oci-set-client-identifier-notes">  <h3 class="title">注释</h3>  <div class="caution"><strong class="caution">警告</strong><h1 class="title">Roundtrip Gotcha</h1><p class="para">一些 OCI8 函数会导致 Roundtrips. 对数据库来说当启用结果缓存时，查询可能不产生 Roundtrips。</p></div> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.oci-set-client-identifier-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link oci_set_module_name()} - Sets the module name</span></li>    <li class="member"><span class="function">{@link oci_set_action()} - Sets the action name</span></li>    <li class="member"><span class="function">{@link oci_set_client_info()} - Sets the client information</span></li>    <li class="member"><span class="function">{@link oci_set_db_operation()} - Sets the database operation</span></li>   </ul>  </span> </div></div>
 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
function oci_set_client_identifier($connection, $client_identifier) {}

/**
*<div id="function.oci-set-edition" class="refentry"> <div class="refnamediv">  <h1 class="refname">oci_set_edition</h1>  <p class="verinfo">(PHP 5 &gt;= 5.3.2, PHP 7, PHP 8, PECL OCI8 &gt;= 1.4.0)</p><p class="refpurpose"><span class="refname">oci_set_edition</span> &mdash; <span class="dc-title">Sets the database edition</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-set-edition-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>oci_set_edition</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$edition</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   Sets the database &quot;edition&quot; of objects to be used by a subsequent   connections.  </p>  <p class="para">   Oracle Editions allow concurrent versions of applications to run   using the same schema and object names.  This is useful for   upgrading live systems.  </p>  <p class="para">   Call <span class="function"><strong style="color:#CC7832">oci_set_edition()</strong></span> before calling   <span class="function">{@link oci_connect()}</span>, <span class="function">{@link oci_pconnect()}</span>   or <span class="function">{@link oci_new_connect()}</span>.  </p>  <p class="para">   If an edition is set that is not valid in the database, connection   will fail even if <span class="function"><strong style="color:#CC7832">oci_set_edition()</strong></span> returns success.  </p>  <p class="para">   When using persistent connections, if a connection with the   requested edition setting already exists, it is reused.  Otherwise,   a different persistent connection is created  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-set-edition-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">edition</span></dt>     <dd>      <p class="para">       Oracle Database edition name previously created with the <abbr>SQL</abbr>       &quot;<span>CREATE EDITION</span>&quot; command.      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-set-edition-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.oci-set-edition-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="example-5690">    <p><strong>示例 #1 Two scripts can use different versions of myfunc() at the same time</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br /></span><span style="color: #FF8000">// File 1<br /><br /></span><span style="color: #007700">echo </span><span style="color: #DD0000">"Version 1 of application\n"</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">oci_set_edition</span><span style="color: #007700">(</span><span style="color: #DD0000">'ORA$BASE'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$c </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">, </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$s </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$c</span><span style="color: #007700">, </span><span style="color: #DD0000">"begin :r := myfunc(); end;"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_bind_by_name</span><span style="color: #007700">(</span><span style="color: #9876AA">$s</span><span style="color: #007700">, </span><span style="color: #DD0000">":r"</span><span style="color: #007700">, </span><span style="color: #9876AA">$r</span><span style="color: #007700">, </span><span style="color: #9876AA">20</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$s</span><span style="color: #007700">);<br />echo </span><span style="color: #DD0000">"The result is </span><span style="color: #9876AA">$r</span><span style="color: #DD0000">\n"</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div></div><div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br /></span><span style="color: #FF8000">// File 2<br /><br /></span><span style="color: #007700">echo </span><span style="color: #DD0000">"Version 2 of application\n"</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">oci_set_edition</span><span style="color: #007700">(</span><span style="color: #DD0000">'E1'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$c </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">, </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$s </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$c</span><span style="color: #007700">, </span><span style="color: #DD0000">"begin :r := myfunc(); end;"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_bind_by_name</span><span style="color: #007700">(</span><span style="color: #9876AA">$s</span><span style="color: #007700">, </span><span style="color: #DD0000">":r"</span><span style="color: #007700">, </span><span style="color: #9876AA">$r</span><span style="color: #007700">, </span><span style="color: #9876AA">20</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$s</span><span style="color: #007700">);<br />echo </span><span style="color: #DD0000">"The result is </span><span style="color: #9876AA">$r</span><span style="color: #DD0000">\n"</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.oci-set-edition-notes">  <h3 class="title">注释</h3>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <strong>Oracle version requirement</strong><br />   <p class="para">    This function is available from Oracle 11<em>g</em>R2 onwards.   </p>  </p></blockquote>  <div class="caution"><strong class="caution">警告</strong>   <h1 class="title">Persistent connections</h1>   <p class="para">    To avoid inconsistencies and unexpected errors, do not use ALTER    SESSION SET EDITION to change the edition on persistent connections.   </p>  </div>  <div class="caution"><strong class="caution">警告</strong>   <h1 class="title">DRCP Connection Pooling</h1>   <p class="para">    To avoid inconsistencies and unexpected errors when using editions    and <a href="https://www.php.net/manual/zh/oci8.connection.php" class="link">DRCP</a> with Oracle    11.2.0.1, keep a one-to-one correspondence between    the <a href="https://www.php.net/manual/zh/oci8.configuration.php#ini.oci8.connection-class" class="link">oci8.connection_class</a>    and the edition name used by applications.  Each pooled server of    a given connection class should only be used with one edition.    This restriction has been removed with Oracle 11.2.0.2.   </p>  </div> </div></div>
 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
function oci_set_edition($edition) {}

/**
*<div id="function.oci-set-module-name" class="refentry"> <div class="refnamediv">  <h1 class="refname">oci_set_module_name</h1>  <p class="verinfo">(PHP 5 &gt;= 5.3.2, PHP 7, PHP 8, PECL OCI8 &gt;= 1.4.0)</p><p class="refpurpose"><span class="refname">oci_set_module_name</span> &mdash; <span class="dc-title">Sets the module name</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-set-module-name-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>oci_set_module_name</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$connection</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$name</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   Sets the module name for Oracle tracing.  </p>  <p class="para">   The module name is registered with the database when the next   &#039;round-trip&#039; from PHP to the database occurs, typically when an SQL   statement is executed.  </p>  <p class="para">   The name can subsequently be queried from database administration   views such as <span>V$SESSION</span>.  It can be used for   tracing and monitoring such as with <span>V$SQLAREA</span>   and <span>DBMS_MONITOR.SERV_MOD_ACT_STAT_ENABLE</span>.  </p>  <p class="para">   The value may be retained across persistent connections.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-set-module-name-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">connection</span></dt>     <dd>      <p class="para">Oracle 连接标识，由<span class="function">{@link oci_connect()}</span>，<span class="function">{@link oci_pconnect()}</span>，或<span class="function">{@link oci_new_connect()}</span> 返回。</p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">name</span></dt>     <dd>      <p class="para">       User chosen <span class="type" style="color:#EAB766">string</span> up to 48 bytes long.      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-set-module-name-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.oci-set-module-name-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="example-5693">    <p><strong>示例 #1 Setting the module name</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$c </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">, </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// Record the module<br /></span><span style="color: #9876AA">oci_set_module_name</span><span style="color: #007700">(</span><span style="color: #9876AA">$c</span><span style="color: #007700">, </span><span style="color: #DD0000">'Home Page'</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// Code that causes a round-trip, for example a query:<br /></span><span style="color: #9876AA">$s </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$c</span><span style="color: #007700">, </span><span style="color: #DD0000">'select * from dual'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$s</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_fetch_all</span><span style="color: #007700">(</span><span style="color: #9876AA">$s</span><span style="color: #007700">, </span><span style="color: #9876AA">$res</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">sleep</span><span style="color: #007700">(</span><span style="color: #9876AA">30</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>// While the script is running, the administrator can see the<br>// modules in use:<br><br>sqlplus system/welcome<br>SQL&gt; select module from v$session;<br></span></div>    </div>   </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.oci-set-module-name-notes">  <h3 class="title">注释</h3>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>: <strong>Oracle 版本需求</strong><br /><p class="para">当 PHP 是与 Oracle 数据库 10<em>g</em> 及更新版本的 扩展库链接时，此函数可用。</p></p></blockquote>  <div class="tip"><strong class="tip">小技巧</strong><h1 class="title">性能</h1> <p class="para">使用旧版的 OCI8 或 ORACLE 数据库，可使用 Oracle <span>DBMS_APPLICATION_INFO</span> 包来设置客户端信息。这比使用 <span class="function">{@link oci_set_client_info()}</span> 较低效。</p></div>  <div class="caution"><strong class="caution">警告</strong><h1 class="title">Roundtrip Gotcha</h1><p class="para">一些 OCI8 函数会导致 Roundtrips. 对数据库来说当启用结果缓存时，查询可能不产生 Roundtrips。</p></div> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.oci-set-module-name-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link oci_set_action()} - Sets the action name</span></li>    <li class="member"><span class="function">{@link oci_set_client_info()} - Sets the client information</span></li>    <li class="member"><span class="function">{@link oci_set_client_identifier()} - Sets the client identifier</span></li>    <li class="member"><span class="function">{@link oci_set_db_operation()} - Sets the database operation</span></li>   </ul>  </span> </div></div>
 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
function oci_set_module_name($connection, $module_name) {}

/**
*<div id="function.oci-set-action" class="refentry"> <div class="refnamediv">  <h1 class="refname">oci_set_action</h1>  <p class="verinfo">(PHP 5 &gt;= 5.3.2, PHP 7, PHP 8, PECL OCI8 &gt;= 1.4.0)</p><p class="refpurpose"><span class="refname">oci_set_action</span> &mdash; <span class="dc-title">Sets the action name</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-set-action-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>oci_set_action</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$connection</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$action</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   Sets the action name for Oracle tracing.  </p>  <p class="para">   The action name is registered with the database when the next   &#039;round-trip&#039; from PHP to the database occurs, typically when an SQL   statement is executed.  </p>  <p class="para">   The action name can subsequently be queried from database administration   views such as <span>V$SESSION</span>.  It can be used for   tracing and monitoring such as with <span>V$SQLAREA</span>   and <span>DBMS_MONITOR.SERV_MOD_ACT_STAT_ENABLE</span>.  </p>  <p class="para">   The value may be retained across persistent connections.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-set-action-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">connection</span></dt>     <dd>      <p class="para">Oracle 连接标识，由<span class="function">{@link oci_connect()}</span>，<span class="function">{@link oci_pconnect()}</span>，或<span class="function">{@link oci_new_connect()}</span> 返回。</p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">action</span></dt>     <dd>      <p class="para">       User chosen string up to 32 bytes long.      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-set-action-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.oci-set-action-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="example-5675">    <p><strong>示例 #1 Setting the action</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$c </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">, </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// Record the action<br /></span><span style="color: #9876AA">oci_set_action</span><span style="color: #007700">(</span><span style="color: #9876AA">$c</span><span style="color: #007700">, </span><span style="color: #DD0000">'Friend Lookup'</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// Code that causes a round-trip, for example a query:<br /></span><span style="color: #9876AA">$s </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$c</span><span style="color: #007700">, </span><span style="color: #DD0000">'select * from dual'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$s</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_fetch_all</span><span style="color: #007700">(</span><span style="color: #9876AA">$s</span><span style="color: #007700">, </span><span style="color: #9876AA">$res</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">sleep</span><span style="color: #007700">(</span><span style="color: #9876AA">30</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>// While the script is running, the administrator can see the actions<br>// being performed:<br><br>sqlplus system/welcome<br>SQL&gt; select action from v$session;<br></span></div>    </div>   </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.oci-set-action-notes">  <h3 class="title">注释</h3>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>: <strong>Oracle 版本需求</strong><br /><p class="para">当 PHP 是与 Oracle 数据库 10<em>g</em> 及更新版本的 扩展库链接时，此函数可用。</p></p></blockquote>  <div class="tip"><strong class="tip">小技巧</strong><h1 class="title">性能</h1> <p class="para">使用旧版的 OCI8 或 ORACLE 数据库，可使用 Oracle <span>DBMS_APPLICATION_INFO</span> 包来设置客户端信息。这比使用 <span class="function">{@link oci_set_client_info()}</span> 较低效。</p></div>  <div class="caution"><strong class="caution">警告</strong><h1 class="title">Roundtrip Gotcha</h1><p class="para">一些 OCI8 函数会导致 Roundtrips. 对数据库来说当启用结果缓存时，查询可能不产生 Roundtrips。</p></div> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.oci-set-action-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link oci_set_module_name()} - Sets the module name</span></li>    <li class="member"><span class="function">{@link oci_set_client_info()} - Sets the client information</span></li>    <li class="member"><span class="function">{@link oci_set_client_identifier()} - Sets the client identifier</span></li>    <li class="member"><span class="function">{@link oci_set_db_operation()} - Sets the database operation</span></li>   </ul>  </span> </div></div>
 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
function oci_set_action($connection, $action_name) {}

/**
*<div id="function.oci-set-client-info" class="refentry"> <div class="refnamediv">  <h1 class="refname">oci_set_client_info</h1>  <p class="verinfo">(PHP 5 &gt;= 5.3.2, PHP 7, PHP 8, PECL OCI8 &gt;= 1.4.0)</p><p class="refpurpose"><span class="refname">oci_set_client_info</span> &mdash; <span class="dc-title">Sets the client information</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-set-client-info-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>oci_set_client_info</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$connection</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$client_info</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   Sets the client information for Oracle tracing.  </p>  <p class="para">   The client information is registered with the database when the next   &#039;round-trip&#039; from PHP to the database occurs, typically when an SQL   statement is executed.  </p>  <p class="para">   The client information can subsequently be queried from database    administration views such as <span>V$SESSION</span>.  </p>  <p class="para">   The value may be retained across persistent connections.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-set-client-info-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">connection</span></dt>     <dd>      <p class="para">Oracle 连接标识，由<span class="function">{@link oci_connect()}</span>，<span class="function">{@link oci_pconnect()}</span>，或<span class="function">{@link oci_new_connect()}</span> 返回。</p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">client_info</span></dt>     <dd>      <p class="para">       User chosen string up to 64 bytes long.      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-set-client-info-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.oci-set-client-info-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="example-5684">    <p><strong>示例 #1 Setting the client information</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$c </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'hr'</span><span style="color: #007700">, </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">, </span><span style="color: #DD0000">'localhost/XE'</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// Record the client information<br /></span><span style="color: #9876AA">oci_set_client_info</span><span style="color: #007700">(</span><span style="color: #9876AA">$c</span><span style="color: #007700">, </span><span style="color: #DD0000">'My Application Version 2'</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// Code that causes a round-trip, for example a query:<br /></span><span style="color: #9876AA">$s </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_parse</span><span style="color: #007700">(</span><span style="color: #9876AA">$c</span><span style="color: #007700">, </span><span style="color: #DD0000">'select * from dual'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_execute</span><span style="color: #007700">(</span><span style="color: #9876AA">$s</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_fetch_all</span><span style="color: #007700">(</span><span style="color: #9876AA">$s</span><span style="color: #007700">, </span><span style="color: #9876AA">$res</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">sleep</span><span style="color: #007700">(</span><span style="color: #9876AA">30</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>// While the script is running, the administrator can see the client<br>// information:<br><br>sqlplus system/welcome<br>SQL&gt; select client_info from v$session;<br></span></div>    </div>   </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.oci-set-client-info-notes">  <h3 class="title">注释</h3>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>: <strong>Oracle 版本需求</strong><br /><p class="para">当 PHP 是与 Oracle 数据库 10<em>g</em> 及更新版本的 扩展库链接时，此函数可用。</p></p></blockquote>  <div class="tip"><strong class="tip">小技巧</strong><h1 class="title">性能</h1> <p class="para">使用旧版的 OCI8 或 ORACLE 数据库，可使用 Oracle <span>DBMS_APPLICATION_INFO</span> 包来设置客户端信息。这比使用 <span class="function"><strong style="color:#CC7832">oci_set_client_info()</strong></span> 较低效。</p></div>  <div class="caution"><strong class="caution">警告</strong><h1 class="title">Roundtrip Gotcha</h1><p class="para">一些 OCI8 函数会导致 Roundtrips. 对数据库来说当启用结果缓存时，查询可能不产生 Roundtrips。</p></div> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.oci-set-client-info-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="function">{@link oci_set_module_name()} - Sets the module name</span></li>    <li class="member"><span class="function">{@link oci_set_action()} - Sets the action name</span></li>    <li class="member"><span class="function">{@link oci_set_client_identifier()} - Sets the client identifier</span></li>    <li class="member"><span class="function">{@link oci_set_db_operation()} - Sets the database operation</span></li>   </ul>  </span> </div></div>
 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
function oci_set_client_info($connection, $client_info) {}

/**
*<div id="function.oci-password-change" class="refentry"> <div class="refnamediv">  <h1 class="refname">oci_password_change</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8, PECL OCI8 &gt;= 1.1.0)</p><p class="refpurpose"><span class="refname">oci_password_change</span> &mdash; <span class="dc-title">修改 Oracle 用户的密码</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-password-change-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>oci_password_change</strong></span>(<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$connection</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$username</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$old_password</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$new_password</span></span><br>): <span class="type" style="color:#EAB766">bool</span></div>  <div class="methodsynopsis dc-description"><span class="methodname" style="color:#CC7832"><strong>oci_password_change</strong></span>(<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$database_name</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$username</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$old_password</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$new_password</span></span><br>): <span class="type" style="color:#EAB766">resource</span></div>  <p class="para rdfs-comment">   修改用户 <span class="parameter" style="color:#3A95FF">username</span> 的密码。  </p>  <p class="para">   <span class="function"><strong style="color:#CC7832">oci_password_change()</strong></span> 函数对于 PHP 命令行脚本或在整个 PHP 应用程序中使用非持久连接时最有用。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-password-change-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">connection</span></dt>     <dd>      <p class="para">       Oracle 连接标识符，由 <span class="function">{@link oci_connect()}</span> 或 <span class="function">{@link oci_pconnect()}</span> 返回。      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">username</span></dt>     <dd>      <p class="para">       Oracle 用户名。      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">old_password</span></dt>     <dd>      <p class="para">       旧密码。      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">new_password</span></dt>     <dd>      <p class="para">       要设置的新密码。      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">database_name</span></dt>     <dd>      <p class="para">       数据库名。      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.oci-password-change-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   提供 <span class="parameter" style="color:#3A95FF">database_name</span> 时，<span class="function"><strong style="color:#CC7832">oci_password_change()</strong></span> 成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。提供   <span class="parameter" style="color:#3A95FF">connection</span> 时，<span class="function"><strong style="color:#CC7832">oci_password_change()</strong></span> 成功时返回连接资源， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.oci-password-change-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="example-5654">    <p><strong>示例 #1 <span class="function"><strong style="color:#CC7832">oci_password_change()</strong></span> 示例，更改已连接用户的密码</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$dbase      </span><span style="color: #007700">= </span><span style="color: #DD0000">'localhost/orcl'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$user       </span><span style="color: #007700">= </span><span style="color: #DD0000">'cj'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$current_pw </span><span style="color: #007700">= </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$new_pw     </span><span style="color: #007700">= </span><span style="color: #DD0000">'geelong'</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">$c </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_pconnect</span><span style="color: #007700">(</span><span style="color: #9876AA">$user</span><span style="color: #007700">, </span><span style="color: #9876AA">$current_pw</span><span style="color: #007700">, </span><span style="color: #9876AA">$dbase</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">oci_password_change</span><span style="color: #007700">(</span><span style="color: #9876AA">$c</span><span style="color: #007700">, </span><span style="color: #9876AA">$user</span><span style="color: #007700">, </span><span style="color: #9876AA">$current_pw</span><span style="color: #007700">, </span><span style="color: #9876AA">$new_pw</span><span style="color: #007700">);<br />echo </span><span style="color: #DD0000">"New password is : " </span><span style="color: #007700">. </span><span style="color: #9876AA">$new_pw </span><span style="color: #007700">. </span><span style="color: #DD0000">"\n"</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span>  <p class="para">   <div class="example" id="xmlwriter-open-uri.example.basic">    <p><strong>示例 #2 <span class="function"><strong style="color:#CC7832">oci_password_change()</strong></span> 示例之一步更改密码并连接</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$dbase      </span><span style="color: #007700">= </span><span style="color: #DD0000">'localhost/orcl'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$user       </span><span style="color: #007700">= </span><span style="color: #DD0000">'cj'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$current_pw </span><span style="color: #007700">= </span><span style="color: #DD0000">'welcome'</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$new_pw     </span><span style="color: #007700">= </span><span style="color: #DD0000">'geelong'</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">$c </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_pconnect</span><span style="color: #007700">(</span><span style="color: #9876AA">$user</span><span style="color: #007700">, </span><span style="color: #9876AA">$current_pw</span><span style="color: #007700">, </span><span style="color: #9876AA">$dbase</span><span style="color: #007700">);<br />if (!</span><span style="color: #9876AA">$c</span><span style="color: #007700">) {<br />    </span><span style="color: #9876AA">$m </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_error</span><span style="color: #007700">();<br />    if (</span><span style="color: #9876AA">$m</span><span style="color: #007700">[</span><span style="color: #DD0000">'code'</span><span style="color: #007700">] == </span><span style="color: #9876AA">28001</span><span style="color: #007700">) { </span><span style="color: #FF8000">// "ORA-28001: the password has expired"<br />        // Login and reset password at the same time<br />        </span><span style="color: #9876AA">$c </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_password_change</span><span style="color: #007700">(</span><span style="color: #9876AA">$dbase</span><span style="color: #007700">, </span><span style="color: #9876AA">$user</span><span style="color: #007700">, </span><span style="color: #9876AA">$current_pw</span><span style="color: #007700">, </span><span style="color: #9876AA">$new_pw</span><span style="color: #007700">);<br />        if (</span><span style="color: #9876AA">$c</span><span style="color: #007700">) {<br />            echo </span><span style="color: #DD0000">"New password is : " </span><span style="color: #007700">. </span><span style="color: #9876AA">$new_pw </span><span style="color: #007700">. </span><span style="color: #DD0000">"\n"</span><span style="color: #007700">;<br />        }<br />    }<br />}<br /><br />if (!</span><span style="color: #9876AA">$c</span><span style="color: #007700">) {  </span><span style="color: #FF8000">// The original error wasn't 28001, or the password change failed<br />    </span><span style="color: #9876AA">$m </span><span style="color: #007700">= </span><span style="color: #9876AA">oci_error</span><span style="color: #007700">();<br />    </span><span style="color: #9876AA">trigger_error</span><span style="color: #007700">(</span><span style="color: #DD0000">'Could not connect to database: '</span><span style="color: #007700">. </span><span style="color: #9876AA">$m</span><span style="color: #007700">[</span><span style="color: #DD0000">'message'</span><span style="color: #007700">], </span><span style="color: #9876AA">E_USER_ERROR</span><span style="color: #007700">);<br />}<br /><br /></span><span style="color: #FF8000">// Use the connection $c<br />// ...<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.oci-password-change-notes">  <h3 class="title">注释</h3>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    使用此函数或直接在 Oracle 中更改密码应该小心。这是因为 PHP 应用程序可能会通过使用旧密码进行身份验证来继续成功地重用持久连接。最佳做法是每当更改用户密码时重新启动所有 Web 服务器。   </p>  </p></blockquote>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    如果将 Oracle 客户端库或数据库从 11.2.0.3 之前的版本升级到 11.2.0.3 或更高版本，<span class="function"><strong style="color:#CC7832">oci_password_change()</strong></span>    可能会给出错误“ORA-1017: invalid username/password”，除非同时升级客户端和服务器版本。   </p>  </p></blockquote>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    第二个 <span class="function"><strong style="color:#CC7832">oci_password_change()</strong></span> 语法自 OCI8 版本 1.1 起可用。   </p>  </p></blockquote> </div></div>
 * @return bool <b>TRUE</b> on success or <b>FALSE</b> on failure.*/
function oci_password_change($connection, $username, $old_password, $new_password) {}

/**
*<div id="function.oci-new-collection" class="refentry"> <div class="refnamediv">  <h1 class="refname">oci_new_collection</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8, PECL OCI8 &gt;= 1.1.0)</p><p class="refpurpose"><span class="refname">oci_new_collection</span> &mdash; <span class="dc-title">分配新的 collection 对象</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.oci-new-collection-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>oci_new_collection</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$connection</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$type_name</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">string</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">$schema</span><span class="initializer"> = <strong><span>null</span></strong></span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.ocicollection.php" class="type OCICollection" style="color:#EAB766">OCICollection</a></span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>  <p class="para rdfs-comment">   分配新的 collection 对象。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.oci-new-collection-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="par