"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />  error_reporting</span><span style="color: #007700">(</span><span style="color: #9876AA">E_ALL</span><span style="color: #007700">);<br /><br />  </span><span style="color: #9876AA">pg_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">"host=localhost port=5432 dbname=mary"</span><span style="color: #007700">);<br />  echo </span><span style="color: #9876AA">pg_dbname</span><span style="color: #007700">(); </span><span style="color: #FF8000">// mary<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span> </div></div>
