     * {@see RarEntry::getAttr()} on entries whose host OS is UNIX and with the constant
     * {@see RarEntry::ATTRIBUTE_UNIX_FINAL_QUARTET}.
     */
    public const ATTRIBUTE_UNIX_BLOCK_DEV = 24576;

    /**
     * Unix regular files (not directories) will have attributes whose last four bits have this value. To be used with
     * {@see RarEntry::getAttr()} on entries whose host OS is UNIX and with the constant
     * {@see RarEntry::ATTRIBUTE_UNIX_FINAL_QUARTET}. See also {@see RarEntry::isDirectory()}, which also works with
     * entries that were added in other operating systems.
     */
    public const ATTRIBUTE_UNIX_REGULAR_FILE = 32768;

    /**
     * Unix symbolic links will have attributes whose last four bits have this value. To be used with
     * {@see RarEntry::getAttr()} on entries whose host OS is UNIX and with the constant
     * {@see RarEntry::ATTRIBUTE_UNIX_FINAL_QUARTET}.
     */
    public const ATTRIBUTE_UNIX_SYM_LINK = 40960;

    /**
     * Unix sockets will have attributes whose last four bits have this value. To be used with
     * {@see RarEntry::getAttr()} on entries whose host OS is UNIX and with the constant
     * {@see RarEntry::ATTRIBUTE_UNIX_FINAL_QUARTET}.
     */
    public const ATTRIBUTE_UNIX_SOCKET = 49152;

/**
*<div id="rarentry.extract" class="refentry"> <div class="refnamediv">  <h1 class="refname">RarEntry::extract</h1>  <p class="verinfo">(PECL rar &gt;= 0.1)</p><p class="refpurpose"><span class="refname">RarEntry::extract</span> &mdash; <span class="dc-title">Extract entry from the archive</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-rarentry.extract-description">  <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description">    <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>RarEntry::extract</strong></span>(<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$dir</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$filepath</span><span class="initializer"> = &quot;&quot;</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$password</span><span class="initializer"> = NULL</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">bool</span> <span class="parameter" style="color:#3A95FF">$extended_data</span><span class="initializer"> = <strong><span>false</span></strong></span></span><br>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   <span class="methodname" style="color:#CC7832"><strong>RarEntry::extract()</strong></span> extracts the entry&#039;s data.   It will create new file in the specified   <span class="parameter" style="color:#3A95FF">dir</span> with the name identical to the entry&#039;s name,   unless the second argument is specified. See below for more information.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-rarentry.extract-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">dir</span></dt>     <dd>      <p class="para">       Path to the directory where files should be extracted. This parameter is       considered if and only if <span class="parameter" style="color:#3A95FF">filepath</span> is not. If both       parameters are empty an extraction to the current directory will be       attempted.      </p>     </dd>                 <dt><span class="parameter" style="color:#3A95FF">filepath</span></dt>     <dd>      <p class="para">       Path (relative or absolute) containing the directory and filename of the       extracted file. This parameter overrides both the parameter       <span class="parameter" style="color:#3A95FF">dir</span> and the original file name.      </p>     </dd>                 <dt><span class="parameter" style="color:#3A95FF">password</span></dt>     <dd>      <p class="para">       The password used to encrypt this entry. If the entry is not encrypted, this value will not be used and can       be omitted. If this parameter is omitted and the entry is encrypted, the password given to       <span class="function"><a href="https://www.php.net/manual/zh/rararchive.open.php" class="function">rar_open()</a></span>, if any, will be used. If a wrong password is given, either explicitly       or implicitly via <span class="function"><a href="https://www.php.net/manual/zh/rararchive.open.php" class="function">rar_open()</a></span>, CRC checking will fail and this method will fail and return <strong><span>false</span></strong>.       If no password is given and one is required, this method will fail and return <strong><span>false</span></strong>.       You can check whether an entry is encrypted with <span class="methodname" style="color:#CC7832">{@link RarEntry::isEncrypted()}</span>.      </p>     </dd>           <dt><span class="parameter" style="color:#3A95FF">extended_data</span></dt>    <dd>     <p class="para">       If <strong><span>true</span></strong>, extended information such as NTFS ACLs and Unix owner information will be set in the extract       files, as long as it&#039;s present in the archive.     </p>    </dd>      </dl>  </span>  <div class="warning"><strong class="warning">警告</strong>      <p class="para">Prior to version 2.0.0, this function would not handle relative      paths correctly. Use <span class="function">{@link realpath()}</span> as a workaround.</p>  </div> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-rarentry.extract-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 changelog" id="refsect1-rarentry.extract-changelog">  <h3 class="title">更新日志</h3>  <span>   <table class="doctable informaltable">         <thead>      <tr>       <th>版本</th>       <th>说明</th>      </tr>     </thead>     <tbody class="tbody">      <tr>       <td>PECL rar 3.0.0</td>       <td>        <span class="parameter" style="color:#3A95FF">extended_data</span> was added.       </td>      </tr>      <tr>       <td>PECL rar 3.0.0</td>       <td>        Support for RAR archives with repeated entry names is no longer defective.       </td>      </tr>     </tbody>       </table>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-rarentry.extract-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="example-2432">    <p><strong>示例 #1 <span class="methodname" style="color:#CC7832"><strong>RarEntry::extract()</strong></span> example</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$rar_file </span><span style="color: #007700">= </span><span style="color: #9876AA">rar_open</span><span style="color: #007700">(</span><span style="color: #DD0000">'example.rar'</span><span style="color: #007700">) or die(</span><span style="color: #DD0000">"Failed to open Rar archive"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$entry </span><span style="color: #007700">= </span><span style="color: #9876AA">rar_entry_get</span><span style="color: #007700">(</span><span style="color: #9876AA">$rar_file</span><span style="color: #007700">, </span><span style="color: #DD0000">'Dir/file.txt'</span><span style="color: #007700">) or die(</span><span style="color: #DD0000">"Failed to find such entry"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$entry</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">extract</span><span style="color: #007700">(</span><span style="color: #DD0000">'/dir/to'</span><span style="color: #007700">); </span><span style="color: #FF8000">// create /dir/to/Dir/file.txt<br /></span><span style="color: #9876AA">$entry</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">extract</span><span style="color: #007700">(</span><span style="color: #9876AA">false</span><span style="color: #007700">, </span><span style="color: #DD0000">'/dir/to/new_name.txt'</span><span style="color: #007700">); </span><span style="color: #FF8000">// create /dir/to/new_name.txt<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>   <div class="example" id="example-2435">    <p><strong>示例 #2 How to extract all files in archive: </strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br /></span><span style="color: #FF8000">// example by Erik Jenssen aka erix <br /><br /></span><span style="color: #9876AA">$filename </span><span style="color: #007700">= </span><span style="color: #DD0000">"foobar.rar"</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$filepath </span><span style="color: #007700">= </span><span style="color: #DD0000">"/home/foo/bar/"</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">$rar_file </span><span style="color: #007700">= </span><span style="color: #9876AA">rar_open</span><span style="color: #007700">(</span><span style="color: #9876AA">$filepath</span><span style="color: #007700">.</span><span style="color: #9876AA">$filename</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$list </span><span style="color: #007700">= </span><span style="color: #9876AA">rar_list</span><span style="color: #007700">(</span><span style="color: #9876AA">$rar_file</span><span style="color: #007700">);<br />foreach(</span><span style="color: #9876AA">$list </span><span style="color: #007700">as </span><span style="color: #9876AA">$file</span><span style="color: #007700">) {<br />    </span><span style="color: #9876AA">$entry </span><span style="color: #007700">= </span><span style="color: #9876AA">rar_entry_get</span><span style="color: #007700">(</span><span style="color: #9876AA">$rar_file</span><span style="color: #007700">, </span><span style="color: #9876AA">$file</span><span style="color: #007700">);<br />    </span><span style="color: #9876AA">$entry</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">extract</span><span style="color: #007700">(</span><span style="color: #DD0000">"."</span><span style="color: #007700">); </span><span style="color: #FF8000">// extract to the current dir<br /></span><span style="color: #007700">}<br /></span><span style="color: #9876AA">rar_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$rar_file</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-rarentry.extract-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="methodname" style="color:#CC7832">{@link RarEntry::getStream()} - Get file handler for entry</span></li>    <li class="member"><a href="https://www.php.net/manual/zh/wrappers.rar.php" class="link"><span>rar://</span> wrapper</a></li>   </ul>  </span> </div></div>
     * @return bool TRUE on success or FALSE on failure*/
    public function extract($dir, $filepath = "", $password = null, $extended_data = false) {}

/**
*<div id="rarentry.getattr" class="refentry"> <div class="refnamediv">  <h1 class="refname">RarEntry::getAttr</h1>  <p class="verinfo">(PECL rar &gt;= 0.1)</p><p class="refpurpose"><span class="refname">RarEntry::getAttr</span> &mdash; <span class="dc-title">Get attributes of the entry</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-rarentry.getattr-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>RarEntry::getAttr</strong></span>(): <span class="type" style="color:#EAB766">int</span></div>  <p class="para rdfs-comment">   Returns the OS-dependent attributes of the archive entry.   </p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-rarentry.getattr-parameters">  <h3 class="title">参数</h3>  <span>此函数没有参数。</span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-rarentry.getattr-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   Returns the attributes or <strong><span>false</span></strong> on error.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-rarentry.getattr-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="example-2438">    <p><strong>示例 #1 <span class="methodname" style="color:#CC7832"><strong>RarEntry::getAttr()</strong></span> example</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$rar_file </span><span style="color: #007700">= </span><span style="color: #9876AA">rar_open</span><span style="color: #007700">(</span><span style="color: #DD0000">'example.rar'</span><span style="color: #007700">) or die(</span><span style="color: #DD0000">"Can't open Rar archive"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$entry </span><span style="color: #007700">= </span><span style="color: #9876AA">rar_entry_get</span><span style="color: #007700">(</span><span style="color: #9876AA">$rar_file</span><span style="color: #007700">, </span><span style="color: #DD0000">'dir/in/the/archive'</span><span style="color: #007700">) or die(</span><span style="color: #DD0000">"Can't find such entry"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$host_os </span><span style="color: #007700">= </span><span style="color: #9876AA">$entry</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">getHostOs</span><span style="color: #007700">();<br /></span><span style="color: #9876AA">$attr </span><span style="color: #007700">= </span><span style="color: #9876AA">$entry</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">getAttr</span><span style="color: #007700">();<br /><br />switch(</span><span style="color: #9876AA">$host_os</span><span style="color: #007700">) {<br />    case </span><span style="color: #9876AA">RAR_HOST_MSDOS</span><span style="color: #007700">:<br />    case </span><span style="color: #9876AA">RAR_HOST_OS2</span><span style="color: #007700">:<br />    case </span><span style="color: #9876AA">RAR_HOST_WIN32</span><span style="color: #007700">:<br />    case </span><span style="color: #9876AA">RAR_HOST_MACOS</span><span style="color: #007700">:<br />        </span><span style="color: #9876AA">printf</span><span style="color: #007700">(</span><span style="color: #DD0000">"%c%c%c%c%c%c\n"</span><span style="color: #007700">,<br />                (</span><span style="color: #9876AA">$attr </span><span style="color: #007700">&amp; </span><span style="color: #9876AA">0x08</span><span style="color: #007700">) ? </span><span style="color: #DD0000">'V' </span><span style="color: #007700">: </span><span style="color: #DD0000">'.'</span><span style="color: #007700">,<br />                (</span><span style="color: #9876AA">$attr </span><span style="color: #007700">&amp; </span><span style="color: #9876AA">0x10</span><span style="color: #007700">) ? </span><span style="color: #DD0000">'D' </span><span style="color: #007700">: </span><span style="color: #DD0000">'.'</span><span style="color: #007700">,<br />                (</span><span style="color: #9876AA">$attr </span><span style="color: #007700">&amp; </span><span style="color: #9876AA">0x01</span><span style="color: #007700">) ? </span><span style="color: #DD0000">'R' </span><span style="color: #007700">: </span><span style="color: #DD0000">'.'</span><span style="color: #007700">,<br />                (</span><span style="color: #9876AA">$attr </span><span style="color: #007700">&amp; </span><span style="color: #9876AA">0x02</span><span style="color: #007700">) ? </span><span style="color: #DD0000">'H' </span><span style="color: #007700">: </span><span style="color: #DD0000">'.'</span><span style="color: #007700">,<br />                (</span><span style="color: #9876AA">$attr </span><span style="color: #007700">&amp; </span><span style="color: #9876AA">0x04</span><span style="color: #007700">) ? </span><span style="color: #DD0000">'S' </span><span style="color: #007700">: </span><span style="color: #DD0000">'.'</span><span style="color: #007700">,<br />                (</span><span style="color: #9876AA">$attr </span><span style="color: #007700">&amp; </span><span style="color: #9876AA">0x20</span><span style="color: #007700">) ? </span><span style="color: #DD0000">'A' </span><span style="color: #007700">: </span><span style="color: #DD0000">'.'</span><span style="color: #007700">);<br />        break;<br />    case </span><span style="color: #9876AA">RAR_HOST_UNIX</span><span style="color: #007700">:<br />    case </span><span style="color: #9876AA">RAR_HOST_BEOS</span><span style="color: #007700">:<br />        switch (</span><span style="color: #9876AA">$attr </span><span style="color: #007700">&amp; </span><span style="color: #9876AA">0xF000</span><span style="color: #007700">)<br />        {<br />            case </span><span style="color: #9876AA">0x4000</span><span style="color: #007700">:<br />                </span><span style="color: #9876AA">printf</span><span style="color: #007700">(</span><span style="color: #DD0000">"d"</span><span style="color: #007700">);<br />                break;<br />            case </span><span style="color: #9876AA">0xA000</span><span style="color: #007700">:<br />                </span><span style="color: #9876AA">printf</span><span style="color: #007700">(</span><span style="color: #DD0000">"l"</span><span style="color: #007700">);<br />                break;<br />            default:<br />                </span><span style="color: #9876AA">printf</span><span style="color: #007700">(</span><span style="color: #DD0000">"-"</span><span style="color: #007700">);<br />                break;<br />        }<br />        </span><span style="color: #9876AA">printf</span><span style="color: #007700">(</span><span style="color: #DD0000">"%c%c%c%c%c%c%c%c%c\n"</span><span style="color: #007700">,<br />                (</span><span style="color: #9876AA">$attr </span><span style="color: #007700">&amp; </span><span style="color: #9876AA">0x0100</span><span style="color: #007700">) ? </span><span style="color: #DD0000">'r' </span><span style="color: #007700">: </span><span style="color: #DD0000">'-'</span><span style="color: #007700">,<br />                (</span><span style="color: #9876AA">$attr </span><span style="color: #007700">&amp; </span><span style="color: #9876AA">0x0080</span><span style="color: #007700">) ? </span><span style="color: #DD0000">'w' </span><span style="color: #007700">: </span><span style="color: #DD0000">'-'</span><span style="color: #007700">,<br />                (</span><span style="color: #9876AA">$attr </span><span style="color: #007700">&amp; </span><span style="color: #9876AA">0x0040</span><span style="color: #007700">) ? ((</span><span style="color: #9876AA">$attr </span><span style="color: #007700">&amp; </span><span style="color: #9876AA">0x0800</span><span style="color: #007700">) ? </span><span style="color: #DD0000">'s'</span><span style="color: #007700">:</span><span style="color: #DD0000">'x'</span><span style="color: #007700">):((</span><span style="color: #9876AA">$attr </span><span style="color: #007700">&amp; </span><span style="color: #9876AA">0x0800</span><span style="color: #007700">) ? </span><span style="color: #DD0000">'S'</span><span style="color: #007700">:</span><span style="color: #DD0000">'-'</span><span style="color: #007700">),<br />                (</span><span style="color: #9876AA">$attr </span><span style="color: #007700">&amp; </span><span style="color: #9876AA">0x0020</span><span style="color: #007700">) ? </span><span style="color: #DD0000">'r' </span><span style="color: #007700">: </span><span style="color: #DD0000">'-'</span><span style="color: #007700">,<br />                (</span><span style="color: #9876AA">$attr </span><span style="color: #007700">&amp; </span><span style="color: #9876AA">0x0010</span><span style="color: #007700">) ? </span><span style="color: #DD0000">'w' </span><span style="color: #007700">: </span><span style="color: #DD0000">'-'</span><span style="color: #007700">,<br />                (</span><span style="color: #9876AA">$attr </span><span style="color: #007700">&amp; </span><span style="color: #9876AA">0x0008</span><span style="color: #007700">) ? ((</span><span style="color: #9876AA">$attr </span><span style="color: #007700">&amp; </span><span style="color: #9876AA">0x0400</span><span style="color: #007700">) ? </span><span style="color: 