<span style="color: #9876AA">rar_open</span><span style="color: #007700">(</span><span style="color: #DD0000">'example.rar'</span><span style="color: #007700">) or die(</span><span style="color: #DD0000">"Failed to open Rar archive"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$entry </span><span style="color: #007700">= </span><span style="color: #9876AA">rar_entry_get</span><span style="color: #007700">(</span><span style="color: #9876AA">$rar_file</span><span style="color: #007700">, </span><span style="color: #DD0000">'Dir/file.txt'</span><span style="color: #007700">) or die(</span><span style="color: #DD0000">"Failed to find such entry"</span><span style="color: #007700">);<br /><br />switch (</span><span style="color: #9876AA">$entry</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">getHostOs</span><span style="color: #007700">()) {<br />    case </span><span style="color: #9876AA">RarEntry</span><span style="color: #007700">::</span><span style="color: #9876AA">HOST_MSDOS</span><span style="color: #007700">:<br />        echo </span><span style="color: #DD0000">"MS-DOS\n"</span><span style="color: #007700">;<br />        break;<br />    case </span><span style="color: #9876AA">RarEntry</span><span style="color: #007700">::</span><span style="color: #9876AA">HOST_OS2</span><span style="color: #007700">:<br />        echo </span><span style="color: #DD0000">"OS2\n"</span><span style="color: #007700">;<br />        break;<br />    case </span><span style="color: #9876AA">RarEntry</span><span style="color: #007700">::</span><span style="color: #9876AA">HOST_WIN32</span><span style="color: #007700">:<br />        echo </span><span style="color: #DD0000">"Win32\n"</span><span style="color: #007700">;<br />        break;<br />    case </span><span style="color: #9876AA">RarEntry</span><span style="color: #007700">::</span><span style="color: #9876AA">HOST_MACOS</span><span style="color: #007700">:<br />        echo </span><span style="color: #DD0000">"MacOS\n"</span><span style="color: #007700">;<br />        break;<br />    case </span><span style="color: #9876AA">RarEntry</span><span style="color: #007700">::</span><span style="color: #9876AA">HOST_UNIX</span><span style="color: #007700">:<br />        echo </span><span style="color: #DD0000">"Unix/Linux\n"</span><span style="color: #007700">;<br />        break;<br />    case </span><span style="color: #9876AA">RarEntry</span><span style="color: #007700">::</span><span style="color: #9876AA">HOST_BEOS</span><span style="color: #007700">:<br />        echo </span><span style="color: #DD0000">"BeOS\n"</span><span style="color: #007700">;<br />        break;<br />}<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>      <div class="example" id="example-2444">    <p><strong>示例 #2 <span class="methodname" style="color:#CC7832"><strong>RarEntry::getHostOs()</strong></span> example (version &lt;= 1.0.0)</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$rar_file </span><span style="color: #007700">= </span><span style="color: #9876AA">rar_open</span><span style="color: #007700">(</span><span style="color: #DD0000">'example.rar'</span><span style="color: #007700">) or die(</span><span style="color: #DD0000">"Failed to open Rar archive"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$entry </span><span style="color: #007700">= </span><span style="color: #9876AA">rar_entry_get</span><span style="color: #007700">(</span><span style="color: #9876AA">$rar_file</span><span style="color: #007700">, </span><span style="color: #DD0000">'Dir/file.txt'</span><span style="color: #007700">) or die(</span><span style="color: #DD0000">"Failed to find such entry"</span><span style="color: #007700">);<br /><br />switch (</span><span style="color: #9876AA">$entry</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">getHostOs</span><span style="color: #007700">()) {<br />    case </span><span style="color: #9876AA">RAR_HOST_MSDOS</span><span style="color: #007700">:<br />        echo </span><span style="color: #DD0000">"MS-DOS\n"</span><span style="color: #007700">;<br />        break;<br />    case </span><span style="color: #9876AA">RAR_HOST_OS2</span><span style="color: #007700">:<br />        echo </span><span style="color: #DD0000">"OS2\n"</span><span style="color: #007700">;<br />        break;<br />    case </span><span style="color: #9876AA">RAR_HOST_WIN32</span><span style="color: #007700">:<br />        echo </span><span style="color: #DD0000">"Win32\n"</span><span style="color: #007700">;<br />        break;<br />    case </span><span style="color: #9876AA">RAR_HOST_MACOS</span><span style="color: #007700">:<br />        echo </span><span style="color: #DD0000">"MacOS\n"</span><span style="color: #007700">;<br />        break;<br />    case </span><span style="color: #9876AA">RAR_HOST_UNIX</span><span style="color: #007700">:<br />        echo </span><span style="color: #DD0000">"Unix/Linux\n"</span><span style="color: #007700">;<br />        break;<br />    case </span><span style="color: #9876AA">RAR_HOST_BEOS</span><span style="color: #007700">:<br />        echo </span><span style="color: #DD0000">"BeOS\n"</span><span style="color: #007700">;<br />        break;<br />}<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-rarentry.gethostos-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="methodname" style="color:#CC7832">{@link RarEntry::extract()} - Extract entry from the archive</span></li>   </ul>  </span> </div></div>
