function sodium_bin2base64(string $string, int $id): string {}

/**
*<div id="function.sodium-base642bin" class="refentry"> <div class="refnamediv">  <h1 class="refname">sodium_base642bin</h1>  <p class="verinfo">(PHP 7 &gt;= 7.2.0, PHP 8)</p><p class="refpurpose"><span class="refname">sodium_base642bin</span> &mdash; <span class="dc-title">Decodes a base64-encoded string into raw binary.</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.sodium-base642bin-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>sodium_base642bin</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$string</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$id</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$ignore</span><span class="initializer"> = &quot;&quot;</span></span>): <span class="type" style="color:#EAB766">string</span></div>  <p class="para rdfs-comment">   Converts a base64 encoded string into raw binary. Unlike <span class="function">{@link base64_decode()}</span>,   <span class="function"><strong style="color:#CC7832">sodium_base642bin()</strong></span> is constant-time (a property that is important for any code that   touches cryptographic inputs, such as plaintexts or keys) and supports multiple character sets.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.sodium-base642bin-parameters">  <h3 class="title">参数</h3>  <dl>       <dt><span class="parameter" style="color:#3A95FF">string</span></dt>    <dd>     <span>      <span class="type" style="color:#EAB766">string</span>; Encoded string.     </span>    </dd>          <dt><span class="parameter" style="color:#3A95FF">id</span></dt>    <dd>     <p class="para">      <ul class="simplelist">       <li class="member">        <strong><span>SODIUM_BASE64_VARIANT_ORIGINAL</span></strong> for standard (<span>A-Za-z0-9/\+</span>)        Base64 encoding.       </li>       <li class="member">        <strong><span>SODIUM_BASE64_VARIANT_ORIGINAL_NO_PADDING</span></strong> for standard (<span>A-Za-z0-9/\+</span>)        Base64 encoding, without <span>=</span> padding characters.       </li>       <li class="member">        <strong><span>SODIUM_BASE64_VARIANT_URLSAFE</span></strong> for URL-safe (<span>A-Za-z0-9\-_</span>) Base64 encoding.       </li>       <li class="member">        <strong><span>SODIUM_BASE64_VARIANT_URLSAFE_NO_PADDING</span></strong> for URL-safe (<span>A-Za-z0-9\-_</span>)        Base64 encoding, without <span>=</span> padding characters.       </li>      </ul>     </p>    </dd>          <dt><span class="parameter" style="color:#3A95FF">ignore</span></dt>    <dd>     <p class="para">      Characters to ignore when decoding (e.g. whitespace characters).     </p>    </dd>     </dl> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.sodium-base642bin-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   Decoded string.  </p> </div></div>
