/**
*<div id="class.solrclient" class="reference"> <h1 class="title">The SolrClient class</h1>  <div class="partintro"><p class="verinfo">(PECL solr &gt;= 0.9.2)</p>  <div class="section" id="solrclient.intro">   <h2 class="title">简介</h2>   <p class="para">    Used to send requests to a Solr server. Currently, cloning and serialization of SolrClient instances is not supported.   </p>  </div>  <div class="section" id="solrclient.synopsis">   <h2 class="title">类摘要</h2>   <div class="classsynopsis">    <span class="ooclass"><strong class="classname"></strong></span>    <div class="classsynopsisinfo">     <span class="ooclass">      <span class="modifier">final</span>      <span class="modifier">class</span> <strong class="classname">SolrClient</strong>     </span>     {</div>    <div class="classsynopsisinfo classsynopsisinfo_comment">// 常量 </div>    <div class="fieldsynopsis">     <span class="modifier">const</span>     <span class="type" style="color:#EAB766">int</span>      <var class="fieldsynopsis_varname"><a href="https://www.php.net/manual/zh/class.solrclient.php#solrclient.constants.search-servlet-type"><var class="varname">SEARCH_SERVLET_TYPE</var></a></var><span class="initializer"> = 1</span>;</div>    <div class="fieldsynopsis"><span class="modifier">const</span>     <span class="type" style="color:#EAB766">int</span>      <var class="fieldsynopsis_varname"><a href="https://www.php.net/manual/zh/class.solrclient.php#solrclient.constants.update-servlet-type"><var class="varname">UPDATE_SERVLET_TYPE</var></a></var><span class="initializer"> = 2</span>;</div>    <div class="fieldsynopsis"><span class="modifier">const</span>     <span class="type" style="color:#EAB766">int</span>      <var class="fieldsynopsis_varname"><a href="https://www.php.net/manual/zh/class.solrclient.php#solrclient.constants.threads-servlet-type"><var class="varname">THREADS_SERVLET_TYPE</var></a></var><span class="initializer"> = 4</span>;</div>    <div class="fieldsynopsis"><span class="modifier">const</span>     <span class="type" style="color:#EAB766">int</span>      <var class="fieldsynopsis_varname"><a href="https://www.php.net/manual/zh/class.solrclient.php#solrclient.constants.ping-servlet-type"><var class="varname">PING_SERVLET_TYPE</var></a></var><span class="initializer"> = 8</span>;</div>    <div class="fieldsynopsis"><span class="modifier">const</span>     <span class="type" style="color:#EAB766">int</span>      <var class="fieldsynopsis_varname"><a href="https://www.php.net/manual/zh/class.solrclient.php#solrclient.constants.terms-servlet-type"><var class="varname">TERMS_SERVLET_TYPE</var></a></var><span class="initializer"> = 16</span>;</div>    <div class="fieldsynopsis"><span class="modifier">const</span>     <span class="type" style="color:#EAB766">int</span>      <var class="fieldsynopsis_varname"><a href="https://www.php.net/manual/zh/class.solrclient.php#solrclient.constants.system-servlet-type"><var class="varname">SYSTEM_SERVLET_TYPE</var></a></var><span class="initializer"> = 32</span>;</div>    <div class="fieldsynopsis"><span class="modifier">const</span>     <span class="type" style="color:#EAB766">string</span>      <var class="fieldsynopsis_varname"><a href="https://www.php.net/manual/zh/class.solrclient.php#solrclient.constants.default-search-servlet"><var class="varname">DEFAULT_SEARCH_SERVLET</var></a></var><span class="initializer"> = select</span>;</div>    <div class="fieldsynopsis"><span class="modifier">const</span>     <span class="type" style="color:#EAB766">string</span>      <var class="fieldsynopsis_varname"><a href="https://www.php.net/manual/zh/class.solrclient.php#solrclient.constants.default-update-servlet"><var class="varname">DEFAULT_UPDATE_SERVLET</var></a></var><span class="initializer"> = update</span>;</div>    <div class="fieldsynopsis"><span class="modifier">const</span>     <span class="type" style="color:#EAB766">string</span>      <var class="fieldsynopsis_varname"><a href="https://www.php.net/manual/zh/class.solrclient.php#solrclient.constants.default-threads-servlet"><var class="varname">DEFAULT_THREADS_SERVLET</var></a></var><span class="initializer"> = admin/threads</span>;</div>    <div class="fieldsynopsis"><span class="modifier">const</span>     <span class="type" style="color:#EAB766">string</span>      <var class="fieldsynopsis_varname"><a href="https://www.php.net/manual/zh/class.solrclient.php#solrclient.constants.default-ping-servlet"><var class="varname">DEFAULT_PING_SERVLET</var></a></var><span class="initializer"> = admin/ping</span>;</div>    <div class="fieldsynopsis"><span class="modifier">const</span>     <span class="type" style="color:#EAB766">string</span>      <var class="fieldsynopsis_varname"><a href="https://www.php.net/manual/zh/class.solrclient.php#solrclient.constants.default-terms-servlet"><var class="varname">DEFAULT_TERMS_SERVLET</var></a></var><span class="initializer"> = terms</span>;</div>    <div class="fieldsynopsis"><span class="modifier">const</span>     <span class="type" style="color:#EAB766">string</span>      <var class="fieldsynopsis_varname"><a href="https://www.php.net/manual/zh/class.solrclient.php#solrclient.constants.default-system-servlet"><var class="varname">DEFAULT_SYSTEM_SERVLET</var></a></var><span class="initializer"> = admin/system</span>;</div>        <div class="classsynopsisinfo classsynopsisinfo_comment">// 方法 </div>    <div class="constructorsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.construct.php" class="methodname" style="color:#CC7832">__construct</a></span>(<span class="methodparam"><span class="type" style="color:#EAB766">array</span> <span class="parameter" style="color:#3A95FF">$clientOptions</span></span>)</div>    <div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.adddocument.php" class="methodname" style="color:#CC7832">addDocument</a></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrinputdocument.php" class="type SolrInputDocument" style="color:#EAB766">SolrInputDocument</a></span> <span class="parameter" style="color:#3A95FF">$doc</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">bool</span> <span class="parameter" style="color:#3A95FF">$overwrite</span><span class="initializer"> = <strong><span>true</span></strong></span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$commitWithin</span><span class="initializer"> = 0</span></span>): <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrupdateresponse.php" class="type SolrUpdateResponse" style="color:#EAB766">SolrUpdateResponse</a></span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.adddocuments.php" class="methodname" style="color:#CC7832">addDocuments</a></span>(<span class="methodparam"><span class="type" style="color:#EAB766">array</span> <span class="parameter" style="color:#3A95FF">$docs</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">bool</span> <span class="parameter" style="color:#3A95FF">$overwrite</span><span class="initializer"> = <strong><span>true</span></strong></span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$commitWithin</span><span class="initializer"> = 0</span></span>): <span class="type" style="color:#EAB766"><span class="type void" style="color:#EAB766">void</span></span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.commit.php" class="methodname" style="color:#CC7832">commit</a></span>(<span class="methodparam"><span class="type" style="color:#EAB766">bool</span> <span class="parameter" style="color:#3A95FF">$softCommit</span><span class="initializer"> = <strong><span>false</span></strong></span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">bool</span> <span class="parameter" style="color:#3A95FF">$waitSearcher</span><span class="initializer"> = <strong><span>true</span></strong></span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">bool</span> <span class="parameter" style="color:#3A95FF">$expungeDeletes</span><span class="initializer"> = <strong><span>false</span></strong></span></span>): <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrupdateresponse.php" class="type SolrUpdateResponse" style="color:#EAB766">SolrUpdateResponse</a></span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.deletebyid.php" class="methodname" style="color:#CC7832">deleteById</a></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$id</span></span>): <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrupdateresponse.php" class="type SolrUpdateResponse" style="color:#EAB766">SolrUpdateResponse</a></span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.deletebyids.php" class="methodname" style="color:#CC7832">deleteByIds</a></span>(<span class="methodparam"><span class="type" style="color:#EAB766">array</span> <span class="parameter" style="color:#3A95FF">$ids</span></span>): <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrupdateresponse.php" class="type SolrUpdateResponse" style="color:#EAB766">SolrUpdateResponse</a></span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.deletebyqueries.php" class="methodname" style="color:#CC7832">deleteByQueries</a></span>(<span class="methodparam"><span class="type" style="color:#EAB766">array</span> <span class="parameter" style="color:#3A95FF">$queries</span></span>): <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrupdateresponse.php" class="type SolrUpdateResponse" style="color:#EAB766">SolrUpdateResponse</a></span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.deletebyquery.php" class="methodname" style="color:#CC7832">deleteByQuery</a></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$query</span></span>): <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrupdateresponse.php" class="type SolrUpdateResponse" style="color:#EAB766">SolrUpdateResponse</a></span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.getbyid.php" class="methodname" style="color:#CC7832">getById</a></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$id</span></span>): <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrqueryresponse.php" class="type SolrQueryResponse" style="color:#EAB766">SolrQueryResponse</a></span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.getbyids.php" class="methodname" style="color:#CC7832">getByIds</a></span>(<span class="methodparam"><span class="type" style="color:#EAB766">array</span> <span class="parameter" style="color:#3A95FF">$ids</span></span>): <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrqueryresponse.php" class="type SolrQueryResponse" style="color:#EAB766">SolrQueryResponse</a></span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.getdebug.php" class="methodname" style="color:#CC7832">getDebug</a></span>(): <span class="type" style="color:#EAB766">string</span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.getoptions.php" class="methodname" style="color:#CC7832">getOptions</a></span>(): <span class="type" style="color:#EAB766">array</span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.optimize.php" class="methodname" style="color:#CC7832">optimize</a></span>(<span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$maxSegments</span><span class="initializer"> = 1</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">bool</span> <span class="parameter" style="color:#3A95FF">$softCommit</span><span class="initializer"> = <strong><span>true</span></strong></span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">bool</span> <span class="parameter" style="color:#3A95FF">$waitSearcher</span><span class="initializer"> = <strong><span>true</span></strong></span></span>): <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrupdateresponse.php" class="type SolrUpdateResponse" style="color:#EAB766">SolrUpdateResponse</a></span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.ping.php" class="methodname" style="color:#CC7832">ping</a></span>(): <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrpingresponse.php" class="type SolrPingResponse" style="color:#EAB766">SolrPingResponse</a></span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.query.php" class="methodname" style="color:#CC7832">query</a></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrparams.php" class="type SolrParams" style="color:#EAB766">SolrParams</a></span> <span class="parameter" style="color:#3A95FF">$query</span></span>): <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrqueryresponse.php" class="type SolrQueryResponse" style="color:#EAB766">SolrQueryResponse</a></span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.request.php" class="methodname" style="color:#CC7832">request</a></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$raw_request</span></span>): <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrupdateresponse.php" class="type SolrUpdateResponse" style="color:#EAB766">SolrUpdateResponse</a></span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.rollback.php" class="methodname" style="color:#CC7832">rollback</a></span>(): <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrupdateresponse.php" class="type SolrUpdateResponse" style="color:#EAB766">SolrUpdateResponse</a></span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.setresponsewriter.php" class="methodname" style="color:#CC7832">setResponseWriter</a></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$responseWriter</span></span>): <span class="type" style="color:#EAB766"><span class="type void" style="color:#EAB766">void</span></span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.setservlet.php" class="methodname" style="color:#CC7832">setServlet</a></span>(<span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$type</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$value</span></span>): <span class="type" style="color:#EAB766">bool</span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.system.php" class="methodname" style="color:#CC7832">system</a></span>(): <span class="type" style="color:#EAB766"><span class="type void" style="color:#EAB766">void</span></span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.threads.php" class="methodname" style="color:#CC7832">threads</a></span>(): <span class="type" style="color:#EAB766"><span class="type void" style="color:#EAB766">void</span></span></div>    <div class="destructorsynopsis dc-description"><span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrclient.destruct.php" class="methodname" style="color:#CC7832">__destruct</a></span>()</div>   }</div>  </div>  <div class="section" id="solrclient.constants">   <h2 class="title">预定义常量</h2>   <dl>         <dt id="solrclient.constants.search-servlet-type"><strong><span>SolrClient::SEARCH_SERVLET_TYPE</span></strong></dt>     <dd>      <p class="para">Used when updating the search servlet.</p>     </dd>             <dt id="solrclient.constants.update-servlet-type"><strong><span>SolrClient::UPDATE_SERVLET_TYPE</span></strong></dt>     <dd>      <p class="para">Used when updating the update servlet.</p>     </dd>             <dt id="solrclient.constants.threads-servlet-type"><strong><span>SolrClient::THREADS_SERVLET_TYPE</span></strong></dt>     <dd>      <p class="para">Used when updating the threads servlet.</p>     </dd>             <dt id="solrclient.constants.ping-servlet-type"><strong><span>SolrClient::PING_SERVLET_TYPE</span></strong></dt>     <dd>      <p class="para">Used when updating the ping servlet.</p>     </dd>             <dt id="solrclient.constants.terms-servlet-type"><strong><span>SolrClient::TERMS_SERVLET_TYPE</span></strong></dt>     <dd>      <p class="para">Used when updating the terms servlet.</p>     </dd>                 <dt id="solrclient.constants.system-servlet-type"><strong><span>SolrClient::SYSTEM_SERVLET_TYPE</span></strong></dt>     <dd>      <p class="para">Used when retrieving system information from the system servlet.</p>     </dd>             <dt id="solrclient.constants.default-search-servlet"><strong><span>SolrClient::DEFAULT_SEARCH_SERVLET</span></strong></dt>     <dd>      <p class="para">This is the initial value for the search servlet.</p>     </dd>             <dt id="solrclient.constants.default-update-servlet"><strong><span>SolrClient::DEFAULT_UPDATE_SERVLET</span></strong></dt>     <dd>      <p class="para">This is the initial value for the update servlet.</p>     </dd>             <dt id="solrclient.constants.default-threads-servlet"><strong><span>SolrClient::DEFAULT_THREADS_SERVLET</span></strong></dt>     <dd>      <p class="para">This is the initial value for the threads servlet.</p>     </dd>             <dt id="solrclient.constants.default-ping-servlet"><strong><span>SolrClient::DEFAULT_PING_SERVLET</span></strong></dt>     <dd>      <p class="para">This is the initial value for the ping servlet.</p>     </dd>             <dt id="solrclient.constants.default-terms-servlet"><strong><span>SolrClient::DEFAULT_TERMS_SERVLET</span></strong></dt>     <dd>      <p class="para">This is the initial value for the terms servlet used for the TermsComponent</p>     </dd>                 <dt id="solrclient.constants.default-system-servlet"><strong><span>SolrClient::DEFAULT_SYSTEM_SERVLET</span></strong></dt>     <dd>      <p class="para">This is the initial value for the system servlet used to obtain Solr Server information</p>     </dd>       </dl>  </div> </div> <h2>目录</h2><ul class="chunklist chunklist_reference"><li>{@link SolrClient::addDocument} — Adds a document to the index</li><li>{@link SolrClient::addDocuments} — Adds a collection of SolrInputDocument instances to the index</li><li>{@link SolrClient::commit} — Finalizes all add/deletes made to the index</li><li>{@link SolrClient::__construct} — Constructor for the SolrClient object</li><li>{@link SolrClient::deleteById} — Delete by Id</li><li>{@link SolrClient::deleteByIds} — Deletes by Ids</li><li>{@link SolrClient::deleteByQueries} — Removes all documents matching any of the queries</li><li>{@link SolrClient::deleteByQuery} — Deletes all documents matching the given query</li><li>{@link SolrClient::__destruct} — Destructor for SolrClient</li><li>{@link SolrClient::getById} — Get Document By Id. Utilizes Solr Realtime Get (RTG)</li><li>{@link SolrClient::getByIds} — Get Documents by their Ids. Utilizes Solr Realtime Get (RTG)</li><li>{@link SolrClient::getDebug} — Returns the debug data for the last connection attempt</li><li>{@link SolrClient::getOptions} — Returns the client options set internally</li><li>{@link SolrClient::optimize} — Defragments the index</li><li>{@link SolrClient::ping} — Checks if Solr server is still up</li><li>{@link SolrClient::query} — Sends a query to the server</li><li>{@link SolrClient::request} — Sends a raw update request</li><li>{@link SolrClient::rollback} — Rollbacks all add/deletes made to the index since the last commit</li><li>{@link SolrClient::setResponseWriter} — Sets the response writer used to prepare the response from Solr</li><li>{@link SolrClient::setServlet} — Changes the specified servlet type to a new value</li><li>{@link SolrClient::system} — Retrieve Solr Server information</li><li>{@link SolrClient::threads} — Checks the threads status</li></ul></div>
*/
final class SolrClient
{
    /** @var int Used when updating the search servlet. */
    public const SEARCH_SERVLET_TYPE = 1;

    /** @var int Used when updating the update servlet. */
    public const UPDATE_SERVLET_TYPE = 2;

    /** @var int Used when updating the threads servlet. */
    public const THREADS_SERVLET_TYPE = 4;

    /** @var int Used when updating the ping servlet. */
    public const PING_SERVLET_TYPE = 8;

    /** @var int Used when updating the terms servlet. */
    public const TERMS_SERVLET_TYPE = 16;

    /** @var int Used when retrieving system information from the system servlet. */
    public const SYSTEM_SERVLET_TYPE = 32;

    /** @var string This is the initial value for the search servlet. */
    public const DEFAULT_SEARCH_SERVLET = 'select';

    /** @var string This is the initial value for the update servlet. */
    public const DEFAULT_UPDATE_SERVLET = 'update';

    /** @var string This is the initial value for the threads servlet. */
    public const DEFAULT_THREADS_SERVLET = 'admin/threads';

    /** @var string This is the initial value for the ping servlet. */
    public const DEFAULT_PING_SERVLET = 'admin/ping';

    /** @var string This is the initial value for the terms servlet used for the TermsComponent. */
    public const DEFAULT_TERMS_SERVLET = 'terms';

    /** @var string This is the initial value for the system servlet used to obtain Solr Server information. */
    public const DEFAULT_SYSTEM_SERVLET = 'admin/system';

/**
*<div id="solrclient.adddocument" class="refentry"> <div class="refnamediv">  <h1 class="refname">SolrClient::addDocument</h1>  <p class="verinfo">(PECL solr &gt;= 0.9.2)</p><p class="refpurpose"><span class="refname">SolrClient::addDocument</span> &mdash; <span class="dc-title">Adds a document to the index</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-solrclient.adddocument-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>SolrClient::addDocument</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrinputdocument.php" class="type SolrInputDocument" style="color:#EAB766">SolrInputDocument</a></span> <span class="parameter" style="color:#3A95FF">$doc</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">bool</span> <span class="parameter" style="color:#3A95FF">$overwrite</span><span class="initializer"> = <strong><span>true</span></strong></span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$commitWithin</span><span class="initializer"> = 0</span></span>): <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrupdateresponse.php" class="type SolrUpdateResponse" style="color:#EAB766">SolrUpdateResponse</a></span></div>  <p class="para rdfs-comment">   This method adds a document to the index.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-solrclient.adddocument-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">doc</span></dt>     <dd>      <p class="para">       The SolrInputDocument instance.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">overwrite</span></dt>     <dd>      <p class="para">       Whether to overwrite existing document or not. If <strong><span>false</span></strong> there will be duplicates (several documents with the same ID).              </p>        <div class="warning"><strong class="warning">警告</strong>            <p class="para">              PECL Solr &lt; 2.0 $allowDups was used instead of $overwrite, which does the same functionality with exact opposite bool flag.            </p>            <p class="para">              $allowDups = false is the same as $overwrite = true            </p>        </div>     </dd>             <dt><span class="parameter" style="color:#3A95FF">commitWithin</span></dt>     <dd>      <p class="para">        Number of milliseconds within which to auto commit this document. Available since Solr 1.4 . Default (0) means disabled.      </p>      <p class="para">        When this value specified, it leaves the control of when to do the commit        to Solr itself, optimizing number of commits to a minimum while still        fulfilling        the update latency requirements, and Solr will automatically do a commit        when the oldest add in the buffer is due.       </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-solrclient.adddocument-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   Returns a <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrupdateresponse.php" class="type SolrUpdateResponse" style="color:#EAB766">SolrUpdateResponse</a></span> object or throws an Exception on failure.  </p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 errors" id="refsect1-solrclient.adddocument-errors">  <h3 class="title">错误／异常</h3>  <p class="para">   Throws <span class="classname"><a href="https://www.php.net/manual/zh/class.solrclientexception.php" class="classname">SolrClientException</a></span> if the client had failed, or there was a connection issue.  </p>  <p class="para">   Throws <span class="classname"><a href="https://www.php.net/manual/zh/class.solrserverexception.php" class="classname">SolrServerException</a></span> if the Solr Server had failed to process the request.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-solrclient.adddocument-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="">    <p><strong>示例 #1 <span class="methodname" style="color:#CC7832"><strong>SolrClient::addDocument()</strong></span> example</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$options </span><span style="color: #007700">= array<br />(<br />    </span><span style="color: #DD0000">'hostname' </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">SOLR_SERVER_HOSTNAME</span><span style="color: #007700">,<br />    </span><span style="color: #DD0000">'login'    </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">SOLR_SERVER_USERNAME</span><span style="color: #007700">,<br />    </span><span style="color: #DD0000">'password' </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">SOLR_SERVER_PASSWORD</span><span style="color: #007700">,<br />    </span><span style="color: #DD0000">'port'     </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">SOLR_SERVER_PORT</span><span style="color: #007700">,<br />);<br /><br /></span><span style="color: #9876AA">$client </span><span style="color: #007700">= new </span><span style="color: #9876AA">SolrClient</span><span style="color: #007700">(</span><span style="color: #9876AA">$options</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$doc </span><span style="color: #007700">= new </span><span style="color: #9876AA">SolrInputDocument</span><span style="color: #007700">();<br /><br /></span><span style="color: #9876AA">$doc</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">addField</span><span style="color: #007700">(</span><span style="color: #DD0000">'id'</span><span style="color: #007700">, </span><span style="color: #9876AA">334455</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$doc</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">addField</span><span style="color: #007700">(</span><span style="color: #DD0000">'cat'</span><span style="color: #007700">, </span><span style="color: #DD0000">'Software'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$doc</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">addField</span><span style="color: #007700">(</span><span style="color: #DD0000">'cat'</span><span style="color: #007700">, </span><span style="color: #DD0000">'Lucene'</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$updateResponse </span><span style="color: #007700">= </span><span style="color: #9876AA">$client</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">addDocument</span><span style="color: #007700">(</span><span style="color: #9876AA">$doc</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// you will have to commit changes to be written if you didn't use $commitWithin<br /></span><span style="color: #9876AA">$client</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">commit</span><span style="color: #007700">();<br /><br /></span><span style="color: #9876AA">print_r</span><span style="color: #007700">(</span><span style="color: #9876AA">$updateResponse</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">getResponse</span><span style="color: #007700">());<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents"><p>以上示例的输出类似于：</p></div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span><br>SolrObject Object<br>(<br>    [responseHeader] =&gt; SolrObject Object<br>        (<br>            [status] =&gt; 0<br>            [QTime] =&gt; 1<br>        )<br><br>)<br><br></span></div>    </div>   </div>   </span>   <p class="para">   <div class="example" id="">    <p><strong>示例 #2 <span class="methodname" style="color:#CC7832"><strong>SolrClient::addDocument()</strong></span> example 2</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$options </span><span style="color: #007700">= array<br />(<br />    </span><span style="color: #DD0000">'hostname' </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">SOLR_SERVER_HOSTNAME</span><span style="color: #007700">,<br />    </span><span style="color: #DD0000">'login'    </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">SOLR_SERVER_USERNAME</span><span style="color: #007700">,<br />    </span><span style="color: #DD0000">'password' </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">SOLR_SERVER_PASSWORD</span><span style="color: #007700">,<br />    </span><span style="color: #DD0000">'port'     </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">SOLR_SERVER_PORT</span><span style="color: #007700">,<br />);<br /><br /></span><span style="color: #9876AA">$client </span><span style="color: #007700">= new </span><span style="color: #9876AA">SolrClient</span><span style="color: #007700">(</span><span style="color: #9876AA">$options</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$doc </span><span style="color: #007700">= new </span><span style="color: #9876AA">SolrInputDocument</span><span style="color: #007700">();<br /><br /></span><span style="color: #9876AA">$doc</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">addField</span><span style="color: #007700">(</span><span style="color: #DD0000">'id'</span><span style="color: #007700">, </span><span style="color: #9876AA">334455</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$doc</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">addField</span><span style="color: #007700">(</span><span style="color: #DD0000">'cat'</span><span style="color: #007700">, </span><span style="color: #DD0000">'Software'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$doc</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">addField</span><span style="color: #007700">(</span><span style="color: #DD0000">'cat'</span><span style="color: #007700">, </span><span style="color: #DD0000">'Lucene'</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// No need to call commit() because $commitWithin is passed, so Solr Server will auto commit within 10 seconds<br /></span><span style="color: #9876AA">$updateResponse </span><span style="color: #007700">= </span><span style="color: #9876AA">$client</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">addDocument</span><span style="color: #007700">(</span><span style="color: #9876AA">$doc</span><span style="color: #007700">, </span><span style="color: #9876AA">false</span><span style="color: #007700">, </span><span style="color: #9876AA">10000</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">print_r</span><span style="color: #007700">(</span><span style="color: #9876AA">$updateResponse</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">getResponse</span><span style="color: #007700">());<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents"><p>以上示例的输出类似于：</p></div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span><br>SolrObject Object<br>(<br>    [responseHeader] =&gt; SolrObject Object<br>        (<br>            [status] =&gt; 0<br>            [QTime] =&gt; 1<br>        )<br><br>)<br><br></span></div>    </div>   </div>     </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-solrclient.adddocument-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="methodname" style="color:#CC7832">{@link SolrClient::addDocuments()} - Adds a collection of SolrInputDocument instances to the index</span></li>    <li class="member"><span class="methodname" style="color:#CC7832">{@link SolrClient::commit()} - Finalizes all add/deletes made to the index</span></li>   </ul>  </span> </div></div>
     * @return SolrUpdateResponse <p>*/
    public function addDocument(SolrInputDocument $doc, $overwrite = true, $commitWithin = 0) {}

/**
*<div id="solrclient.adddocuments" class="refentry"> <div class="refnamediv">  <h1 class="refname">SolrClient::addDocuments</h1>  <p class="verinfo">(PECL solr &gt;= 0.9.2)</p><p class="refpurpose"><span class="refname">SolrClient::addDocuments</span> &mdash; <span class="dc-title">Adds a collection of SolrInputDocument instances to the index</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-solrclient.adddocuments-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>SolrClient::addDocuments</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">array</span> <span class="parameter" style="color:#3A95FF">$docs</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">bool</span> <span class="parameter" style="color:#3A95FF">$overwrite</span><span class="initializer"> = <strong><span>true</span></strong></span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$commitWithin</span><span class="initializer"> = 0</span></span>): <span class="type" style="color:#EAB766"><span class="type void" style="color:#EAB766">void</span></span></div>  <p class="para rdfs-comment">   Adds a collection of documents to the index.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-solrclient.adddocuments-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">docs</span></dt>     <dd>      <p class="para">       An array containing the collection of SolrInputDocument instances. This array must be an actual variable.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">overwrite</span></dt>     <dd>      <p class="para">       Whether to overwrite existing documents or not. If <strong><span>false</span></strong> there will be duplicates (several documents with the same ID).              </p>        <div class="warning"><strong class="warning">警告</strong>            <p class="para">              PECL Solr &lt; 2.0 $allowDups was used instead of $overwrite, which does the same functionality with exact opposite bool flag.            </p>            <p class="para">              $allowDups = false is the same as $overwrite = true            </p>        </div>     </dd>             <dt><span class="parameter" style="color:#3A95FF">commitWithin</span></dt>     <dd>      <p class="para">        Number of milliseconds within which to auto commit this document. Available since Solr 1.4 . Default (0) means disabled.      </p>      <p class="para">        When this value specified, it leaves the control of when to do the commit        to Solr itself, optimizing number of commits to a minimum while still        fulfilling        the update latency requirements, and Solr will automatically do a commit        when the oldest add in the buffer is due.       </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-solrclient.adddocuments-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">    Returns a <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrupdateresponse.php" class="type SolrUpdateResponse" style="color:#EAB766">SolrUpdateResponse</a></span> object or throws an exception on failure.  </p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 errors" id="refsect1-solrclient.adddocuments-errors">  <h3 class="title">错误／异常</h3>  <p class="para">   Throws <span class="classname"><a href="https://www.php.net/manual/zh/class.solrclientexception.php" class="classname">SolrClientException</a></span> if the client had failed, or there was a connection issue.  </p>  <p class="para">   Throws <span class="classname"><a href="https://www.php.net/manual/zh/class.solrserverexception.php" class="classname">SolrServerException</a></span> if the Solr Server had failed to process the request.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-solrclient.adddocuments-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="">    <p><strong>示例 #1 <span class="methodname" style="color:#CC7832"><strong>SolrClient::addDocuments()</strong></span> example</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /><br />$options </span><span style="color: #007700">= array<br />(<br />    </span><span style="color: #DD0000">'hostname' </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">SOLR_SERVER_HOSTNAME</span><span style="color: #007700">,<br />    </span><span style="color: #DD0000">'login'    </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">SOLR_SERVER_USERNAME</span><span style="color: #007700">,<br />    </span><span style="color: #DD0000">'password' </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">SOLR_SERVER_PASSWORD</span><span style="color: #007700">,<br />    </span><span style="color: #DD0000">'port'     </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">SOLR_SERVER_PORT</span><span style="color: #007700">,<br />);<br /><br /></span><span style="color: #9876AA">$client </span><span style="color: #007700">= new </span><span style="color: #9876AA">SolrClient</span><span style="color: #007700">(</span><span style="color: #9876AA">$options</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$doc </span><span style="color: #007700">= new </span><span style="color: #9876AA">SolrInputDocument</span><span style="color: #007700">();<br /><br /></span><span style="color: #9876AA">$doc</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">addField</span><span style="color: #007700">(</span><span style="color: #DD0000">'id'</span><span style="color: #007700">, </span><span style="color: #9876AA">334455</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$doc</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">addField</span><span style="color: #007700">(</span><span style="color: #DD0000">'cat'</span><span style="color: #007700">, </span><span style="color: #DD0000">'Software'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$doc</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">addField</span><span style="color: #007700">(</span><span style="color: #DD0000">'cat'</span><span style="color: #007700">, </span><span style="color: #DD0000">'Lucene'</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$doc2 </span><span style="color: #007700">= clone </span><span style="color: #9876AA">$doc</span><span style="color: #007700">;<br /><br /></span><span style="color: #9876AA">$doc2</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">deleteField</span><span style="color: #007700">(</span><span style="color: #DD0000">'id'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$doc2</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">addField</span><span style="color: #007700">(</span><span style="color: #DD0000">'id'</span><span style="color: #007700">, </span><span style="color: #9876AA">334456</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$docs </span><span style="color: #007700">= array(</span><span style="color: #9876AA">$doc</span><span style="color: #007700">, </span><span style="color: #9876AA">$doc2</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$updateResponse </span><span style="color: #007700">= </span><span style="color: #9876AA">$client</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">addDocuments</span><span style="color: #007700">(</span><span style="color: #9876AA">$docs</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// no changes will be written to disk unless $commitWithin is passed or SolrClient::commit is called<br /><br /></span><span style="color: #9876AA">print_r</span><span style="color: #007700">(</span><span style="color: #9876AA">$updateResponse</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">getResponse</span><span style="color: #007700">());<br /><br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>    <div class="example-contents"><p>以上示例的输出类似于：</p></div>    <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>SolrObject Object<br>(<br>    [responseHeader] =&gt; SolrObject Object<br>        (<br>            [status] =&gt; 0<br>            [QTime] =&gt; 2<br>        )<br><br>)<br><br></span></div>    </div>   </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-solrclient.adddocuments-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="methodname" style="color:#CC7832">{@link SolrClient::addDocument()} - Adds a document to the index</span></li>    <li class="member"><span class="methodname" style="color:#CC7832">{@link SolrClient::commit()} - Finalizes all add/deletes made to the index</span></li>   </ul>  </span> </div></div>
     * @return SolrUpdateResponse <p>*/
    public function addDocuments(array $docs, $overwrite = true, $commitWithin = 0) {}

/**
*<div id="solrclient.commit" class="refentry"> <div class="refnamediv">  <h1 class="refname">SolrClient::commit</h1>  <p class="verinfo">(PECL solr &gt;= 0.9.2)</p><p class="refpurpose"><span class="refname">SolrClient::commit</span> &mdash; <span class="dc-title">Finalizes all add/deletes made to the index</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-solrclient.commit-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>SolrClient::commit</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">bool</span> <span class="parameter" style="color:#3A95FF">$softCommit</span><span class="initializer"> = <strong><span>false</span></strong></span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">bool</span> <span class="parameter" style="color:#3A95FF">$waitSearcher</span><span class="initializer"> = <strong><span>true</span></strong></span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">bool</span> <span class="parameter" style="color:#3A95FF">$expungeDeletes</span><span class="initializer"> = <strong><span>false</span></strong></span></span>): <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrupdateresponse.php" class="type SolrUpdateResponse" style="color:#EAB766">SolrUpdateResponse</a></span></div>  <p class="para rdfs-comment">   This method finalizes all add/deletes made to the index.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-solrclient.commit-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">softCommit</span></dt>     <dd>      <p class="para">       This will refresh the &#039;view&#039; of the index in a more performant manner, but without &quot;on-disk&quot; guarantees. (Solr4.0+)      </p>      <p class="para">          A soft commit is much faster since it only makes index changes visible and does not fsync index files or write a new index descriptor.           If the JVM crashes or there is a loss of power, changes that occurred after the last hard commit will be lost.           Search collections that have near-real-time requirements (that want index changes to be quickly visible to searches) will want to soft commit often but hard commit less frequently.       </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">waitSearcher</span></dt>     <dd>      <p class="para">       block until a new searcher is opened and registered as the main query searcher, making the changes visible.       </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">expungeDeletes</span></dt>     <dd>      <p class="para">       Merge segments with deletes away. (Solr1.4+)      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-solrclient.commit-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   Returns a <span class="classname"><a href="https://www.php.net/manual/zh/class.solrupdateresponse.php" class="classname">SolrUpdateResponse</a></span> object on success or throws an exception on failure.  </p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 errors" id="refsect1-solrclient.commit-errors">  <h3 class="title">错误／异常</h3>  <p class="para">   Throws <span class="classname"><a href="https://www.php.net/manual/zh/class.solrclientexception.php" class="classname">SolrClientException</a></span> if the client had failed, or there was a connection issue.  </p>  <p class="para">   Throws <span class="classname"><a href="https://www.php.net/manual/zh/class.solrserverexception.php" class="classname">SolrServerException</a></span> if the Solr Server had failed to process the request.  </p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 changelog" id="refsect1-solrclient.commit-changelog">  <h3 class="title">更新日志</h3>  <span>   <table class="doctable informaltable">         <thead>      <tr>       <th>版本</th>       <th>说明</th>      </tr>     </thead>     <tbody class="tbody">      <tr>       <td>PECL solr 1.1.0, 2.0.0</td>       <td>        $maxSegments removed       </td>      </tr>      <tr>       <td>PECL solr 2.0.0b</td>       <td>        API Changed: SolrClient::commit ([ int $maxSegments = 0 [, bool $softCommit = false [, bool $waitSearcher = true[, bool $expungeDeletes = false ]]] )       </td>      </tr>            <tr>       <td>PECL solr 0.9.2</td>       <td>        Signature: SolrClient::commit ([ int $maxSegments = 1 [, bool $waitFlush = true [, bool $waitSearcher = true ]]] ).         $waitFlush: Block until index changes are flushed to disk.       </td>      </tr>     </tbody>       </table>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-solrclient.commit-notes">  <h3 class="title">注释</h3>  <div class="warning"><strong class="warning">警告</strong>   <p class="para">        PECL Solr &gt;= 2.0 only supports Solr Server &gt;= 4.0    </p>  </div> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-solrclient.commit-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="methodname" style="color:#CC7832">{@link SolrClient::optimize()} - Defragments the index</span></li>    <li class="member"><span class="methodname" style="color:#CC7832">{@link SolrClient::rollback()} - Rollbacks all add/deletes made to the index since the last commit</span></li>   </ul>  </span> </div></div>
     * @return SolrUpdateResponse <p>*/
    public function commit($softCommit = false, $waitSearcher = true, $expungeDeletes = false) {}

    /**
     * (PECL solr &gt;= 0.9.2)<br/>
     * SolrClient constructor.
     * @link https://php.net/manual/en/solrclient.construct.php
     * @param array $clientOptions <p>
     * This is an array containing one of the following keys :
     * </p>
     * <ul>
     * <li><b>secure</b>: (Boolean value indicating whether or not to connect in secure mode)</li>
     * <li><b>hostname</b>: (The hostname for the Solr server)</li>
     * <li><b>port</b>: (The port number)</li>
     * <li><b>path</b>: (The path to solr)</li>
     * <li><b>wt</b>: (The name of the response writer e.g. xml, json)</li>
     * <li><b>login</b>: (The username used for HTTP Authentication, if any)</li>
     * <li><b>password</b>: (The HTTP Authentication password)</li>
     * <li><b>proxy_host</b>: (The hostname for the proxy server, if any)</li>
     * <li><b>proxy_port</b>: (The proxy port)</li>
     * <li><b>proxy_login</b>: (The proxy username)</li>
     * <li><b>proxy_password</b>: (The proxy password)</li>
     * <li><b>timeout</b>: (This is maximum time in seconds allowed for the http data transfer operation. Default is 30
     * seconds)</li>
     * <li><b>ssl_cert</b>: (File name to a PEM-formatted file containing the private key + private certificate
     * (concatenated in that order) )</li>
     * <li><b>ssl_key</b>: (File name to a PEM-formatted private key file only)</li>
     * <li><b>ssl_keypassword</b>: (Password for private key)</li>
     * <li><b>ssl_cainfo</b>: (Name of file holding one or more CA certificates to verify peer with)</li>
     * <li><b>ssl_capath</b>: (Name of directory holding multiple CA certificates to verify peer with )</li>
     * </ul>
     * <p>
     * Please note the if the ssl_cert file only contains the private certificate, you have to specify a separate
     * ssl_key file.
     * </p>
     * <p>
     * The ssl_keypassword option is required if the ssl_cert or ssl_key options are set.
     * </p>
     * @throws SolrIllegalArgumentException
     */
    public function __construct(array $clientOptions) {}

/**
*<div id="solrclient.deletebyid" class="refentry"> <div class="refnamediv">  <h1 class="refname">SolrClient::deleteById</h1>  <p class="verinfo">(PECL solr &gt;= 0.9.2)</p><p class="refpurpose"><span class="refname">SolrClient::deleteById</span> &mdash; <span class="dc-title">Delete by Id</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-solrclient.deletebyid-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>SolrClient::deleteById</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$id</span></span>): <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrupdateresponse.php" class="type SolrUpdateResponse" style="color:#EAB766">SolrUpdateResponse</a></span></div>  <p class="para rdfs-comment">   Deletes the document with the specified ID. Where ID is the value of the uniqueKey field declared in the schema  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-solrclient.deletebyid-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">id</span></dt>     <dd>      <p class="para">       The value of the uniqueKey field declared in the schema      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-solrclient.deletebyid-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   Returns a <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrupdateresponse.php" class="type SolrUpdateResponse" style="color:#EAB766">SolrUpdateResponse</a></span> on success and throws an exception on failure.  </p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 errors" id="refsect1-solrclient.deletebyid-errors">  <h3 class="title">错误／异常</h3>  <p class="para">   Throws <span class="classname"><a href="https://www.php.net/manual/zh/class.solrclientexception.php" class="classname">SolrClientException</a></span> if the client had failed, or there was a connection issue.  </p>  <p class="para">   Throws <span class="classname"><a href="https://www.php.net/manual/zh/class.solrserverexception.php" class="classname">SolrServerException</a></span> if the Solr Server had failed to process the request.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-solrclient.deletebyid-seealso">  <h3 class="title">参见</h3>  <span>   <ul class="simplelist">    <li class="member"><span class="methodname" style="color:#CC7832">{@link SolrClient::deleteByIds()} - Deletes by Ids</span></li>    <li class="member"><span class="methodname" style="color:#CC7832">{@link SolrClient::deleteByQuery()} - Deletes all documents matching the given query</span></li>    <li class="member"><span class="methodname" style="color:#CC7832">{@link SolrClient::deleteByQueries()} - Removes all documents matching any of the queries</span></li>   </ul>  </span> </div></div>
     * @return SolrUpdateResponse <p>*/
    public function deleteById($id) {}

