/**
*<div id="class.solrutils" class="reference"> <h1 class="title">The SolrUtils class</h1>  <div class="partintro"><p class="verinfo">(PECL solr &gt;= 0.9.2)</p>  <div class="section" id="solrutils.intro">   <h2 class="title">简介</h2>   <p class="para">    Contains utility methods for retrieving the current extension version and preparing query phrases.   </p>   <p class="para">Also contains method for escaping query strings and parsing XML responses.</p>  </div>  <div class="section" id="solrutils.synopsis">   <h2 class="title">类摘要</h2>   <div class="classsynopsis">    <span class="ooclass"><strong class="classname"></strong></span>    <div class="classsynopsisinfo">     <span class="ooclass">      <span class="modifier">abstract</span>      <span class="modifier">class</span> <strong class="classname">SolrUtils</strong>     </span>     {</div>        <div class="classsynopsisinfo classsynopsisinfo_comment">// 方法 </div>    <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="modifier">static</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrutils.digestxmlresponse.php" class="methodname" style="color:#CC7832">digestXmlResponse</a></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$xmlresponse</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$parse_mode</span><span class="initializer"> = 0</span></span>): <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrobject.php" class="type SolrObject" style="color:#EAB766">SolrObject</a></span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="modifier">static</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrutils.escapequerychars.php" class="methodname" style="color:#CC7832">escapeQueryChars</a></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$str</span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">string</span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="modifier">static</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrutils.getsolrversion.php" class="methodname" style="color:#CC7832">getSolrVersion</a></span>(): <span class="type" style="color:#EAB766">string</span></div><div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="modifier">static</span> <span class="methodname" style="color:#CC7832"><a href="https://www.php.net/manual/zh/solrutils.queryphrase.php" class="methodname" style="color:#CC7832">queryPhrase</a></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$str</span></span>): <span class="type" style="color:#EAB766">string</span></div>   }</div>  </div> </div> <h2>目录</h2><ul class="chunklist chunklist_reference"><li>{@link SolrUtils::digestXmlResponse} — Parses an response XML string into a SolrObject</li><li>{@link SolrUtils::escapeQueryChars} — Escapes a lucene query string</li><li>{@link SolrUtils::getSolrVersion} — Returns the current version of the Solr extension</li><li>{@link SolrUtils::queryPhrase} — Prepares a phrase from an unescaped lucene string</li></ul></div>
*/
abstract class SolrUtils
{
/**
*<div id="solrutils.digestxmlresponse" class="refentry"> <div class="refnamediv">  <h1 class="refname">SolrUtils::digestXmlResponse</h1>  <p class="verinfo">(PECL solr &gt;= 0.9.2)</p><p class="refpurpose"><span class="refname">SolrUtils::digestXmlResponse</span> &mdash; <span class="dc-title">Parses an response XML string into a SolrObject</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-solrutils.digestxmlresponse-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="modifier">static</span> <span class="methodname" style="color:#CC7832"><strong>SolrUtils::digestXmlResponse</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$xmlresponse</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">$parse_mode</span><span class="initializer"> = 0</span></span>): <span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.solrobject.php" class="type SolrObject" style="color:#EAB766">SolrObject</a></span></div>  <p class="para rdfs-comment">   This method parses an response XML string from the Apache Solr server into a SolrObject. It throws a SolrException if there was an error.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-solrutils.digestxmlresponse-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">xmlresponse</span></dt>     <dd>      <p class="para">       The XML response string from the Solr server.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">parse_mode</span></dt>     <dd>      <p class="para">       Use SolrResponse::PARSE_SOLR_OBJ or SolrResponse::PARSE_SOLR_DOC      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-solrutils.digestxmlresponse-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   Returns the SolrObject representing the XML response.  </p>    <p class="para">If the parse_mode parameter is set to SolrResponse::PARSE_SOLR_OBJ Solr documents will be parses as SolrObject instances.</p>   <p class="para">If it is set to SolrResponse::PARSE_SOLR_DOC, they will be parsed as SolrDocument instances.</p>   </div></div>
     * @return SolrObject <p>*/
    public static function digestXmlResponse($xmlresponse, $parse_mode = 0) {}

/**
*<div id="solrutils.escapequerychars" class="refentry"> <div class="refnamediv">  <h1 class="refname">SolrUtils::escapeQueryChars</h1>  <p class="verinfo">(PECL solr &gt;= 0.9.2)</p><p class="refpurpose"><span class="refname">SolrUtils::escapeQueryChars</span> &mdash; <span class="dc-title">Escapes a lucene query string</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-solrutils.escapequerychars-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="modifier">static</span> <span class="methodname" style="color:#CC7832"><strong>SolrUtils::escapeQueryChars</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$str</span></span>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">string</span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>  <p class="para rdfs-comment">   Lucene supports escaping special characters that are part of the query syntax.  </p>  <p class="para">The current list special characters are:</p>  <p class="para"><div class="descriptioncode"><span>+ - &amp;&amp; || ! ( ) { } [ ] ^ &quot; ~ * ? : \ /</span></div></p>  <p class="para">These characters are part of the query syntax and must be escaped</p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-solrutils.escapequerychars-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">str</span></dt>     <dd>      <p class="para">       This is the query string to be escaped.      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-solrutils.escapequerychars-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">  Returns the escaped string 或者在失败时返回 <strong><span>false</span></strong>.  </p> </div></div>
     * @return string|false <p>*/
    public static function escapeQueryChars($str) {}

/**
*<div id="solrutils.getsolrversion" class="refentry"> <div class="refnamediv">  <h1 class="refname">SolrUtils::getSolrVersion</h1>  <p class="verinfo">(PECL solr &gt;= 0.9.2)</p><p class="refpurpose"><span class="refname">SolrUtils::getSolrVersion</span> &mdash; <span class="dc-title">Returns the current version of the Solr extension</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-solrutils.getsolrversion-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="modifier">static</span> <span class="methodname" style="color:#CC7832"><strong>SolrUtils::getSolrVersion</strong></span>(): <span class="type" style="color:#EAB766">string</span></div>  <p class="para rdfs-comment">   Returns the current Solr version.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-solrutils.getsolrversion-parameters">  <h3 class="title">参数</h3>  <span>此函数没有参数。</span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-solrutils.getsolrversion-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   The current version of the Apache Solr extension.  </p> </div></div>
     * @return string <p>*/
    public static function getSolrVersion() {}

/**
*<div id="solrutils.queryphrase" class="refentry"> <div class="refnamediv">  <h1 class="refname">SolrUtils::queryPhrase</h1>  <p class="verinfo">(PECL solr &gt;= 0.9.2)</p><p class="refpurpose"><span class="refname">SolrUtils::queryPhrase</span> &mdash; <span class="dc-title">Prepares a phrase from an unescaped lucene string</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-solrutils.queryphrase-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description"><span class="modifier">public</span> <span class="modifier">static</span> <span class="methodname" style="color:#CC7832"><strong>SolrUtils::queryPhrase</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$str</span></span>): <span class="type" style="color:#EAB766">string</span></div>  <p class="para rdfs-comment">   Prepares a phrase from an unescaped lucene string.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-solrutils.queryphrase-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">str</span></dt>     <dd>      <p class="para">       The lucene phrase.      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-solrutils.queryphrase-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   Returns the phrase contained in double quotes.  </p> </div></div>
     * @return string <p>*/
    public static function queryPhrase($str) {}
}
