<?php

use JetBrains\PhpStorm\ArrayShape;
use JetBrains\PhpStorm\Deprecated;
use JetBrains\PhpStorm\Internal\LanguageLevelTypeAware;
use JetBrains\PhpStorm\Internal\PhpStormStubsElementAvailable;
use JetBrains\PhpStorm\Pure;

/**
*<div id="function.stream-select" class="refentry"> <div class="refnamediv">  <h1 class="refname">stream_select</h1>  <p class="verinfo">(PHP 4 &gt;= 4.3.0, PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">stream_select</span> &mdash; <span class="dc-title">Runs the equivalent of the select() system call on the given   arrays of streams with a timeout specified by seconds and microseconds</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.stream-select-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>stream_select</strong></span>(<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">array</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">&$read</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">array</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">&$write</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">array</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">&$except</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">int</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">$seconds</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">int</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">$microseconds</span><span class="initializer"> = <strong><span>null</span></strong></span></span><br>): <span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">int</span>|<span class="type" style="color:#EAB766"><span class="type false" style="color:#EAB766">false</span></span></span></div>  <p class="para rdfs-comment">   The <span class="function"><strong style="color:#CC7832">stream_select()</strong></span> function accepts arrays of streams and   waits for them to change status. Its operation is equivalent to that of   the <span class="function">{@link socket_select()}</span> function except in that it acts on streams.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.stream-select-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">read</span></dt>     <dd>      <p class="para">       The streams listed in the <span class="parameter" style="color:#3A95FF">read</span> array will be watched to       see if characters become available for reading (more precisely, to see if       a read will not block - in particular, a stream resource is also ready on       end-of-file, in which case an <span class="function">{@link fread()}</span> will return       a zero length string).      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">write</span></dt>     <dd>      <p class="para">       The streams listed in the <span class="parameter" style="color:#3A95FF">write</span> array will be       watched to see if a write will not block.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">except</span></dt>     <dd>      <p class="para">       The streams listed in the <span class="parameter" style="color:#3A95FF">except</span> array will be       watched for high priority exceptional (&quot;out-of-band&quot;) data arriving.      </p>      <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:        <p class="para">        When <span class="function"><strong style="color:#CC7832">stream_select()</strong></span> returns, the arrays        <span class="parameter" style="color:#3A95FF">read</span>, <span class="parameter" style="color:#3A95FF">write</span> and        <span class="parameter" style="color:#3A95FF">except</span> are modified to indicate which stream        resource(s) actually changed status.        The original keys of the <span class="type" style="color:#EAB766">array</span>s are preserved.       </p>      </p></blockquote>     </dd>             <dt><span class="parameter" style="color:#3A95FF">seconds</span></dt>     <dd>      <p class="para">       The <span class="parameter" style="color:#3A95FF">seconds</span> and <span class="parameter" style="color:#3A95FF">microseconds</span>       together form the <em>timeout</em> parameter,       <span class="parameter" style="color:#3A95FF">seconds</span> specifies the number of seconds while       <span class="parameter" style="color:#3A95FF">microseconds</span> the number of microseconds.       The <span class="parameter" style="color:#3A95FF">timeout</span> is an upper bound on the amount of time       that <span class="function"><strong style="color:#CC7832">stream_select()</strong></span> will wait before it returns.       If <span class="parameter" style="color:#3A95FF">seconds</span> and <span class="parameter" style="color:#3A95FF">microseconds</span> are       both set to <span>0</span>, <span class="function"><strong style="color:#CC7832">stream_select()</strong></span> will       not wait for data - instead it will return immediately, indicating the       current status of the streams.      </p>      <p class="para">       If <span class="parameter" style="color:#3A95FF">seconds</span> is <strong><span>null</span></strong> <span class="function"><strong style="color:#CC7832">stream_select()</strong></span>       can block indefinitely, returning only when an event on one of the       watched streams occurs (or if a signal interrupts the system call).      </p>      <div class="warning"><strong class="warning">警告</strong>       <p class="para">        Using a timeout value of <span>0</span> allows you to        instantaneously poll the status of the streams, however, it is NOT a        good idea to use a <span>0</span> timeout value in a loop as it        will cause your script to consume too much CPU time.       </p>       <p class="para">        It is much better to specify a timeout value of a few seconds, although        if you need to be checking and running other code concurrently, using a        timeout value of at least <span>200000</span> microseconds will        help reduce the CPU usage of your script.       </p>       <p class="para">        Remember that the timeout value is the maximum time that will elapse;        <span class="function"><strong style="color:#CC7832">stream_select()</strong></span> will return as soon as the        requested streams are ready for use.       </p>      </div>     </dd>             <dt><span class="parameter" style="color:#3A95FF">microseconds</span></dt>     <dd>      <p class="para">       See <span class="parameter" style="color:#3A95FF">seconds</span> description.      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.stream-select-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   On success <span class="function"><strong style="color:#CC7832">stream_select()</strong></span> returns the number of   stream resources contained in the modified arrays, which may be zero if   the timeout expires before anything interesting happens. On error <strong><span>false</span></strong>   is returned and a warning raised (this can happen if the system call is   interrupted by an incoming signal).  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 changelog" id="refsect1-function.stream-select-changelog">  <h3 class="title">更新日志</h3>  <table class="doctable informaltable">       <thead>     <tr>      <th>版本</th>      <th>说明</th>     </tr>    </thead>    <tbody class="tbody">     <tr>      <td>8.1.0</td>      <td>       <span class="parameter" style="color:#3A95FF">microseconds</span> is now nullable.      </td>     </tr>    </tbody>     </table> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.stream-select-examples">  <h3 class="title">示例</h3>  <span>   <div class="example" id="">    <p><strong>示例 #1 <span class="function"><strong style="color:#CC7832">stream_select()</strong></span> Example</strong></p>    <div class="example-contents"><p>     This example checks to see if data has arrived for reading on either     <span class="parameter" style="color:#3A95FF">$stream1</span> or <span class="parameter" style="color:#3A95FF">$stream2</span>.     Since the timeout value is <span>0</span> it will return     immediately:    </p></div>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /></span><span style="color: #FF8000">// Prepare the read array <br /></span><span style="color: #9876AA">$read   </span><span style="color: #007700">= array(</span><span style="color: #9876AA">$stream1</span><span style="color: #007700">, </span><span style="color: #9876AA">$stream2</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$write  </span><span style="color: #007700">= </span><span style="color: #9876AA">NULL</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">$except </span><span style="color: #007700">= </span><span style="color: #9876AA">NULL</span><span style="color: #007700">;<br />if (</span><span style="color: #9876AA">false </span><span style="color: #007700">=== (</span><span style="color: #9876AA">$num_changed_streams </span><span style="color: #007700">= </span><span style="color: #9876AA">stream_select</span><span style="color: #007700">(</span><span style="color: #9876AA">$read</span><span style="color: #007700">, </span><span style="color: #9876AA">$write</span><span style="color: #007700">, </span><span style="color: #9876AA">$except</span><span style="color: #007700">, </span><span style="color: #9876AA">0</span><span style="color: #007700">))) {<br />    </span><span style="color: #FF8000">// Error handling <br /></span><span style="color: #007700">} elseif (</span><span style="color: #9876AA">$num_changed_streams </span><span style="color: #007700">&gt; </span><span style="color: #9876AA">0</span><span style="color: #007700">) {<br />    </span><span style="color: #FF8000">// At least on one of the streams something interesting happened <br /></span><span style="color: #007700">}<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>    </div>   </div>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 notes" id="refsect1-function.stream-select-notes">  <h3 class="title">注释</h3>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    Due to a limitation in the current Zend Engine it is not possible to pass a    constant modifier like <strong><span>null</span></strong> directly as a parameter to a function    which expects this parameter to be passed by reference. Instead use a    temporary variable or an expression with the leftmost member being a    temporary variable:    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />$e </span><span style="color: #007700">= </span><span style="color: #9876AA">NULL</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">stream_select</span><span style="color: #007700">(</span><span style="color: #9876AA">$r</span><span style="color: #007700">, </span><span style="color: #9876AA">$w</span><span style="color: #007700">, </span><span style="color: #9876AA">$e</span><span style="color: #007700">, </span><span style="color: #9876AA">0</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>   </div>   </p>  </p></blockquote>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">   Be sure to use the <span>===</span> operator when checking for an   error. Since the <span class="function"><strong style="color:#CC7832">stream_select()</strong></span> may return 0 the   comparison with <span>==</span> would evaluate to <strong><span>true</span></strong>:   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />$e </span><span style="color: #007700">= </span><span style="color: #9876AA">NULL</span><span style="color: #007700">;<br />if (</span><span style="color: #9876AA">false </span><span style="color: #007700">=== </span><span style="color: #9876AA">stream_select</span><span style="color: #007700">(</span><span style="color: #9876AA">$r</span><span style="color: #007700">, </span><span style="color: #9876AA">$w</span><span style="color: #007700">, </span><span style="color: #9876AA">$e</span><span style="color: #007700">, </span><span style="color: #9876AA">0</span><span style="color: #007700">)) {<br />    echo </span><span style="color: #DD0000">"stream_select() failed\n"</span><span style="color: #007700">;<br />}<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>   </div>   </p>  </p></blockquote>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    If you read/write to a stream returned in the arrays be aware that    they do not necessarily read/write the full amount of data you have    requested. Be prepared to even only be able to read/write a single    byte.   </p>  </p></blockquote>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    Some streams (like <span>zlib</span>) cannot be selected by this    function.   </p>  </p></blockquote>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <strong>Windows compatibility</strong><br />   <p class="para">    Use of <span class="function"><strong style="color:#CC7832">stream_select()</strong></span> on    file descriptors returned by <span class="function">{@link proc_open()}</span> will fail    and return <strong><span>false</span></strong> under Windows.   </p>   <p class="para">    <strong><span>STDIN</span></strong> from a console changes status as soon as <em>any</em>    input events are available, but reading from the stream may still block.   </p>  </p></blockquote> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.stream-select-seealso">  <h3 class="title">参见</h3>  <ul class="simplelist">   <li class="member"><span class="function">{@link stream_set_blocking()} - 为资源流设置阻塞或者阻塞模式</span></li>  </ul> </div></div>
 * @return int|false On success stream_select returns the number of*/
function stream_select(
    ?array &$read,
    ?array &$write,
    ?array &$except,
    ?int $seconds,
    #[LanguageLevelTypeAware(['8.1' => 'int|null'], default: 'int')] $microseconds
): int|false {}

/**
*<div id="function.stream-context-create" class="refentry"> <div class="refnamediv">  <h1 class="refname">stream_context_create</h1>  <p class="verinfo">(PHP 4 &gt;= 4.3.0, PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">stream_context_create</span> &mdash; <span class="dc-title">创建资源流上下文</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.stream-context-create-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>stream_context_create</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">array</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">$options</span><span class="initializer"> = <strong><span>null</span></strong></span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">?</span><span class="type" style="color:#EAB766"><span class="type" style="color:#EAB766">array</span><span class="type" style="color:#EAB766"></span></span> <span class="parameter" style="color:#3A95FF">$params</span><span class="initializer"> = <strong><span>null</span></strong></span></span>): <span class="type" style="color:#EAB766">resource</span></div>  <p class="para rdfs-comment">   创建并返回一个资源流上下文，该资源流中包含了 <span class="parameter" style="color:#3A95FF">options</span> 中提前设定的所有参数的值。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.stream-context-create-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">options</span></dt>     <dd>      <p class="para">       必须是一个二维关联数组或 <strong><span>null</span></strong>，二维关联数组格式如下：<span>$arr[&#039;wrapper&#039;][&#039;option&#039;] = $value</span>。请参考       <a href="https://www.php.net/manual/zh/context.php" class="link">上下文（Context）选项</a> 中可用的封装协议和选项列表。      </p>      <p class="para">       默认为 <strong><span>null</span></strong>。      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">params</span></dt>     <dd>      <p class="para">       必须是 <span>$arr[&#039;parameter&#039;] = $value</span> 格式的关联数组或 <strong><span>null</span></strong>。       请参考 <a href="https://www.php.net/manual/zh/context.params.php" class="link">上下文（Context）参数</a> 里的标准资源流参数列表。      </p>     </dd>       </dl>  </span> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.stream-context-create-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   上下文资源流，类型为 <span class="type" style="color:#EAB766">resource</span> 。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 changelog" id="refsect1-function.stream-context-create-changelog">  <h3 class="title">更新日志</h3>  <table class="doctable informaltable">       <thead>     <tr>      <th>版本</th>      <th>说明</th>     </tr>    </thead>    <tbody class="tbody">     <tr>      <td>8.0.0</td>      <td>       现在 <span class="parameter" style="color:#3A95FF">options</span> 和 <span class="parameter" style="color:#3A95FF">params</span> 可以为 null。      </td>     </tr>    </tbody>     </table> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.stream-context-create-examples">  <h3 class="title">示例</h3>  <div class="example" id="">   <p><strong>示例 #1 使用 <span class="function"><strong style="color:#CC7832">stream_context_create()</strong></span></strong></p>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />$opts </span><span style="color: #007700">= array(<br />  </span><span style="color: #DD0000">'http'</span><span style="color: #007700">=&gt;array(<br />    </span><span style="color: #DD0000">'method'</span><span style="color: #007700">=&gt;</span><span style="color: #DD0000">"GET"</span><span style="color: #007700">,<br />    </span><span style="color: #DD0000">'header'</span><span style="color: #007700">=&gt;</span><span style="color: #DD0000">"Accept-language: en\r\n" </span><span style="color: #007700">.<br />              </span><span style="color: #DD0000">"Cookie: foo=bar\r\n"<br />  </span><span style="color: #007700">)<br />);<br /><br /></span><span style="color: #9876AA">$context </span><span style="color: #007700">= </span><span style="color: #9876AA">stream_context_create</span><span style="color: #007700">(</span><span style="color: #9876AA">$opts</span><span style="color: #007700">);<br /><br /></span><span style="color: #FF8000">// 包含上面的 header 头，向 www.example.com<br />   发送 HTTP 请求 <br /></span><span style="color: #9876AA">$fp </span><span style="color: #007700">= </span><span style="color: #9876AA">fopen</span><span style="color: #007700">(</span><span style="color: #DD0000">'http://www.example.com'</span><span style="color: #007700">, </span><span style="color: #DD0000">'r'</span><span style="color: #007700">, </span><span style="color: #9876AA">false</span><span style="color: #007700">, </span><span style="color: #9876AA">$context</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">fpassthru</span><span style="color: #007700">(</span><span style="color: #9876AA">$fp</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">fclose</span><span style="color: #007700">(</span><span style="color: #9876AA">$fp</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>   </div>  </div> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.stream-context-create-seealso">  <h3 class="title">参见</h3>  <ul class="simplelist">   <li class="member"><span class="function">{@link stream_context_set_option()} - 对资源流、数据包或者上下文设置参数</span></li>   <li class="member">支持的封装协议列表（<a href="https://www.php.net/manual/zh/wrappers.php" class="xref">支持的协议和封装协议</a>）</li>   <li class="member">上下文选项（<a href="https://www.php.net/manual/zh/context.php" class="xref">上下文（Context）选项和参数</a>）</li>  </ul> </div></div>
 * @return resource A stream context resource.*/
function stream_context_create(?array $options, ?array $params) {}

/**
*<div id="function.stream-context-set-params" class="refentry"> <div class="refnamediv">  <h1 class="refname">stream_context_set_params</h1>  <p class="verinfo">(PHP 4 &gt;= 4.3.0, PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">stream_context_set_params</span> &mdash; <span class="dc-title">Set parameters for a stream/wrapper/context</span></p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.stream-context-set-params-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>stream_context_set_params</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">resource</span> <span class="parameter" style="color:#3A95FF">$context</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">array</span> <span class="parameter" style="color:#3A95FF">$params</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   Sets parameters on the specified context.  </p> </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.stream-context-set-params-parameters">  <h3 class="title">参数</h3>  <span>   <dl>         <dt><span class="parameter" style="color:#3A95FF">context</span></dt>     <dd>      <p class="para">       The stream or context to apply the parameters too.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">params</span></dt>     <dd>      <p class="para">       An associative array of parameters to be set in the following format:       <span>$params[&#039;paramname&#039;] = &quot;paramvalue&quot;;</span>.      </p>      <table class="doctable table">       <caption><strong>Supported parameters</strong></caption>               <thead>         <tr>  