<?php

// Start of uopz v5.0.2

/**
*  Mark function as public, the default      
*/
define('ZEND_ACC_PUBLIC', 1);

/**
*  Mark function as protected      
*/
define('ZEND_ACC_PROTECTED', 2);

/**
*   Mark function as private                             
*/
define('ZEND_ACC_PRIVATE', 4);

/**
*   Mark function as static                              
*/
define('ZEND_ACC_STATIC', 16);

/**
*    Mark function as final       
*/
define('ZEND_ACC_FINAL', 32);

/**
*  Mark function as abstract      
*/
define('ZEND_ACC_ABSTRACT', 64);

/**
*  Used for getting flags only.       Removed as of uopz 5.0.0.      
*/
define('ZEND_ACC_FETCH', PHP_INT_MAX);

/**
 * The bitmask of ZEND_ACC_PUBLIC | ZEND_ACC_PROTECTED | ZEND_ACC_PRIVATE
 */
define('ZEND_ACC_PPP_MASK', ZEND_ACC_PUBLIC|ZEND_ACC_PROTECTED|ZEND_ACC_PRIVATE);

/**
*<div id="function.uopz-add-function" class="refentry"> <div class="refnamediv">  <h1 class="refname">uopz_add_function</h1>  <p class="verinfo">(PECL uopz 5, PECL uopz 6, PECL uopz 7)</p><p class="refpurpose"><span class="refname">uopz_add_function</span> &mdash; <span class="dc-title">Adds non-existent function or method</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.uopz-add-function-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>uopz_add_function</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$function</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.closure.php" class="type Closure" style="color:#EAB766">Closure</a></span> <span class="parameter" style="color:#3A95FF">$handler</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">&$flags</span><span class="initializer"> = ZEND_ACC_PUBLIC</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <div class="methodsynopsis dc-description"><span class="methodname" style="color:#CC7832"><strong>uopz_add_function</strong></span>(<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$class</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$function</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.closure.php" class="type Closure" style="color:#EAB766">Closure</a></span> <span class="parameter" style="color:#3A95FF">$handler</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">&$flags</span><span class="initializer"> = ZEND_ACC_PUBLIC</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">int</span> <span class="parameter" style="color:#3A95FF">&$all</span><span class="initializer"> = <strong><span>true</span></strong></span></span><br>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   Adds a non-existent function or method.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.uopz-add-function-parameters">  <h3 class="title">参数</h3>  <dl>       <dt><span class="parameter" style="color:#3A95FF">class</span></dt>    <dd>     <span>      The name of the class.     </span>    </dd>          <dt><span class="parameter" style="color:#3A95FF">function</span></dt>    <dd>     <p class="para">      The name of the function or method.     </p>    </dd>          <dt><span class="parameter" style="color:#3A95FF">handler</span></dt>    <dd>     <p class="para">      The <span class="classname"><a href="https://www.php.net/manual/zh/class.closure.php" class="classname">Closure</a></span> that defines the new function or method.     </p>    </dd>          <dt><span class="parameter" style="color:#3A95FF">flags</span></dt>    <dd>     <p class="para">      Flags to set for the new function or method.     </p>    </dd>          <dt><span class="parameter" style="color:#3A95FF">all</span></dt>    <dd>     <p class="para">      Whether all classes that descend from <span class="parameter" style="color:#3A95FF">class</span> will      also be affected.     </p>    </dd>     </dl> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.uopz-add-function-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 errors" id="refsect1-function.uopz-add-function-errors">  <h3 class="title">错误／异常</h3>  <p class="para">   <span class="function"><strong style="color:#CC7832">uopz_add_function()</strong></span> throws a <span class="classname"><a href="https://www.php.net/manual/zh/class.runtimeexception.php" class="classname">RuntimeException</a></span>   if the function or method to add already exists.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.uopz-add-function-examples">  <h3 class="title">示例</h3>  <div class="example" id="example-1823">   <p><strong>示例 #1 Basic <span class="function"><strong style="color:#CC7832">uopz_add_function()</strong></span> Usage</strong></p>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />uopz_add_function</span><span style="color: #007700">(</span><span style="color: #DD0000">'foo'</span><span style="color: #007700">, function () {echo </span><span style="color: #DD0000">'bar'</span><span style="color: #007700">;});<br /></span><span style="color: #9876AA">foo</span><span style="color: #007700">();<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>   </div>   <div class="example-contents"><p>以上示例会输出：</p></div>   <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>bar<br></span></div>   </div>  </div> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.uopz-add-function-seealso">  <h3 class="title">参见</h3>  <ul class="simplelist">   <li class="member"><span class="function">{@link uopz_del_function()} - Deletes previously added function or method</span></li>   <li class="member"><span class="function">{@link uopz_set_return()} - Provide a return value for an existing function</span></li>  </ul> </div></div>
