function uopz_implement(...$arguments): bool {}

/**
*<div id="function.uopz-redefine" class="refentry"> <div class="refnamediv">  <h1 class="refname">uopz_redefine</h1>  <p class="verinfo">(PECL uopz 1, PECL uopz 2, PECL uopz 5, PECL uopz 6, PECL uopz 7)</p><p class="refpurpose"><span class="refname">uopz_redefine</span> &mdash; <span class="dc-title">Redefine a constant</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.uopz-redefine-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>uopz_redefine</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$constant</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/language.types.declarations.php#language.types.declarations.mixed" class="type mixed" style="color:#EAB766">mixed</a></span> <span class="parameter" style="color:#3A95FF">$value</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <div class="methodsynopsis dc-description"><span class="methodname" style="color:#CC7832"><strong>uopz_redefine</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$class</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$constant</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/language.types.declarations.php#language.types.declarations.mixed" class="type mixed" style="color:#EAB766">mixed</a></span> <span class="parameter" style="color:#3A95FF">$value</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment"> Redefines the given <span class="parameter" style="color:#3A95FF">constant</span> as <span class="parameter" style="color:#3A95FF">value</span>  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.uopz-redefine-parameters">  <h3 class="title">参数</h3>  <dl>       <dt><span class="parameter" style="color:#3A95FF">class</span></dt>    <dd>     <span> The name of the class containing the constant     </span>    </dd>          <dt><span class="parameter" style="color:#3A95FF">constant</span></dt>    <dd>     <p class="para"> The name of the constant     </p>    </dd>          <dt><span class="parameter" style="color:#3A95FF">value</span></dt>    <dd>     <p class="para"> The new value for the constant, must be a valid type for a constant variable     </p>    </dd>     </dl> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.uopz-redefine-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.uopz-redefine-examples">  <h3 class="title">示例</h3>  <div class="example" id="example-1886">   <p><strong>示例 #1 <span class="function"><strong style="color:#CC7832">uopz_redefine()</strong></span> example</strong></p>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />define</span><span style="color: #007700">(</span><span style="color: #DD0000">"MY"</span><span style="color: #007700">, </span><span style="color: #9876AA">100</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">uopz_redefine</span><span style="color: #007700">(</span><span style="color: #DD0000">"MY"</span><span style="color: #007700">, </span><span style="color: #9876AA">1000</span><span style="color: #007700">);<br /><br />echo </span><span style="color: #9876AA">MY</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>   </div>   <div class="example-contents"><p>以上示例会输出：</p></div>   <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>1000<br></span></div>   </div>  </div> </div></div>
 * @return bool*/
function uopz_redefine(...$arguments): bool {}

/**
*<div id="function.uopz-set-hook" class="refentry"> <div class="refnamediv">  <h1 class="refname">uopz_set_hook</h1>  <p class="verinfo">(PECL uopz 5, PECL uopz 6, PECL uopz 7)</p><p class="refpurpose"><span class="refname">uopz_set_hook</span> &mdash; <span class="dc-title">Sets hook to execute when entering a function or method</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.uopz-set-hook-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>uopz_set_hook</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$function</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.closure.php" class="type Closure" style="color:#EAB766">Closure</a></span> <span class="parameter" style="color:#3A95FF">$hook</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <div class="methodsynopsis dc-description"><span class="methodname" style="color:#CC7832"><strong>uopz_set_hook</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$class</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$function</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/class.closure.php" class="type Closure" style="color:#EAB766">Closure</a></span> <span class="parameter" style="color:#3A95FF">$hook</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   Sets a hook to execute when entering a function or method.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.uopz-set-hook-parameters">  <h3 class="title">参数</h3>  <dl>       <dt><span class="parameter" style="color:#3A95FF">class</span></dt>    <dd>     <span>      The name of the class.     </span>    </dd>          <dt><span class="parameter" style="color:#3A95FF">function</span></dt>    <dd>     <p class="para">      The name of the function or method.     </p>    </dd>          <dt><span class="parameter" style="color:#3A95FF">hook</span></dt>    <dd>     <p class="para">      A closure to execute when entering the function or method.     </p>    </dd>     </dl> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.uopz-set-hook-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span>true</span></strong>， 或者在失败时返回 <strong><span>false</span></strong>。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.uopz-set-hook-examples">  <h3 class="title">示例</h3>  <div class="example" id="example-1898">   <p><strong>示例 #1 Basic <span class="function"><strong style="color:#CC7832">uopz_set_hook()</strong></span> Usage</strong></p>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /></span><span style="color: #007700">function </span><span style="color: #9876AA">foo</span><span style="color: #007700">() {<br />    echo </span><span style="color: #DD0000">'foo'</span><span style="color: #007700">;<br />}<br /></span><span style="color: #9876AA">uopz_set_hook</span><span style="color: #007700">(</span><span style="color: #DD0000">'foo'</span><span style="color: #007700">, function () {echo </span><span style="color: #DD0000">'bar'</span><span style="color: #007700">;});<br /></span><span style="color: #9876AA">foo</span><span style="color: #007700">();<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>   </div>   <div class="example-contents"><p>以上示例会输出：</p></div>   <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>barfoo<br></span></div>   </div>  </div> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.uopz-set-hook-seealso">  <h3 class="title">参见</h3>  <ul class="simplelist">   <li class="member"><span class="function">{@link uopz_get_hook()} - Gets previously set hook on function or method</span></li>   <li class="member"><span class="function">{@link uopz_unset_hook()} - Removes previously set hook on function or method</span></li>  </ul> </div></div>
 * @return bool TRUE on success or FALSE on failure*/
function uopz_set_hook(...$arguments): bool {}

/**
*<div id="function.uopz-set-mock" class="refentry"> <div class="refnamediv">  <h1 class="refname">uopz_set_mock</h1>  <p class="verinfo">(PECL uopz 5, PECL uopz 6, PECL uopz 7)</p><p class="refpurpose"><span class="refname">uopz_set_mock</span> &mdash; <span class="dc-title">Use mock instead of class for new objects</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.uopz-set-mock-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>uopz_set_mock</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$class</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/language.types.declarations.php#language.types.declarations.mixed" class="type mixed" style="color:#EAB766">mixed</a></span> <span class="parameter" style="color:#3A95FF">$mock</span></span>): <span class="type" style="color:#EAB766"><span class="type void" style="color:#EAB766">void</span></span></div>  <p class="para rdfs-comment">   If <span class="parameter" style="color:#3A95FF">mock</span> is a string containing the name of a class then it will be instantiated instead of   <span class="parameter" style="color:#3A95FF">class</span>. <span class="parameter" style="color:#3A95FF">mock</span> can also be an object.  </p>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    Only dynamic access to properties and methods will use the <span class="parameter" style="color:#3A95FF">mock</span> object.    Static access still uses the original <span class="parameter" style="color:#3A95FF">class</span>.    See {@link example} below.   </p>  </p></blockquote> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.uopz-set-mock-parameters">  <h3 class="title">参数</h3>  <dl>       <dt><span class="parameter" style="color:#3A95FF">class</span></dt>    <dd>     <span>      The name of the class to be mocked.     </span>    </dd>          <dt><span class="parameter" style="color:#3A95FF">mock</span></dt>    <dd>     <p class="para">      The mock to use in the form of a string containing the name of the class to use or an object.      If a string is passed, it has to be the fully qualified class name. It is      recommended to use the <span>::class</span> magic constant in this case.     </p>    </dd>     </dl> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.uopz-set-mock-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   没有返回值。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 changelog" id="refsect1-function.uopz-set-mock-changelog">  <h3 class="title">更新日志</h3>  <table class="doctable informaltable">       <thead>     <tr>      <th>版本</th>      <th>说明</th>     </tr>    </thead>    <tbody class="tbody">     <tr>      <td>uopz 6.0.0</td>      <td>       Mocking static members is no longer supported by this function.       <span class="function">{@link uopz_redefine()}</span> and <span class="function">{@link uopz_set_return()}</span>,       or <a href="https://www.php.net/manual/zh/book.componere.php" class="link">Componere</a> can be used instead.      </td>     </tr>    </tbody>     </table> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.uopz-set-mock-examples">  <h3 class="title">示例</h3>  <div class="example" id="example-1901">   <p><strong>示例 #1 <span class="function"><strong style="color:#CC7832">uopz_set_mock()</strong></span> example</strong></p>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /></span><span style="color: #007700">class </span><span style="color: #9876AA">A </span><span style="color: #007700">{<br />    public function </span><span style="color: #9876AA">who</span><span style="color: #007700">() {<br />        echo </span><span style="color: #DD0000">"A"</span><span style="color: #007700">;<br />    }<br />}<br /><br />class </span><span style="color: #9876AA">mockA </span><span style="color: #007700">{<br />    public function </span><span style="color: #9876AA">who</span><span style="color: #007700">() {<br />        echo </span><span style="color: #DD0000">"mockA"</span><span style="color: #007700">;<br />    }<br />}<br /><br /></span><span style="color: #9876AA">uopz_set_mock</span><span style="color: #007700">(</span><span style="color: #9876AA">A</span><span style="color: #007700">::class, </span><span style="color: #9876AA">mockA</span><span style="color: #007700">::class);<br />(new </span><span style="color: #9876AA">A</span><span style="color: #007700">)-&gt;</span><span style="color: #9876AA">who</span><span style="color: #007700">();<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>   </div>   <div class="example-contents"><p>以上示例会输出：</p></div>   <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>mockA<br></span></div>   </div>  </div>  <div class="example" id="example-1904">   <p><strong>示例 #2 <span class="function"><strong style="color:#CC7832">uopz_set_mock()</strong></span> example</strong></p>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /></span><span style="color: #007700">class </span><span style="color: #9876AA">A </span><span style="color: #007700">{<br />    public function </span><span style="color: #9876AA">who</span><span style="color: #007700">() {<br />        echo </span><span style="color: #DD0000">"A"</span><span style="color: #007700">;<br />    }<br />}<br /><br /></span><span style="color: #9876AA">uopz_set_mock</span><span style="color: #007700">(</span><span style="color: #9876AA">A</span><span style="color: #007700">::class, new class {<br />                            public function </span><span style="color: #9876AA">who</span><span style="color: #007700">() {<br />                                echo </span><span style="color: #DD0000">"mockA"</span><span style="color: #007700">;<br />                            }<br />                        });<br />(new </span><span style="color: #9876AA">A</span><span style="color: #007700">)-&gt;</span><span style="color: #9876AA">who</span><span style="color: #007700">();<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>   </div>   <div class="example-contents"><p>以上示例会输出：</p></div>   <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>mockA<br></span></div>   </div>  </div>  <div class="example" id="example-1907">   <p><strong>示例 #3 <span class="function"><strong style="color:#CC7832">uopz_set_mock()</strong></span> and static members</strong></p>   <div class="example-contents"><p>    As of uopz 6.0.0 mocking static members is no longer supported.   </p></div>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /></span><span style="color: #007700">class </span><span style="color: #9876AA">A </span><span style="color: #007700">{<br />    const </span><span style="color: #9876AA">CON </span><span style="color: #007700">= </span><span style="color: #DD0000">'A'</span><span style="color: #007700">;<br />    public static function </span><span style="color: #9876AA">who</span><span style="color: #007700">() {<br />        echo </span><span style="color: #DD0000">"A"</span><span style="color: #007700">;<br />    }<br />}<br /><br /></span><span style="color: #9876AA">uopz_set_mock</span><span style="color: #007700">(</span><span style="color: #9876AA">A</span><span style="color: #007700">::class, new class {<br />                            const </span><span style="color: #9876AA">CON </span><span style="color: #007700">= </span><span style="color: #DD0000">'mockA'</span><span style="color: #007700">;<br />                            public static function </span><span style="color: #9876AA">who</span><span style="color: #007700">() {<br />                                echo </span><span style="color: #DD0000">"mockA"</span><span style="color: #007700">;<br />                            }<br />                        });<br />echo </span><span style="color: #9876AA">A</span><span style="color: #007700">::</span><span style="color: #9876AA">CON</span><span style="color: #007700">, </span><span style="color: #9876AA">PHP_EOL</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">A</span><span style="color: #007700">::</span><span style="color: #9876AA">who</span><span style="color: #007700">();<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>   </div>   <div class="example-contents"><p>以上示例会输出：</p></div>   <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>A<br>A<br></span></div>   </div>    <div class="example-contents"><p>     Output of the above example in uopz 5:    </p></div>   <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>mockA<br>mockA<br></span></div>   </div>  </div> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.uopz-set-mock-seealso">  <h3 class="title">参见</h3>  <ul class="simplelist">   <li class="member"><span class="function">{@link uopz_get_mock()} - Get the current mock for a class</span></li>   <li class="member"><span class="function">{@link uopz_unset_mock()} - Unset previously set mock</span></li>  </ul> </div></div>
 * @return void*/
function uopz_set_mock(...$arguments): void {}

/**
*<div id="function.uopz-set-property" class="refentry"> <div class="refnamediv">  <h1 class="refname">uopz_set_property</h1>  <p class="verinfo">(PECL uopz 5, PECL uopz 6, PECL uopz 7)</p><p class="refpurpose"><span class="refname">uopz_set_property</span> &mdash; <span class="dc-title">Sets value of existing class or instance property</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.uopz-set-property-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>uopz_set_property</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$class</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$property</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/language.types.declarations.php#language.types.declarations.mixed" class="type mixed" style="color:#EAB766">mixed</a></span> <span class="parameter" style="color:#3A95FF">$value</span></span>): <span class="type" style="color:#EAB766"><span class="type void" style="color:#EAB766">void</span></span></div>  <div class="methodsynopsis dc-description"><span class="methodname" style="color:#CC7832"><strong>uopz_set_property</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">object</span> <span class="parameter" style="color:#3A95FF">$instance</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$property</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/language.types.declarations.php#language.types.declarations.mixed" class="type mixed" style="color:#EAB766">mixed</a></span> <span class="parameter" style="color:#3A95FF">$value</span></span>): <span class="type" style="color:#EAB766"><span class="type void" style="color:#EAB766">void</span></span></div>  <p class="para rdfs-comment">   Sets the value of an existing static class property, if <span class="parameter" style="color:#3A95FF">class</span>   is given, or the value of an instance property (regardless whether the instance property already exists), if <span class="parameter" style="color:#3A95FF">instance</span>   is given.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.uopz-set-property-parameters">  <h3 class="title">参数</h3>  <dl>       <dt><span class="parameter" style="color:#3A95FF">class</span></dt>    <dd>     <span>      The name of the class.     </span>    </dd>          <dt><span class="parameter" style="color:#3A95FF">instance</span></dt>    <dd>     <p class="para">      The object instance.     </p>    </dd>          <dt><span class="parameter" style="color:#3A95FF">property</span></dt>    <dd>     <p class="para">      The name of the property.     </p>    </dd>          <dt><span class="parameter" style="color:#3A95FF">value</span></dt>    <dd>     <p class="para">      The value to assign to the property.     </p>    </dd>     </dl> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.uopz-set-property-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   没有返回值。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.uopz-set-property-examples">  <h3 class="title">示例</h3>  <div class="example" id="example-1910">   <p><strong>示例 #1 Basic <span class="function"><strong style="color:#CC7832">uopz_set_property()</strong></span> Usage</strong></p>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /></span><span style="color: #007700">class </span><span style="color: #9876AA">Foo </span><span style="color: #007700">{<br />   private static </span><span style="color: #9876AA">$staticBar</span><span style="color: #007700">;<br />   private </span><span style="color: #9876AA">$bar</span><span style="color: #007700">;<br />   public static function </span><span style="color: #9876AA">testStaticBar</span><span style="color: #007700">() {<br />      return </span><span style="color: #9876AA">self</span><span style="color: #007700">::</span><span style="color: #9876AA">$staticBar</span><span style="color: #007700">;<br />   }<br />   public function </span><span style="color: #9876AA">testBar</span><span style="color: #007700">() {<br />      return </span><span style="color: #9876AA">$this</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">bar</span><span style="color: #007700">;<br />   }<br />}<br /></span><span style="color: #9876AA">$foo </span><span style="color: #007700">= new </span><span style="color: #9876AA">Foo</span><span style="color: #007700">;<br /></span><span style="color: #9876AA">uopz_set_property</span><span style="color: #007700">(</span><span style="color: #DD0000">'Foo'</span><span style="color: #007700">, </span><span style="color: #DD0000">'staticBar'</span><span style="color: #007700">, </span><span style="color: #9876AA">10</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">uopz_set_property</span><span style="color: #007700">(</span><span style="color: #9876AA">$foo</span><span style="color: #007700">, </span><span style="color: #DD0000">'bar'</span><span style="color: #007700">, </span><span style="color: #9876AA">100</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">Foo</span><span style="color: #007700">::</span><span style="color: #9876AA">testStaticBar</span><span style="color: #007700">());<br /></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">$foo</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">testBar</span><span style="color: #007700">());<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>   </div>   <div class="example-contents"><p>以上示例会输出：</p></div>   <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>int(10)<br><br></span></div>   </div>  </div> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.uopz-set-property-seealso">  <h3 class="title">参见</h3>  <ul class="simplelist">   <li class="member"><span class="function">{@link uopz_get_property()} - Gets value of class or instance property</span></li>  </ul> </div></div>
 * @return void*/
function uopz_set_property(...$arguments): void {}

/**
*<div id="function.uopz-set-return" class="refentry"> <div class="refnamediv">  <h1 class="refname">uopz_set_return</h1>  <p class="verinfo">(PECL uopz 5, PECL uopz 6, PECL uopz 7)</p><p class="refpurpose"><span class="refname">uopz_set_return</span> &mdash; <span class="dc-title">Provide a return value for an existing function</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.uopz-set-return-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>uopz_set_return</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$function</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/language.types.declarations.php#language.types.declarations.mixed" class="type mixed" style="color:#EAB766">mixed</a></span> <span class="parameter" style="color:#3A95FF">$value</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">bool</span> <span class="parameter" style="color:#3A95FF">$execute</span><span class="initializer"> = <strong><span>false</span></strong></span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <div class="methodsynopsis dc-description"><span class="methodname" style="color:#CC7832"><strong>uopz_set_return</strong></span>(<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$class</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$function</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://www.php.net/manual/zh/language.types.declarations.php#language.types.declarations.mixed" class="type mixed" style="color:#EAB766">mixed</a></span> <span class="parameter" style="color:#3A95FF">$value</span></span>,<br>&nbsp;&nbsp;&nbsp;&nbsp;<span class="methodparam"><span class="type" style="color:#EAB766">bool</span> <span class="parameter" style="color:#3A95FF">$execute</span><span class="initializer"> = <strong><span>false</span></strong></span></span><br>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment">   Sets the return value of the <span class="parameter" style="color:#3A95FF">function</span> to <span class="parameter" style="color:#3A95FF">value</span>. If <span class="parameter" style="color:#3A95FF">value</span>   is a Closure and <span class="parameter" style="color:#3A95FF">execute</span> is set, the Closure will be executed in place of the original function.   It is possible to call the original function from the Closure.  </p>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    <p class="para">    This function replaces <span class="methodname" style="color:#CC7832">{@link uopz_rename()}</span>.   </p>  </p></blockquote> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.uopz-set-return-parameters">  <h3 class="title">参数</h3>  <dl>       <dt><span class="parameter" style="color:#3A95FF">class</span></dt>    <dd>     <span> The name of the class containing the function     </span>    </dd>          <dt><span class="parameter" style="color:#3A95FF">function</span></dt>    <dd>     <p class="para"> The name of an existing function     </p>    </dd>          <dt><span class="parameter" style="color:#3A95FF">value</span></dt>    <dd>     <p class="para"> The value the function should return. If a Closure is provided and the execute flag is set, the Closure will be executed in place of the original function.     </p>    </dd>          <dt><span class="parameter" style="color:#3A95FF">execute</span></dt>    <dd>     <p class="para"> If true, and a Closure was provided as the value, the Closure will be executed in place of the original function.     </p>    </dd>     </dl> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.uopz-set-return-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">  True if succeeded, false otherwise.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.uopz-set-return-examples">  <h3 class="title">示例</h3>  <div class="example" id="example-1913">   <p><strong>示例 #1 <span class="function"><strong style="color:#CC7832">uopz_set_return()</strong></span> example</strong></p>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />uopz_set_return</span><span style="color: #007700">(</span><span style="color: #DD0000">"strlen"</span><span style="color: #007700">, </span><span style="color: #9876AA">42</span><span style="color: #007700">);<br />echo </span><span style="color: #9876AA">strlen</span><span style="color: #007700">(</span><span style="color: #DD0000">"Banana"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>   </div>   <div class="example-contents"><p>以上示例会输出：</p></div>   <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>42<br></span></div>   </div>  </div>  <div class="example" id="example-1916">   <p><strong>示例 #2 <span class="function"><strong style="color:#CC7832">uopz_set_return()</strong></span> example</strong></p>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br />uopz_set_return</span><span style="color: #007700">(</span><span style="color: #DD0000">"strlen"</span><span style="color: #007700">, function(</span><span style="color: #9876AA">$str</span><span style="color: #007700">) { return </span><span style="color: #9876AA">strlen</span><span style="color: #007700">(</span><span style="color: #9876AA">$str</span><span style="color: #007700">) * </span><span style="color: #9876AA">2</span><span style="color: #007700">; }, </span><span style="color: #9876AA">true </span><span style="color: #007700">);<br />echo </span><span style="color: #9876AA">strlen</span><span style="color: #007700">(</span><span style="color: #DD0000">"Banana"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>   </div>   <div class="example-contents"><p>以上示例会输出：</p></div>   <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>12<br></span></div>   </div>  </div>  <div class="example" id="example-1919">   <p><strong>示例 #3 <span class="function"><strong style="color:#CC7832">uopz_set_return()</strong></span> class example</strong></p>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /></span><span style="color: #007700">class </span><span style="color: #9876AA">My </span><span style="color: #007700">{<br />    public static function </span><span style="color: #9876AA">strlen</span><span style="color: #007700">(</span><span style="color: #9876AA">$arg</span><span style="color: #007700">) {<br />        return </span><span style="color: #9876AA">strlen</span><span style="color: #007700">(</span><span style="color: #9876AA">$arg</span><span style="color: #007700">);<br />    }<br />}<br /></span><span style="color: #9876AA">uopz_set_return</span><span style="color: #007700">(</span><span style="color: #9876AA">My</span><span style="color: #007700">::class, </span><span style="color: #DD0000">"strlen"</span><span style="color: #007700">, function(</span><span style="color: #9876AA">$str</span><span style="color: #007700">) { return </span><span style="color: #9876AA">strlen</span><span style="color: #007700">(</span><span style="color: #9876AA">$str</span><span style="color: #007700">) * </span><span style="color: #9876AA">2</span><span style="color: #007700">; }, </span><span style="color: #9876AA">true </span><span style="color: #007700">);<br />echo </span><span style="color: #9876AA">My</span><span style="color: #007700">::</span><span style="color: #9876AA">strlen</span><span style="color: #007700">(</span><span style="color: #DD0000">"Banana"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>   </div>   <div class="example-contents"><p>以上示例会输出：</p></div>   <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>12<br></span></div>   </div>  </div> </div></div>
 * @return bool*/
function uopz_set_return(...$arguments): bool {}

/**
*<div id="function.uopz-set-static" class="refentry"> <div class="refnamediv">  <h1 class="refname">uopz_set_static</h1>  <p class="verinfo">(PECL uopz 5, PECL uopz , PECL uopz 7)</p><p class="refpurpose"><span class="refname">uopz_set_static</span> &mdash; <span class="dc-title">Sets the static variables in function or method scope</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.uopz-set-static-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>uopz_set_static</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$function</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">array</span> <span class="parameter" style="color:#3A95FF">$static</span></span>): <span class="type" style="color:#EAB766"><span class="type void" style="color:#EAB766">void</span></span></div>  <div class="methodsynopsis dc-description"><span class="methodname" style="color:#CC7832"><strong>uopz_set_static</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$class</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$function</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">array</span> <span class="parameter" style="color:#3A95FF">$static</span></span>): <span class="type" style="color:#EAB766"><span class="type void" style="color:#EAB766">void</span></span></div>  <p class="para rdfs-comment">   Sets the static variables in function or method scope.  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.uopz-set-static-parameters">  <h3 class="title">参数</h3>  <dl>       <dt><span class="parameter" style="color:#3A95FF">class</span></dt>    <dd>     <span>      The name of the class.     </span>    </dd>          <dt><span class="parameter" style="color:#3A95FF">function</span></dt>    <dd>     <p class="para">      The name of the function or method.     </p>    </dd>          <dt><span class="parameter" style="color:#3A95FF">static</span></dt>    <dd>     <p class="para">      The associative <span class="type" style="color:#EAB766">array</span> of variable names mapped to their      values.     </p>    </dd>     </dl> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-function.uopz-set-static-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">    没有返回值。  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 examples" id="refsect1-function.uopz-set-static-examples">  <h3 class="title">示例</h3>  <div class="example" id="example-1922">   <p><strong>示例 #1 Basic <span class="function"><strong style="color:#CC7832">uopz_set_static()</strong></span> Usage</strong></p>   <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#232525"><span><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br /></span><span style="color: #007700">function </span><span style="color: #9876AA">foo</span><span style="color: #007700">() {<br />    static </span><span style="color: #9876AA">$bar </span><span style="color: #007700">= </span><span style="color: #DD0000">'baz'</span><span style="color: #007700">;<br />    </span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">$bar</span><span style="color: #007700">);<br />}<br /></span><span style="color: #9876AA">uopz_set_static</span><span style="color: #007700">(</span><span style="color: #DD0000">'foo'</span><span style="color: #007700">, [</span><span style="color: #DD0000">'bar' </span><span style="color: #007700">=&gt; </span><span style="color: #DD0000">'qux'</span><span style="color: #007700">]);<br /></span><span style="color: #9876AA">foo</span><span style="color: #007700">();<br /></span><span style="color: #9876AA">?&gt;</span></span></span></div>   </div>   <div class="example-contents"><p>以上示例会输出：</p></div>   <div class="example-contents screen" style="color:AFB1B3;background:black;padding-left:5px;"><div class="cdata"><span>string(3) &quot;qux&quot;<br></span></div>   </div>  </div> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-function.uopz-set-static-seealso">  <h3 class="title">参见</h3>  <ul class="simplelist">   <li class="member"><span class="function">{@link uopz_get_static()} - Gets the static variables from function or method scope</span></li>  </ul> </div></div>
 * @return void*/
function uopz_set_static(...$arguments): void {}

/**
*<div id="function.uopz-undefine" class="refentry"> <div class="refnamediv">  <h1 class="refname">uopz_undefine</h1>  <p class="verinfo">(PECL uopz 1, PECL uopz 2, PECL uopz 5, PECL uopz 6, PECL uopz 7)</p><p class="refpurpose"><span class="refname">uopz_undefine</span> &mdash; <span class="dc-title">Undefine a constant</span></p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-function.uopz-undefine-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>uopz_undefine</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$constant</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <div class="methodsynopsis dc-description"><span class="methodname" style="color:#CC7832"><strong>uopz_undefine</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$class</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766">string</span> <span class="parameter" style="color:#3A95FF">$constant</span></span>): <span class="type" style="color:#EAB766">bool</span></div>  <p class="para rdfs-comment"> Removes the constant at runtime  </p> </div> <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-function.uopz-undefine-parameters">  <h3 class="title">参数</h3>  <dl>       <dt><span class="parameter" style="color:#3A95FF">class</span></dt>    <dd>     <span> The name of the class containing <span class="parameter" style="color:#3A95FF">constant</span>     </span>    </dd>          <dt><span class="parameter" style="color:#3A95FF">constant</span></dt>    <dd>     <p class="para"> The name of an existing constant